import { test, expect, devices } from '@playwright/test';

test.describe('Cross-Browser Compatibility', () => {
  const browsers = ['chromium', 'firefox', 'webkit'];
  
  browsers.forEach(browserName => {
    test.describe(`${browserName} compatibility`, () => {
      test.beforeEach(async ({ page }) => {
        await page.goto('/');
      });

      test(`should load homepage correctly in ${browserName}`, async ({ page }) => {
        // Check basic page elements
        await expect(page.locator('[data-testid="header"]')).toBeVisible();
        await expect(page.locator('[data-testid="navigation"]')).toBeVisible();
        await expect(page.locator('[data-testid="footer"]')).toBeVisible();
        
        // Check hero section
        await expect(page.locator('[data-testid="hero-section"]')).toBeVisible();
        
        // Check featured products
        await expect(page.locator('[data-testid="featured-products"]')).toBeVisible();
      });

      test(`should handle navigation in ${browserName}`, async ({ page }) => {
        // Test main navigation links
        await page.click('text=Products');
        await expect(page).toHaveURL('/products');
        
        await page.click('text=About');
        await expect(page).toHaveURL('/about');
        
        await page.click('text=Contact');
        await expect(page).toHaveURL('/contact');
        
        // Test logo click returns to home
        await page.click('[data-testid="logo"]');
        await expect(page).toHaveURL('/');
      });

      test(`should handle forms correctly in ${browserName}`, async ({ page }) => {
        // Test contact form
        await page.goto('/contact');
        
        await page.fill('[data-testid="contact-name"]', 'Test User');
        await page.fill('[data-testid="contact-email"]', 'test@example.com');
        await page.fill('[data-testid="contact-message"]', 'Test message');
        
        await page.click('[data-testid="contact-submit"]');
        
        // Should show success message
        await expect(page.locator('text=Message sent successfully')).toBeVisible();
      });

      test(`should handle responsive design in ${browserName}`, async ({ page }) => {
        // Test desktop view
        await page.setViewportSize({ width: 1200, height: 800 });
        await expect(page.locator('[data-testid="desktop-nav"]')).toBeVisible();
        
        // Test tablet view
        await page.setViewportSize({ width: 768, height: 1024 });
        await page.waitForTimeout(500);
        
        // Test mobile view
        await page.setViewportSize({ width: 375, height: 667 });
        await page.waitForTimeout(500);
        
        // Mobile menu should be visible
        const mobileMenuBtn = page.locator('[data-testid="mobile-menu-btn"]');
        if (await mobileMenuBtn.isVisible()) {
          await mobileMenuBtn.click();
          await expect(page.locator('[data-testid="mobile-menu"]')).toBeVisible();
        }
      });

      test(`should handle JavaScript interactions in ${browserName}`, async ({ page }) => {
        // Test cart functionality
        await page.goto('/products');
        await page.locator('[data-testid^="product-card-"]').first().click();
        
        // Add to cart
        await page.click('[data-testid="add-to-cart-btn"]');
        
        // Cart badge should update
        await expect(page.locator('[data-testid="cart-badge"]')).toBeVisible();
        
        // Open cart drawer
        await page.click('[data-testid="cart-icon"]');
        await expect(page.locator('[data-testid="cart-drawer"]')).toBeVisible();
      });

      test(`should handle CSS animations in ${browserName}`, async ({ page }) => {
        // Test hover effects
        const productCard = page.locator('[data-testid^="product-card-"]').first();
        await page.goto('/products');
        
        // Hover over product card
        await productCard.hover();
        
        // Should have hover state (this is basic - actual animation testing would need more sophisticated checks)
        await expect(productCard).toBeVisible();
        
        // Test loading animations
        await page.goto('/products?category=fiction');
        
        // Should show loading state briefly
        const loadingSpinner = page.locator('[data-testid="loading-spinner"]');
        // Note: Loading might be too fast to catch, so we just check it doesn't error
      });

      test(`should handle local storage in ${browserName}`, async ({ page }) => {
        // Add item to cart (should use localStorage for guest users)
        await page.goto('/products');
        await page.locator('[data-testid^="product-card-"]').first().click();
        await page.click('[data-testid="add-to-cart-btn"]');
        
        // Refresh page
        await page.reload();
        
        // Cart should persist
        await expect(page.locator('[data-testid="cart-badge"]')).toBeVisible();
      });

      test(`should handle cookies in ${browserName}`, async ({ page }) => {
        // Accept cookies if banner appears
        const cookieBanner = page.locator('[data-testid="cookie-banner"]');
        if (await cookieBanner.isVisible()) {
          await page.click('[data-testid="accept-cookies"]');
          await expect(cookieBanner).not.toBeVisible();
        }
        
        // Refresh page - cookie banner should not appear again
        await page.reload();
        await expect(cookieBanner).not.toBeVisible();
      });

      test(`should handle images correctly in ${browserName}`, async ({ page }) => {
        await page.goto('/products');
        
        // Check that product images load
        const productImages = page.locator('[data-testid^="product-image-"]');
        const count = await productImages.count();
        
        if (count > 0) {
          const firstImage = productImages.first();
          await expect(firstImage).toBeVisible();
          
          // Check that image has loaded (has src attribute)
          const src = await firstImage.getAttribute('src');
          expect(src).toBeTruthy();
        }
      });

      test(`should handle fonts correctly in ${browserName}`, async ({ page }) => {
        // Check that custom fonts are loaded
        await page.goto('/');
        
        const heading = page.locator('h1').first();
        if (await heading.isVisible()) {
          const fontFamily = await heading.evaluate(el => 
            window.getComputedStyle(el).fontFamily
          );
          
          // Should not be using default system fonts only
          expect(fontFamily).toBeTruthy();
        }
      });

      test(`should handle print styles in ${browserName}`, async ({ page }) => {
        await page.goto('/products');
        await page.locator('[data-testid^="product-card-"]').first().click();
        
        // Emulate print media
        await page.emulateMedia({ media: 'print' });
        
        // Page should still be readable
        await expect(page.locator('[data-testid="product-title"]')).toBeVisible();
        
        // Reset to screen media
        await page.emulateMedia({ media: 'screen' });
      });
    });
  });

  test.describe('Mobile Device Compatibility', () => {
    const mobileDevices = [
      devices['iPhone 12'],
      devices['Pixel 5'],
      devices['iPad'],
    ];

    mobileDevices.forEach(device => {
      test(`should work on ${device.name}`, async ({ browser }) => {
        const context = await browser.newContext({
          ...device,
        });
        const page = await context.newPage();
        
        await page.goto('/');
        
        // Basic functionality should work
        await expect(page.locator('[data-testid="header"]')).toBeVisible();
        
        // Touch interactions should work
        await page.goto('/products');
        const productCard = page.locator('[data-testid^="product-card-"]').first();
        await productCard.tap();
        
        // Should navigate to product page
        await expect(page).toHaveURL(/\/products\/[^\/]+$/);
        
        await context.close();
      });
    });
  });

  test.describe('Accessibility Compatibility', () => {
    test('should be keyboard navigable', async ({ page }) => {
      await page.goto('/');
      
      // Tab through navigation
      await page.keyboard.press('Tab');
      await page.keyboard.press('Tab');
      await page.keyboard.press('Tab');
      
      // Should be able to activate links with Enter
      await page.keyboard.press('Enter');
      
      // Should navigate somewhere
      await page.waitForTimeout(1000);
      expect(page.url()).not.toBe('/');
    });

    test('should have proper ARIA labels', async ({ page }) => {
      await page.goto('/');
      
      // Check for ARIA landmarks
      await expect(page.locator('[role="navigation"]')).toBeVisible();
      await expect(page.locator('[role="main"]')).toBeVisible();
      
      // Check for ARIA labels on interactive elements
      const buttons = page.locator('button');
      const count = await buttons.count();
      
      for (let i = 0; i < Math.min(count, 5); i++) {
        const button = buttons.nth(i);
        const ariaLabel = await button.getAttribute('aria-label');
        const text = await button.textContent();
        
        // Button should have either aria-label or text content
        expect(ariaLabel || text).toBeTruthy();
      }
    });

    test('should have proper heading hierarchy', async ({ page }) => {
      await page.goto('/');
      
      // Should have h1
      await expect(page.locator('h1')).toBeVisible();
      
      // Check heading hierarchy (h1 -> h2 -> h3, etc.)
      const headings = await page.locator('h1, h2, h3, h4, h5, h6').all();
      
      if (headings.length > 1) {
        // Basic check that we have headings
        expect(headings.length).toBeGreaterThan(0);
      }
    });

    test('should have sufficient color contrast', async ({ page }) => {
      await page.goto('/');
      
      // This is a basic test - full color contrast testing would require specialized tools
      // We just check that text is visible and readable
      const textElements = page.locator('p, span, div').filter({ hasText: /\w+/ });
      const count = await textElements.count();
      
      if (count > 0) {
        const firstText = textElements.first();
        await expect(firstText).toBeVisible();
        
        // Check that text has color (not transparent)
        const color = await firstText.evaluate(el => 
          window.getComputedStyle(el).color
        );
        expect(color).not.toBe('rgba(0, 0, 0, 0)');
      }
    });
  });

  test.describe('Performance Compatibility', () => {
    test('should load within acceptable time limits', async ({ page }) => {
      const startTime = Date.now();
      
      await page.goto('/');
      
      const loadTime = Date.now() - startTime;
      
      // Should load within 5 seconds (generous for E2E testing)
      expect(loadTime).toBeLessThan(5000);
    });

    test('should handle slow network conditions', async ({ page }) => {
      // Simulate slow 3G
      await page.route('**/*', route => {
        setTimeout(() => route.continue(), 100);
      });
      
      await page.goto('/');
      
      // Should still load successfully
      await expect(page.locator('[data-testid="header"]')).toBeVisible({ timeout: 10000 });
    });

    test('should handle offline conditions gracefully', async ({ page }) => {
      await page.goto('/');
      
      // Go offline
      await page.context().setOffline(true);
      
      // Try to navigate
      await page.click('text=Products');
      
      // Should show offline message or cached content
      const offlineMessage = page.locator('text=You are offline');
      const cachedContent = page.locator('[data-testid="product-grid"]');
      
      // Either offline message or cached content should be visible
      await expect(offlineMessage.or(cachedContent)).toBeVisible({ timeout: 5000 });
      
      // Go back online
      await page.context().setOffline(false);
    });
  });
});

import { test, expect } from '@playwright/test';

test.describe('Shopping Flow', () => {
  test.beforeEach(async ({ page }) => {
    await page.goto('/');
  });

  test('should browse products and view details', async ({ page }) => {
    // Navigate to products page
    await page.click('text=Products');
    await expect(page).toHaveURL('/products');

    // Should show product grid
    await expect(page.locator('[data-testid="product-grid"]')).toBeVisible();
    
    // Should have at least one product
    const productCards = page.locator('[data-testid^="product-card-"]');
    await expect(productCards.first()).toBeVisible();

    // Click on first product to view details
    await productCards.first().click();
    
    // Should navigate to product detail page
    await expect(page).toHaveURL(/\/products\/[^\/]+$/);
    
    // Should show product details
    await expect(page.locator('[data-testid="product-title"]')).toBeVisible();
    await expect(page.locator('[data-testid="product-price"]')).toBeVisible();
    await expect(page.locator('[data-testid="product-description"]')).toBeVisible();
    await expect(page.locator('[data-testid="add-to-cart-btn"]')).toBeVisible();
  });

  test('should search for products', async ({ page }) => {
    // Use search functionality
    await page.fill('[data-testid="search-input"]', 'fiction');
    await page.press('[data-testid="search-input"]', 'Enter');

    // Should show search results
    await expect(page).toHaveURL(/\/products\?.*search=fiction/);
    await expect(page.locator('[data-testid="search-results"]')).toBeVisible();
    
    // Results should contain search term
    const productTitles = page.locator('[data-testid^="product-title-"]');
    const count = await productTitles.count();
    
    if (count > 0) {
      const firstTitle = await productTitles.first().textContent();
      expect(firstTitle?.toLowerCase()).toContain('fiction');
    }
  });

  test('should filter products by category', async ({ page }) => {
    await page.goto('/products');
    
    // Click on category filter
    await page.click('[data-testid="category-filter-fiction"]');
    
    // Should update URL and show filtered results
    await expect(page).toHaveURL(/\/products\?.*category=fiction/);
    
    // All visible products should be in fiction category
    const categoryBadges = page.locator('[data-testid^="product-category-"]');
    const count = await categoryBadges.count();
    
    for (let i = 0; i < count; i++) {
      const categoryText = await categoryBadges.nth(i).textContent();
      expect(categoryText?.toLowerCase()).toBe('fiction');
    }
  });

  test('should filter products by price range', async ({ page }) => {
    await page.goto('/products');
    
    // Set price range filter
    await page.fill('[data-testid="price-min"]', '10');
    await page.fill('[data-testid="price-max"]', '50');
    await page.click('[data-testid="apply-filters"]');
    
    // Should show products within price range
    const prices = page.locator('[data-testid^="product-price-"]');
    const count = await prices.count();
    
    for (let i = 0; i < count; i++) {
      const priceText = await prices.nth(i).textContent();
      const price = parseFloat(priceText?.replace(/[^0-9.]/g, '') || '0');
      expect(price).toBeGreaterThanOrEqual(10);
      expect(price).toBeLessThanOrEqual(50);
    }
  });

  test('should sort products', async ({ page }) => {
    await page.goto('/products');
    
    // Sort by price ascending
    await page.selectOption('[data-testid="sort-select"]', 'price_asc');
    
    // Wait for results to update
    await page.waitForTimeout(1000);
    
    // Verify sorting
    const prices = page.locator('[data-testid^="product-price-"]');
    const count = await prices.count();
    
    if (count > 1) {
      const firstPrice = parseFloat((await prices.first().textContent())?.replace(/[^0-9.]/g, '') || '0');
      const lastPrice = parseFloat((await prices.last().textContent())?.replace(/[^0-9.]/g, '') || '0');
      expect(firstPrice).toBeLessThanOrEqual(lastPrice);
    }
  });

  test('should handle pagination', async ({ page }) => {
    await page.goto('/products');
    
    // Check if pagination exists
    const pagination = page.locator('[data-testid="pagination"]');
    
    if (await pagination.isVisible()) {
      const nextButton = page.locator('[data-testid="next-page"]');
      
      if (await nextButton.isVisible() && !(await nextButton.isDisabled())) {
        // Click next page
        await nextButton.click();
        
        // Should update URL with page parameter
        await expect(page).toHaveURL(/\/products\?.*page=2/);
        
        // Should show different products
        await expect(page.locator('[data-testid="product-grid"]')).toBeVisible();
      }
    }
  });

  test('should show product availability', async ({ page }) => {
    await page.goto('/products');
    
    const productCard = page.locator('[data-testid^="product-card-"]').first();
    await productCard.click();
    
    // Should show stock status
    const stockStatus = page.locator('[data-testid="stock-status"]');
    await expect(stockStatus).toBeVisible();
    
    const statusText = await stockStatus.textContent();
    expect(statusText).toMatch(/In Stock|Out of Stock|Limited Stock/);
  });

  test('should handle out of stock products', async ({ page }) => {
    // This test assumes there's at least one out of stock product
    await page.goto('/products');
    
    // Look for out of stock product
    const outOfStockProduct = page.locator('[data-testid="product-card"]:has-text("Out of Stock")').first();
    
    if (await outOfStockProduct.isVisible()) {
      await outOfStockProduct.click();
      
      // Add to cart button should be disabled
      const addToCartBtn = page.locator('[data-testid="add-to-cart-btn"]');
      await expect(addToCartBtn).toBeDisabled();
      
      // Should show out of stock message
      await expect(page.locator('text=Out of Stock')).toBeVisible();
    }
  });

  test('should show related products', async ({ page }) => {
    await page.goto('/products');
    
    const productCard = page.locator('[data-testid^="product-card-"]').first();
    await productCard.click();
    
    // Should show related products section
    const relatedProducts = page.locator('[data-testid="related-products"]');
    
    if (await relatedProducts.isVisible()) {
      const relatedItems = page.locator('[data-testid^="related-product-"]');
      expect(await relatedItems.count()).toBeGreaterThan(0);
    }
  });

  test('should handle product images', async ({ page }) => {
    await page.goto('/products');
    
    const productCard = page.locator('[data-testid^="product-card-"]').first();
    await productCard.click();
    
    // Should show product image
    const productImage = page.locator('[data-testid="product-image"]');
    await expect(productImage).toBeVisible();
    
    // Image should be loaded
    await expect(productImage).toHaveAttribute('src', /.+/);
    
    // Test image gallery if multiple images exist
    const imageGallery = page.locator('[data-testid="image-gallery"]');
    
    if (await imageGallery.isVisible()) {
      const thumbnails = page.locator('[data-testid^="thumbnail-"]');
      const count = await thumbnails.count();
      
      if (count > 1) {
        // Click on second thumbnail
        await thumbnails.nth(1).click();
        
        // Main image should change
        await page.waitForTimeout(500);
        const newImageSrc = await productImage.getAttribute('src');
        expect(newImageSrc).toBeTruthy();
      }
    }
  });
});

import React, { Component, ErrorInfo, ReactNode } from 'react';
import { toast } from 'sonner';
import { errorHandler } from '../lib/services/ErrorHandler';
import { Button } from '@/components/ui/button';
import { AlertTriangle, RefreshCw } from 'lucide-react';

interface Props {
  children: ReactNode;
  fallback?: ReactNode;
  onError?: (error: Error, errorInfo: ErrorInfo) => void;
}

interface State {
  hasError: boolean;
  error?: Error;
  errorInfo?: ErrorInfo;
  retryCount: number;
}

/**
 * Enhanced Error Boundary with recovery options and proper error logging
 * Replaces console.error with structured error handling
 */
export class ErrorBoundary extends Component<Props, State> {
  private maxRetries = 3;

  constructor(props: Props) {
    super(props);
    this.state = {
      hasError: false,
      retryCount: 0
    };
  }

  static getDerivedStateFromError(error: Error): State {
    return {
      hasError: true,
      error,
      retryCount: 0
    };
  }

  componentDidCatch(error: Error, errorInfo: ErrorInfo) {
    // Log error using our centralized error handler instead of console.error
    errorHandler.error(error, {
      component: 'ErrorBoundary',
      action: 'component_error',
      additionalData: {
        componentStack: errorInfo.componentStack,
        errorBoundary: true,
        retryCount: this.state.retryCount
      }
    });

    // Call custom error handler if provided
    if (this.props.onError) {
      this.props.onError(error, errorInfo);
    }

    // Show user-friendly error notification
    toast.error('Something went wrong. Please try refreshing the page.');

    this.setState({
      error,
      errorInfo
    });
  }

  handleRetry = () => {
    const newRetryCount = this.state.retryCount + 1;
    
    if (newRetryCount <= this.maxRetries) {
      errorHandler.info(`Error boundary retry attempt ${newRetryCount}`, {
        component: 'ErrorBoundary',
        action: 'retry',
        additionalData: {
          retryCount: newRetryCount,
          maxRetries: this.maxRetries
        }
      });

      this.setState({
        hasError: false,
        error: undefined,
        errorInfo: undefined,
        retryCount: newRetryCount
      });
    } else {
      errorHandler.warn('Maximum retry attempts reached', {
        component: 'ErrorBoundary',
        action: 'max_retries_reached',
        additionalData: {
          retryCount: newRetryCount,
          maxRetries: this.maxRetries
        }
      });
      
      toast.error('Unable to recover. Please refresh the page manually.');
    }
  };

  handleRefresh = () => {
    errorHandler.info('User initiated page refresh from error boundary', {
      component: 'ErrorBoundary',
      action: 'manual_refresh'
    });
    
    window.location.reload();
  };

  render() {
    if (this.state.hasError) {
      // Use custom fallback if provided
      if (this.props.fallback) {
        return this.props.fallback;
      }

      // Default error UI
      return (
        <div className="min-h-screen flex items-center justify-center bg-gray-50 px-4">
          <div className="max-w-md w-full bg-white rounded-lg shadow-lg p-6 text-center">
            <div className="flex justify-center mb-4">
              <AlertTriangle className="h-12 w-12 text-red-500" />
            </div>
            
            <h1 className="text-xl font-semibold text-gray-900 mb-2">
              Something went wrong
            </h1>
            
            <p className="text-gray-600 mb-6">
              We're sorry, but something unexpected happened. Our team has been notified.
            </p>

            {import.meta.env.DEV && this.state.error && (
              <div className="mb-6 p-4 bg-red-50 rounded-lg text-left">
                <h3 className="text-sm font-medium text-red-800 mb-2">
                  Error Details (Development)
                </h3>
                <pre className="text-xs text-red-700 overflow-auto max-h-32">
                  {this.state.error.message}
                  {this.state.error.stack}
                </pre>
              </div>
            )}

            <div className="flex flex-col sm:flex-row gap-3 justify-center">
              {this.state.retryCount < this.maxRetries && (
                <Button
                  onClick={this.handleRetry}
                  variant="default"
                  className="flex items-center gap-2"
                >
                  <RefreshCw className="h-4 w-4" />
                  Try Again ({this.maxRetries - this.state.retryCount} left)
                </Button>
              )}
              
              <Button
                onClick={this.handleRefresh}
                variant="outline"
                className="flex items-center gap-2"
              >
                <RefreshCw className="h-4 w-4" />
                Refresh Page
              </Button>
            </div>

            <p className="text-xs text-gray-500 mt-4">
              Error ID: {Date.now().toString(36)}
            </p>
          </div>
        </div>
      );
    }

    return this.props.children;
  }
}

/**
 * Higher-order component to wrap components with error boundary
 */
export function withErrorBoundary<P extends object>(
  Component: React.ComponentType<P>,
  fallback?: ReactNode,
  onError?: (error: Error, errorInfo: ErrorInfo) => void
) {
  const WrappedComponent = (props: P) => (
    <ErrorBoundary fallback={fallback} onError={onError}>
      <Component {...props} />
    </ErrorBoundary>
  );

  WrappedComponent.displayName = `withErrorBoundary(${Component.displayName || Component.name})`;
  
  return WrappedComponent;
}

/**
 * Hook for handling errors in functional components
 */
export function useErrorHandler() {
  const handleError = React.useCallback((error: Error, context?: any) => {
    errorHandler.error(error, {
      component: 'useErrorHandler',
      additionalData: context
    });
  }, []);

  return { handleError };
}

export default ErrorBoundary;

import React, { Component, ErrorInfo, ReactNode } from 'react';
import ErrorPage from '../pages/ErrorPage';
import sentryService, { ErrorContext } from '../lib/services/sentryService';
import { errorHandler } from '../lib/services/ErrorHandler';

interface Props {
  children: ReactNode;
  fallback?: ReactNode;
  errorContext?: Partial<ErrorContext>;
}

interface State {
  hasError: boolean;
  error: Error | null;
  errorInfo: ErrorInfo | null;
  errorType: 'network' | 'server' | 'forbidden' | 'timeout' | 'chunk' | 'generic';
  retryCount: number;
  errorId: string | null;
}

class ErrorHandler extends Component<Props, State> {
  private maxRetries = 3;
  private retryTimeouts: number[] = [1000, 3000, 5000]; // Progressive retry delays

  constructor(props: Props) {
    super(props);
    this.state = {
      hasError: false,
      error: null,
      errorInfo: null,
      errorType: 'generic',
      retryCount: 0,
      errorId: null
    };
  }

  static getDerivedStateFromError(error: Error): Partial<State> {
    // Determine error type based on error message or properties
    let errorType: State['errorType'] = 'generic';
    
    if (error.message.includes('Network') || error.message.includes('fetch')) {
      errorType = 'network';
    } else if (error.message.includes('500') || error.message.includes('Internal Server')) {
      errorType = 'server';
    } else if (error.message.includes('403') || error.message.includes('Forbidden')) {
      errorType = 'forbidden';
    } else if (error.message.includes('timeout') || error.message.includes('408')) {
      errorType = 'timeout';
    } else if (error.message.includes('Loading chunk') || error.message.includes('ChunkLoadError')) {
      errorType = 'chunk';
    }

    // Generate unique error ID for tracking
    const errorId = `err_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

    return {
      hasError: true,
      error,
      errorType,
      errorId
    };
  }

  componentDidCatch(error: Error, errorInfo: ErrorInfo) {
    this.setState({
      error,
      errorInfo
    });

    // Log error to Sentry with enhanced context
    this.logErrorToService(error, errorInfo);
  }

  private logErrorToService = (error: Error, errorInfo: ErrorInfo) => {
    const context: ErrorContext = {
      ...this.props.errorContext,
      page: window.location.pathname,
      component: 'ErrorBoundary',
      additionalData: {
        errorId: this.state.errorId,
        retryCount: this.state.retryCount,
        errorType: this.state.errorType,
        componentStack: errorInfo.componentStack,
        timestamp: new Date().toISOString(),
        userAgent: navigator.userAgent,
        url: window.location.href,
        referrer: document.referrer,
        viewport: {
          width: window.innerWidth,
          height: window.innerHeight
        },
        connection: (navigator as any).connection ? {
          effectiveType: (navigator as any).connection.effectiveType,
          downlink: (navigator as any).connection.downlink,
          rtt: (navigator as any).connection.rtt
        } : null
      }
    };

    // Log to Sentry
    sentryService.captureError(error, context);

    // Add breadcrumb for error boundary activation
    sentryService.addBreadcrumb(
      `Error boundary caught error: ${error.message}`,
      'error',
      'error',
      {
        errorType: this.state.errorType,
        errorId: this.state.errorId,
        retryCount: this.state.retryCount
      }
    );

    // Use our centralized error handler for development logging
    if (import.meta.env.DEV) {
      errorHandler.error(error, {
        component: 'ErrorBoundary',
        action: 'component_error_caught',
        additionalData: {
          ...context.additionalData,
          errorInfo,
          isDevelopment: true
        }
      });
    }
  };

  private handleRetry = () => {
    const { retryCount } = this.state;
    
    if (retryCount >= this.maxRetries) {
      sentryService.captureMessage(
        `Max retry attempts reached for error: ${this.state.error?.message}`,
        'warning',
        {
          errorId: this.state.errorId,
          retryCount,
          additionalData: { maxRetries: this.maxRetries }
        }
      );
      return;
    }

    // Add breadcrumb for retry attempt
    sentryService.addBreadcrumb(
      `Retrying error recovery (attempt ${retryCount + 1}/${this.maxRetries})`,
      'user',
      'info',
      {
        errorId: this.state.errorId,
        errorType: this.state.errorType,
        retryCount: retryCount + 1
      }
    );

    // Handle chunk loading errors by reloading the page
    if (this.state.errorType === 'chunk') {
      window.location.reload();
      return;
    }

    // Progressive retry with delay
    const delay = this.retryTimeouts[retryCount] || this.retryTimeouts[this.retryTimeouts.length - 1];
    
    setTimeout(() => {
      this.setState({
        hasError: false,
        error: null,
        errorInfo: null,
        errorType: 'generic',
        retryCount: retryCount + 1,
        errorId: null
      });
    }, delay);
  };

  private handleReload = () => {
    sentryService.addBreadcrumb(
      'User initiated page reload from error boundary',
      'user',
      'info',
      {
        errorId: this.state.errorId,
        errorType: this.state.errorType
      }
    );
    
    window.location.reload();
  };

  private shouldShowRetry = (): boolean => {
    const { errorType, retryCount } = this.state;
    
    // Always allow retry for chunk errors (they usually resolve with reload)
    if (errorType === 'chunk') return true;
    
    // Allow retry for network and timeout errors
    if (errorType === 'network' || errorType === 'timeout') {
      return retryCount < this.maxRetries;
    }
    
    // Allow limited retries for server errors
    if (errorType === 'server') {
      return retryCount < 2;
    }
    
    // No retry for forbidden errors
    if (errorType === 'forbidden') return false;
    
    // Default: allow one retry for generic errors
    return retryCount < 1;
  };

  render() {
    if (this.state.hasError) {
      // Custom fallback UI
      if (this.props.fallback) {
        return this.props.fallback;
      }

      // Default error page with enhanced functionality
      return (
        <ErrorPage
          errorType={this.state.errorType}
          errorMessage={this.state.error?.message}
          errorId={this.state.errorId}
          showRetry={this.shouldShowRetry()}
          onRetry={this.handleRetry}
          onReload={this.handleReload}
          retryCount={this.state.retryCount}
          maxRetries={this.maxRetries}
        />
      );
    }

    return this.props.children;
  }
}

export default ErrorHandler;

// Hook for functional components to handle errors
export const useErrorHandler = () => {
  const [error, setError] = React.useState<Error | null>(null);

  const resetError = () => setError(null);

  const captureError = (error: Error, context?: ErrorContext) => {
    // Log to Sentry before throwing
    sentryService.captureError(error, {
      ...context,
      component: 'useErrorHandler',
      page: window.location.pathname
    });
    
    setError(error);
  };

  React.useEffect(() => {
    if (error) {
      throw error;
    }
  }, [error]);

  return { captureError, resetError };
};

// Higher-order component for wrapping components with error handling
export const withErrorHandler = <P extends object>(
  Component: React.ComponentType<P>,
  fallback?: ReactNode,
  errorContext?: Partial<ErrorContext>
) => {
  const WrappedComponent = (props: P) => (
    <ErrorHandler fallback={fallback} errorContext={errorContext}>
      <Component {...props} />
    </ErrorHandler>
  );
  
  // Preserve component name for debugging
  WrappedComponent.displayName = `withErrorHandler(${Component.displayName || Component.name})`;
  
  return WrappedComponent;
};

// Utility function to handle async errors with Sentry integration
export const handleAsyncError = async <T,>(
  asyncFn: () => Promise<T>,
  context?: ErrorContext,
  onError?: (error: Error) => void
): Promise<T | null> => {
  try {
    return await asyncFn();
  } catch (error) {
    const err = error instanceof Error ? error : new Error('Unknown error');
    
    // Log to Sentry
    sentryService.captureError(err, {
      ...context,
      action: 'async_operation'
    });
    
    if (onError) {
      onError(err);
    }
    
    return null;
  }
};

// Enhanced network error handler with Sentry integration
export const handleNetworkError = (error: any, context?: ErrorContext) => {
  let errorMessage = '';
  let errorType = 'network';

  if (!navigator.onLine) {
    errorMessage = 'Network error: You appear to be offline. Please check your internet connection.';
  } else if (error.name === 'AbortError') {
    errorMessage = 'Network error: Request was cancelled or timed out.';
    errorType = 'timeout';
  } else if (error.status === 0) {
    errorMessage = 'Network error: Unable to connect to server. Please check your internet connection.';
  } else if (error.status >= 500) {
    errorMessage = 'Server error: Our servers are experiencing issues. Please try again later.';
    errorType = 'server';
  } else if (error.status === 403) {
    errorMessage = 'Forbidden: You don\'t have permission to access this resource.';
    errorType = 'forbidden';
  } else if (error.status === 404) {
    errorMessage = 'Not found: The requested resource could not be found.';
  } else {
    errorMessage = `Network error: ${error.message || 'An unexpected error occurred'}`;
  }

  const networkError = new Error(errorMessage);
  
  // Log to Sentry with network context
  sentryService.captureError(networkError, {
    ...context,
    action: 'network_request',
    additionalData: {
      errorType,
      status: error.status,
      statusText: error.statusText,
      url: error.url || error.config?.url,
      method: error.config?.method,
      online: navigator.onLine,
      connectionType: (navigator as any).connection?.effectiveType
    }
  });

  throw networkError;
};

// Retry wrapper for network operations
export const withRetry = async <T,>(
  operation: () => Promise<T>,
  maxRetries: number = 3,
  delay: number = 1000,
  context?: ErrorContext
): Promise<T> => {
  let lastError: Error;
  
  for (let attempt = 1; attempt <= maxRetries; attempt++) {
    try {
      return await operation();
    } catch (error) {
      lastError = error instanceof Error ? error : new Error(String(error));
      
      if (attempt === maxRetries) {
        sentryService.captureError(lastError, {
          ...context,
          action: 'retry_failed',
          additionalData: {
            attempts: attempt,
            maxRetries
          }
        });
        throw lastError;
      }
      
      // Log retry attempt
      sentryService.addBreadcrumb(
        `Retry attempt ${attempt}/${maxRetries} failed`,
        'retry',
        'warning',
        {
          error: lastError.message,
          attempt,
          maxRetries,
          nextDelay: delay * attempt
        }
      );
      
      // Progressive delay
      await new Promise(resolve => setTimeout(resolve, delay * attempt));
    }
  }
  
  throw lastError!;
};

