        data: { session: { user: mockUser } },
        error: null
      });
      
      mockSupabaseFrom.then
        .mockResolvedValueOnce({ data: { ...mockUser, role: 'customer' }, error: null }) // Auth user fetch
        .mockResolvedValueOnce({ data: [], error: null }) // Initial cart load
