        expect(screen.getByTestId('checkout-total')).toHaveTextContent(`Total: $${(mockCartItem.quantity * mockCartItem.price).toFixed(2)}`);
      });

      // Complete checkout
      const checkoutButton = screen.getByTestId('complete-checkout');
      await user.click(checkoutButton);

      // Verify cart is cleared
      await waitFor(() => {
        expect(screen.getByTestId('total-items')).toHaveTextContent('Items: 0');
      });
    });

    it('should handle guest checkout flow', async () => {
      const user = userEvent.setup();
      
      (supabase.auth.getSession as any).mockResolvedValue({
        data: { session: null },
        error: null
      });
      
      localStorageMock.getItem.mockReturnValue(JSON.stringify([mockCartItem]));

      render(
        <TestWrapper>
          <MockCartSummary />
          <MockCheckoutFlow />
        </TestWrapper>
      );

      // Wait for guest cart to load
      await waitFor(() => {
        expect(screen.getByTestId('guest-checkout')).toBeInTheDocument();
        expect(screen.getByTestId('checkout-total')).toHaveTextContent(`Total: $${(mockCartItem.quantity * mockCartItem.price).toFixed(2)}`);
      });

      // Complete checkout
      const checkoutButton = screen.getByTestId('complete-checkout');
      await user.click(checkoutButton);

      // Verify cart is cleared
      await waitFor(() => {
        expect(screen.getByTestId('total-items')).toHaveTextContent('Items: 0');
      });
    });
  });

  describe('Error Handling Integration', () => {
    it('should handle database errors gracefully', async () => {
      const user = userEvent.setup();
      
      (supabase.auth.getSession as any).mockResolvedValue({
        data: { session: { user: mockUser } },
        error: null
      });
      
      mockSupabaseFrom.then
        .mockResolvedValueOnce({ data: { ...mockUser, role: 'customer' }, error: null })
        .mockResolvedValueOnce({ data: null, error: new Error('Database error') });

      render(
        <TestWrapper>
          <MockProductCard product={mockProduct} />
          <MockCartSummary />
        </TestWrapper>
      );

      // Wait for error handling
      await waitFor(() => {
        expect(screen.getByTestId('total-items')).toHaveTextContent('Items: 0');
      });

      // Try to add product - should handle error gracefully
      const addButton = screen.getByTestId(`add-to-cart-${mockProduct.id}`);
      await user.click(addButton);

      // Cart should remain empty due to error
      expect(screen.getByTestId('total-items')).toHaveTextContent('Items: 0');
    });

    it('should handle network errors with retry', async () => {
      const user = userEvent.setup();
      
      (supabase.auth.getSession as any).mockResolvedValue({
        data: { session: { user: mockUser } },
        error: null
      });
      
      mockSupabaseFrom.then
        .mockResolvedValueOnce({ data: { ...mockUser, role: 'customer' }, error: null })
        .mockResolvedValueOnce({ data: [], error: null })
        .mockRejectedValueOnce(new Error('Network error'))
        .mockResolvedValueOnce({ data: [mockCartItem], error: null });

      render(
        <TestWrapper>
          <MockProductCard product={mockProduct} />
          <MockCartSummary />
        </TestWrapper>
      );

      await waitFor(() => {
        expect(screen.getByTestId('total-items')).toHaveTextContent('Items: 0');
      });

      // First attempt should fail
      const addButton = screen.getByTestId(`add-to-cart-${mockProduct.id}`);
      await user.click(addButton);

      // Retry should succeed
      await user.click(addButton);

      await waitFor(() => {
        expect(screen.getByTestId('total-items')).toHaveTextContent('Items: 2');
      });
    });
  });
});

import React, { useState, useCallback } from 'react';
import { supabase } from '@/integrations/supabase/client';
import { Button } from '@/components/ui/button';
import { Upload, X, Image } from 'lucide-react';

interface ImageUploadProps {
  images: string[];
  onImagesChange: (images: string[]) => void;
  maxImages?: number;
  disabled?: boolean;
}

const ImageUpload: React.FC<ImageUploadProps> = ({ 
  images, 
  onImagesChange, 
  maxImages = 5,
  disabled = false
}) => {
  const [uploading, setUploading] = useState(false);
  const [dragActive, setDragActive] = useState(false);

  const uploadImage = async (file: File): Promise<string> => {
    const fileExt = file.name.split('.').pop();
    const fileName = `${Date.now()}-${Math.random().toString(36).substring(2)}.${fileExt}`;
    
    const { data, error } = await supabase.storage
      .from('product-images')
      .upload(fileName, file);

    if (error) throw error;

    const { data: { publicUrl } } = supabase.storage
      .from('product-images')
      .getPublicUrl(fileName);

    return publicUrl;
  };

  const handleFileUpload = async (files: FileList) => {
    if (disabled) return;
    
    if (images.length + files.length > maxImages) {
      alert(`Maximum ${maxImages} images allowed`);
      return;
    }

    setUploading(true);
    try {
      const uploadPromises = Array.from(files).map(uploadImage);
      const newUrls = await Promise.all(uploadPromises);
      onImagesChange([...images, ...newUrls]);
    } catch (error) {
      console.error('Error uploading images:', error);
      alert('Error uploading images');
    } finally {
      setUploading(false);
    }
  };

  const handleDrop = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    setDragActive(false);
    
    if (disabled) return;
    
    const files = e.dataTransfer.files;
    if (files.length > 0) {
      handleFileUpload(files);
    }
  }, [images, maxImages, disabled]);

  const handleDragOver = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    if (!disabled) {
      setDragActive(true);
    }
  }, [disabled]);

  const handleDragLeave = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    setDragActive(false);
  }, []);

  const removeImage = (index: number) => {
    if (disabled) return;
    const newImages = images.filter((_, i) => i !== index);
    onImagesChange(newImages);
  };

  return (
    <div className="space-y-4">
      <label className="block text-sm font-medium text-gray-700">
        Product Images ({images.length}/{maxImages})
      </label>
      
      {/* Upload Area */}
      <div
        className={`border-2 border-dashed rounded-lg p-6 text-center transition-colors ${
          dragActive && !disabled
            ? 'border-naaz-green bg-green-50' 
            : disabled
            ? 'border-gray-200 bg-gray-50'
            : 'border-gray-300 hover:border-gray-400'
        }`}
        onDrop={handleDrop}
        onDragOver={handleDragOver}
        onDragLeave={handleDragLeave}
      >
        <Upload className={`h-8 w-8 mx-auto mb-2 ${disabled ? 'text-gray-300' : 'text-gray-400'}`} />
        <p className={`text-sm mb-2 ${disabled ? 'text-gray-400' : 'text-gray-600'}`}>
          {disabled ? 'Image upload disabled' : 'Drag and drop images here, or click to select'}
        </p>
        <input
          type="file"
          multiple
          accept="image/*"
          onChange={(e) => e.target.files && handleFileUpload(e.target.files)}
          className="hidden"
          id="image-upload"
          disabled={uploading || images.length >= maxImages || disabled}
        />
        <Button
          type="button"
          variant="outline"
          onClick={() => document.getElementById('image-upload')?.click()}
          disabled={uploading || images.length >= maxImages || disabled}
        >
          {uploading ? 'Uploading...' : 'Select Images'}
        </Button>
      </div>

      {/* Image Preview Grid */}
      {images.length > 0 && (
        <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
          {images.map((url, index) => (
            <div key={index} className="relative group">
              <img
                src={url}
                alt={`Product image ${index + 1}`}
                className="w-full h-24 object-cover rounded border"
              />
              {!disabled && (
                <button
                  type="button"
                  onClick={() => removeImage(index)}
                  className="absolute -top-2 -right-2 bg-red-500 text-white rounded-full p-1 opacity-0 group-hover:opacity-100 transition-opacity"
                >
                  <X className="h-3 w-3" />
                </button>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ImageUpload;

import React, { useState } from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Badge } from '@/components/ui/badge';
import { useOrderDetails, useAddOrderNote, useUpdateOrderItems } from '@/lib/hooks/admin/useOrderDetails';
import { useUpdateOrderStatus } from '@/lib/hooks/admin/useAdminOrders';
import { Package, User, MapPin, Clock, FileText, Edit, Save, X } from 'lucide-react';
import { format } from 'date-fns';

interface OrderDetailsModalProps {
  orderId: string;
  isOpen: boolean;
  onClose: () => void;
}

const OrderDetailsModal = ({ orderId, isOpen, onClose }: OrderDetailsModalProps) => {
  const { data, isLoading } = useOrderDetails(orderId);
  const addOrderNote = useAddOrderNote();
  const updateOrderStatus = useUpdateOrderStatus();
  const updateOrderItems = useUpdateOrderItems();

  const [newNote, setNewNote] = useState('');
  const [editingStatus, setEditingStatus] = useState(false);
  const [newStatus, setNewStatus] = useState('');
  const [trackingNumber, setTrackingNumber] = useState('');
  const [editingItems, setEditingItems] = useState(false);
  const [itemUpdates, setItemUpdates] = useState<Record<string, { quantity: number; price: number }>>({});

  if (isLoading || !data) {
    return (
      <Dialog open={isOpen} onOpenChange={onClose}>
        <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
          <div className="flex items-center justify-center h-64">
            <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-naaz-green"></div>
          </div>
        </DialogContent>
      </Dialog>
    );
  }

  const { order, statusHistory, notes } = data;

  const handleAddNote = async () => {
    if (!newNote.trim()) return;
    
    try {
      await addOrderNote.mutateAsync({
        orderId,
        note: newNote,
        isInternal: true
      });
      setNewNote('');
    } catch (error) {
      console.error('Error adding note:', error);
    }
  };

  const handleStatusUpdate = async () => {
    if (!newStatus) return;

    try {
      await updateOrderStatus.mutateAsync({
        orderId,
        status: newStatus as any,
        trackingNumber: trackingNumber || undefined
      });
      setEditingStatus(false);
      setNewStatus('');
      setTrackingNumber('');
    } catch (error) {
      console.error('Error updating status:', error);
    }
  };

  const handleSaveItemUpdates = async () => {
    const updates = Object.entries(itemUpdates).map(([itemId, update]) => ({
      id: itemId,
      ...update
    }));

    if (updates.length === 0) {
      setEditingItems(false);
      return;
    }

    try {
      await updateOrderItems.mutateAsync({
        orderId,
        items: updates
      });
      setEditingItems(false);
      setItemUpdates({});
    } catch (error) {
      console.error('Error updating items:', error);
    }
  };

  const getStatusColor = (status: string) => {
    const colors: Record<string, string> = {
      pending: 'bg-yellow-100 text-yellow-800',
      processing: 'bg-blue-100 text-blue-800',
      shipped: 'bg-purple-100 text-purple-800',
      delivered: 'bg-green-100 text-green-800',
      cancelled: 'bg-red-100 text-red-800',
      refunded: 'bg-gray-100 text-gray-800'
    };
    return colors[status] || 'bg-gray-100 text-gray-800';
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-6xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center justify-between">
            <div className="flex items-center space-x-2">
              <Package className="h-5 w-5" />
              <span>Order #{order.order_number || order.id.slice(0, 8)}</span>
            </div>
            <Badge className={getStatusColor(order.status)}>
              {order.status.charAt(0).toUpperCase() + order.status.slice(1)}
            </Badge>
          </DialogTitle>
        </DialogHeader>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Main Order Info */}
          <div className="lg:col-span-2 space-y-6">
            {/* Order Items */}
            <div className="bg-white rounded-lg border p-4">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-lg font-semibold flex items-center">
                  <Package className="h-4 w-4 mr-2" />
                  Order Items
                </h3>
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => {
                    if (editingItems) {
                      setEditingItems(false);
                      setItemUpdates({});
                    } else {
                      setEditingItems(true);
                      // Initialize item updates with current values
                      const initialUpdates: Record<string, { quantity: number; price: number }> = {};
                      order.order_items?.forEach(item => {
                        initialUpdates[item.id] = {
                          quantity: item.quantity,
                          price: item.price
                        };
                      });
                      setItemUpdates(initialUpdates);
                    }
                  }}
                >
                  {editingItems ? <X className="h-4 w-4" /> : <Edit className="h-4 w-4" />}
                  {editingItems ? 'Cancel' : 'Edit'}
                </Button>
              </div>

              <div className="space-y-3">
                {order.order_items?.map((item) => (
                  <div key={item.id} className="flex items-center justify-between p-3 border rounded">
                    <div className="flex items-center space-x-3">
                      {item.products?.images?.[0] && (
                        <img
                          src={item.products.images[0]}
                          alt={item.products?.name}
                          className="h-12 w-12 object-cover rounded"
                        />
                      )}
                      <div>
                        <h4 className="font-medium">{item.products?.name}</h4>
                        <p className="text-sm text-gray-500">SKU: {item.product_id.slice(0, 8)}</p>
                      </div>
                    </div>
                    
                    <div className="flex items-center space-x-4">
                      {editingItems ? (
                        <>
                          <div className="flex items-center space-x-2">
                            <label className="text-sm">Qty:</label>
                            <Input
                              type="number"
                              min="1"
                              value={itemUpdates[item.id]?.quantity || item.quantity}
                              onChange={(e) => setItemUpdates(prev => ({
                                ...prev,
                                [item.id]: {
                                  ...prev[item.id],
                                  quantity: parseInt(e.target.value) || 1
                                }
                              }))}
                              className="w-16"
                            />
                          </div>
                          <div className="flex items-center space-x-2">
                            <label className="text-sm"></label>
                            <Input
                              type="number"
                              step="0.01"
                              value={itemUpdates[item.id]?.price || item.price}
                              onChange={(e) => setItemUpdates(prev => ({
                                ...prev,
                                [item.id]: {
                                  ...prev[item.id],
                                  price: parseFloat(e.target.value) || 0
                                }
                              }))}
                              className="w-20"
                            />
                          </div>
                        </>
                      ) : (
                        <>
                          <span className="text-sm">Qty: {item.quantity}</span>
                          <span className="font-medium">{item.price}</span>
                        </>
                      )}
                    </div>
                  </div>
                ))}
              </div>

              {editingItems && (
                <div className="flex justify-end space-x-2 mt-4">
                  <Button onClick={handleSaveItemUpdates} disabled={updateOrderItems.isPending}>
                    <Save className="h-4 w-4 mr-2" />
                    Save Changes
                  </Button>
                </div>
              )}

              <div className="text-right mt-4 pt-4 border-t">
                <span className="text-lg font-bold">Total: {order.total}</span>
              </div>
            </div>

            {/* Customer & Shipping Info */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div className="bg-white rounded-lg border p-4">
                <h3 className="text-lg font-semibold flex items-center mb-3">
                  <User className="h-4 w-4 mr-2" />
                  Customer Details
                </h3>
                <div className="space-y-2">
                  <p><strong>Order Date:</strong> {format(new Date(order.created_at!), 'PPp')}</p>
                  <p><strong>Payment Method:</strong> {order.payment_method?.toUpperCase()}</p>
                  <p><strong>Payment Status:</strong> {order.payment_status}</p>
                  {order.upi_reference_code && (
                    <p><strong>UPI Reference:</strong> {order.upi_reference_code}</p>
                  )}
                </div>
              </div>

              <div className="bg-white rounded-lg border p-4">
                <h3 className="text-lg font-semibold flex items-center mb-3">
                  <MapPin className="h-4 w-4 mr-2" />
                  Shipping Address
                </h3>
                {order.shipping_address && (
                  <div className="space-y-1 text-sm">
                    <p>{(order.shipping_address as any).name}</p>
                    <p>{(order.shipping_address as any).line1}</p>
                    {(order.shipping_address as any).line2 && <p>{(order.shipping_address as any).line2}</p>}
                    <p>{(order.shipping_address as any).city}, {(order.shipping_address as any).state}</p>
                    <p>{(order.shipping_address as any).postalCode}</p>
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* Sidebar */}
          <div className="space-y-6">
            {/* Status Management */}
            <div className="bg-white rounded-lg border p-4">
              <h3 className="text-lg font-semibold mb-3">Status Management</h3>
              
              {editingStatus ? (
                <div className="space-y-3">
                  <select
                    value={newStatus}
                    onChange={(e) => setNewStatus(e.target.value)}
                    className="w-full p-2 border rounded"
                  >
                    <option value="">Select Status</option>
                    <option value="pending">Pending</option>
                    <option value="processing">Processing</option>
                    <option value="shipped">Shipped</option>
                    <option value="delivered">Delivered</option>
                    <option value="cancelled">Cancelled</option>
                    <option value="refunded">Refunded</option>
                  </select>
                  
                  <Input
                    placeholder="Tracking Number (optional)"
                    value={trackingNumber}
                    onChange={(e) => setTrackingNumber(e.target.value)}
                  />
                  
                  <div className="flex space-x-2">
                    <Button onClick={handleStatusUpdate} disabled={updateOrderStatus.isPending}>
                      Save
                    </Button>
                    <Button variant="outline" onClick={() => setEditingStatus(false)}>
                      Cancel
                    </Button>
                  </div>
                </div>
              ) : (
                <Button onClick={() => setEditingStatus(true)} className="w-full">
                  Update Status
                </Button>
              )}
            </div>

            {/* Status History */}
            <div className="bg-white rounded-lg border p-4">
              <h3 className="text-lg font-semibold flex items-center mb-3">
                <Clock className="h-4 w-4 mr-2" />
                Status History
              </h3>
              <div className="space-y-3 max-h-48 overflow-y-auto">
                {statusHistory?.map((history) => (
                  <div key={history.id} className="text-sm border-l-2 border-gray-200 pl-3">
                    <div className="flex justify-between items-start">
                      <Badge className={getStatusColor(history.status)} variant="secondary">
                        {history.status}
                      </Badge>
                      <span className="text-xs text-gray-500">
                        {format(new Date(history.created_at), 'MMM dd, HH:mm')}
                      </span>
                    </div>
                    {history.notes && <p className="text-gray-600 mt-1">{history.notes}</p>}
                  </div>
                ))}
              </div>
            </div>

            {/* Order Notes */}
            <div className="bg-white rounded-lg border p-4">
              <h3 className="text-lg font-semibold flex items-center mb-3">
                <FileText className="h-4 w-4 mr-2" />
                Order Notes
              </h3>
              
              <div className="space-y-3 mb-4 max-h-48 overflow-y-auto">
                {notes?.map((note) => (
                  <div key={note.id} className="text-sm p-2 bg-gray-50 rounded border-l-4 border-blue-200">
                    <p>{note.note}</p>
                    <p className="text-xs text-gray-500 mt-1">
                      {format(new Date(note.created_at), 'MMM dd, HH:mm')}
                    </p>
                  </div>
                ))}
              </div>

              <div className="space-y-2">
                <Textarea
                  placeholder="Add internal note..."
                  value={newNote}
                  onChange={(e) => setNewNote(e.target.value)}
                  rows={3}
                />
                <Button 
                  onClick={handleAddNote} 
                  disabled={addOrderNote.isPending || !newNote.trim()}
                  size="sm"
                  className="w-full"
                >
                  Add Note
                </Button>
              </div>
            </div>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
};

export default OrderDetailsModal;

import React from 'react';
import { Check } from 'lucide-react';

interface Step {
  id: number;
  title: string;
  icon: React.ComponentType<any>;
}

interface CheckoutStepsProps {
  steps: Step[];
  currentStep: number;
}

const CheckoutSteps: React.FC<CheckoutStepsProps> = ({ steps, currentStep }) => {
  return (
    <div className="flex items-center justify-center mb-8">
      {steps.map((step, index) => {
        const Icon = step.icon;
        const isCompleted = step.id < currentStep;
        const isCurrent = step.id === currentStep;
        
        return (
          <React.Fragment key={step.id}>
            <div className="flex flex-col items-center">
              <div
                className={`w-12 h-12 rounded-full flex items-center justify-center mb-2 ${
                  isCompleted
                    ? 'bg-naaz-green text-white'
                    : isCurrent
                    ? 'bg-naaz-gold text-white'
                    : 'bg-gray-200 text-gray-400'
                }`}
              >
                {isCompleted ? <Check size={20} /> : <Icon size={20} />}
              </div>
              <span
                className={`text-sm font-medium ${
                  isCompleted || isCurrent ? 'text-naaz-green' : 'text-gray-400'
                }`}
              >
                {step.title}
              </span>
            </div>
            {index < steps.length - 1 && (
              <div
                className={`w-20 h-0.5 mx-4 mt-6 ${
                  step.id < currentStep ? 'bg-naaz-green' : 'bg-gray-200'
                }`}
              />
            )}
          </React.Fragment>
        );
      })}
    </div>
  );
};

