import React, { useEffect, useState } from 'react';
import { AlertTriangle, Clock, Shield } from 'lucide-react';
import { Alert, AlertDescription, AlertTitle } from '../ui/alert';
import { Button } from '../ui/button';
import { Progress } from '../ui/progress';

interface RateLimitNotificationProps {
  isRateLimited: boolean;
  retryAfter?: number | null;
  resetTime?: Date | null;
  remaining?: number;
  maxRequests?: number;
  action?: string;
  onRetry?: () => void;
  onDismiss?: () => void;
  className?: string;
}

export const RateLimitNotification: React.FC<RateLimitNotificationProps> = ({
  isRateLimited,
  retryAfter,
  resetTime,
  remaining = 0,
  maxRequests = 100,
  action = 'this action',
  onRetry,
  onDismiss,
  className = ''
}) => {
  const [timeLeft, setTimeLeft] = useState<number>(retryAfter || 0);
  const [progress, setProgress] = useState<number>(0);

  useEffect(() => {
    if (!isRateLimited || !retryAfter) {
      setTimeLeft(0);
      return;
    }

    setTimeLeft(retryAfter);
    
    const interval = setInterval(() => {
      setTimeLeft(prev => {
        const newTime = Math.max(0, prev - 1);
        if (newTime === 0 && onRetry) {
          // Auto-retry when time is up
          setTimeout(onRetry, 100);
        }
        return newTime;
      });
    }, 1000);

    return () => clearInterval(interval);
  }, [isRateLimited, retryAfter, onRetry]);

  useEffect(() => {
    if (maxRequests > 0) {
      setProgress((remaining / maxRequests) * 100);
    }
  }, [remaining, maxRequests]);

  const formatTime = (seconds: number): string => {
    if (seconds < 60) {
      return `${seconds} second${seconds !== 1 ? 's' : ''}`;
    }
    
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    
    if (minutes < 60) {
      return remainingSeconds > 0 
        ? `${minutes}m ${remainingSeconds}s`
        : `${minutes} minute${minutes !== 1 ? 's' : ''}`;
    }
    
    const hours = Math.floor(minutes / 60);
    const remainingMinutes = minutes % 60;
    
    return remainingMinutes > 0
      ? `${hours}h ${remainingMinutes}m`
      : `${hours} hour${hours !== 1 ? 's' : ''}`;
  };

  const getAlertVariant = () => {
    if (isRateLimited) return 'destructive';
    if (remaining < maxRequests * 0.2) return 'default'; // Warning when < 20% remaining
    return 'default';
  };

  const getIcon = () => {
    if (isRateLimited) return <AlertTriangle className="h-4 w-4" />;
    if (remaining < maxRequests * 0.2) return <Clock className="h-4 w-4" />;
    return <Shield className="h-4 w-4" />;
  };

  const getTitle = () => {
    if (isRateLimited) return 'Rate Limit Exceeded';
    if (remaining < maxRequests * 0.2) return 'Rate Limit Warning';
    return 'Rate Limit Status';
  };

  const getDescription = () => {
    if (isRateLimited) {
      return `You've exceeded the rate limit for ${action}. Please wait ${formatTime(timeLeft)} before trying again.`;
    }
    
    if (remaining < maxRequests * 0.2) {
      return `You have ${remaining} requests remaining for ${action}. The limit will reset ${resetTime ? `at ${resetTime.toLocaleTimeString()}` : 'soon'}.`;
    }
    
    return `You have ${remaining} of ${maxRequests} requests remaining for ${action}.`;
  };

  // Don't show notification if not rate limited and plenty of requests remaining
  if (!isRateLimited && remaining > maxRequests * 0.8) {
    return null;
  }

  return (
    <Alert variant={getAlertVariant()} className={`${className} transition-all duration-300`}>
      {getIcon()}
      <AlertTitle className="flex items-center justify-between">
        {getTitle()}
        {onDismiss && (
          <Button
            variant="ghost"
            size="sm"
            onClick={onDismiss}
            className="h-6 w-6 p-0 hover:bg-transparent"
          >
            
          </Button>
        )}
      </AlertTitle>
      <AlertDescription className="space-y-3">
        <p>{getDescription()}</p>
        
        {/* Progress bar showing remaining requests */}
        {!isRateLimited && (
          <div className="space-y-1">
            <div className="flex justify-between text-xs text-muted-foreground">
              <span>Requests used</span>
              <span>{maxRequests - remaining} / {maxRequests}</span>
            </div>
            <Progress value={100 - progress} className="h-2" />
          </div>
        )}
        
        {/* Countdown timer when rate limited */}
        {isRateLimited && timeLeft > 0 && (
          <div className="space-y-2">
            <div className="flex items-center gap-2 text-sm">
              <Clock className="h-4 w-4" />
              <span>Time remaining: {formatTime(timeLeft)}</span>
            </div>
            <Progress 
              value={(timeLeft / (retryAfter || 1)) * 100} 
              className="h-2"
            />
          </div>
        )}
        
        {/* Action buttons */}
        <div className="flex gap-2 pt-2">
          {isRateLimited && timeLeft === 0 && onRetry && (
            <Button
              size="sm"
              onClick={onRetry}
              className="h-8"
            >
              Try Again
            </Button>
          )}
          
          {resetTime && (
            <div className="text-xs text-muted-foreground flex items-center">
              Resets at {resetTime.toLocaleTimeString()}
            </div>
          )}
        </div>
      </AlertDescription>
    </Alert>
  );
};

/**
 * Hook to manage rate limit notification state
 */
export const useRateLimitNotification = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [notificationData, setNotificationData] = useState<Partial<RateLimitNotificationProps>>({});

  const showNotification = (data: Partial<RateLimitNotificationProps>) => {
    setNotificationData(data);
    setIsVisible(true);
  };

  const hideNotification = () => {
    setIsVisible(false);
    setNotificationData({});
  };

  const updateNotification = (data: Partial<RateLimitNotificationProps>) => {
    setNotificationData(prev => ({ ...prev, ...data }));
  };

  return {
    isVisible,
    notificationData,
    showNotification,
    hideNotification,
    updateNotification
  };
};

export default RateLimitNotification;

/**
 * Monitoring Dashboard Component
 * Development-only dashboard for monitoring application health and performance
 */

import { useState, useEffect } from 'react';
import { healthCheckService } from '@/lib/services/HealthCheck';
import { productionMonitoring } from '@/lib/services/ProductionMonitoring';
import { criticalPathMonitoring } from '@/lib/services/CriticalPathMonitoring';

interface HealthStatus {
  status: 'healthy' | 'degraded' | 'unhealthy';
  timestamp: string;
  checks: any;
}

export const MonitoringDashboard = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [healthStatus, setHealthStatus] = useState<HealthStatus | null>(null);
  const [metrics, setMetrics] = useState<any[]>([]);
  const [pathSummaries, setPathSummaries] = useState<Map<string, any>>(new Map());

  useEffect(() => {
    if (isOpen) {
      // Refresh data when dashboard is open
      refreshData();
      const interval = setInterval(refreshData, 5000);
      return () => clearInterval(interval);
    }
  }, [isOpen]);

  const refreshData = async () => {
    try {
      const health = await healthCheckService.getHealthStatus();
      setHealthStatus(health);
      
      const currentMetrics = productionMonitoring.getMetrics();
      setMetrics(currentMetrics.slice(-10)); // Last 10 metrics
      
      const summaries = criticalPathMonitoring.getAllPathSummaries();
      setPathSummaries(summaries);
    } catch (error) {
      console.error('Failed to refresh monitoring data:', error);
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'healthy':
      case 'pass':
        return 'text-green-600';
      case 'degraded':
      case 'warn':
        return 'text-yellow-600';
      case 'unhealthy':
      case 'fail':
        return 'text-red-600';
      default:
        return 'text-gray-600';
    }
  };

  const formatDuration = (ms: number) => {
    if (ms < 1000) return `${ms.toFixed(0)}ms`;
    return `${(ms / 1000).toFixed(2)}s`;
  };

  if (!import.meta.env.DEV) {
    return null; // Only show in development
  }

  return (
    <>
      {/* Toggle Button */}
      <button
        onClick={() => setIsOpen(!isOpen)}
        className="fixed bottom-4 right-20 z-50 bg-blue-600 text-white p-3 rounded-full shadow-lg hover:bg-blue-700 transition-colors"
        title="Monitoring Dashboard"
      >
        
      </button>

      {/* Dashboard Panel */}
      {isOpen && (
        <div className="fixed bottom-20 right-4 z-50 bg-white border border-gray-300 rounded-lg shadow-xl w-96 max-h-96 overflow-y-auto">
          <div className="p-4 border-b border-gray-200 bg-gray-50">
            <div className="flex justify-between items-center">
              <h3 className="font-semibold text-gray-800">Monitoring Dashboard</h3>
              <button
                onClick={() => setIsOpen(false)}
                className="text-gray-500 hover:text-gray-700"
              >
                
              </button>
            </div>
          </div>

          <div className="p-4 space-y-4">
            {/* Health Status */}
            <div>
              <h4 className="font-medium text-gray-700 mb-2">Health Status</h4>
              {healthStatus ? (
                <div className="space-y-2">
                  <div className={`font-medium ${getStatusColor(healthStatus.status)}`}>
                    Overall: {healthStatus.status.toUpperCase()}
                  </div>
                  <div className="grid grid-cols-2 gap-2 text-sm">
                    {Object.entries(healthStatus.checks).map(([key, check]: [string, any]) => (
                      <div key={key} className="flex justify-between">
                        <span className="capitalize">{key}:</span>
                        <span className={getStatusColor(check.status)}>
                          {check.status}
                        </span>
                      </div>
                    ))}
                  </div>
                </div>
              ) : (
                <div className="text-gray-500">Loading...</div>
              )}
            </div>

            {/* Performance Metrics */}
            <div>
              <h4 className="font-medium text-gray-700 mb-2">Recent Metrics</h4>
              <div className="space-y-1 text-sm">
                {metrics.length > 0 ? (
                  metrics.map((metric, index) => (
                    <div key={index} className="flex justify-between">
                      <span className="truncate">{metric.name}:</span>
                      <span className="font-mono">
                        {formatDuration(metric.value)}
                      </span>
                    </div>
                  ))
                ) : (
                  <div className="text-gray-500">No metrics available</div>
                )}
              </div>
            </div>

            {/* Critical Paths */}
            <div>
              <h4 className="font-medium text-gray-700 mb-2">Critical Paths</h4>
              <div className="space-y-2 text-sm">
                {pathSummaries.size > 0 ? (
                  Array.from(pathSummaries.entries()).map(([path, summary]) => (
                    <div key={path} className="border border-gray-200 rounded p-2">
                      <div className="font-medium text-gray-800 capitalize">
                        {path.replace(/_/g, ' ')}
                      </div>
                      <div className="grid grid-cols-2 gap-1 text-xs text-gray-600">
                        <span>Duration: {formatDuration(summary.averageDuration)}</span>
                        <span>Success: {summary.successRate.toFixed(1)}%</span>
                      </div>
                    </div>
                  ))
                ) : (
                  <div className="text-gray-500">No path data available</div>
                )}
              </div>
            </div>

            {/* Actions */}
            <div className="pt-2 border-t border-gray-200">
              <div className="flex gap-2">
                <button
                  onClick={refreshData}
                  className="px-3 py-1 bg-blue-100 text-blue-700 rounded text-sm hover:bg-blue-200"
                >
                  Refresh
                </button>
                <button
                  onClick={() => {
                    console.log('Health Status:', healthStatus);
                    console.log('Metrics:', metrics);
                    console.log('Path Summaries:', pathSummaries);
                  }}
                  className="px-3 py-1 bg-gray-100 text-gray-700 rounded text-sm hover:bg-gray-200"
                >
                  Log Data
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default MonitoringDashboard;

import React from 'react';
import { useFormContext, FieldPath, FieldValues, Controller } from 'react-hook-form';
import { Label } from '../ui/label';
import { Input } from '../ui/input';
import { Textarea } from '../ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '../ui/select';
import { Checkbox } from '../ui/checkbox';
import { cn } from '@/lib/utils';
import { sanitizationService } from '@/lib/services/sanitizationService';

export interface FormFieldProps<T extends FieldValues = FieldValues> {
  name: FieldPath<T>;
  label?: string;
  placeholder?: string;
  description?: string;
  type?: 'text' | 'email' | 'password' | 'number' | 'tel' | 'url' | 'textarea' | 'select' | 'checkbox';
  options?: Array<{ value: string; label: string }>;
  required?: boolean;
  disabled?: boolean;
  className?: string;
  sanitize?: boolean;
  maxLength?: number;
  rows?: number;
}

export function FormField<T extends FieldValues = FieldValues>({
  name,
  label,
  placeholder,
  description,
  type = 'text',
  options = [],
  required = false,
  disabled = false,
  className,
  sanitize = true,
  maxLength,
  rows = 3,
}: FormFieldProps<T>) {
  const {
    control,
    formState: { errors },
  } = useFormContext<T>();

  const error = errors[name];
  const fieldId = `field-${name}`;

  const sanitizeValue = (value: string): string => {
    if (!sanitize || !value || typeof value !== 'string') {
      return value;
    }

    switch (type) {
      case 'email':
        return sanitizationService.sanitizeEmail(value);
      case 'tel':
        return sanitizationService.sanitizePhone(value);
      case 'url':
        return sanitizationService.sanitizeUrl(value);
      case 'textarea':
        return sanitizationService.sanitizeInput(value, maxLength);
      default:
        return sanitizationService.sanitizeInput(value, maxLength);
    }
  };

  const renderInput = (field: any) => {
    const commonProps = {
      ...field,
      id: fieldId,
      placeholder,
      disabled,
      className: cn(
        error && 'border-red-500 focus:border-red-500',
        className
      ),
      onChange: (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
        const value = e.target.value;
        const sanitizedValue = sanitizeValue(value);
        field.onChange(sanitizedValue);
      },
    };

    switch (type) {
      case 'textarea':
        return (
          <Textarea
            {...commonProps}
            rows={rows}
            maxLength={maxLength}
          />
        );

      case 'select':
        return (
          <Select
            value={field.value || ''}
            onValueChange={field.onChange}
            disabled={disabled}
          >
            <SelectTrigger
              id={fieldId}
              className={cn(
                error && 'border-red-500 focus:border-red-500',
                className
              )}
            >
              <SelectValue placeholder={placeholder} />
            </SelectTrigger>
            <SelectContent>
              {options.map((option) => (
                <SelectItem key={option.value} value={option.value}>
                  {option.label}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        );

      case 'checkbox':
        return (
          <div className="flex items-center space-x-2">
            <Checkbox
              id={fieldId}
              checked={field.value || false}
              onCheckedChange={field.onChange}
              disabled={disabled}
              className={cn(
                error && 'border-red-500',
                className
              )}
            />
            {label && (
              <Label
                htmlFor={fieldId}
                className={cn(
                  'text-sm font-medium leading-none peer-disabled:cursor-not-allowed peer-disabled:opacity-70',
                  error && 'text-red-500'
                )}
              >
                {label}
                {required && <span className="text-red-500 ml-1">*</span>}
              </Label>
            )}
          </div>
        );

      case 'number':
        return (
          <Input
            {...commonProps}
            type="number"
            onChange={(e) => {
              const value = e.target.value;
              const numValue = value === '' ? '' : parseFloat(value);
              field.onChange(numValue);
            }}
          />
        );

      default:
        return (
          <Input
            {...commonProps}
            type={type}
            maxLength={maxLength}
          />
        );
    }
  };

  return (
    <div className="space-y-2">
      {label && type !== 'checkbox' && (
        <Label
          htmlFor={fieldId}
          className={cn(
            'text-sm font-medium leading-none peer-disabled:cursor-not-allowed peer-disabled:opacity-70',
            error && 'text-red-500'
          )}
        >
          {label}
          {required && <span className="text-red-500 ml-1">*</span>}
        </Label>
      )}

      <Controller
        name={name}
        control={control}
        render={({ field }) => renderInput(field)}
      />

      {description && (
        <p className="text-sm text-gray-600">
          {description}
        </p>
      )}

      {error && (
        <p className="text-sm text-red-500 font-medium">
          {error.message as string}
        </p>
      )}
    </div>
  );
}

import React from 'react';
import { FormProvider, FieldValues } from 'react-hook-form';
import { ZodSchema } from 'zod';
import { useFormValidation, FormValidationConfig } from '@/lib/hooks/useFormValidation';
import { Button } from '@/components/ui/button';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Loader2, AlertCircle } from 'lucide-react';
import { cn } from '@/lib/utils';
import { FormField } from '@/components/forms/FormField';

export interface ValidatedFormProps<T extends FieldValues> {
  schema: ZodSchema<T>;
  onSubmit: (data: T) => Promise<void> | void;
  children: React.ReactNode;
  className?: string;
  submitButtonText?: string;
  submitButtonClassName?: string;
  showSubmitButton?: boolean;
  disabled?: boolean;
  validationConfig?: Partial<FormValidationConfig<T>>;
  defaultValues?: Partial<T>;
}

export function ValidatedForm<T extends FieldValues>({
  schema,
  onSubmit,
  children,
  className,
  submitButtonText = 'Submit',
  submitButtonClassName,
  showSubmitButton = true,
  disabled = false,
  validationConfig = {},
  defaultValues,
}: ValidatedFormProps<T>) {
  const form = useFormValidation<T>({
    schema,
    onSubmit,
    ...validationConfig,
  }, {
    defaultValues: defaultValues as any,
  });

  const {
    handleSubmitWithValidation,
    isSubmitting,
    submitError,
    clearSubmitError,
    formState: { isValid, isDirty }
  } = form;

  const handleFormSubmit = handleSubmitWithValidation(onSubmit);

  React.useEffect(() => {
    if (submitError) {
      // Clear error after 5 seconds
      const timer = setTimeout(clearSubmitError, 5000);
      return () => clearTimeout(timer);
    }
  }, [submitError, clearSubmitError]);

  return (
    <FormProvider {...form}>
      <form
        onSubmit={handleFormSubmit}
        className={cn('space-y-6', className)}
        noValidate
      >
        {submitError && (
          <Alert variant="destructive">
            <AlertCircle className="h-4 w-4" />
            <AlertDescription>
              {submitError}
            </AlertDescription>
          </Alert>
        )}

        {children}

        {showSubmitButton && (
          <Button
            type="submit"
            disabled={disabled || isSubmitting || (!isDirty && !isValid)}
            className={cn('w-full', submitButtonClassName)}
          >
            {isSubmitting && <Loader2 className="mr-2 h-4 w-4 animate-spin" />}
            {isSubmitting ? 'Processing...' : submitButtonText}
          </Button>
        )}
      </form>
    </FormProvider>
  );
}

// Specialized form components for common use cases

export interface ContactFormProps {
  onSubmit: (data: any) => Promise<void>;
  className?: string;
}

export function ContactForm({ onSubmit, className }: ContactFormProps) {
  const [schema, setSchema] = React.useState<ZodSchema | null>(null);

  React.useEffect(() => {
    import('@/lib/validation/schemas').then(({ contactFormSchema }) => {
      setSchema(contactFormSchema);
    });
  }, []);

  if (!schema) {
    return <div>Loading...</div>;
  }

  return (
    <ValidatedForm
      schema={schema}
      onSubmit={onSubmit}
      className={className}
      submitButtonText="Send Message"
      validationConfig={{
        sanitizationConfig: {
          name: { maxLength: 50 },
          email: { maxLength: 254 },
          phone: { maxLength: 20 },
          subject: { maxLength: 100 },
          message: { maxLength: 1000, stripTags: true },
        },
      }}
    >
      <FormField
        name="name"
        label="Full Name"
        placeholder="Enter your full name"
        required
        maxLength={50}
      />
      
      <FormField
        name="email"
        label="Email Address"
        type="email"
        placeholder="Enter your email address"
        required
        maxLength={254}
      />
      
      <FormField
        name="phone"
        label="Phone Number"
        type="tel"
        placeholder="Enter your phone number"
        maxLength={20}
      />
      
      <FormField
        name="subject"
        label="Subject"
        placeholder="Enter message subject"
        required
        maxLength={100}
      />
      
      <FormField
        name="category"
        label="Category"
        type="select"
        options={[
          { value: 'general', label: 'General Inquiry' },
          { value: 'support', label: 'Support' },
          { value: 'order', label: 'Order Related' },
          { value: 'feedback', label: 'Feedback' },
          { value: 'complaint', label: 'Complaint' },
        ]}
        required
      />
      
      <FormField
        name="message"
        label="Message"
        type="textarea"
        placeholder="Enter your message"
        required
        maxLength={1000}
        rows={5}
      />
    </ValidatedForm>
  );
}

export interface NewsletterFormProps {
