export interface AuthFormProps {
  type: 'signin' | 'signup' | 'forgot' | 'reset';
  onSubmit: (data: any) => Promise<void>;
  className?: string;
}

export function AuthForm({ type, onSubmit, className }: AuthFormProps) {
  const [schema, setSchema] = React.useState<ZodSchema | null>(null);

  React.useEffect(() => {
    import('@/lib/validation/schemas').then((schemas) => {
      switch (type) {
        case 'signin':
          setSchema(schemas.signInSchema);
          break;
        case 'signup':
          setSchema(schemas.signUpSchema);
          break;
        case 'forgot':
          setSchema(schemas.forgotPasswordSchema);
          break;
        case 'reset':
          setSchema(schemas.resetPasswordSchema);
          break;
      }
    });
  }, [type]);

  if (!schema) {
    return <div>Loading...</div>;
  }

  const getSubmitText = () => {
    switch (type) {
      case 'signin':
        return 'Sign In';
      case 'signup':
        return 'Create Account';
      case 'forgot':
        return 'Send Reset Link';
      case 'reset':
        return 'Reset Password';
      default:
        return 'Submit';
    }
  };

  return (
    <ValidatedForm
      schema={schema}
      onSubmit={onSubmit}
      className={className}
      submitButtonText={getSubmitText()}
      validationConfig={{
        sanitizationConfig: {
          email: { maxLength: 254 },
          fullName: { maxLength: 50 },
          password: { maxLength: 128 },
          confirmPassword: { maxLength: 128 },
        },
        enableRealTimeValidation: false, // Only validate on submit for auth
      }}
    >
      {type === 'signup' && (
        <FormField
          name="fullName"
          label="Full Name"
          placeholder="Enter your full name"
          required
          maxLength={50}
        />
      )}
      
      {type !== 'reset' && (
        <FormField
          name="email"
          label="Email Address"
          type="email"
          placeholder="Enter your email address"
          required
          maxLength={254}
        />
      )}
      
      {(type === 'signin' || type === 'signup' || type === 'reset') && (
        <FormField
          name="password"
          label="Password"
          type="password"
          placeholder="Enter your password"
          required
          maxLength={128}
        />
      )}
      
      {(type === 'signup' || type === 'reset') && (
        <FormField
          name="confirmPassword"
          label="Confirm Password"
          type="password"
          placeholder="Confirm your password"
          required
          maxLength={128}
        />
      )}
      
      {type === 'signup' && (
        <FormField
          name="acceptTerms"
          label="I accept the Terms and Conditions"
          type="checkbox"
          required
        />
      )}
      
      {type === 'signin' && (
        <FormField
          name="rememberMe"
          label="Remember me"
          type="checkbox"
        />
      )}
    </ValidatedForm>
  );
}

