import { useState, useEffect } from 'react';
import { Mail, Settings, CheckCircle, AlertCircle, Bell, BookOpen, Tag } from 'lucide-react';
import { supabase } from '@/integrations/supabase/client';
import { useAuth } from '@/lib/context/AuthContext';

interface EmailPreferences {
  order_updates: boolean;
  shipping_notifications: boolean;
  new_arrivals: boolean;
  special_offers: boolean;
  islamic_insights: boolean;
  newsletter: boolean;
}

interface EmailPreferencesProps {
  className?: string;
}

export const EmailPreferences = ({ className = '' }: EmailPreferencesProps) => {
  const { user } = useAuth();
  const [preferences, setPreferences] = useState<EmailPreferences>({
    order_updates: true,
    shipping_notifications: true,
    new_arrivals: true,
    special_offers: true,
    islamic_insights: true,
    newsletter: true
  });
  const [isLoading, setIsLoading] = useState(false);
  const [isSaving, setIsSaving] = useState(false);
  const [status, setStatus] = useState<'idle' | 'success' | 'error'>('idle');
  const [message, setMessage] = useState('');

  useEffect(() => {
    if (user) {
      loadPreferences();
    }
  }, [user]);

  const loadPreferences = async () => {
    if (!user?.email) return;

    setIsLoading(true);
    try {
      // Try to get preferences from user profile first
      const { data: profile } = await supabase
        .from('profiles')
        .select('email_preferences')
        .eq('id', user.id)
        .single();

      if (profile?.email_preferences) {
        setPreferences({ ...preferences, ...profile.email_preferences });
      } else {
        // Check newsletter subscription
        const { data: newsletter } = await supabase
          .from('newsletter_subscribers')
          .select('preferences, is_active')
          .eq('email', user.email.toLowerCase())
          .single();

        if (newsletter) {
          setPreferences({
            ...preferences,
            newsletter: newsletter.is_active,
            ...newsletter.preferences
          });
        }
      }
    } catch (error) {
      console.error('Error loading email preferences:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const savePreferences = async () => {
    if (!user?.email) return;

    setIsSaving(true);
    setStatus('idle');
    setMessage('');

    try {
      // Update user profile preferences
      const { error: profileError } = await supabase
        .from('profiles')
        .update({
          email_preferences: preferences,
          updated_at: new Date().toISOString()
        })
        .eq('id', user.id);

      if (profileError) {
        throw profileError;
      }

      // Update newsletter subscription if exists
      if (preferences.newsletter) {
        const { error: newsletterError } = await supabase
          .from('newsletter_subscribers')
          .upsert({
            email: user.email.toLowerCase(),
            name: user.name || 'Subscriber',
            is_active: true,
            preferences: {
              new_arrivals: preferences.new_arrivals,
              special_offers: preferences.special_offers,
              islamic_insights: preferences.islamic_insights
            },
            updated_at: new Date().toISOString()
          });

        if (newsletterError) {
          console.error('Newsletter update error:', newsletterError);
        }
      } else {
        // Unsubscribe from newsletter
        await supabase
          .from('newsletter_subscribers')
          .update({ is_active: false })
          .eq('email', user.email.toLowerCase());
      }

      setStatus('success');
      setMessage('Email preferences updated successfully!');
    } catch (error) {
      console.error('Error saving email preferences:', error);
      setStatus('error');
      setMessage('Failed to update preferences. Please try again.');
    } finally {
      setIsSaving(false);
    }
  };

  const handlePreferenceChange = (key: keyof EmailPreferences, value: boolean) => {
    setPreferences(prev => ({
      ...prev,
      [key]: value
    }));
  };

  const preferenceOptions = [
    {
      key: 'order_updates' as keyof EmailPreferences,
      label: 'Order Updates',
      description: 'Receive notifications about your order status changes',
      icon: <Bell className="w-5 h-5" />,
      required: true
    },
    {
      key: 'shipping_notifications' as keyof EmailPreferences,
      label: 'Shipping Notifications',
      description: 'Get notified when your orders are shipped and delivered',
      icon: <Mail className="w-5 h-5" />,
      required: true
    },
    {
      key: 'newsletter' as keyof EmailPreferences,
      label: 'Newsletter',
      description: 'Receive our weekly newsletter with Islamic insights and updates',
      icon: <BookOpen className="w-5 h-5" />,
      required: false
    },
    {
      key: 'new_arrivals' as keyof EmailPreferences,
      label: 'New Arrivals',
      description: 'Be the first to know about new Islamic books and publications',
      icon: <BookOpen className="w-5 h-5" />,
      required: false
    },
    {
      key: 'special_offers' as keyof EmailPreferences,
      label: 'Special Offers',
      description: 'Receive exclusive discounts and promotional offers',
      icon: <Tag className="w-5 h-5" />,
      required: false
    },
    {
      key: 'islamic_insights' as keyof EmailPreferences,
      label: 'Islamic Insights',
      description: 'Get weekly Islamic knowledge, quotes, and spiritual guidance',
      icon: <BookOpen className="w-5 h-5" />,
      required: false
    }
  ];

  if (isLoading) {
    return (
      <div className={`bg-white rounded-lg shadow-sm p-6 ${className}`}>
        <div className="animate-pulse">
          <div className="h-6 bg-gray-200 rounded w-1/3 mb-4"></div>
          <div className="space-y-4">
            {[1, 2, 3, 4].map((i) => (
              <div key={i} className="flex items-center space-x-3">
                <div className="w-4 h-4 bg-gray-200 rounded"></div>
                <div className="flex-1">
                  <div className="h-4 bg-gray-200 rounded w-1/4 mb-1"></div>
                  <div className="h-3 bg-gray-200 rounded w-1/2"></div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className={`bg-white rounded-lg shadow-sm p-6 ${className}`}>
      <div className="flex items-center mb-6">
        <Settings className="w-5 h-5 text-naaz-green mr-2" />
        <h2 className="text-lg font-semibold text-gray-900">Email Preferences</h2>
      </div>

      <p className="text-gray-600 mb-6">
        Choose which emails you'd like to receive from us. You can update these preferences at any time.
      </p>

      <div className="space-y-4">
        {preferenceOptions.map((option) => (
          <div key={option.key} className="flex items-start space-x-3 p-4 border border-gray-200 rounded-lg">
            <div className="flex-shrink-0 mt-1">
              <label className="relative inline-flex items-center cursor-pointer">
                <input
                  type="checkbox"
                  checked={preferences[option.key]}
                  onChange={(e) => handlePreferenceChange(option.key, e.target.checked)}
                  disabled={option.required || isSaving}
                  className="sr-only peer"
                />
                <div className="w-5 h-5 bg-gray-200 peer-focus:outline-none peer-focus:ring-4 peer-focus:ring-naaz-green/20 rounded border-2 border-gray-300 peer-checked:bg-naaz-green peer-checked:border-naaz-green relative">
                  {preferences[option.key] && (
                    <CheckCircle className="w-3 h-3 text-white absolute top-0.5 left-0.5" />
                  )}
                </div>
              </label>
            </div>
            
            <div className="flex-1">
              <div className="flex items-center space-x-2 mb-1">
                <div className="text-naaz-green">
                  {option.icon}
                </div>
                <h3 className="font-medium text-gray-900">
                  {option.label}
                  {option.required && (
                    <span className="text-red-500 ml-1">*</span>
                  )}
                </h3>
              </div>
              <p className="text-sm text-gray-600">{option.description}</p>
              {option.required && (
                <p className="text-xs text-gray-500 mt-1">
                  Required for account functionality
                </p>
              )}
            </div>
          </div>
        ))}
      </div>

      <div className="mt-6 flex items-center justify-between">
        <button
          onClick={savePreferences}
          disabled={isSaving}
          className="bg-naaz-green text-white px-6 py-2 rounded-lg hover:bg-naaz-green/90 transition-colors font-medium disabled:opacity-50 disabled:cursor-not-allowed flex items-center"
        >
          {isSaving ? (
            <>
              <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
              Saving...
            </>
          ) : (
            'Save Preferences'
          )}
        </button>

        {/* Status Messages */}
        {status === 'success' && (
          <div className="flex items-center text-green-600 text-sm">
            <CheckCircle className="w-4 h-4 mr-2" />
            {message}
          </div>
        )}

        {status === 'error' && (
          <div className="flex items-center text-red-600 text-sm">
            <AlertCircle className="w-4 h-4 mr-2" />
            {message}
          </div>
        )}
      </div>

      <div className="mt-6 p-4 bg-gray-50 rounded-lg">
        <h4 className="font-medium text-gray-900 mb-2">Privacy Notice</h4>
        <p className="text-sm text-gray-600">
          We respect your privacy and will never share your email address with third parties. 
          You can unsubscribe from any email list at any time by clicking the unsubscribe link 
          in our emails or by updating your preferences here.
        </p>
      </div>
    </div>
  );
};

export default EmailPreferences;

import React, { useState } from 'react';
import { Star, Package, Truck, ThumbsUp } from 'lucide-react';
import { useCreateOrderFeedback, useOrderFeedback } from '@/lib/hooks/useReviews';
import { Button } from '@/components/ui/button';
import { Textarea } from '@/components/ui/textarea';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';

interface OrderFeedbackFormProps {
  orderId: number;
  orderNumber: string;
}

const OrderFeedbackForm: React.FC<OrderFeedbackFormProps> = ({ orderId, orderNumber }) => {
  const { data: existingFeedback } = useOrderFeedback(orderId);
  const createFeedback = useCreateOrderFeedback();
  
  const [overallRating, setOverallRating] = useState(5);
  const [deliveryRating, setDeliveryRating] = useState(5);
  const [packagingRating, setPackagingRating] = useState(5);
  const [wouldRecommend, setWouldRecommend] = useState(true);
  const [feedbackText, setFeedbackText] = useState('');
  const [improvementSuggestions, setImprovementSuggestions] = useState('');

  const StarRating = ({ 
    value, 
    onChange, 
    label, 
    icon: Icon 
  }: { 
    value: number; 
    onChange: (value: number) => void; 
    label: string;
    icon: React.ComponentType<any>;
  }) => (
    <div className="space-y-2">
      <div className="flex items-center space-x-2">
        <Icon className="w-5 h-5 text-naaz-green" />
        <span className="font-medium">{label}</span>
      </div>
      <div className="flex space-x-1">
        {[1, 2, 3, 4, 5].map((star) => (
          <button
            key={star}
            type="button"
            onClick={() => onChange(star)}
            className={`${
              star <= value ? 'text-yellow-400' : 'text-gray-300'
            } hover:text-yellow-400 cursor-pointer`}
          >
            <Star className="h-6 w-6 fill-current" />
          </button>
        ))}
      </div>
    </div>
  );

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    
    try {
      await createFeedback.mutateAsync({
        orderId,
        overallRating,
        deliveryRating,
        packagingRating,
        wouldRecommend,
        feedbackText,
        improvementSuggestions,
      });
    } catch (error) {
      console.error('Error submitting feedback:', error);
    }
  };

  if (existingFeedback) {
    return (
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center space-x-2">
            <ThumbsUp className="w-5 h-5 text-green-600" />
            <span>Thank you for your feedback!</span>
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div className="text-center">
                <div className="text-2xl font-bold text-naaz-green">
                  {existingFeedback.overall_rating}
                </div>
                <div className="text-sm text-gray-600">Overall Rating</div>
              </div>
              <div className="text-center">
                <div className="text-2xl font-bold text-naaz-green">
                  {existingFeedback.delivery_rating}
                </div>
                <div className="text-sm text-gray-600">Delivery</div>
              </div>
              <div className="text-center">
                <div className="text-2xl font-bold text-naaz-green">
                  {existingFeedback.packaging_rating}
                </div>
                <div className="text-sm text-gray-600">Packaging</div>
              </div>
            </div>
            
            {existingFeedback.would_recommend && (
              <Badge className="bg-green-100 text-green-800">
                Would recommend to others
              </Badge>
            )}
            
            {existingFeedback.feedback_text && (
              <div>
                <h4 className="font-medium mb-2">Your Feedback:</h4>
                <p className="text-gray-700">{existingFeedback.feedback_text}</p>
              </div>
            )}
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card>
      <CardHeader>
        <CardTitle>Share Your Experience</CardTitle>
        <p className="text-gray-600">
          Help us improve by sharing your feedback for order #{orderNumber}
        </p>
      </CardHeader>
      <CardContent>
        <form onSubmit={handleSubmit} className="space-y-6">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <StarRating
              value={overallRating}
              onChange={setOverallRating}
              label="Overall Experience"
              icon={ThumbsUp}
            />
            <StarRating
              value={deliveryRating}
              onChange={setDeliveryRating}
              label="Delivery Service"
              icon={Truck}
            />
            <StarRating
              value={packagingRating}
              onChange={setPackagingRating}
              label="Packaging Quality"
              icon={Package}
            />
          </div>

          <div className="flex items-center space-x-2">
            <input
              type="checkbox"
              id="wouldRecommend"
              checked={wouldRecommend}
              onChange={(e) => setWouldRecommend(e.target.checked)}
              className="rounded border-gray-300 text-naaz-green focus:ring-naaz-green"
            />
            <label htmlFor="wouldRecommend" className="text-sm text-gray-700">
              I would recommend Naaz Book Depot to others
            </label>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              What did you like about your order?
            </label>
            <Textarea
              value={feedbackText}
              onChange={(e) => setFeedbackText(e.target.value)}
              rows={3}
              placeholder="Tell us what went well..."
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              How can we improve? (Optional)
            </label>
            <Textarea
              value={improvementSuggestions}
              onChange={(e) => setImprovementSuggestions(e.target.value)}
              rows={3}
              placeholder="Share your suggestions for improvement..."
            />
          </div>

          <Button 
            type="submit" 
            className="w-full bg-naaz-green hover:bg-naaz-green/90"
            disabled={createFeedback.isPending}
          >
            {createFeedback.isPending ? 'Submitting...' : 'Submit Feedback'}
          </Button>
        </form>
      </CardContent>
    </Card>
  );
};

export default OrderFeedbackForm;

import React, { useState, useEffect } from 'react';
import { Button } from '../ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '../ui/card';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '../ui/select';
import { Loader2, CreditCard, Globe, AlertCircle, CheckCircle, DollarSign } from 'lucide-react';
import { paypalService, PayPalOrderRequest } from '../../lib/services/paypalService';
import { toast } from 'sonner';

interface PayPalPaymentProps {
  orderId: string;
  amount: number;
  customerInfo: {
    firstName: string;
    lastName: string;
    email: string;
    phone?: string;
  };
  shippingAddress?: {
    addressLine1: string;
    addressLine2?: string;
    city: string;
    state: string;
    postalCode: string;
    countryCode: string;
  };
  productInfo: string;
  onSuccess: (transactionId: string) => void;
  onFailure: (error: string) => void;
}

export const PayPalPayment: React.FC<PayPalPaymentProps> = ({
  orderId,
  amount,
  customerInfo,
  shippingAddress,
  productInfo,
  onSuccess,
  onFailure
}) => {
  const [loading, setLoading] = useState(false);
  const [selectedCurrency, setSelectedCurrency] = useState('USD');
  const [convertedAmount, setConvertedAmount] = useState(0);
  const [paypalOrderId, setPaypalOrderId] = useState<string | null>(null);
  const [paymentStatus, setPaymentStatus] = useState<'idle' | 'creating' | 'pending' | 'success' | 'failed'>('idle');
  const [approvalUrl, setApprovalUrl] = useState<string | null>(null);

  const supportedCurrencies = paypalService.getSupportedCurrencies();

  // Currency conversion rates (in production, use real-time rates)
  const conversionRates: Record<string, number> = {
    'USD': 0.012,
    'EUR': 0.011,
    'GBP': 0.0095,
    'CAD': 0.016,
    'AUD': 0.018,
    'JPY': 1.8
  };

  useEffect(() => {
    // Detect user's currency on component mount
    paypalService.detectUserCurrency().then(currency => {
      if (supportedCurrencies.includes(currency)) {
        setSelectedCurrency(currency);
      }
    });
  }, []);

  useEffect(() => {
    // Update converted amount when currency changes
    const rate = conversionRates[selectedCurrency] || conversionRates['USD'];
    const converted = Math.round(amount * rate * 100) / 100;
    setConvertedAmount(converted);
  }, [amount, selectedCurrency]);

  const createPayPalOrder = async () => {
    setLoading(true);
    setPaymentStatus('creating');

    try {
      const orderRequest: PayPalOrderRequest = {
        orderId,
        amount,
        currency: selectedCurrency,
        description: productInfo,
        customerInfo,
        shippingAddress
      };

      const paypalOrder = await paypalService.createOrder(orderRequest);
      setPaypalOrderId(paypalOrder.id);

      // Find approval URL
      const approvalLink = paypalOrder.links.find(link => link.rel === 'approve');
      if (approvalLink) {
        setApprovalUrl(approvalLink.href);
        setPaymentStatus('pending');
        toast.success('PayPal order created. Please complete the payment.');
      } else {
        throw new Error('No approval URL found in PayPal response');
      }
    } catch (error) {
      console.error('PayPal order creation error:', error);
      setPaymentStatus('failed');
      onFailure(error instanceof Error ? error.message : 'Failed to create PayPal order');
      toast.error('Failed to create PayPal order');
    } finally {
      setLoading(false);
    }
  };

  const openPayPalCheckout = () => {
    if (approvalUrl) {
      // Open PayPal in a new window
      const paypalWindow = window.open(
        approvalUrl,
        'paypal-checkout',
        'width=500,height=600,scrollbars=yes,resizable=yes'
      );

      // Poll for window closure to detect completion
      const pollTimer = setInterval(() => {
        if (paypalWindow?.closed) {
          clearInterval(pollTimer);
          // Check payment status after window closes
          checkPaymentStatus();
        }
      }, 1000);

      // Stop polling after 10 minutes
      setTimeout(() => {
        clearInterval(pollTimer);
        if (paypalWindow && !paypalWindow.closed) {
          paypalWindow.close();
        }
      }, 600000);
    }
  };

  const checkPaymentStatus = async () => {
    if (!paypalOrderId) return;

    try {
      const orderDetails = await paypalService.getOrderDetails(paypalOrderId);
      
      if (orderDetails.status === 'APPROVED') {
        // Capture the payment
        const capture = await paypalService.capturePayment(paypalOrderId);
        
        if (capture.status === 'COMPLETED') {
          setPaymentStatus('success');
          onSuccess(capture.id);
          toast.success('PayPal payment completed successfully!');
        } else {
          throw new Error('Payment capture failed');
        }
      } else if (orderDetails.status === 'CANCELLED') {
        setPaymentStatus('failed');
        onFailure('Payment was cancelled');
        toast.error('Payment was cancelled');
      }
    } catch (error) {
      console.error('Payment status check error:', error);
      setPaymentStatus('failed');
      onFailure('Failed to verify payment status');
      toast.error('Failed to verify payment status');
    }
  };

  const getCurrencySymbol = (currency: string): string => {
    const symbols: Record<string, string> = {
      'USD': '$',
      'EUR': '',
      'GBP': '',
      'CAD': 'C$',
      'AUD': 'A$',
      'JPY': ''
    };
    return symbols[currency] || '$';
  };

  const formatAmount = (amount: number, currency: string): string => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: currency,
      minimumFractionDigits: currency === 'JPY' ? 0 : 2
    }).format(amount);
  };

  return (
    <div className="space-y-6">
      {/* PayPal Payment Card */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center">
            <Globe className="w-5 h-5 mr-2 text-blue-600" />
            PayPal International Payment
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            {/* Currency Selection */}
            <div>
              <label className="block text-sm font-medium mb-2">Select Currency</label>
              <Select value={selectedCurrency} onValueChange={setSelectedCurrency}>
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {supportedCurrencies.map(currency => (
                    <SelectItem key={currency} value={currency}>
                      <div className="flex items-center">
                        <span className="mr-2">{getCurrencySymbol(currency)}</span>
                        <span>{currency}</span>
                      </div>
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            {/* Amount Display */}
            <div className="bg-blue-50 p-4 rounded-lg">
              <div className="flex justify-between items-center mb-2">
                <span className="text-sm text-gray-600">Original Amount (INR):</span>
                <span className="font-medium">{amount.toFixed(2)}</span>
              </div>
              <div className="flex justify-between items-center">
                <span className="font-medium">Amount to Pay:</span>
                <span className="text-2xl font-bold text-blue-600">
                  {formatAmount(convertedAmount, selectedCurrency)}
                </span>
              </div>
              <div className="text-xs text-gray-500 mt-1">
                Exchange rate is approximate and may vary at the time of payment
              </div>
            </div>

            {/* Order Information */}
            <div className="text-sm text-gray-600">
              <p><strong>Order ID:</strong> {orderId}</p>
              <p><strong>Description:</strong> {productInfo}</p>
            </div>

            {/* Payment Status */}
            {paymentStatus === 'idle' && (
              <Button
                onClick={createPayPalOrder}
                disabled={loading}
                className="w-full bg-blue-600 hover:bg-blue-700"
              >
                {loading ? (
                  <>
                    <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                    Creating PayPal Order...
                  </>
                ) : (
                  <>
                    <DollarSign className="w-4 h-4 mr-2" />
                    Pay {formatAmount(convertedAmount, selectedCurrency)} with PayPal
                  </>
                )}
              </Button>
            )}

            {paymentStatus === 'pending' && approvalUrl && (
              <div className="space-y-4">
                <div className="bg-yellow-50 p-4 rounded-lg">
                  <div className="flex items-start">
                    <AlertCircle className="w-5 h-5 text-yellow-600 mr-2 mt-0.5" />
                    <div className="text-sm text-yellow-800">
                      <p className="font-medium">PayPal Order Created</p>
                      <p>Click the button below to complete your payment on PayPal's secure checkout page.</p>
                    </div>
                  </div>
                </div>

                <Button
                  onClick={openPayPalCheckout}
                  className="w-full bg-blue-600 hover:bg-blue-700"
                >
                  <Globe className="w-4 h-4 mr-2" />
                  Complete Payment on PayPal
                </Button>

                <div className="text-center">
                  <Button
                    variant="outline"
                    onClick={checkPaymentStatus}
                    className="text-sm"
                  >
                    Check Payment Status
                  </Button>
                </div>
              </div>
            )}

            {paymentStatus === 'creating' && (
              <div className="flex items-center justify-center py-4">
                <Loader2 className="w-5 h-5 animate-spin mr-2" />
                <span>Creating PayPal order...</span>
              </div>
            )}

            {paymentStatus === 'success' && (
              <div className="bg-green-50 p-4 rounded-lg">
                <div className="flex items-center">
                  <CheckCircle className="w-5 h-5 text-green-600 mr-2" />
                  <div className="text-sm text-green-800">
                    <p className="font-medium">Payment Successful!</p>
                    <p>Your PayPal payment has been processed successfully.</p>
                  </div>
                </div>
              </div>
            )}

            {paymentStatus === 'failed' && (
              <div className="bg-red-50 p-4 rounded-lg">
                <div className="flex items-center">
                  <AlertCircle className="w-5 h-5 text-red-600 mr-2" />
                  <div className="text-sm text-red-800">
                    <p className="font-medium">Payment Failed</p>
                    <p>There was an issue processing your PayPal payment. Please try again.</p>
                  </div>
                </div>
                <Button
                  onClick={() => {
                    setPaymentStatus('idle');
                    setPaypalOrderId(null);
                    setApprovalUrl(null);
                  }}
                  variant="outline"
                  className="mt-3 w-full"
                >
                  Try Again
                </Button>
              </div>
            )}

            {/* PayPal Benefits */}
            <div className="bg-gray-50 p-4 rounded-lg">
              <h4 className="font-medium mb-2">Why choose PayPal?</h4>
              <ul className="text-sm text-gray-600 space-y-1">
                <li> Secure international payments</li>
                <li> Buyer protection included</li>
                <li> Multiple currency support</li>
                <li> No need to share card details</li>
                <li> Instant payment confirmation</li>
              </ul>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
};

import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '../ui/card';
import { Button } from '../ui/button';
import { Badge } from '../ui/badge';
import { Loader2, Shield, AlertTriangle, CheckCircle, Clock, CreditCard } from 'lucide-react';
import { PayUPayment } from '@/components/payment/PayUPayment';
import { PayPalPayment } from '@/components/payment/PayPalPayment';
import { paymentOrchestrator, PaymentMethod, PaymentRequest } from '../../lib/services/paymentOrchestrator';
import { toast } from 'sonner';

interface UnifiedPaymentProps {
  orderId: string;
  amount: number;
  currency: string;
  customerInfo: {
    firstName: string;
    lastName: string;
    email: string;
    phone: string;
    country: string;
  };
  shippingAddress?: {
    addressLine1: string;
    addressLine2?: string;
    city: string;
    state: string;
    postalCode: string;
    countryCode: string;
  };
  productInfo: string;
  onSuccess: (transactionId: string, paymentMethod: string) => void;
  onFailure: (error: string) => void;
}

export const UnifiedPayment: React.FC<UnifiedPaymentProps> = ({
  orderId,
  amount,
  currency,
  customerInfo,
  shippingAddress,
  productInfo,
  onSuccess,
  onFailure
}) => {
  const [availablePaymentMethods, setAvailablePaymentMethods] = useState<PaymentMethod[]>([]);
  const [selectedPaymentMethod, setSelectedPaymentMethod] = useState<string | null>(null);
  const [loading, setLoading] = useState(true);
  const [processing, setProcessing] = useState(false);
  const [paymentStatus, setPaymentStatus] = useState<'idle' | 'processing' | 'success' | 'failed'>('idle');
  const [userLocation, setUserLocation] = useState<string>('IN');

  useEffect(() => {
    initializePaymentMethods();
  }, [currency, customerInfo.country]);

  const initializePaymentMethods = async () => {
    setLoading(true);
    try {
      // Detect user location
      const location = await paymentOrchestrator.detectUserLocation();
      setUserLocation(location);

      // Get available payment methods
      const methods = paymentOrchestrator.getAvailablePaymentMethods(location, currency);
      setAvailablePaymentMethods(methods);

      // Auto-select the first available method
      if (methods.length > 0) {
        setSelectedPaymentMethod(methods[0].id);
      }
    } catch (error) {
      console.error('Error initializing payment methods:', error);
      toast.error('Failed to load payment methods');
    } finally {
      setLoading(false);
    }
  };

  const handlePaymentMethodSelect = (methodId: string) => {
    setSelectedPaymentMethod(methodId);
    setPaymentStatus('idle');
  };

  const calculateTotalWithFees = (methodId: string) => {
    const { fees, total } = paymentOrchestrator.calculateFees(amount, methodId);
    return { fees, total };
  };

  const handleCODPayment = async () => {
    setProcessing(true);
    setPaymentStatus('processing');

    try {
      const paymentRequest: PaymentRequest = {
        orderId,
        amount,
        currency,
        customerInfo,
        shippingAddress,
        productInfo
      };

      const result = await paymentOrchestrator.processPayment(paymentRequest, 'cod');
      
      if (result.success) {
        setPaymentStatus('success');
        onSuccess(result.transactionId || '', 'cod');
        toast.success('Order placed successfully! Pay on delivery.');
      } else {
        throw new Error(result.error || 'COD payment failed');
      }
    } catch (error) {
      setPaymentStatus('failed');
      onFailure(error instanceof Error ? error.message : 'COD payment failed');
      toast.error('Failed to place COD order');
    } finally {
      setProcessing(false);
    }
  };

  const getPaymentMethodIcon = (method: PaymentMethod) => {
    const iconMap: Record<string, React.ReactNode> = {
      'cod': <span className="text-2xl"></span>,
      'payu_upi': <span className="text-2xl"></span>,
      'payu_card': <CreditCard className="w-6 h-6" />,
      'paypal': <span className="text-2xl"></span>
    };
    return iconMap[method.id] || <CreditCard className="w-6 h-6" />;
  };

  const getPaymentMethodColor = (method: PaymentMethod) => {
    const colorMap: Record<string, string> = {
      'cod': 'bg-green-50 border-green-200 hover:bg-green-100',
      'payu_upi': 'bg-blue-50 border-blue-200 hover:bg-blue-100',
      'payu_card': 'bg-purple-50 border-purple-200 hover:bg-purple-100',
      'paypal': 'bg-yellow-50 border-yellow-200 hover:bg-yellow-100'
    };
    return colorMap[method.id] || 'bg-gray-50 border-gray-200 hover:bg-gray-100';
  };

  if (loading) {
    return (
      <Card>
        <CardContent className="pt-6">
          <div className="flex items-center justify-center py-8">
            <Loader2 className="w-6 h-6 animate-spin mr-2" />
            <span>Loading payment methods...</span>
          </div>
        </CardContent>
      </Card>
    );
  }

  if (availablePaymentMethods.length === 0) {
    return (
      <Card>
        <CardContent className="pt-6">
          <div className="text-center py-8">
            <AlertTriangle className="w-12 h-12 text-yellow-500 mx-auto mb-4" />
            <h3 className="text-lg font-semibold mb-2">No Payment Methods Available</h3>
            <p className="text-gray-600">Please contact support for assistance.</p>
          </div>
        </CardContent>
      </Card>
    );
  }

  const selectedMethod = availablePaymentMethods.find(m => m.id === selectedPaymentMethod);

  return (
    <div className="space-y-6">
      {/* Payment Method Selection */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center">
            <Shield className="w-5 h-5 mr-2" />
            Choose Payment Method
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid gap-4">
            {availablePaymentMethods.map((method) => {
              const { fees, total } = calculateTotalWithFees(method.id);
              const isSelected = selectedPaymentMethod === method.id;

              return (
                <div
                  key={method.id}
                  className={`p-4 border-2 rounded-lg cursor-pointer transition-all ${
                    isSelected 
                      ? 'border-blue-500 bg-blue-50' 
                      : getPaymentMethodColor(method)
                  }`}
                  onClick={() => handlePaymentMethodSelect(method.id)}
                >
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-3">
                      {getPaymentMethodIcon(method)}
                      <div>
                        <h3 className="font-semibold">{method.name}</h3>
                        <p className="text-sm text-gray-600">{method.description}</p>
                        <div className="flex items-center space-x-2 mt-1">
                          <Badge variant="outline" className="text-xs">
                            <Clock className="w-3 h-3 mr-1" />
                            {method.processingTime}
                          </Badge>
                          {method.type === 'international' && (
                            <Badge variant="secondary" className="text-xs">
                              International
                            </Badge>
                          )}
                        </div>
                      </div>
                    </div>
                    <div className="text-right">
                      <div className="font-semibold">
                        {currency} {total.toFixed(2)}
                      </div>
                      {fees > 0 && (
                        <div className="text-xs text-gray-500">
                          +{currency} {fees.toFixed(2)} fees
                        </div>
                      )}
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        </CardContent>
      </Card>

      {/* Selected Payment Method Details */}
      {selectedMethod && (
        <div>
          {selectedMethod.id === 'cod' && (
            <Card>
              <CardHeader>
                <CardTitle>Cash on Delivery</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  <div className="bg-green-50 p-4 rounded-lg">
                    <div className="flex items-start">
                      <CheckCircle className="w-5 h-5 text-green-600 mr-2 mt-0.5" />
                      <div className="text-sm text-green-800">
                        <p className="font-medium">Pay on Delivery</p>
                        <p>You can pay in cash when your order is delivered to your doorstep.</p>
                      </div>
                    </div>
                  </div>

                  <div className="bg-gray-50 p-4 rounded-lg">
                    <div className="flex justify-between items-center mb-2">
                      <span>Order Amount:</span>
                      <span className="font-semibold">{currency} {amount.toFixed(2)}</span>
                    </div>
                    <div className="flex justify-between items-center">
                      <span>Payment Fees:</span>
                      <span className="font-semibold text-green-600">FREE</span>
                    </div>
                    <hr className="my-2" />
                    <div className="flex justify-between items-center font-bold">
                      <span>Total Amount:</span>
                      <span>{currency} {amount.toFixed(2)}</span>
                    </div>
                  </div>

                  <Button
                    onClick={handleCODPayment}
                    disabled={processing}
                    className="w-full bg-green-600 hover:bg-green-700"
                  >
                    {processing ? (
                      <>
                        <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                        Placing Order...
                      </>
                    ) : (
                      `Place Order - ${currency} ${amount.toFixed(2)}`
                    )}
                  </Button>
                </div>
              </CardContent>
            </Card>
          )}

          {(selectedMethod.id === 'payu_upi' || selectedMethod.id === 'payu_card') && (
            <PayUPayment
              orderId={orderId}
              amount={amount}
              customerInfo={{
                firstName: customerInfo.firstName,
                email: customerInfo.email,
                phone: customerInfo.phone
              }}
              productInfo={productInfo}
              onSuccess={(transactionId) => onSuccess(transactionId, selectedMethod.id)}
              onFailure={onFailure}
            />
          )}

          {selectedMethod.id === 'paypal' && (
            <PayPalPayment
              orderId={orderId}
              amount={amount}
              customerInfo={customerInfo}
              shippingAddress={shippingAddress}
              productInfo={productInfo}
              onSuccess={(transactionId) => onSuccess(transactionId, 'paypal')}
              onFailure={onFailure}
            />
          )}
        </div>
      )}

      {/* Security Notice */}
      <Card>
        <CardContent className="pt-6">
          <div className="flex items-center text-sm text-gray-600">
            <Shield className="w-4 h-4 mr-2" />
            <span>
              Your payment information is secure and encrypted. We use industry-standard security measures to protect your data.
            </span>
          </div>
        </CardContent>
      </Card>
    </div>
  );
};

import React, { useState } from 'react';
import { Star, Edit, Trash2, ThumbsUp, ThumbsDown, Flag, TrendingUp, Award } from 'lucide-react';
import { 
  useProductReviews, 
  useCreateReview, 
  useUpdateReview, 
  useDeleteReview,
  useProductReviewStats,
  useVoteHelpful,
  useReportReview
} from '@/lib/hooks/useReviews';
import { useAuth } from '@/lib/context/AuthContext';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import { 
  Dialog, 
  DialogContent, 
  DialogHeader, 
  DialogTitle, 
  DialogTrigger 
} from '@/components/ui/dialog';
import { 
  Select, 
  SelectContent, 
  SelectItem, 
  SelectTrigger, 
  SelectValue 
} from '@/components/ui/select';
import { Textarea } from '@/components/ui/textarea';
import { toast } from 'sonner';

interface ProductReviewsProps {
  productId: string;
}

const ProductReviews: React.FC<ProductReviewsProps> = ({ productId }) => {
  const { user, isAuthenticated } = useAuth();
  const { data: reviews = [], isLoading } = useProductReviews(productId, user?.id);
  const { data: reviewStats } = useProductReviewStats(productId);
  const createReview = useCreateReview();
  const updateReview = useUpdateReview();
  const deleteReview = useDeleteReview();
  const voteHelpful = useVoteHelpful();
  const reportReview = useReportReview();

  const [showForm, setShowForm] = useState(false);
  const [editingReview, setEditingReview] = useState<any>(null);
  const [rating, setRating] = useState(5);
  const [title, setTitle] = useState('');
  const [comment, setComment] = useState('');
  const [wouldRecommend, setWouldRecommend] = useState(false);
  const [reportingReview, setReportingReview] = useState<number | null>(null);
  const [reportReason, setReportReason] = useState<string>('');
  const [reportDescription, setReportDescription] = useState('');

  const userHasReview = reviews.find(review => review.user_id === user?.id);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    
    try {
      if (editingReview) {
        await updateReview.mutateAsync({
          reviewId: editingReview.id,
          rating,
          comment,
        });
        setEditingReview(null);
      } else {
        await createReview.mutateAsync({
          productId,
          rating,
          title,
          comment,
          wouldRecommend,
        });
      }
      
      setShowForm(false);
      setRating(5);
      setTitle('');
      setComment('');
      setWouldRecommend(false);
    } catch (error) {
      console.error('Error saving review:', error);
    }
  };

  const handleEdit = (review: any) => {
    setEditingReview(review);
    setRating(review.rating);
    setTitle(review.title || '');
    setComment(review.comment || '');
    setWouldRecommend(review.would_recommend || false);
    setShowForm(true);
  };

  const handleVoteHelpful = async (reviewId: number, isHelpful: boolean) => {
    try {
      await voteHelpful.mutateAsync({ reviewId, isHelpful });
    } catch (error) {
      console.error('Error voting:', error);
    }
  };

  const handleReportReview = async () => {
    if (!reportingReview || !reportReason) return;
    
    try {
      await reportReview.mutateAsync({
        reviewId: reportingReview,
        reason: reportReason as any,
        description: reportDescription,
      });
      setReportingReview(null);
      setReportReason('');
      setReportDescription('');
    } catch (error) {
      console.error('Error reporting review:', error);
    }
  };

  const handleDelete = async (reviewId: string) => {
    if (window.confirm('Are you sure you want to delete this review?')) {
      try {
        await deleteReview.mutateAsync(reviewId);
      } catch (error) {
        console.error('Error deleting review:', error);
      }
    }
  };

  const StarRating = ({ value, onChange, readonly = false }: { value: number; onChange?: (value: number) => void; readonly?: boolean }) => (
    <div className="flex space-x-1">
      {[1, 2, 3, 4, 5].map((star) => (
        <button
          key={star}
          type="button"
          disabled={readonly}
          onClick={() => onChange?.(star)}
          className={`${
            star <= value ? 'text-yellow-400' : 'text-gray-300'
          } ${readonly ? 'cursor-default' : 'hover:text-yellow-400 cursor-pointer'}`}
        >
          <Star className="h-5 w-5 fill-current" />
        </button>
      ))}
    </div>
  );

  if (isLoading) {
    return <div className="animate-pulse">Loading reviews...</div>;
  }

  return (
    <div className="space-y-6">
      {/* Review Statistics */}
      {reviewStats && reviewStats.total_reviews > 0 && (
        <div className="bg-white border rounded-lg p-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <div className="flex items-center space-x-4 mb-4">
                <div className="text-center">
                  <div className="text-3xl font-bold text-naaz-green">
                    {reviewStats.average_rating.toFixed(1)}
                  </div>
                  <StarRating value={Math.round(reviewStats.average_rating)} readonly />
                  <div className="text-sm text-gray-500 mt-1">
                    {reviewStats.total_reviews} reviews
                  </div>
                </div>
                <div className="flex-1">
                  {[5, 4, 3, 2, 1].map((stars) => (
                    <div key={stars} className="flex items-center space-x-2 mb-1">
                      <span className="text-sm w-8">{stars}</span>
                      <Progress 
                        value={(reviewStats.rating_distribution[stars.toString() as keyof typeof reviewStats.rating_distribution] / reviewStats.total_reviews) * 100} 
                        className="flex-1 h-2" 
                      />
                      <span className="text-sm text-gray-500 w-8">
                        {reviewStats.rating_distribution[stars.toString() as keyof typeof reviewStats.rating_distribution]}
                      </span>
                    </div>
                  ))}
                </div>
              </div>
            </div>
            
            <div>
              <h4 className="font-semibold mb-3">Sentiment Analysis</h4>
              <div className="space-y-2">
                <div className="flex items-center justify-between">
                  <span className="text-green-600">Positive</span>
                  <span className="font-medium">{reviewStats.sentiment_distribution.positive}</span>
                </div>
                <div className="flex items-center justify-between">
                  <span className="text-gray-600">Neutral</span>
                  <span className="font-medium">{reviewStats.sentiment_distribution.neutral}</span>
                </div>
                <div className="flex items-center justify-between">
                  <span className="text-red-600">Negative</span>
                  <span className="font-medium">{reviewStats.sentiment_distribution.negative}</span>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      <div className="flex justify-between items-center">
        <h3 className="text-xl font-playfair font-semibold text-naaz-green">
          Customer Reviews ({reviews.length})
        </h3>
        
        {isAuthenticated && !userHasReview && !showForm && (
          <Button 
            onClick={() => setShowForm(true)}
            className="bg-naaz-green hover:bg-naaz-green/90"
          >
            Write a Review
          </Button>
        )}
      </div>

      {showForm && (
        <div className="bg-white border rounded-lg p-6">
          <h4 className="text-lg font-semibold mb-4">
            {editingReview ? 'Edit Your Review' : 'Write a Review'}
          </h4>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Rating *
              </label>
              <StarRating value={rating} onChange={setRating} />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Title *
              </label>
              <Input
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                placeholder="Summarize your review in a few words"
                required
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Review *
              </label>
              <Textarea
                value={comment}
                onChange={(e) => setComment(e.target.value)}
                rows={4}
                placeholder="Share your detailed thoughts about this product..."
                required
              />
            </div>
            <div className="flex items-center space-x-2">
              <input
                type="checkbox"
                id="wouldRecommend"
                checked={wouldRecommend}
                onChange={(e) => setWouldRecommend(e.target.checked)}
                className="rounded border-gray-300 text-naaz-green focus:ring-naaz-green"
              />
              <label htmlFor="wouldRecommend" className="text-sm text-gray-700">
                I would recommend this product to others
              </label>
            </div>
            <div className="flex gap-2">
              <Button type="submit" className="bg-naaz-green hover:bg-naaz-green/90">
                {editingReview ? 'Update Review' : 'Submit Review'}
              </Button>
              <Button
                type="button"
                variant="outline"
                onClick={() => {
                  setShowForm(false);
                  setEditingReview(null);
                  setRating(5);
                  setTitle('');
                  setComment('');
                  setWouldRecommend(false);
                }}
              >
                Cancel
              </Button>
            </div>
          </form>
        </div>
      )}

      <div className="space-y-4">
        {reviews.map((review) => (
          <div key={review.id} className="bg-white border rounded-lg p-4">
            <div className="flex justify-between items-start mb-3">
              <div className="flex items-start space-x-3">
                <div className="w-10 h-10 bg-naaz-green rounded-full flex items-center justify-center text-white font-medium">
                  {review.profiles?.name?.[0] || 'U'}
                </div>
                <div className="flex-1">
                  <div className="flex items-center space-x-2 mb-1">
                    <p className="font-medium">{review.profiles?.name || 'Anonymous'}</p>
                    {review.verified_purchase && (
                      <Badge variant="secondary" className="text-xs">
                        <Award className="w-3 h-3 mr-1" />
                        Verified Purchase
                      </Badge>
                    )}
                    {review.is_featured && (
                      <Badge variant="default" className="text-xs bg-naaz-gold">
                        Featured
                      </Badge>
                    )}
                  </div>
                  <div className="flex items-center space-x-2 mb-2">
                    <StarRating value={review.rating} readonly />
                    {review.analytics?.sentiment_label && (
                      <Badge 
                        variant="outline" 
                        className={`text-xs ${
                          review.analytics.sentiment_label === 'positive' ? 'text-green-600 border-green-200' :
                          review.analytics.sentiment_label === 'negative' ? 'text-red-600 border-red-200' :
                          'text-gray-600 border-gray-200'
                        }`}
                      >
                        <TrendingUp className="w-3 h-3 mr-1" />
                        {review.analytics.sentiment_label}
                      </Badge>
                    )}
                  </div>
                  {review.title && (
                    <h5 className="font-semibold text-gray-900 mb-2">{review.title}</h5>
                  )}
                </div>
              </div>
              
              <div className="flex space-x-2">
                {review.user_id === user?.id ? (
                  <>
                    <button
                      onClick={() => handleEdit(review)}
                      className="text-gray-500 hover:text-naaz-green"
                    >
                      <Edit className="h-4 w-4" />
                    </button>
                    <button
                      onClick={() => handleDelete(review.id.toString())}
                      className="text-gray-500 hover:text-red-600"
                    >
                      <Trash2 className="h-4 w-4" />
                    </button>
                  </>
                ) : (
                  isAuthenticated && (
                    <Dialog>
                      <DialogTrigger asChild>
                        <button
                          onClick={() => setReportingReview(review.id)}
                          className="text-gray-500 hover:text-red-600"
                        >
                          <Flag className="h-4 w-4" />
                        </button>
                      </DialogTrigger>
                      <DialogContent>
                        <DialogHeader>
                          <DialogTitle>Report Review</DialogTitle>
                        </DialogHeader>
                        <div className="space-y-4">
                          <Select value={reportReason} onValueChange={setReportReason}>
                            <SelectTrigger>
                              <SelectValue placeholder="Select a reason" />
                            </SelectTrigger>
                            <SelectContent>
                              <SelectItem value="spam">Spam</SelectItem>
                              <SelectItem value="inappropriate">Inappropriate content</SelectItem>
                              <SelectItem value="fake">Fake review</SelectItem>
                              <SelectItem value="offensive">Offensive language</SelectItem>
                              <SelectItem value="other">Other</SelectItem>
                            </SelectContent>
                          </Select>
                          <Textarea
                            value={reportDescription}
                            onChange={(e) => setReportDescription(e.target.value)}
                            placeholder="Additional details (optional)"
                            rows={3}
                          />
                          <Button 
                            onClick={handleReportReview}
                            disabled={!reportReason}
                            className="w-full"
                          >
                            Submit Report
                          </Button>
                        </div>
                      </DialogContent>
                    </Dialog>
                  )
                )}
              </div>
            </div>
            
            {review.comment && (
              <p className="text-gray-700 mb-3">{review.comment}</p>
            )}

            {review.would_recommend && (
              <div className="flex items-center space-x-1 mb-3">
                <ThumbsUp className="w-4 h-4 text-green-600" />
                <span className="text-sm text-green-600">Recommends this product</span>
              </div>
            )}
            
            <div className="flex items-center justify-between text-sm text-gray-500">
              <span>{new Date(review.created_at).toLocaleDateString()}</span>
              
              {isAuthenticated && review.user_id !== user?.id && (
                <div className="flex items-center space-x-4">
                  <span>Was this helpful?</span>
                  <div className="flex items-center space-x-2">
                    <button
                      onClick={() => handleVoteHelpful(review.id, true)}
                      className={`flex items-center space-x-1 px-2 py-1 rounded ${
                        review.user_helpful_vote === true 
                          ? 'bg-green-100 text-green-700' 
                          : 'hover:bg-gray-100'
                      }`}
                    >
                      <ThumbsUp className="w-3 h-3" />
                      <span>Yes</span>
                    </button>
                    <button
                      onClick={() => handleVoteHelpful(review.id, false)}
                      className={`flex items-center space-x-1 px-2 py-1 rounded ${
                        review.user_helpful_vote === false 
                          ? 'bg-red-100 text-red-700' 
                          : 'hover:bg-gray-100'
                      }`}
                    >
                      <ThumbsDown className="w-3 h-3" />
                      <span>No</span>
                    </button>
                  </div>
                  {review.helpful_count > 0 && (
                    <span className="text-xs">
                      {review.helpful_count} found this helpful
                    </span>
                  )}
                </div>
              )}
            </div>
          </div>
        ))}
        
        {reviews.length === 0 && (
          <div className="text-center py-8 text-gray-500">
            No reviews yet. Be the first to review this product!
          </div>
        )}
      </div>
    </div>
  );
};

