import * as React from "react"
import * as LabelPrimitive from "@radix-ui/react-label"
import { Slot } from "@radix-ui/react-slot"
import {
  Controller,
  ControllerProps,
  FieldPath,
  FieldValues,
  FormProvider,
  useFormContext,
} from "react-hook-form"

import { cn } from "@/lib/utils"
import { Label } from "@/components/ui/label"

const Form = FormProvider

type FormFieldContextValue<
  TFieldValues extends FieldValues = FieldValues,
  TName extends FieldPath<TFieldValues> = FieldPath<TFieldValues>
> = {
  name: TName
}

const FormFieldContext = React.createContext<FormFieldContextValue>(
  {} as FormFieldContextValue
)

const FormField = <
  TFieldValues extends FieldValues = FieldValues,
  TName extends FieldPath<TFieldValues> = FieldPath<TFieldValues>
>({
  ...props
}: ControllerProps<TFieldValues, TName>) => {
  return (
    <FormFieldContext.Provider value={{ name: props.name }}>
      <Controller {...props} />
    </FormFieldContext.Provider>
  )
}

const useFormField = () => {
  const fieldContext = React.useContext(FormFieldContext)
  const itemContext = React.useContext(FormItemContext)
  const { getFieldState, formState } = useFormContext()

  const fieldState = getFieldState(fieldContext.name, formState)

  if (!fieldContext) {
    throw new Error("useFormField should be used within <FormField>")
  }

  const { id } = itemContext

  return {
    id,
    name: fieldContext.name,
    formItemId: `${id}-form-item`,
    formDescriptionId: `${id}-form-item-description`,
    formMessageId: `${id}-form-item-message`,
    ...fieldState,
  }
}

type FormItemContextValue = {
  id: string
}

const FormItemContext = React.createContext<FormItemContextValue>(
  {} as FormItemContextValue
)

const FormItem = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => {
  const id = React.useId()

  return (
    <FormItemContext.Provider value={{ id }}>
      <div ref={ref} className={cn("space-y-2", className)} {...props} />
    </FormItemContext.Provider>
  )
})
FormItem.displayName = "FormItem"

const FormLabel = React.forwardRef<
  React.ElementRef<typeof LabelPrimitive.Root>,
  React.ComponentPropsWithoutRef<typeof LabelPrimitive.Root>
>(({ className, ...props }, ref) => {
  const { error, formItemId } = useFormField()

  return (
    <Label
      ref={ref}
      className={cn(error && "text-destructive", className)}
      htmlFor={formItemId}
      {...props}
    />
  )
})
FormLabel.displayName = "FormLabel"

const FormControl = React.forwardRef<
  React.ElementRef<typeof Slot>,
  React.ComponentPropsWithoutRef<typeof Slot>
>(({ ...props }, ref) => {
  const { error, formItemId, formDescriptionId, formMessageId } = useFormField()

  return (
    <Slot
      ref={ref}
      id={formItemId}
      aria-describedby={
        !error
          ? `${formDescriptionId}`
          : `${formDescriptionId} ${formMessageId}`
      }
      aria-invalid={!!error}
      {...props}
    />
  )
})
FormControl.displayName = "FormControl"

const FormDescription = React.forwardRef<
  HTMLParagraphElement,
  React.HTMLAttributes<HTMLParagraphElement>
>(({ className, ...props }, ref) => {
  const { formDescriptionId } = useFormField()

  return (
    <p
      ref={ref}
      id={formDescriptionId}
      className={cn("text-sm text-muted-foreground", className)}
      {...props}
    />
  )
})
FormDescription.displayName = "FormDescription"

const FormMessage = React.forwardRef<
  HTMLParagraphElement,
  React.HTMLAttributes<HTMLParagraphElement>
>(({ className, children, ...props }, ref) => {
  const { error, formMessageId } = useFormField()
  const body = error ? String(error?.message) : children

  if (!body) {
    return null
  }

  return (
    <p
      ref={ref}
      id={formMessageId}
      className={cn("text-sm font-medium text-destructive", className)}
      {...props}
    >
      {body}
    </p>
  )
})
FormMessage.displayName = "FormMessage"

export {
  useFormField,
  Form,
  FormItem,
  FormLabel,
  FormControl,
  FormDescription,
  FormMessage,
  FormField,
}

/**
 * Cache Configuration
 * Centralized caching strategies for optimal performance
 */

import { QueryClient } from '@tanstack/react-query';

// Cache durations in milliseconds
export const CACHE_TIMES = {
  VERY_SHORT: 1 * 60 * 1000,      // 1 minute
  SHORT: 5 * 60 * 1000,           // 5 minutes
  MEDIUM: 15 * 60 * 1000,         // 15 minutes
  LONG: 30 * 60 * 1000,           // 30 minutes
  VERY_LONG: 60 * 60 * 1000,      // 1 hour
  PERSISTENT: 24 * 60 * 60 * 1000  // 24 hours
};

// Query keys for consistent caching
export const QUERY_KEYS = {
  PRODUCTS: 'products',
  PRODUCT_DETAIL: 'product-detail',
  CATEGORIES: 'categories',
  CART: 'cart',
  USER: 'user',
  ORDERS: 'orders',
  ADMIN_STATS: 'admin-stats',
  ADMIN_PRODUCTS: 'admin-products',
  ADMIN_ORDERS: 'admin-orders',
  ADMIN_USERS: 'admin-users'
} as const;

// Cache configuration by data type
export const CACHE_CONFIG = {
  // Static or rarely changing data
  categories: {
    staleTime: CACHE_TIMES.VERY_LONG,
    gcTime: CACHE_TIMES.PERSISTENT
  },
  
  // Product data - moderate caching
  products: {
    staleTime: CACHE_TIMES.MEDIUM,
    gcTime: CACHE_TIMES.LONG
  },
  
  // User-specific data - short caching
  cart: {
    staleTime: CACHE_TIMES.SHORT,
    gcTime: CACHE_TIMES.MEDIUM
  },
  
  // Real-time data - very short caching
  orders: {
    staleTime: CACHE_TIMES.VERY_SHORT,
    gcTime: CACHE_TIMES.SHORT
  },
  
  // Admin data - short caching for freshness
  admin: {
    staleTime: CACHE_TIMES.SHORT,
    gcTime: CACHE_TIMES.MEDIUM
  }
};

// Browser storage configuration
export const STORAGE_CONFIG = {
  localStorage: {
    maxAge: CACHE_TIMES.PERSISTENT,
    serialize: JSON.stringify,
    deserialize: JSON.parse
  },
  sessionStorage: {
    maxAge: CACHE_TIMES.LONG,
    serialize: JSON.stringify,
    deserialize: JSON.parse
  },
  indexedDB: {
    maxAge: CACHE_TIMES.PERSISTENT * 7, // 7 days
    dbName: 'naaz-cache',
    version: 1
  }
};

// Memory cache for frequently accessed data
class MemoryCache {
  private cache = new Map<string, { data: any; timestamp: number; ttl: number }>();
  private maxSize = 100; // Maximum number of entries

  set(key: string, data: any, ttl: number = CACHE_TIMES.SHORT): void {
    // Remove oldest entries if cache is full
    if (this.cache.size >= this.maxSize) {
      const oldestKey = this.cache.keys().next().value;
      this.cache.delete(oldestKey);
    }

    this.cache.set(key, {
      data,
      timestamp: Date.now(),
      ttl
    });
  }

  get(key: string): any | null {
    const entry = this.cache.get(key);
    if (!entry) return null;

    // Check if entry has expired
    if (Date.now() - entry.timestamp > entry.ttl) {
      this.cache.delete(key);
      return null;
    }

    return entry.data;
  }

  delete(key: string): void {
    this.cache.delete(key);
  }

  clear(): void {
    this.cache.clear();
  }

  size(): number {
    return this.cache.size;
  }
}

export const memoryCache = new MemoryCache();

// Optimized Query Client configuration with persistence
export const createOptimizedQueryClient = (): QueryClient => {
  const queryClient = new QueryClient({
    defaultOptions: {
      queries: {
        // Default cache settings
        staleTime: CACHE_TIMES.SHORT,
        gcTime: CACHE_TIMES.MEDIUM,
        
        // Retry configuration
        retry: (failureCount, error: any) => {
          // Don't retry on 4xx errors except timeout and rate limit
          if (error?.status && error.status >= 400 && error.status < 500) {
            return error.status === 408 || error.status === 429;
          }
          return failureCount < 2;
        },
        
        // Progressive retry delay
        retryDelay: (attemptIndex) => Math.min(1000 * 2 ** attemptIndex, 30000),
        
        // Network mode for offline support
        networkMode: 'offlineFirst',
        
        // Refetch configuration
        refetchOnWindowFocus: false,
        refetchOnReconnect: true,
        refetchOnMount: true
      },
      
      mutations: {
        // Mutation retry configuration
        retry: (failureCount, error: any) => {
          // Don't retry mutations on client errors
          if (error?.status && error.status >= 400 && error.status < 500) {
            return false;
          }
          return failureCount < 1; // Only retry once for mutations
        },
        
        networkMode: 'offlineFirst'
      }
    }
  });

  // Note: Persistence can be added later with proper packages

  return queryClient;
};

// Cache invalidation strategies
export const CACHE_INVALIDATION = {
  // Invalidate product cache when product is updated
  onProductUpdate: (queryClient: QueryClient, productId?: string) => {
    queryClient.invalidateQueries({ queryKey: [QUERY_KEYS.PRODUCTS] });
    if (productId) {
      queryClient.invalidateQueries({ queryKey: [QUERY_KEYS.PRODUCT_DETAIL, productId] });
    }
  },
  
  // Invalidate cart cache when cart is updated
  onCartUpdate: (queryClient: QueryClient) => {
    queryClient.invalidateQueries({ queryKey: [QUERY_KEYS.CART] });
  },
  
  // Invalidate order cache when order is created/updated
  onOrderUpdate: (queryClient: QueryClient) => {
    queryClient.invalidateQueries({ queryKey: [QUERY_KEYS.ORDERS] });
    queryClient.invalidateQueries({ queryKey: [QUERY_KEYS.ADMIN_ORDERS] });
  },
  
  // Invalidate user cache when user data changes
  onUserUpdate: (queryClient: QueryClient) => {
    queryClient.invalidateQueries({ queryKey: [QUERY_KEYS.USER] });
  },
  
  // Clear all cache on logout
  onLogout: (queryClient: QueryClient) => {
    queryClient.clear();
  }
};

// Prefetch strategies for better performance
export const PREFETCH_STRATEGIES = {
  // Prefetch product details when hovering over product cards
  prefetchProductDetails: (queryClient: QueryClient, productId: string) => {
    queryClient.prefetchQuery({
      queryKey: [QUERY_KEYS.PRODUCT_DETAIL, productId],
      staleTime: CACHE_TIMES.MEDIUM
    });
  },
  
  // Prefetch cart data on app initialization
  prefetchCartData: (queryClient: QueryClient) => {
    queryClient.prefetchQuery({
      queryKey: [QUERY_KEYS.CART],
      staleTime: CACHE_TIMES.SHORT
    });
  },
  
  // Prefetch categories for navigation
  prefetchCategories: (queryClient: QueryClient) => {
    queryClient.prefetchQuery({
      queryKey: [QUERY_KEYS.CATEGORIES],
      staleTime: CACHE_TIMES.VERY_LONG
    });
  }
};

export default {
  CACHE_TIMES,
  QUERY_KEYS,
  CACHE_CONFIG,
  createOptimizedQueryClient,
  CACHE_INVALIDATION,
  PREFETCH_STRATEGIES
};

import React, { createContext, useContext, useState, ReactNode, useEffect } from 'react';
import { supabase } from '@/integrations/supabase/client';
import type { Session } from '@supabase/supabase-js';
import { useRoleFetch } from '@/lib/hooks/useRoleFetch';
import { useAddressManagement } from '@/lib/hooks/useAddressManagement';
import { clearAuthCache } from '@/lib/utils/authOperations';
import type { AuthUser, AuthContextType, Order, LoginResult } from '@/lib/types/auth';
import { errorMonitoring } from '../services/ErrorMonitoring';

const AuthContext = createContext<AuthContextType | undefined>(undefined);

const ADMIN_CACHE_KEY = 'admin-status';
const ADMIN_CACHE_EXPIRY_KEY = 'admin-status-expiry';
const CACHE_DURATION = 5 * 60 * 1000; // 5 minutes

function getCachedAdminStatus(userId: string): boolean | null {
  try {
    const cached = sessionStorage.getItem(`${ADMIN_CACHE_KEY}-${userId}`);
    const expiry = sessionStorage.getItem(`${ADMIN_CACHE_EXPIRY_KEY}-${userId}`);
    if (cached && expiry && Date.now() < parseInt(expiry)) {
      return cached === 'true';
    }
  } catch (error) {
    import('../utils/consoleMigration').then(({ logWarning }) => {
      logWarning('Failed to get cached admin status', { error });
    });
  }
  return null;
}

function setCachedAdminStatus(userId: string, isAdmin: boolean) {
  try {
    sessionStorage.setItem(`${ADMIN_CACHE_KEY}-${userId}`, isAdmin.toString());
    sessionStorage.setItem(`${ADMIN_CACHE_EXPIRY_KEY}-${userId}`, (Date.now() + CACHE_DURATION).toString());
  } catch (error) {
    import('../utils/consoleMigration').then(({ logWarning }) => {
      logWarning('Failed to cache admin status', { error });
    });
  }
}

async function checkAdminStatus(userId?: string): Promise<boolean> {
  if (!userId) return false;
  
  // Check cache first
  const cachedResult = getCachedAdminStatus(userId);
  if (cachedResult !== null) return cachedResult;
  
  try {
    const { data: isAdminResult, error: adminError } = await supabase.rpc('is_admin');
    if (adminError) {
      import('../utils/consoleMigration').then(({ handleDatabaseError }) => {
        handleDatabaseError(adminError, 'admin_check');
      });
      return false;
    }
    const isAdmin = Boolean(isAdminResult);
    setCachedAdminStatus(userId, isAdmin);
    return isAdmin;
  } catch (error) {
    import('../utils/consoleMigration').then(({ handleAuthError }) => {
      handleAuthError(error, { operation: 'admin_check' });
    });
    return false;
  }
}

export const AuthProvider: React.FC<{ children: ReactNode }> = ({ children }) => {
  const [user, setUser] = useState<AuthUser | null>(null);
  const [session, setSession] = useState<Session | null>(null);
  const [loading, setLoading] = useState(true);
  const [orders] = useState<Order[]>([]);
  const [isAdmin, setIsAdmin] = useState(false);

  const { fetchUserRoles } = useRoleFetch();
  const { addresses, addAddress, updateAddress, deleteAddress } = useAddressManagement();

  useEffect(() => {
    let isMounted = true;
    
    const handleAuthChange = async (event: string, session: Session | null) => {
      if (!isMounted) return;
      
      import('../utils/consoleMigration').then(({ logInfo }) => {
        logInfo('Auth state change', { event, hasSession: !!session });
      });
      
      setSession(session);
      
      if (session?.user) {
        const userData: AuthUser = {
          ...session.user,
          name: session.user.user_metadata?.name || session.user.email?.split('@')[0] || 'User',
          joinDate: session.user.created_at
        };
        setUser(userData);
        
        // Set user context in error monitoring
        errorMonitoring.setUserContext({
          id: userData.id,
          email: userData.email,
          username: userData.name,
          role: 'user' // Will be updated when roles are fetched
        });
        
        // Fetch user roles
        try {
          const roles = await fetchUserRoles(session.user.id);
          if (isMounted) {
            setUser(prev => prev ? { ...prev, roles } : null);
          }
        } catch (error) {
          import('../utils/consoleMigration').then(({ handleAuthError }) => {
            handleAuthError(error, { operation: 'fetch_user_roles' });
          });
        }
        
        // Check admin status
        const adminStatus = await checkAdminStatus(session.user.id);
        if (isMounted) {
          setIsAdmin(adminStatus);
        }
      } else {
        // User logged out
        setUser(null);
        setIsAdmin(false);
        clearAuthCache();
      }
      
      if (isMounted) {
        setLoading(false);
      }
    };

    const { data: { subscription } } = supabase.auth.onAuthStateChange(handleAuthChange);

    // Initial session check
    const initializeAuth = async () => {
      try {
        const { data: { session } } = await supabase.auth.getSession();
        if (!isMounted) return;
        
        await handleAuthChange('INITIAL_SESSION', session);
      } catch (error) {
        import('../utils/consoleMigration').then(({ handleAuthError }) => {
          handleAuthError(error, { operation: 'auth_initialization' });
        });
        if (isMounted) {
          setUser(null);
          setSession(null);
          setIsAdmin(false);
          setLoading(false);
        }
      }
    };

    initializeAuth();

    // Failsafe timeout
    const timeout = setTimeout(() => {
      if (isMounted) {
        setLoading(false);
      }
    }, 3000);

    return () => {
      isMounted = false;
      clearTimeout(timeout);
      subscription.unsubscribe();
    };
  }, [fetchUserRoles]);

  const login = async (email: string, password: string): Promise<LoginResult> => {
    try {
      const { data, error } = await supabase.auth.signInWithPassword({
        email,
        password,
      });

      if (error) {
        import('../utils/consoleMigration').then(({ handleAuthError }) => {
          handleAuthError(error, { operation: 'login' });
        });
        return { error, user: null, session: null };
      }

      import('../utils/consoleMigration').then(({ logInfo }) => {
        logInfo('Login successful', { hasUser: !!data.user, hasSession: !!data.session });
      });
      return { error: null, user: data.user, session: data.session };
    } catch (err) {
      import('../utils/consoleMigration').then(({ handleAuthError }) => {
        handleAuthError(err, { operation: 'login_exception' });
      });
      return { error: err, user: null, session: null };
    }
  };

  const register = async (userData: { email: string; password: string; name: string }) => {
    try {
      const { data, error } = await supabase.auth.signUp({
        email: userData.email,
        password: userData.password,
        options: {
          data: {
            name: userData.name,
          },
          emailRedirectTo: `${window.location.origin}/`,
        },
      });

      if (error) {
        import('../utils/consoleMigration').then(({ handleAuthError }) => {
          handleAuthError(error, { operation: 'registration' });
        });
        return { error };
      }

      return { error: null, data };
    } catch (err) {
      import('../utils/consoleMigration').then(({ handleAuthError }) => {
        handleAuthError(err, { operation: 'registration_exception' });
      });
      return { error: err };
    }
  };

  const logout = async () => {
    try {
      import('../utils/consoleMigration').then(({ logInfo }) => {
        logInfo('Logging out user', { userId: user?.id });
      });
      
      // Clear cache before logout
      clearAuthCache();
      
      // Perform Supabase logout
      const { error } = await supabase.auth.signOut();
      if (error) {
        import('../utils/consoleMigration').then(({ handleAuthError }) => {
          handleAuthError(error, { operation: 'logout' });
        });
      }
      
      // Clear context state immediately
      setUser(null);
      setSession(null);
      setIsAdmin(false);
      
      // Clear user context in error monitoring
      errorMonitoring.clearUserContext();
      
      import('../utils/consoleMigration').then(({ logInfo }) => {
        logInfo('Logout completed successfully');
      });
    } catch (error) {
      import('../utils/consoleMigration').then(({ handleAuthError }) => {
        handleAuthError(error, { operation: 'logout_exception' });
      });
      // Clear state even on error
      setUser(null);
      setSession(null);
      setIsAdmin(false);
      clearAuthCache();
    }
  };

  const updateProfile = async (userData: Partial<AuthUser>) => {
    if (!user) {
      throw new Error('No user logged in');
    }

    try {
      const { error } = await supabase.auth.updateUser({
        data: userData,
      });

      if (error) {
        import('../utils/consoleMigration').then(({ handleAuthError }) => {
          handleAuthError(error, { operation: 'profile_update' });
        });
        throw error;
      }

      // Update local user state
      setUser(prev => prev ? { ...prev, ...userData } : null);
    } catch (error) {
      import('../utils/consoleMigration').then(({ handleAuthError }) => {
        handleAuthError(error, { operation: 'profile_update_exception' });
      });
      throw error;
    }
  };

  import('../utils/consoleMigration').then(({ logDebug }) => {
    logDebug('Auth context state', { 
      hasUser: !!user, 
      isAdmin, 
      roles: user?.roles, 
      loading 
    });
  });

  const value: AuthContextType = {
    user,
    session,
    isAuthenticated: !!user,
    isAdmin,
    login,
    register,
    logout,
    updateProfile,
    orders,
    addresses,
    addAddress,
    updateAddress,
    deleteAddress,
    loading,
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

