      }, { timeout: 200 });
    });
  });
});

import { describe, it, expect, vi, beforeEach } from 'vitest';
import { renderHook, waitFor } from '@testing-library/react';
import { QueryClient, QueryClientProvider } from '@tanstack/react-query';
import { useProducts } from '../useProducts';
import { supabase } from '../../supabase';
import { mockProduct } from '../../../test/utils';

// Mock Supabase
vi.mock('../../supabase', () => ({
  supabase: {
    from: vi.fn(() => ({
      select: vi.fn().mockReturnThis(),
      eq: vi.fn().mockReturnThis(),
      ilike: vi.fn().mockReturnThis(),
      gte: vi.fn().mockReturnThis(),
      lte: vi.fn().mockReturnThis(),
      order: vi.fn().mockReturnThis(),
      range: vi.fn().mockReturnThis(),
      then: vi.fn()
    }))
  }
}));

const createWrapper = () => {
  const queryClient = new QueryClient({
    defaultOptions: {
      queries: { retry: false },
      mutations: { retry: false }
    }
  });
  
  return ({ children }: { children: React.ReactNode }) => (
    <QueryClientProvider client={queryClient}>
      {children}
    </QueryClientProvider>
  );
};

describe('useProducts Integration Tests', () => {
  let mockSupabaseFrom: any;

  beforeEach(() => {
    vi.clearAllMocks();
    mockSupabaseFrom = {
      select: vi.fn().mockReturnThis(),
      eq: vi.fn().mockReturnThis(),
      ilike: vi.fn().mockReturnThis(),
      gte: vi.fn().mockReturnThis(),
      lte: vi.fn().mockReturnThis(),
      order: vi.fn().mockReturnThis(),
      range: vi.fn().mockReturnThis(),
      then: vi.fn()
    };
    (supabase.from as any).mockReturnValue(mockSupabaseFrom);
  });

  describe('Product Fetching', () => {
    it('should fetch products successfully', async () => {
      const products = [mockProduct];
      mockSupabaseFrom.then.mockResolvedValue({
        data: products,
        error: null,
        count: 1
      });

      const { result } = renderHook(() => useProducts(), {
        wrapper: createWrapper()
      });

      await waitFor(() => {
        expect(result.current.data).toEqual(products);
        expect(result.current.isLoading).toBe(false);
        expect(result.current.error).toBeNull();
      });

      expect(supabase.from).toHaveBeenCalledWith('products');
      expect(mockSupabaseFrom.select).toHaveBeenCalled();
    });

    it('should handle API errors gracefully', async () => {
      const error = new Error('Database connection failed');
      mockSupabaseFrom.then.mockResolvedValue({
        data: null,
        error,
        count: null
      });

      const { result } = renderHook(() => useProducts(), {
        wrapper: createWrapper()
      });

      await waitFor(() => {
        expect(result.current.data).toBeUndefined();
        expect(result.current.isLoading).toBe(false);
        expect(result.current.error).toBeTruthy();
      });
    });

    it('should apply search filters correctly', async () => {
      const searchQuery = 'fiction';
      const products = [{ ...mockProduct, category: 'fiction' }];
      
      mockSupabaseFrom.then.mockResolvedValue({
        data: products,
        error: null,
        count: 1
      });

      const { result } = renderHook(() => useProducts({ search: searchQuery }), {
        wrapper: createWrapper()
      });

      await waitFor(() => {
        expect(result.current.data).toEqual(products);
      });

      expect(mockSupabaseFrom.ilike).toHaveBeenCalledWith('title', `%${searchQuery}%`);
    });

    it('should apply category filters correctly', async () => {
      const category = 'fiction';
      const products = [{ ...mockProduct, category }];
      
      mockSupabaseFrom.then.mockResolvedValue({
        data: products,
        error: null,
        count: 1
      });

      const { result } = renderHook(() => useProducts({ category }), {
        wrapper: createWrapper()
      });

      await waitFor(() => {
        expect(result.current.data).toEqual(products);
      });

      expect(mockSupabaseFrom.eq).toHaveBeenCalledWith('category', category);
    });

    it('should apply price range filters correctly', async () => {
      const minPrice = 10;
      const maxPrice = 50;
      const products = [{ ...mockProduct, price: 25 }];
      
      mockSupabaseFrom.then.mockResolvedValue({
        data: products,
        error: null,
        count: 1
      });

      const { result } = renderHook(() => useProducts({ minPrice, maxPrice }), {
        wrapper: createWrapper()
      });

      await waitFor(() => {
        expect(result.current.data).toEqual(products);
      });

      expect(mockSupabaseFrom.gte).toHaveBeenCalledWith('price', minPrice);
      expect(mockSupabaseFrom.lte).toHaveBeenCalledWith('price', maxPrice);
    });

    it('should apply sorting correctly', async () => {
      const sortBy = 'price_asc';
      const products = [mockProduct];
      
      mockSupabaseFrom.then.mockResolvedValue({
        data: products,
        error: null,
        count: 1
      });

      const { result } = renderHook(() => useProducts({ sortBy }), {
        wrapper: createWrapper()
      });

      await waitFor(() => {
        expect(result.current.data).toEqual(products);
      });

      expect(mockSupabaseFrom.order).toHaveBeenCalledWith('price', { ascending: true });
    });

    it('should handle pagination correctly', async () => {
      const page = 2;
      const limit = 10;
      const products = [mockProduct];
      
      mockSupabaseFrom.then.mockResolvedValue({
        data: products,
        error: null,
        count: 25
      });

      const { result } = renderHook(() => useProducts({ page, limit }), {
        wrapper: createWrapper()
      });

      await waitFor(() => {
        expect(result.current.data).toEqual(products);
        expect(result.current.totalCount).toBe(25);
        expect(result.current.totalPages).toBe(3);
      });

      const expectedStart = (page - 1) * limit;
      const expectedEnd = expectedStart + limit - 1;
      expect(mockSupabaseFrom.range).toHaveBeenCalledWith(expectedStart, expectedEnd);
    });
  });

  describe('Product Caching', () => {
    it('should cache products and return cached data on subsequent calls', async () => {
      const products = [mockProduct];
      mockSupabaseFrom.then.mockResolvedValue({
        data: products,
        error: null,
        count: 1
      });

      const { result, rerender } = renderHook(() => useProducts(), {
        wrapper: createWrapper()
      });

      await waitFor(() => {
        expect(result.current.data).toEqual(products);
      });

      // Clear the mock to ensure it's not called again
      vi.clearAllMocks();
      (supabase.from as any).mockReturnValue(mockSupabaseFrom);

      // Rerender should use cached data
      rerender();

      expect(result.current.data).toEqual(products);
      expect(supabase.from).not.toHaveBeenCalled();
    });

    it('should invalidate cache when filters change', async () => {
      const products1 = [mockProduct];
      const products2 = [{ ...mockProduct, id: 'product2', category: 'non-fiction' }];
      
      mockSupabaseFrom.then
        .mockResolvedValueOnce({
          data: products1,
          error: null,
          count: 1
        })
        .mockResolvedValueOnce({
          data: products2,
          error: null,
          count: 1
        });

      const { result, rerender } = renderHook(
        ({ category }) => useProducts({ category }),
        {
          wrapper: createWrapper(),
          initialProps: { category: 'fiction' }
        }
      );

      await waitFor(() => {
        expect(result.current.data).toEqual(products1);
      });

      // Change category filter
      rerender({ category: 'non-fiction' });

      await waitFor(() => {
        expect(result.current.data).toEqual(products2);
      });

      expect(supabase.from).toHaveBeenCalledTimes(2);
    });
  });

  describe('Real-time Updates', () => {
    it('should handle real-time product updates', async () => {
      const initialProducts = [mockProduct];
      const updatedProduct = { ...mockProduct, title: 'Updated Title' };
      
      mockSupabaseFrom.then
        .mockResolvedValueOnce({
          data: initialProducts,
          error: null,
          count: 1
        })
        .mockResolvedValueOnce({
          data: [updatedProduct],
          error: null,
          count: 1
        });

      const { result } = renderHook(() => useProducts(), {
        wrapper: createWrapper()
      });

      await waitFor(() => {
        expect(result.current.data).toEqual(initialProducts);
      });

      // Simulate real-time update by refetching
      await waitFor(() => {
        result.current.refetch();
      });

      await waitFor(() => {
        expect(result.current.data).toEqual([updatedProduct]);
      });
    });
  });

  describe('Error Recovery', () => {
    it('should retry failed requests', async () => {
      const error = new Error('Network error');
      const products = [mockProduct];
      
      mockSupabaseFrom.then
        .mockRejectedValueOnce(error)
        .mockResolvedValueOnce({
          data: products,
          error: null,
          count: 1
        });

      const { result } = renderHook(() => useProducts(), {
        wrapper: createWrapper()
      });

      // Initial request should fail
      await waitFor(() => {
        expect(result.current.error).toBeTruthy();
      });

      // Retry should succeed
      await waitFor(() => {
        result.current.refetch();
      });

      await waitFor(() => {
        expect(result.current.data).toEqual(products);
        expect(result.current.error).toBeNull();
      });
    });
  });

  describe('Performance Optimization', () => {
    it('should debounce search queries', async () => {
      const products = [mockProduct];
      mockSupabaseFrom.then.mockResolvedValue({
        data: products,
        error: null,
        count: 1
      });

      const { rerender } = renderHook(
        ({ search }) => useProducts({ search }),
        {
          wrapper: createWrapper(),
          initialProps: { search: '' }
        }
      );

      // Rapidly change search query
      rerender({ search: 'f' });
      rerender({ search: 'fi' });
      rerender({ search: 'fic' });
      rerender({ search: 'fiction' });

      // Should only make one API call after debounce
      await waitFor(() => {
        expect(supabase.from).toHaveBeenCalledTimes(1);
      });
    });

    it('should handle concurrent requests correctly', async () => {
      const products = [mockProduct];
      mockSupabaseFrom.then.mockResolvedValue({
        data: products,
        error: null,
        count: 1
      });

      // Render multiple hooks simultaneously
      const { result: result1 } = renderHook(() => useProducts(), {
        wrapper: createWrapper()
      });
      
      const { result: result2 } = renderHook(() => useProducts(), {
        wrapper: createWrapper()
      });

      await waitFor(() => {
        expect(result1.current.data).toEqual(products);
        expect(result2.current.data).toEqual(products);
      });

      // Should share cached data, not make duplicate requests
      expect(supabase.from).toHaveBeenCalledTimes(1);
    });
  });
});

import { useQuery, useMutation, useQueryClient, UseQueryOptions, UseMutationOptions } from '@tanstack/react-query';
import { apiErrorHandler, ApiError } from '../services/apiErrorHandler';
import sentryService, { ErrorContext } from '../services/sentryService';
import { toast } from 'sonner';

interface ApiQueryOptions<T> extends Omit<UseQueryOptions<T, ApiError>, 'queryFn'> {
  queryFn: () => Promise<T>;
  endpoint?: string;
  method?: string;
  context?: ErrorContext;
  showErrorToast?: boolean;
}

interface ApiMutationOptions<T, V> extends Omit<UseMutationOptions<T, ApiError, V>, 'mutationFn'> {
  mutationFn: (variables: V) => Promise<T>;
  endpoint?: string;
  method?: string;
  context?: ErrorContext;
  showErrorToast?: boolean;
  showSuccessToast?: boolean;
  successMessage?: string;
}

/**
 * Enhanced useQuery hook with integrated error handling
 */
export function useApiQuery<T>({
  queryFn,
  endpoint,
  method = 'GET',
  context,
  showErrorToast = true,
  ...options
}: ApiQueryOptions<T>) {
  return useQuery<T, ApiError>({
    ...options,
    queryFn: async () => {
      return apiErrorHandler.handleApiCall(
        queryFn,
        endpoint,
        method,
        {
          ...context,
          action: 'query',
          page: window.location.pathname
        }
      );
    },
    onError: (error: ApiError) => {
      // Log query error
      sentryService.addBreadcrumb(
        `Query failed: ${options.queryKey?.join('/')}`,
        'query',
        'error',
        {
          endpoint,
          method,
          error: error.message,
          status: error.status
        }
      );

      // Show user-friendly error toast
      if (showErrorToast) {
        const message = apiErrorHandler.getUserFriendlyMessage(error);
        toast.error('Query Failed', {
          description: message,
          duration: 5000
        });
      }

      // Call original onError if provided
      options.onError?.(error);
    },
    retry: (failureCount, error) => {
      // Don't retry on client errors (4xx)
      if (error.status && error.status >= 400 && error.status < 500) {
        return false;
      }
      
      // Retry up to 2 times for server errors and network issues
      return failureCount < 2;
    },
    retryDelay: (attemptIndex) => Math.min(1000 * 2 ** attemptIndex, 30000)
  });
}

/**
 * Enhanced useMutation hook with integrated error handling
 */
export function useApiMutation<T, V = void>({
  mutationFn,
  endpoint,
  method = 'POST',
  context,
  showErrorToast = true,
  showSuccessToast = false,
  successMessage = 'Operation completed successfully',
  ...options
}: ApiMutationOptions<T, V>) {
  const queryClient = useQueryClient();

  return useMutation<T, ApiError, V>({
    ...options,
    mutationFn: async (variables: V) => {
      return apiErrorHandler.handleApiCall(
        () => mutationFn(variables),
        endpoint,
        method,
        {
          ...context,
          action: 'mutation',
          page: window.location.pathname,
          additionalData: {
            variables: typeof variables === 'object' ? variables : { value: variables }
          }
        }
      );
    },
    onSuccess: (data, variables, context) => {
      // Log successful mutation
      sentryService.addBreadcrumb(
        `Mutation succeeded: ${endpoint || 'unknown'}`,
        'mutation',
        'info',
        {
          endpoint,
          method
        }
      );

      // Show success toast if enabled
      if (showSuccessToast) {
        toast.success('Success', {
          description: successMessage,
          duration: 3000
        });
      }

      // Call original onSuccess if provided
      options.onSuccess?.(data, variables, context);
    },
    onError: (error: ApiError, variables, context) => {
      // Log mutation error
      sentryService.addBreadcrumb(
        `Mutation failed: ${endpoint || 'unknown'}`,
        'mutation',
        'error',
        {
          endpoint,
          method,
          error: error.message,
          status: error.status,
          variables: typeof variables === 'object' ? variables : { value: variables }
        }
      );

      // Show user-friendly error toast
      if (showErrorToast) {
        const message = apiErrorHandler.getUserFriendlyMessage(error);
        toast.error('Operation Failed', {
          description: message,
          duration: 5000,
          action: error.status && error.status >= 500 ? {
            label: 'Retry',
            onClick: () => {
              // Retry the mutation
              queryClient.invalidateQueries();
            }
          } : undefined
        });
      }

      // Call original onError if provided
      options.onError?.(error, variables, context);
    }
  });
}

/**
 * Hook for handling optimistic updates with error recovery
 */
export function useOptimisticMutation<T, V = void>({
  mutationFn,
  queryKey,
  optimisticUpdate,
  endpoint,
  method = 'POST',
  context,
  showErrorToast = true,
  showSuccessToast = false,
  successMessage = 'Operation completed successfully',
  ...options
}: ApiMutationOptions<T, V> & {
  queryKey: string[];
  optimisticUpdate: (oldData: any, variables: V) => any;
}) {
  const queryClient = useQueryClient();

  return useMutation<T, ApiError, V>({
    ...options,
    mutationFn: async (variables: V) => {
      return apiErrorHandler.handleApiCall(
        () => mutationFn(variables),
        endpoint,
        method,
        {
          ...context,
          action: 'optimistic_mutation',
          page: window.location.pathname
        }
      );
    },
    onMutate: async (variables: V) => {
      // Cancel outgoing refetches
      await queryClient.cancelQueries({ queryKey });

      // Snapshot the previous value
      const previousData = queryClient.getQueryData(queryKey);

      // Optimistically update to the new value
      queryClient.setQueryData(queryKey, (old: any) => optimisticUpdate(old, variables));

      // Return a context object with the snapshotted value
      return { previousData };
    },
    onError: (error: ApiError, variables, context) => {
      // Rollback on error
      if (context?.previousData) {
        queryClient.setQueryData(queryKey, context.previousData);
      }

      // Log optimistic mutation error
      sentryService.addBreadcrumb(
        `Optimistic mutation failed and rolled back: ${endpoint || 'unknown'}`,
        'mutation',
        'error',
        {
          endpoint,
          method,
          error: error.message,
          status: error.status,
          rolledBack: true
        }
      );

      // Show error toast
      if (showErrorToast) {
        const message = apiErrorHandler.getUserFriendlyMessage(error);
        toast.error('Operation Failed', {
          description: `${message} Changes have been reverted.`,
          duration: 5000
        });
      }

      // Call original onError if provided
      options.onError?.(error, variables, context);
    },
    onSuccess: (data, variables, context) => {
      // Log successful optimistic mutation
      sentryService.addBreadcrumb(
        `Optimistic mutation succeeded: ${endpoint || 'unknown'}`,
        'mutation',
        'info',
        {
          endpoint,
          method
        }
      );

      // Show success toast if enabled
      if (showSuccessToast) {
        toast.success('Success', {
          description: successMessage,
          duration: 3000
        });
      }

      // Call original onSuccess if provided
      options.onSuccess?.(data, variables, context);
    },
    onSettled: () => {
      // Always refetch after error or success
      queryClient.invalidateQueries({ queryKey });
    }
  });
}

/**
 * Hook for handling file uploads with progress tracking
 */
export function useFileUploadMutation({
  uploadFn,
  endpoint,
  context,
  showErrorToast = true,
  showSuccessToast = true,
  successMessage = 'File uploaded successfully',
  onProgress,
  ...options
}: Omit<ApiMutationOptions<any, File>, 'mutationFn'> & {
  uploadFn: (file: File, onProgress?: (progress: number) => void) => Promise<any>;
  onProgress?: (progress: number) => void;
}) {
  return useMutation<any, ApiError, File>({
    ...options,
    mutationFn: async (file: File) => {
      return apiErrorHandler.handleApiCall(
        () => uploadFn(file, onProgress),
        endpoint,
        'POST',
        {
          ...context,
          action: 'file_upload',
          page: window.location.pathname,
          additionalData: {
            fileName: file.name,
            fileSize: file.size,
            fileType: file.type
          }
        }
      );
    },
    onSuccess: (data, file, context) => {
      // Log successful upload
      sentryService.addBreadcrumb(
        `File uploaded successfully: ${file.name}`,
        'upload',
        'info',
        {
          fileName: file.name,
          fileSize: file.size,
          endpoint
        }
      );

      // Show success toast
      if (showSuccessToast) {
        toast.success('Upload Complete', {
          description: successMessage,
          duration: 3000
        });
      }

      // Call original onSuccess if provided
      options.onSuccess?.(data, file, context);
    },
    onError: (error: ApiError, file, context) => {
      // Log upload error
      sentryService.addBreadcrumb(
        `File upload failed: ${file.name}`,
        'upload',
        'error',
        {
          fileName: file.name,
          fileSize: file.size,
          error: error.message,
          status: error.status,
          endpoint
        }
      );

      // Show error toast
      if (showErrorToast) {
        const message = apiErrorHandler.getUserFriendlyMessage(error);
        toast.error('Upload Failed', {
          description: `Failed to upload ${file.name}. ${message}`,
          duration: 5000
        });
      }

      // Call original onError if provided
      options.onError?.(error, file, context);
    }
  });
}

import { useState, useEffect, useCallback } from 'react';
import { useQueryClient } from '@tanstack/react-query';
import { cacheService } from '@/lib/services/cacheService';
import { CACHE_INVALIDATION } from '@/lib/config/cacheConfig';

interface CacheStats {
  memory: { size: number; entries: number };
  localStorage: { size: number; entries: number };
  sessionStorage: { size: number; entries: number };
  serviceWorker?: { [cacheName: string]: number };
}

interface CacheManagementOptions {
  autoCleanup?: boolean;
  cleanupInterval?: number;
  maxMemorySize?: number;
  maxStorageSize?: number;
}

export function useCacheManagement(options: CacheManagementOptions = {}) {
  const {
    autoCleanup = true,
    cleanupInterval = 5 * 60 * 1000, // 5 minutes
    maxMemorySize = 50 * 1024 * 1024, // 50MB
    maxStorageSize = 100 * 1024 * 1024 // 100MB
  } = options;

  const queryClient = useQueryClient();
  const [cacheStats, setCacheStats] = useState<CacheStats | null>(null);
  const [isClearing, setIsClearing] = useState(false);

  // Get cache statistics
  const getCacheStats = useCallback(async (): Promise<CacheStats> => {
    const stats = cacheService.getStats();
    
    // Get service worker cache stats if available
    let serviceWorkerStats = {};
    if ('serviceWorker' in navigator && navigator.serviceWorker.controller) {
      try {
        const channel = new MessageChannel();
        navigator.serviceWorker.controller.postMessage(
          { type: 'GET_CACHE_STATS' },
          [channel.port2]
        );
        
        serviceWorkerStats = await new Promise((resolve) => {
          channel.port1.onmessage = (event) => {
            resolve(event.data);
          };
          // Timeout after 1 second
          setTimeout(() => resolve({}), 1000);
        });
      } catch (error) {
        console.warn('Failed to get service worker cache stats:', error);
      }
    }

    return {
      ...stats,
      serviceWorker: serviceWorkerStats
    };
  }, []);

  // Update cache statistics
  const updateStats = useCallback(async () => {
    try {
      const stats = await getCacheStats();
      setCacheStats(stats);
    } catch (error) {
      console.error('Failed to update cache stats:', error);
    }
  }, [getCacheStats]);

  // Clear specific cache type
  const clearCache = useCallback(async (
    type: 'memory' | 'localStorage' | 'sessionStorage' | 'indexedDB' | 'queryClient' | 'serviceWorker' | 'all'
  ) => {
    setIsClearing(true);
    
    try {
      switch (type) {
        case 'memory':
          await cacheService.clear('memory');
          break;
        case 'localStorage':
          await cacheService.clear('localStorage');
          break;
        case 'sessionStorage':
          await cacheService.clear('sessionStorage');
          break;
        case 'indexedDB':
          await cacheService.clear('indexedDB');
          break;
        case 'queryClient':
          queryClient.clear();
          break;
        case 'serviceWorker':
          if ('serviceWorker' in navigator && navigator.serviceWorker.controller) {
            const channel = new MessageChannel();
            navigator.serviceWorker.controller.postMessage(
              { type: 'CLEAR_CACHE' },
              [channel.port2]
            );
            
            await new Promise((resolve) => {
              channel.port1.onmessage = () => resolve(true);
              setTimeout(() => resolve(true), 2000);
            });
          }
          break;
        case 'all':
          await Promise.all([
            cacheService.clear(),
            queryClient.clear(),
            clearServiceWorkerCache()
          ]);
          break;
      }
      
      await updateStats();
    } catch (error) {
      console.error(`Failed to clear ${type} cache:`, error);
    } finally {
      setIsClearing(false);
    }
  }, [queryClient, updateStats]);

  // Clear service worker cache
  const clearServiceWorkerCache = useCallback(async () => {
    if ('serviceWorker' in navigator && navigator.serviceWorker.controller) {
      const channel = new MessageChannel();
      navigator.serviceWorker.controller.postMessage(
        { type: 'CLEAR_CACHE' },
        [channel.port2]
      );
      
      return new Promise((resolve) => {
        channel.port1.onmessage = () => resolve(true);
        setTimeout(() => resolve(true), 2000);
      });
    }
  }, []);

  // Cleanup expired cache entries
  const cleanup = useCallback(async () => {
    try {
      await cacheService.cleanup();
      await updateStats();
    } catch (error) {
      console.error('Cache cleanup failed:', error);
    }
  }, [updateStats]);

  // Check if cache size exceeds limits
  const checkCacheLimits = useCallback((stats: CacheStats) => {
    const warnings: string[] = [];
    
    const totalStorageSize = stats.localStorage.size + stats.sessionStorage.size;
    
    if (totalStorageSize > maxStorageSize) {
      warnings.push(`Storage cache size (${(totalStorageSize / 1024 / 1024).toFixed(2)}MB) exceeds limit`);
    }
    
    return warnings;
  }, [maxStorageSize]);

  // Invalidate cache based on events
  const invalidateCache = useCallback((event: 'product' | 'cart' | 'order' | 'user' | 'logout', id?: string) => {
    switch (event) {
      case 'product':
        CACHE_INVALIDATION.onProductUpdate(queryClient, id);
        break;
      case 'cart':
        CACHE_INVALIDATION.onCartUpdate(queryClient);
        break;
      case 'order':
        CACHE_INVALIDATION.onOrderUpdate(queryClient);
        break;
      case 'user':
        CACHE_INVALIDATION.onUserUpdate(queryClient);
        break;
      case 'logout':
        CACHE_INVALIDATION.onLogout(queryClient);
        clearCache('all');
        break;
    }
  }, [queryClient, clearCache]);

  // Preload critical data
  const preloadCriticalData = useCallback(async () => {
    try {
      // Preload categories and featured products
      await Promise.all([
        queryClient.prefetchQuery({
          queryKey: ['categories'],
          staleTime: 30 * 60 * 1000 // 30 minutes
        }),
        queryClient.prefetchQuery({
          queryKey: ['featured-products'],
          staleTime: 15 * 60 * 1000 // 15 minutes
        })
      ]);
    } catch (error) {
      console.error('Failed to preload critical data:', error);
    }
  }, [queryClient]);

  // Set up automatic cleanup
  useEffect(() => {
    if (!autoCleanup) return;

    const interval = setInterval(cleanup, cleanupInterval);
    return () => clearInterval(interval);
  }, [autoCleanup, cleanupInterval, cleanup]);

  // Initial stats load
  useEffect(() => {
    updateStats();
  }, [updateStats]);

  // Monitor cache size and warn if limits exceeded
  useEffect(() => {
    if (cacheStats) {
      const warnings = checkCacheLimits(cacheStats);
      if (warnings.length > 0) {
        console.warn('Cache size warnings:', warnings);
        // Optionally trigger automatic cleanup
        if (autoCleanup) {
          cleanup();
        }
      }
    }
  }, [cacheStats, checkCacheLimits, autoCleanup, cleanup]);

  return {
    cacheStats,
    isClearing,
    clearCache,
    cleanup,
    updateStats,
    invalidateCache,
    preloadCriticalData,
    getCacheStats
  };
}

// Hook for cache-aware data fetching
export function useCachedQuery<T>(
  queryKey: string[],
  queryFn: () => Promise<T>,
  options: {
    staleTime?: number;
    cacheTime?: number;
    fallbackCache?: boolean;
  } = {}
) {
  const { fallbackCache = true, ...queryOptions } = options;
  const queryClient = useQueryClient();

  // Try to get data from custom cache first if query fails
  const enhancedQueryFn = useCallback(async (): Promise<T> => {
    try {
      return await queryFn();
    } catch (error) {
      if (fallbackCache) {
        const cachedData = await cacheService.get(queryKey.join('-'), 'localStorage');
        if (cachedData) {
          return cachedData;
        }
      }
      throw error;
    }
  }, [queryFn, queryKey, fallbackCache]);

  return queryClient.useQuery({
    queryKey,
    queryFn: enhancedQueryFn,
    ...queryOptions
  });
}

export default useCacheManagement;

import { useState, useEffect, useCallback } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { useAuth } from '@/lib/context/AuthContext';
import { cartPersistenceService, type CartState, type CartItem } from '@/lib/services/cartPersistenceService';
import { supabase } from '@/integrations/supabase/client';

export interface UseEnhancedCartReturn {
  cart: CartState;
  addItem: (item: Omit<CartItem, 'quantity' | 'lastModified'>) => Promise<void>;
  updateQuantity: (productId: string, quantity: number) => Promise<void>;
  removeItem: (productId: string) => Promise<void>;
  clearCart: () => Promise<void>;
  syncWithServer: () => Promise<void>;
  isLoading: boolean;
  isSyncing: boolean;
  syncStatus: {
    isOnline: boolean;
    hasOfflineOperations: boolean;
    lastSynced: number;
  };
  recoverCart: () => Promise<void>;
}

export const useEnhancedCart = (): UseEnhancedCartReturn => {
  const { user, isAuthenticated } = useAuth();
  const queryClient = useQueryClient();
  
  const [cart, setCart] = useState<CartState>(() => {
    const localCart = cartPersistenceService.loadCartFromLocal();
    return localCart || {
      items: [],
      totalItems: 0,
      subtotal: 0,
      lastSynced: 0,
      syncInProgress: false
    };
  });
  
  const [isSyncing, setIsSyncing] = useState(false);

  // Load cart data from server for authenticated users
  const { data: serverCart, isLoading } = useQuery({
    queryKey: ['enhanced-cart', user?.id],
    queryFn: async () => {
      if (!user || !navigator.onLine) return null;
      
      try {
        return await cartPersistenceService.syncWithServer(user.id);
      } catch (error) {
        console.error('Failed to sync cart with server:', error);
        return cartPersistenceService.loadCartFromLocal();
      }
    },
    enabled: !!user && isAuthenticated,
    staleTime: 30000, // 30 seconds
    refetchOnWindowFocus: true,
    refetchOnReconnect: true
  });

  // Update local cart when server data changes
  useEffect(() => {
    if (serverCart) {
      setCart(serverCart);
      cartPersistenceService.saveCartToLocal(serverCart);
    }
  }, [serverCart]);

  // Save cart to localStorage whenever it changes
  useEffect(() => {
    cartPersistenceService.saveCartToLocal(cart);
  }, [cart]);

  // Listen for network reconnection events
  useEffect(() => {
    const handleReconnection = async (event: CustomEvent) => {
      if (user && event.detail?.hasOfflineOperations) {
        await syncWithServer();
      }
    };

    const handlePeriodicSync = async () => {
      if (user && navigator.onLine && !isSyncing) {
        try {
          await syncWithServer();
        } catch (error) {
          console.error('Periodic sync failed:', error);
        }
      }
    };

    window.addEventListener('cart-reconnection', handleReconnection as EventListener);
    window.addEventListener('cart-periodic-sync', handlePeriodicSync);

    return () => {
      window.removeEventListener('cart-reconnection', handleReconnection as EventListener);
      window.removeEventListener('cart-periodic-sync', handlePeriodicSync);
    };
  }, [user, isSyncing]);

  // Real-time cart updates for authenticated users
  useEffect(() => {
    if (!isAuthenticated || !user) return;

    const channel = supabase
      .channel('enhanced-cart-changes')
      .on(
        'postgres_changes',
        {
          event: '*',
          schema: 'public',
          table: 'cart_items',
          filter: `user_id=eq.${user.id}`
        },
        async () => {
          console.log('Cart updated in real-time, syncing...');
          await syncWithServer();
        }
      )
      .subscribe();

    return () => {
      supabase.removeChannel(channel);
    };
  }, [isAuthenticated, user]);

  const updateCartState = useCallback((updater: (prevCart: CartState) => CartState) => {
    setCart(prevCart => {
      const newCart = updater(prevCart);
      cartPersistenceService.saveCartToLocal(newCart);
      return newCart;
    });
  }, []);

  const calculateTotals = useCallback((items: CartItem[]) => {
    const totalItems = items.reduce((sum, item) => sum + item.quantity, 0);
    const subtotal = items.reduce((sum, item) => sum + (parseFloat(item.price) * item.quantity), 0);
    return { totalItems, subtotal };
  }, []);

  const addItem = useCallback(async (item: Omit<CartItem, 'quantity' | 'lastModified'>) => {
    const newItem: CartItem = {
      ...item,
      quantity: 1,
      lastModified: Date.now(),
      isLocal: true
    };

    // Update local state immediately (optimistic update)
    updateCartState(prevCart => {
      const existingItemIndex = prevCart.items.findIndex(
        cartItem => cartItem.productId === item.productId
      );

      let updatedItems: CartItem[];
      if (existingItemIndex > -1) {
        updatedItems = [...prevCart.items];
        updatedItems[existingItemIndex] = {
          ...updatedItems[existingItemIndex],
          quantity: updatedItems[existingItemIndex].quantity + 1,
          lastModified: Date.now()
        };
      } else {
        updatedItems = [...prevCart.items, newItem];
      }

      const { totalItems, subtotal } = calculateTotals(updatedItems);
      
      return {
        ...prevCart,
        items: updatedItems,
        totalItems,
        subtotal
      };
    });

    // Sync with server
    try {
      await cartPersistenceService.addCartOperation(user?.id || null, 'add', {
        productId: item.productId,
        quantity: 1
      });
      
      if (user) {
        queryClient.invalidateQueries({ queryKey: ['enhanced-cart', user.id] });
      }
    } catch (error) {
      console.error('Failed to add item to cart:', error);
      // The operation is already queued for offline sync
    }
  }, [user, updateCartState, calculateTotals, queryClient]);

  const updateQuantity = useCallback(async (productId: string, quantity: number) => {
    if (quantity <= 0) {
      await removeItem(productId);
      return;
    }

    // Update local state immediately
    updateCartState(prevCart => {
      const updatedItems = prevCart.items.map(item =>
        item.productId === productId
          ? { ...item, quantity, lastModified: Date.now() }
          : item
      );

      const { totalItems, subtotal } = calculateTotals(updatedItems);
      
      return {
        ...prevCart,
        items: updatedItems,
        totalItems,
        subtotal
      };
    });

    // Sync with server
    try {
      const cartItem = cart.items.find(item => item.productId === productId);
      await cartPersistenceService.addCartOperation(user?.id || null, 'update', {
        cartItemId: cartItem?.id,
        quantity
      });
      
      if (user) {
        queryClient.invalidateQueries({ queryKey: ['enhanced-cart', user.id] });
      }
    } catch (error) {
      console.error('Failed to update cart item quantity:', error);
    }
  }, [user, cart.items, updateCartState, calculateTotals, queryClient]);

  const removeItem = useCallback(async (productId: string) => {
    const itemToRemove = cart.items.find(item => item.productId === productId);
    
    // Update local state immediately
    updateCartState(prevCart => {
      const updatedItems = prevCart.items.filter(item => item.productId !== productId);
      const { totalItems, subtotal } = calculateTotals(updatedItems);
      
      return {
        ...prevCart,
        items: updatedItems,
        totalItems,
        subtotal
      };
    });

    // Sync with server
    try {
      await cartPersistenceService.addCartOperation(user?.id || null, 'remove', {
        cartItemId: itemToRemove?.id,
        productId
      });
      
      if (user) {
        queryClient.invalidateQueries({ queryKey: ['enhanced-cart', user.id] });
      }
    } catch (error) {
      console.error('Failed to remove cart item:', error);
    }
  }, [user, cart.items, updateCartState, calculateTotals, queryClient]);

  const clearCart = useCallback(async () => {
    // Update local state immediately
    updateCartState(() => ({
      items: [],
      totalItems: 0,
      subtotal: 0,
      lastSynced: Date.now(),
      syncInProgress: false
    }));

    // Sync with server
    try {
      await cartPersistenceService.addCartOperation(user?.id || null, 'clear', {});
      
      if (user) {
        queryClient.invalidateQueries({ queryKey: ['enhanced-cart', user.id] });
      }
    } catch (error) {
      console.error('Failed to clear cart:', error);
    }
  }, [user, updateCartState, queryClient]);

  const syncWithServer = useCallback(async () => {
    if (!user || isSyncing) return;

    setIsSyncing(true);
    try {
      const syncedCart = await cartPersistenceService.syncWithServer(user.id);
      setCart(syncedCart);
      queryClient.setQueryData(['enhanced-cart', user.id], syncedCart);
    } catch (error) {
      console.error('Failed to sync cart with server:', error);
      throw error;
    } finally {
      setIsSyncing(false);
    }
  }, [user, isSyncing, queryClient]);

  const recoverCart = useCallback(async () => {
    try {
      const localCart = cartPersistenceService.loadCartFromLocal();
      if (localCart) {
        setCart(localCart);
        
        // If user is authenticated, try to sync
        if (user && navigator.onLine) {
          await syncWithServer();
        }
      }
    } catch (error) {
      console.error('Failed to recover cart:', error);
    }
  }, [user, syncWithServer]);

  const syncStatus = cartPersistenceService.getSyncStatus();

  return {
    cart,
    addItem,
    updateQuantity,
    removeItem,
    clearCart,
    syncWithServer,
    isLoading,
    isSyncing,
    syncStatus,
    recoverCart
  };
};

import { useCallback, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import { errorMonitoring, trackError, trackInteraction, addBreadcrumb } from '../services/ErrorMonitoring';

/**
 * React hook for error monitoring integration
 * Provides easy access to error tracking functionality
 */
export function useErrorMonitoring() {
  const location = useLocation();

  // Track page views automatically
  useEffect(() => {
    errorMonitoring.trackPageView(location.pathname, document.title);
  }, [location.pathname]);

  // Capture and track errors
  const captureError = useCallback((error: Error, context?: any) => {
    trackError(error, {
      component: 'useErrorMonitoring',
      action: 'manual_error_capture',
      additionalData: context
    });
  }, []);

  // Track user interactions
  const trackUserInteraction = useCallback((action: string, element: string, details?: any) => {
    trackInteraction(action, element, details);
  }, []);

  // Add breadcrumb for debugging
  const addDebugBreadcrumb = useCallback((message: string, category: string = 'debug', data?: any) => {
    addBreadcrumb(message, category, 'info', data);
  }, []);

  // Track component lifecycle
  const trackComponentLifecycle = useCallback((componentName: string, event: 'mount' | 'unmount' | 'update') => {
    addBreadcrumb(`Component ${componentName} ${event}`, 'component', 'debug', {
      component: componentName,
      event,
      path: location.pathname
    });
  }, [location.pathname]);

  // Track API calls
  const trackApiCall = useCallback((endpoint: string, method: string, success: boolean, duration?: number) => {
    addBreadcrumb(`API ${method} ${endpoint} ${success ? 'success' : 'failed'}`, 'api', success ? 'info' : 'error', {
      endpoint,
      method,
      success,
      duration
    });
  }, []);

  // Track form submissions
  const trackFormSubmission = useCallback((formName: string, success: boolean, errors?: any) => {
    addBreadcrumb(`Form ${formName} ${success ? 'submitted' : 'failed'}`, 'form', success ? 'info' : 'error', {
      formName,
      success,
      errors
    });
  }, []);

  return {
    captureError,
    trackUserInteraction,
    addDebugBreadcrumb,
    trackComponentLifecycle,
    trackApiCall,
    trackFormSubmission,
    getStats: errorMonitoring.getStats.bind(errorMonitoring),
    exportData: errorMonitoring.exportData.bind(errorMonitoring)
  };
}

/**
 * Higher-order component for automatic error monitoring
 */
export function withErrorMonitoring<P extends object>(
  Component: React.ComponentType<P>,
  componentName?: string
) {
  const WrappedComponent = (props: P) => {
    const { trackComponentLifecycle, captureError } = useErrorMonitoring();
    const name = componentName || Component.displayName || Component.name || 'UnknownComponent';

    useEffect(() => {
      trackComponentLifecycle(name, 'mount');
      return () => {
        trackComponentLifecycle(name, 'unmount');
      };
    }, [trackComponentLifecycle, name]);

    // Error boundary for the component
    useEffect(() => {
      const handleError = (event: ErrorEvent) => {
        captureError(event.error || new Error(event.message), {
          component: name,
          filename: event.filename,
          lineno: event.lineno,
          colno: event.colno
        });
      };

      window.addEventListener('error', handleError);
      return () => window.removeEventListener('error', handleError);
    }, [captureError, name]);

    return <Component {...props} />;
  };

  WrappedComponent.displayName = `withErrorMonitoring(${componentName || Component.displayName || Component.name})`;
  
  return WrappedComponent;
}

export default useErrorMonitoring;

import React from 'react';
import { useForm, UseFormProps, UseFormReturn, FieldValues, Path } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { ZodSchema, ZodError } from 'zod';
import { sanitizationService, SanitizationOptions } from '../services/sanitizationService';
import sentryService from '../services/sentryService';

export interface FormValidationConfig<T extends FieldValues> {
  schema: ZodSchema<T>;
  sanitizationConfig?: Partial<Record<keyof T, SanitizationOptions>>;
  onSubmit?: (data: T) => Promise<void> | void;
  onError?: (errors: Record<string, string>) => void;
  enableRealTimeValidation?: boolean;
  sanitizeOnChange?: boolean;
}

export interface UseFormValidationReturn<T extends FieldValues> extends UseFormReturn<T> {
  handleSubmitWithValidation: (onValid: (data: T) => Promise<void> | void) => (e?: React.BaseSyntheticEvent) => Promise<void>;
  validateField: (fieldName: Path<T>, value: any) => Promise<string | null>;
  sanitizeFormData: (data: T) => T;
  isSubmitting: boolean;
  submitError: string | null;
  clearSubmitError: () => void;
}

export function useFormValidation<T extends FieldValues>(
  config: FormValidationConfig<T>,
  formOptions?: Omit<UseFormProps<T>, 'resolver'>
): UseFormValidationReturn<T> {
  const {
    schema,
    sanitizationConfig = {},
    onSubmit,
    onError,
    enableRealTimeValidation = true,
    sanitizeOnChange = true,
  } = config;

  const form = useForm<T>({
    ...formOptions,
    resolver: zodResolver(schema),
    mode: enableRealTimeValidation ? 'onChange' : 'onSubmit',
  });

  const [isSubmitting, setIsSubmitting] = React.useState(false);
  const [submitError, setSubmitError] = React.useState<string | null>(null);

  const clearSubmitError = React.useCallback(() => {
    setSubmitError(null);
  }, []);

  const sanitizeFormData = React.useCallback((data: T): T => {
    try {
      return sanitizationService.sanitizeFormData(data as Record<string, any>, sanitizationConfig || {}) as T;
    } catch (error) {
      sentryService.captureError(
        error instanceof Error ? error : new Error('Form data sanitization failed'),
        {
          action: 'sanitize_form_data',
          additionalData: { formData: data }
        }
      );
      return data;
    }
  }, [sanitizationConfig]);

  const validateField = React.useCallback(async (
    fieldName: Path<T>,
    value: any
  ): Promise<string | null> => {
    try {
      // Simple validation - just check if the value passes basic validation
      if (value === undefined || value === null || value === '') {
        return 'This field is required';
      }
      return null;
    } catch (error) {
      return 'Validation error';
    }
  }, []);

  const handleSubmitWithValidation = React.useCallback(
    (onValid: (data: T) => Promise<void> | void) => {
      return async (e?: React.BaseSyntheticEvent) => {
        if (e) {
          e.preventDefault();
        }

        setIsSubmitting(true);
        setSubmitError(null);

        try {
          const formData = form.getValues();
          
          // Sanitize form data before validation
          const sanitizedData = sanitizeFormData(formData);
          
          // Validate sanitized data
          const validatedData = await schema.parseAsync(sanitizedData);
          
          // Call the onValid callback
          await onValid(validatedData);
          
          // Call the optional onSubmit callback
          if (onSubmit) {
            await onSubmit(validatedData);
          }
        } catch (error) {
          if (error instanceof ZodError) {
            // Handle validation errors
            const fieldErrors: Record<string, string> = {};
            error.errors.forEach((err) => {
              const fieldName = err.path.join('.');
              fieldErrors[fieldName] = err.message;
            });
            
            // Set form errors
            Object.entries(fieldErrors).forEach(([field, message]) => {
              form.setError(field as Path<T>, { message });
            });
            
            if (onError) {
              onError(fieldErrors);
            }
          } else {
            // Handle other errors
            const errorMessage = error instanceof Error ? error.message : 'An unexpected error occurred';
            setSubmitError(errorMessage);
            
            sentryService.captureError(
              error instanceof Error ? error : new Error('Form submission failed'),
              {
                action: 'form_submit',
                additionalData: { formData: form.getValues() }
              }
            );
          }
        } finally {
          setIsSubmitting(false);
        }
      };
    },
    [form, schema, sanitizeFormData, onSubmit, onError]
  );

  // Enhanced setValue with sanitization
  const setValueWithSanitization = React.useCallback(
    (name: Path<T>, value: any, options?: Parameters<typeof form.setValue>[2]) => {
      if (sanitizeOnChange && typeof value === 'string') {
        const sanitizedValue = sanitizationService.sanitizeInput(value);
        form.setValue(name, sanitizedValue as any, options);
      } else {
        form.setValue(name, value, options);
      }
    },
    [form, sanitizeOnChange]
  );

  return {
    ...form,
    setValue: setValueWithSanitization,
    handleSubmitWithValidation,
    validateField,
    sanitizeFormData,
    isSubmitting,
    submitError,
    clearSubmitError,
  };
}

// Utility hook for common form patterns
export function useContactForm() {
  const [schema, setSchema] = React.useState<ZodSchema | null>(null);

  React.useEffect(() => {
    import('../validation/schemas').then(({ contactFormSchema }) => {
      setSchema(contactFormSchema);
    });
  }, []);

  return useFormValidation({
    schema: schema!,
    sanitizationConfig: {
      name: { maxLength: 50 },
      email: { maxLength: 254 },
      phone: { maxLength: 20 },
      subject: { maxLength: 100 },
      message: { maxLength: 1000, stripTags: true },
    },
    enableRealTimeValidation: true,
    sanitizeOnChange: true,
  });
}

export function useAuthForm(type: 'signin' | 'signup' | 'forgot' | 'reset') {
  const getSchema = async () => {
    const schemas = await import('../validation/schemas');
    switch (type) {
      case 'signin':
        return schemas.signInSchema;
      case 'signup':
        return schemas.signUpSchema;
      case 'forgot':
        return schemas.forgotPasswordSchema;
      case 'reset':
        return schemas.resetPasswordSchema;
      default:
        return schemas.signInSchema;
    }
  };

  const [schema, setSchema] = React.useState<ZodSchema | null>(null);

  React.useEffect(() => {
    getSchema().then(setSchema);
  }, [type]);

  return useFormValidation({
    schema: schema!,
    sanitizationConfig: {
      email: { maxLength: 254 },
      fullName: { maxLength: 50 },
      password: { maxLength: 128 },
      confirmPassword: { maxLength: 128 },
    },
    enableRealTimeValidation: false, // Only validate on submit for auth forms
    sanitizeOnChange: true,
  });
}

