import { useState, useCallback, useRef, useEffect } from 'react';

export interface LoadingState {
  isLoading: boolean;
  progress?: number;
  message?: string;
  error?: string;
}

export interface LoadingOptions {
  initialMessage?: string;
  showProgress?: boolean;
  timeout?: number;
  onTimeout?: () => void;
}

export const useLoadingState = (options: LoadingOptions = {}) => {
  const [state, setState] = useState<LoadingState>({
    isLoading: false,
    progress: 0,
    message: options.initialMessage,
    error: undefined
  });

  const timeoutRef = useRef<NodeJS.Timeout>();
  const startTimeRef = useRef<number>();

  const startLoading = useCallback((message?: string) => {
    setState({
      isLoading: true,
      progress: 0,
      message: message || options.initialMessage,
      error: undefined
    });

    startTimeRef.current = Date.now();

    // Set timeout if specified
    if (options.timeout) {
      timeoutRef.current = setTimeout(() => {
        setState(prev => ({
          ...prev,
          error: 'Operation timed out'
        }));
        options.onTimeout?.();
      }, options.timeout);
    }
  }, [options.initialMessage, options.timeout, options.onTimeout]);

  const updateProgress = useCallback((progress: number, message?: string) => {
    setState(prev => ({
      ...prev,
      progress: Math.min(100, Math.max(0, progress)),
      message: message || prev.message
    }));
  }, []);

  const setMessage = useCallback((message: string) => {
    setState(prev => ({
      ...prev,
      message
    }));
  }, []);

  const setError = useCallback((error: string) => {
    setState(prev => ({
      ...prev,
      error,
      isLoading: false
    }));

    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
  }, []);

  const stopLoading = useCallback((finalMessage?: string) => {
    setState({
      isLoading: false,
      progress: 100,
      message: finalMessage,
      error: undefined
    });

    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
  }, []);

  const reset = useCallback(() => {
    setState({
      isLoading: false,
      progress: 0,
      message: options.initialMessage,
      error: undefined
    });

    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
  }, [options.initialMessage]);

  // Cleanup timeout on unmount
  useEffect(() => {
    return () => {
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }
    };
  }, []);

  // Calculate elapsed time
  const getElapsedTime = useCallback(() => {
    if (!startTimeRef.current) return 0;
    return Date.now() - startTimeRef.current;
  }, []);

  return {
    ...state,
    startLoading,
    updateProgress,
    setMessage,
    setError,
    stopLoading,
    reset,
    getElapsedTime
  };
};

// Hook for managing multiple loading states
export const useMultipleLoadingStates = () => {
  const [states, setStates] = useState<Record<string, LoadingState>>({});

  const startLoading = useCallback((key: string, message?: string) => {
    setStates(prev => ({
      ...prev,
      [key]: {
        isLoading: true,
        progress: 0,
        message,
        error: undefined
      }
    }));
  }, []);

  const updateProgress = useCallback((key: string, progress: number, message?: string) => {
    setStates(prev => ({
      ...prev,
      [key]: {
        ...prev[key],
        progress: Math.min(100, Math.max(0, progress)),
        message: message || prev[key]?.message
      }
    }));
  }, []);

  const setError = useCallback((key: string, error: string) => {
    setStates(prev => ({
      ...prev,
      [key]: {
        ...prev[key],
        error,
        isLoading: false
      }
    }));
  }, []);

  const stopLoading = useCallback((key: string, finalMessage?: string) => {
    setStates(prev => ({
      ...prev,
      [key]: {
        isLoading: false,
        progress: 100,
        message: finalMessage,
        error: undefined
      }
    }));
  }, []);

  const removeState = useCallback((key: string) => {
    setStates(prev => {
      const newStates = { ...prev };
      delete newStates[key];
      return newStates;
    });
  }, []);

  const getState = useCallback((key: string): LoadingState => {
    return states[key] || {
      isLoading: false,
      progress: 0,
      message: undefined,
      error: undefined
    };
  }, [states]);

  const isAnyLoading = Object.values(states).some(state => state.isLoading);

  return {
    states,
    startLoading,
    updateProgress,
    setError,
    stopLoading,
    removeState,
    getState,
    isAnyLoading
  };
};

// Hook for async operations with loading state
export const useAsyncOperation = <T = any>(
  operation: (...args: any[]) => Promise<T>,
  options: LoadingOptions = {}
) => {
  const loadingState = useLoadingState(options);

  const execute = useCallback(async (...args: any[]): Promise<T | null> => {
    try {
      loadingState.startLoading();
      const result = await operation(...args);
      loadingState.stopLoading('Operation completed successfully');
      return result;
    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : 'Operation failed';
      loadingState.setError(errorMessage);
      return null;
    }
  }, [operation, loadingState]);

  return {
    ...loadingState,
    execute
  };
};

// Hook for debounced loading state
export const useDebouncedLoading = (delay: number = 300) => {
  const [isLoading, setIsLoading] = useState(false);
  const timeoutRef = useRef<NodeJS.Timeout>();

  const startLoading = useCallback(() => {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
    setIsLoading(true);
  }, []);

  const stopLoading = useCallback(() => {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
    
    timeoutRef.current = setTimeout(() => {
      setIsLoading(false);
    }, delay);
  }, [delay]);

  useEffect(() => {
    return () => {
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }
    };
  }, []);

  return {
    isLoading,
    startLoading,
    stopLoading
  };
};

import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { supabase } from '@/integrations/supabase/client';
import { useAuth } from '@/lib/context/AuthContext';
import { useEffect } from 'react';
import type { Tables } from '@/integrations/supabase/types';

export type Order = Tables<'orders'> & {
  order_items: (Tables<'order_items'> & {
    products: Tables<'products'>;
  })[];
};

export const useOrders = () => {
  const { user } = useAuth();
  const queryClient = useQueryClient();

  const query = useQuery({
    queryKey: ['orders', user?.id],
    queryFn: async () => {
      if (!user) throw new Error('User not authenticated');

      const { data, error } = await supabase
        .from('orders')
        .select(`
          *,
          order_items(
            *,
            products(*)
          )
        `)
        .eq('user_id', user.id)
        .order('created_at', { ascending: false });

      if (error) throw error;
      return data as Order[];
    },
    enabled: !!user,
  });

  // Real-time order updates
  useEffect(() => {
    if (!user) return;

    const channel = supabase
      .channel('order-changes')
      .on(
        'postgres_changes',
        {
          event: '*',
          schema: 'public',
          table: 'orders',
          filter: `user_id=eq.${user.id}`
        },
        () => {
          queryClient.invalidateQueries({ queryKey: ['orders', user.id] });
        }
      )
      .subscribe();

    return () => {
      supabase.removeChannel(channel);
    };
  }, [user, queryClient]);

  return query;
};

export const useCreateOrder = () => {
  const queryClient = useQueryClient();
  const { user } = useAuth();

  return useMutation({
    mutationFn: async ({
      cartItems,
      shippingAddress,
      billingAddress,
      total,
      notes,
      paymentMethod = 'cod'
    }: {
      cartItems: any[];
      shippingAddress: any;
      billingAddress?: any;
      total: number;
      notes?: string;
      paymentMethod?: string;
    }) => {
      if (!user) throw new Error('User not authenticated');

      // Generate order number
      const { data: orderNumberData, error: orderNumberError } = await supabase
        .rpc('generate_order_number');

      if (orderNumberError) throw orderNumberError;

      // Start transaction by creating order first
      const { data: order, error: orderError } = await supabase
        .from('orders')
        .insert({
          user_id: user.id,
          total: total,
          status: 'pending',
          order_number: orderNumberData,
          shipping_address: shippingAddress,
          billing_address: billingAddress || shippingAddress,
          notes: notes,
          payment_method: paymentMethod,
          payment_status: paymentMethod === 'upi' ? 'pending' : 'pending',
        })
        .select()
        .single();

      if (orderError) throw orderError;

      // Create order items
      const orderItems = cartItems.map(item => ({
        order_id: order.id,
        product_id: item.productId || item.product_id || item.products?.id,
        quantity: item.quantity,
        price: parseFloat(item.price || item.products?.price || '0'),
      }));

      const { error: itemsError } = await supabase
        .from('order_items')
        .insert(orderItems);

      if (itemsError) throw itemsError;

      // Update stock levels
      for (const item of cartItems) {
        const productId = item.productId || item.product_id || item.products?.id;
        const quantity = item.quantity;

        // Get current stock
        const { data: product, error: productError } = await supabase
          .from('products')
          .select('stock')
          .eq('id', productId)
          .single();

        if (productError) throw productError;

        // Update stock
        const newStock = Math.max(0, product.stock - quantity);
        const { error: stockError } = await supabase
          .from('products')
          .update({ stock: newStock })
          .eq('id', productId);

        if (stockError) throw stockError;
      }

      // Clear cart after successful order (only for non-UPI orders)
      if (paymentMethod !== 'upi') {
        const { error: clearCartError } = await supabase
          .from('cart_items')
          .delete()
          .eq('user_id', user.id);

        if (clearCartError) throw clearCartError;
      }

      // Send order confirmation notification
      try {
        await supabase.functions.invoke('order-notifications', {
          body: {
            orderId: order.id,
            eventType: 'order_created'
          }
        });
      } catch (notificationError) {
        console.error('Error sending order confirmation:', notificationError);
        // Don't fail the order creation if notification fails
      }

      return order;
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['orders'] });
      queryClient.invalidateQueries({ queryKey: ['cart'] });
      queryClient.invalidateQueries({ queryKey: ['products'] });
    },
  });
};

export const useUpdateOrderStatus = () => {
  const queryClient = useQueryClient();

  return useMutation({
    mutationFn: async ({ orderId, status, trackingNumber }: {
      orderId: string;
      status: string;
      trackingNumber?: string;
    }) => {
      const updateData: any = { status };
      if (trackingNumber) {
        updateData.tracking_number = trackingNumber;
      }

      const { data, error } = await supabase
        .from('orders')
        .update(updateData)
        .eq('id', orderId)
        .select()
        .single();

      if (error) throw error;
      return data;
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['orders'] });
    },
  });
};

import { useEffect, useState } from 'react';

export const usePWA = () => {
  const [isInstalled, setIsInstalled] = useState(false);
  const [isSupported, setIsSupported] = useState(false);

  useEffect(() => {
    // Check if PWA is supported
    setIsSupported('serviceWorker' in navigator);

    // Check if already installed
    const checkIfInstalled = () => {
      if (window.matchMedia('(display-mode: standalone)').matches || 
          (window.navigator as any).standalone === true) {
        setIsInstalled(true);
      }
    };

    checkIfInstalled();

    // Register service worker
    if ('serviceWorker' in navigator) {
      navigator.serviceWorker.register('/sw.js')
        .then((registration) => {
          console.log('SW registered: ', registration);
        })
        .catch((registrationError) => {
          console.log('SW registration failed: ', registrationError);
        });
    }

    // Add manifest link to head if not present
    if (!document.querySelector('link[rel="manifest"]')) {
      const manifestLink = document.createElement('link');
      manifestLink.rel = 'manifest';
      manifestLink.href = '/manifest.json';
      document.head.appendChild(manifestLink);
    }

    // Add theme-color meta tag if not present
    if (!document.querySelector('meta[name="theme-color"]')) {
      const themeColorMeta = document.createElement('meta');
      themeColorMeta.name = 'theme-color';
      themeColorMeta.content = '#2D5016';
      document.head.appendChild(themeColorMeta);
    }

  }, []);

  return { isInstalled, isSupported };
};

import { useState, useCallback, useRef, useEffect } from 'react';
import { RateLimitMiddleware, rateLimitMiddlewares, getClientIP, getCurrentUserId } from '../middleware/rateLimitMiddleware';
import { RateLimitConfig } from '../services/rateLimitService';
import { useAuth } from './useAuth';

interface RateLimitOptions {
  maxAttempts?: number;
  timeWindow?: number; // in milliseconds
  blockDuration?: number; // in milliseconds
}

interface UseRateLimitResult {
  isRateLimited: boolean;
  attempts: number;
  remaining: number;
  resetTime: Date | null;
  retryAfter: number | null;
  checkRateLimit: () => Promise<boolean>;
  resetRateLimit: () => void;
  executeWithRateLimit: <T>(fn: () => Promise<T>) => Promise<T>;
}

interface UseAdvancedRateLimitOptions {
  config: RateLimitConfig;
  action: string;
  skipCondition?: () => boolean;
}

/**
 * Legacy rate limit hook for backward compatibility
 */
export function useRateLimit({
  maxAttempts = 5,
  timeWindow = 60000, // 1 minute
  blockDuration = 300000, // 5 minutes
}: RateLimitOptions = {}) {
  const [attempts, setAttempts] = useState(0);
  const [isBlocked, setIsBlocked] = useState(false);
  const attemptsTimestamps = useRef<number[]>([]);
  const blockTimeout = useRef<NodeJS.Timeout>();

  const resetAttempts = useCallback(() => {
    setAttempts(0);
    attemptsTimestamps.current = [];
  }, []);

  const checkRateLimit = useCallback(() => {
    if (isBlocked) {
      return false;
    }

    const now = Date.now();
    const windowStart = now - timeWindow;

    // Remove old attempts
    attemptsTimestamps.current = attemptsTimestamps.current.filter(
      (timestamp) => timestamp > windowStart
    );

    // Add new attempt
    attemptsTimestamps.current.push(now);
    setAttempts(attemptsTimestamps.current.length);

    // Check if rate limit is exceeded
    if (attemptsTimestamps.current.length > maxAttempts) {
      setIsBlocked(true);
      blockTimeout.current = setTimeout(() => {
        setIsBlocked(false);
        resetAttempts();
      }, blockDuration);
      return false;
    }

    return true;
  }, [isBlocked, maxAttempts, timeWindow, blockDuration, resetAttempts]);

  // Cleanup
  const cleanup = useCallback(() => {
    if (blockTimeout.current) {
      clearTimeout(blockTimeout.current);
    }
    resetAttempts();
    setIsBlocked(false);
  }, [resetAttempts]);

  return {
    isRateLimited: isBlocked,
    attempts,
    checkRateLimit,
    resetRateLimit: cleanup,
  };
}

/**
 * Advanced rate limit hook with server-side integration
 */
export function useAdvancedRateLimit({
  config,
  action,
  skipCondition
}: UseAdvancedRateLimitOptions): UseRateLimitResult {
  const { user } = useAuth();
  const [isRateLimited, setIsRateLimited] = useState(false);
  const [attempts, setAttempts] = useState(0);
  const [remaining, setRemaining] = useState(config.maxRequests);
  const [resetTime, setResetTime] = useState<Date | null>(null);
  const [retryAfter, setRetryAfter] = useState<number | null>(null);

  const getUserId = useCallback(() => user?.id, [user?.id]);
  const getIP = useCallback(() => getClientIP(), []);

  const middlewareOptions = {
    config,
    getUserId,
    getClientIP: getIP,
    skipCondition,
    onLimitExceeded: (result: any) => {
      setIsRateLimited(true);
      setRetryAfter(result.retryAfter || null);
    }
  };

  const checkRateLimit = useCallback(async (): Promise<boolean> => {
    try {
      const result = await RateLimitMiddleware.checkRateLimit(action, middlewareOptions);
      
      setIsRateLimited(!result.allowed);
      setRemaining(result.remaining);
      setResetTime(result.resetTime);
      setRetryAfter(result.retryAfter || null);
      
      if (!result.allowed) {
        setAttempts(config.maxRequests);
      }

      return result.allowed;
    } catch (error) {
      // Allow on error
      return true;
    }
  }, [action, middlewareOptions, config.maxRequests]);

  const resetRateLimit = useCallback(() => {
    RateLimitMiddleware.resetRateLimit(middlewareOptions);
    setIsRateLimited(false);
    setAttempts(0);
    setRemaining(config.maxRequests);
    setResetTime(null);
    setRetryAfter(null);
  }, [middlewareOptions, config.maxRequests]);

  const executeWithRateLimit = useCallback(async <T>(fn: () => Promise<T>): Promise<T> => {
    const allowed = await checkRateLimit();
    
    if (!allowed) {
      throw new Error(`Rate limit exceeded for ${action}. Please try again in ${retryAfter} seconds.`);
    }

    let success = false;
    try {
      const result = await fn();
      success = true;
      
      // Record successful request
      await RateLimitMiddleware.recordRequest(action, success, middlewareOptions);
      
      return result;
    } catch (error) {
      // Record failed request
      await RateLimitMiddleware.recordRequest(action, success, middlewareOptions);
      throw error;
    }
  }, [checkRateLimit, action, retryAfter, middlewareOptions]);

  // Update status periodically if rate limited
  useEffect(() => {
    if (!isRateLimited) return;

    const interval = setInterval(async () => {
      const status = RateLimitMiddleware.getRateLimitStatus(middlewareOptions);
      if (status) {
        setIsRateLimited(!status.allowed);
        setRemaining(status.remaining);
        setResetTime(status.resetTime);
        setRetryAfter(status.retryAfter || null);
      } else {
        // Status expired, reset
        resetRateLimit();
      }
    }, 1000);

    return () => clearInterval(interval);
  }, [isRateLimited, middlewareOptions, resetRateLimit]);

  return {
    isRateLimited,
    attempts,
    remaining,
    resetTime,
    retryAfter,
    checkRateLimit,
    resetRateLimit,
    executeWithRateLimit
  };
}

/**
 * Pre-configured rate limit hooks for common actions
