import { useState, useCallback, useEffect } from 'react';
import { sanitizationService } from '../services/sanitizationService';
import { fileUploadSecurity, FileSecurityConfig, FileValidationResult } from '../services/fileUploadSecurity';
import { useAdvancedRateLimit } from './useRateLimit';
import { rateLimitConfigs } from '../services/rateLimitService';
import sentryService from '../services/sentryService';

export interface SecureInputOptions {
  maxLength?: number;
  minLength?: number;
  allowedPatterns?: RegExp[];
  blockedPatterns?: RegExp[];
  requireAlphanumeric?: boolean;
  allowSpecialChars?: string[];
  enableRealTimeValidation?: boolean;
  debounceMs?: number;
  sanitizationLevel?: 'basic' | 'strict' | 'html' | 'sql' | 'nosql';
  customValidator?: (input: string) => boolean | { isValid: boolean; error?: string };
}

export interface SecureInputResult {
  value: string;
  sanitizedValue: string;
  isValid: boolean;
  errors: string[];
  warnings: string[];
  isValidating: boolean;
  validate: () => void;
  reset: () => void;
}

export interface SecureFileUploadOptions {
  securityPolicy: keyof typeof import('../services/fileUploadSecurity').fileSecurityPolicies | FileSecurityConfig;
  enableRealTimeValidation?: boolean;
  onValidationComplete?: (results: FileValidationResult[]) => void;
  onSecurityThreat?: (threat: string) => void;
}

export interface SecureFileUploadResult {
  files: File[];
  validationResults: FileValidationResult[];
  isValidating: boolean;
  hasErrors: boolean;
  errors: string[];
  warnings: string[];
  uploadFiles: (files: FileList | File[]) => Promise<void>;
  removeFile: (index: number) => void;
  reset: () => void;
}

/**
 * Hook for secure text input validation and sanitization
 */
export function useSecureInput(
  initialValue: string = '',
  options: SecureInputOptions = {}
): SecureInputResult {
  const [value, setValue] = useState(initialValue);
  const [sanitizedValue, setSanitizedValue] = useState('');
  const [isValid, setIsValid] = useState(true);
  const [errors, setErrors] = useState<string[]>([]);
  const [warnings, setWarnings] = useState<string[]>([]);
  const [isValidating, setIsValidating] = useState(false);

  // Rate limiting for validation
  const rateLimitHook = useAdvancedRateLimit({
    config: rateLimitConfigs.forms,
    action: 'input_validation'
  });

  const validateInput = useCallback(async (inputValue: string) => {
    if (!rateLimitHook.executeWithRateLimit) return;

    try {
      await rateLimitHook.executeWithRateLimit(async () => {
        setIsValidating(true);

        // Apply sanitization based on level
        let sanitized = inputValue;
        switch (options.sanitizationLevel) {
          case 'strict':
            sanitized = sanitizationService.sanitizeInput(inputValue, options.maxLength);
            break;
          case 'html':
            sanitized = sanitizationService.sanitizeHtml(inputValue);
            break;
          case 'sql':
            sanitized = sanitizationService.sanitizeSQLInput(inputValue);
            break;
          case 'nosql':
            sanitized = JSON.stringify(sanitizationService.sanitizeNoSQLInput(inputValue));
            break;
          default:
            sanitized = sanitizationService.sanitizeInput(inputValue, options.maxLength);
        }

        // Comprehensive validation
        const validationResult = sanitizationService.validateAndSanitizeInput(sanitized, {
          maxLength: options.maxLength,
          minLength: options.minLength,
          allowedPatterns: options.allowedPatterns,
          blockedPatterns: options.blockedPatterns,
          requireAlphanumeric: options.requireAlphanumeric,
          allowSpecialChars: options.allowSpecialChars,
          customValidator: options.customValidator
        });

        // Custom validation
        if (options.customValidator) {
          const customResult = options.customValidator(validationResult.sanitized);
          if (typeof customResult === 'object') {
            if (!customResult.isValid) {
              validationResult.errors.push(customResult.error || 'Custom validation failed');
              validationResult.isValid = false;
            }
          } else if (!customResult) {
            validationResult.errors.push('Custom validation failed');
            validationResult.isValid = false;
          }
        }

        setSanitizedValue(validationResult.sanitized);
        setIsValid(validationResult.isValid);
        setErrors(validationResult.errors);
        setWarnings([]);

        setIsValidating(false);
      });
    } catch (error) {
      setIsValidating(false);
      setIsValid(false);
      setErrors(['Validation rate limit exceeded']);
      
      sentryService.captureError(
        error instanceof Error ? error : new Error('Input validation failed'),
        {
          action: 'secure_input_validation',
          additionalData: { inputValue: inputValue.substring(0, 100), options }
        }
      );
    }
  }, [options, rateLimitHook]);

  // Debounced validation for real-time validation
  useEffect(() => {
    if (!options.enableRealTimeValidation) return;

    const debounceMs = options.debounceMs || 300;
    const timeoutId = setTimeout(() => {
      if (value !== initialValue) {
        validateInput(value);
      }
    }, debounceMs);

    return () => clearTimeout(timeoutId);
  }, [value, validateInput, options.enableRealTimeValidation, options.debounceMs, initialValue]);

  const validate = useCallback(() => {
    validateInput(value);
  }, [value, validateInput]);

  const reset = useCallback(() => {
    setValue(initialValue);
    setSanitizedValue('');
    setIsValid(true);
    setErrors([]);
    setWarnings([]);
    setIsValidating(false);
  }, [initialValue]);

  // Update value and trigger validation if real-time is enabled
  const handleValueChange = useCallback((newValue: string) => {
    setValue(newValue);
    
    if (!options.enableRealTimeValidation) {
      // Immediate basic sanitization for display
      const basicSanitized = sanitizationService.sanitizeInput(newValue);
      setSanitizedValue(basicSanitized);
    }
  }, [options.enableRealTimeValidation]);

  // Return object with setValue method
  const result = {
    value,
    sanitizedValue,
    isValid,
    errors,
    warnings,
    isValidating,
    validate,
    reset,
    setValue: handleValueChange
  };

  return result;


}

/**
 * Hook for secure file upload validation
 */
export function useSecureFileUpload(
  options: SecureFileUploadOptions
): SecureFileUploadResult {
  const [files, setFiles] = useState<File[]>([]);
  const [validationResults, setValidationResults] = useState<FileValidationResult[]>([]);
  const [isValidating, setIsValidating] = useState(false);
  const [errors, setErrors] = useState<string[]>([]);
  const [warnings, setWarnings] = useState<string[]>([]);

  // Rate limiting for file uploads
  const rateLimitHook = useAdvancedRateLimit({
    config: rateLimitConfigs.uploads,
    action: 'file_upload_validation'
  });

  const getSecurityConfig = useCallback((): FileSecurityConfig => {
    if (typeof options.securityPolicy === 'string') {
      const { fileSecurityPolicies } = require('../services/fileUploadSecurity');
      return fileSecurityPolicies[options.securityPolicy];
    }
    return options.securityPolicy;
  }, [options.securityPolicy]);

  const uploadFiles = useCallback(async (fileList: FileList | File[]) => {
    if (!rateLimitHook.executeWithRateLimit) return;

    try {
      await rateLimitHook.executeWithRateLimit(async () => {
        setIsValidating(true);
        setErrors([]);
        setWarnings([]);

        const config = getSecurityConfig();
        const results = await fileUploadSecurity.validateFiles(fileList, config);

        // Check for security threats
        const threats = results.flatMap(result => 
          result.errors.filter(error => 
            error.includes('dangerous') || 
            error.includes('threat') || 
            error.includes('malware')
          )
        );

        if (threats.length > 0 && options.onSecurityThreat) {
          threats.forEach(threat => options.onSecurityThreat!(threat));
        }

        // Collect all errors and warnings
        const allErrors = results.flatMap(result => result.errors);
        const allWarnings = results.flatMap(result => result.warnings);

        // Only keep valid files
        const validFiles = results
          .filter(result => result.isValid && result.sanitizedFile)
          .map(result => result.sanitizedFile!);

        setFiles(validFiles);
        setValidationResults(results);
        setErrors(allErrors);
        setWarnings(allWarnings);
        setIsValidating(false);

        if (options.onValidationComplete) {
          options.onValidationComplete(results);
        }

        // Log security events
        if (allErrors.length > 0) {
          sentryService.addBreadcrumb(
            'File upload validation failed',
            'security',
            'warning',
            { 
              fileCount: Array.from(fileList).length,
              errors: allErrors,
              threats: threats.length
            }
          );
        }
      });
    } catch (error) {
      setIsValidating(false);
      setErrors(['File upload rate limit exceeded']);
      
      sentryService.captureError(
        error instanceof Error ? error : new Error('File upload validation failed'),
        {
          action: 'secure_file_upload',
          additionalData: { fileCount: Array.from(fileList).length }
        }
      );
    }
  }, [rateLimitHook, getSecurityConfig, options]);

  const removeFile = useCallback((index: number) => {
    setFiles(prev => prev.filter((_, i) => i !== index));
    setValidationResults(prev => prev.filter((_, i) => i !== index));
    
    // Recalculate errors and warnings
    const remainingResults = validationResults.filter((_, i) => i !== index);
    setErrors(remainingResults.flatMap(result => result.errors));
    setWarnings(remainingResults.flatMap(result => result.warnings));
  }, [validationResults]);

  const reset = useCallback(() => {
    setFiles([]);
    setValidationResults([]);
    setErrors([]);
    setWarnings([]);
    setIsValidating(false);
  }, []);

  const hasErrors = errors.length > 0;

  return {
    files,
    validationResults,
    isValidating,
    hasErrors,
    errors,
    warnings,
    uploadFiles,
    removeFile,
    reset
  };
}

/**
 * Hook for secure form validation (combines multiple inputs)
 */
export function useSecureForm<T extends Record<string, any>>(
  initialValues: T,
  validationRules: Record<keyof T, SecureInputOptions>
) {
  const [values, setValues] = useState<T>(initialValues);
  const [errors, setErrors] = useState<Partial<Record<keyof T, string[]>>>({});
  const [isValid, setIsValid] = useState(false);
  const [isValidating, setIsValidating] = useState(false);

  const validateField = useCallback(async (fieldName: keyof T, value: any): Promise<any> => {
    const rules = validationRules[fieldName];
    if (!rules) return { isValid: true, sanitized: value, errors: [] };

    const result = sanitizationService.validateAndSanitizeInput(value, rules);
    
    setErrors(prev => ({
      ...prev,
      [fieldName]: result.errors
    }));

    return result;
  }, [validationRules]);

  const validateForm = useCallback(async (): Promise<boolean> => {
    setIsValidating(true);
    const fieldErrors: Partial<Record<keyof T, string[]>> = {};
    let formIsValid = true;

    for (const [fieldName, value] of Object.entries(values)) {
      const result = await validateField(fieldName as keyof T, value);
      if (result && !result.isValid) {
        fieldErrors[fieldName as keyof T] = result.errors;
        formIsValid = false;
      }
    }

    setErrors(fieldErrors);
    setIsValid(formIsValid);
    setIsValidating(false);

    return formIsValid;
  }, [values, validateField]);

  const updateField = useCallback((fieldName: keyof T, value: any) => {
    setValues(prev => ({ ...prev, [fieldName]: value }));
    
    // Real-time validation for the field
    validateField(fieldName, value);
  }, [validateField]);

  const reset = useCallback(() => {
    setValues(initialValues);
    setErrors({});
    setIsValid(false);
    setIsValidating(false);
  }, [initialValues]);

  return {
    values,
    errors,
    isValid,
    isValidating,
    updateField,
    validateForm,
    reset
  };
}

export default {
  useSecureInput,
  useSecureFileUpload,
  useSecureForm
};

import { useState, useCallback, useRef, useEffect } from 'react';

export type FeedbackType = 'success' | 'error' | 'warning' | 'info';

export interface FeedbackMessage {
  id: string;
  type: FeedbackType;
  title?: string;
  message: string;
  duration?: number;
  action?: {
    label: string;
    onClick: () => void;
  };
  persistent?: boolean;
  timestamp: number;
}

export interface FeedbackOptions {
  duration?: number;
  persistent?: boolean;
  action?: {
    label: string;
    onClick: () => void;
  };
}

export const useUserFeedback = () => {
  const [messages, setMessages] = useState<FeedbackMessage[]>([]);
  const timeoutsRef = useRef<Map<string, NodeJS.Timeout>>(new Map());

  const generateId = () => `feedback-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;

  const addMessage = useCallback((
    type: FeedbackType,
    message: string,
    title?: string,
    options: FeedbackOptions = {}
  ) => {
    const id = generateId();
    const duration = options.duration ?? (type === 'error' ? 5000 : 3000);
    
    const feedbackMessage: FeedbackMessage = {
      id,
      type,
      title,
      message,
      duration,
      action: options.action,
      persistent: options.persistent || false,
      timestamp: Date.now()
    };

    setMessages(prev => [...prev, feedbackMessage]);

    // Auto-remove message after duration (unless persistent)
    if (!options.persistent && duration > 0) {
      const timeout = setTimeout(() => {
        removeMessage(id);
      }, duration);
      
      timeoutsRef.current.set(id, timeout);
    }

    return id;
  }, []);

  const removeMessage = useCallback((id: string) => {
    setMessages(prev => prev.filter(msg => msg.id !== id));
    
    const timeout = timeoutsRef.current.get(id);
    if (timeout) {
      clearTimeout(timeout);
      timeoutsRef.current.delete(id);
    }
  }, []);

  const clearAll = useCallback(() => {
    setMessages([]);
    timeoutsRef.current.forEach(timeout => clearTimeout(timeout));
    timeoutsRef.current.clear();
  }, []);

  // Convenience methods
  const showSuccess = useCallback((message: string, title?: string, options?: FeedbackOptions) => {
    return addMessage('success', message, title, options);
  }, [addMessage]);

  const showError = useCallback((message: string, title?: string, options?: FeedbackOptions) => {
    return addMessage('error', message, title, options);
  }, [addMessage]);

  const showWarning = useCallback((message: string, title?: string, options?: FeedbackOptions) => {
    return addMessage('warning', message, title, options);
  }, [addMessage]);

  const showInfo = useCallback((message: string, title?: string, options?: FeedbackOptions) => {
    return addMessage('info', message, title, options);
  }, [addMessage]);

  // Operation feedback helpers
  const showOperationFeedback = useCallback((
    operation: string,
    success: boolean,
    customMessage?: string
  ) => {
    if (success) {
      showSuccess(customMessage || `${operation} completed successfully`);
    } else {
      showError(customMessage || `${operation} failed. Please try again.`);
    }
  }, [showSuccess, showError]);

  const showLoadingFeedback = useCallback((message: string = 'Loading...') => {
    return showInfo(message, undefined, { persistent: true });
  }, [showInfo]);

  // Cleanup timeouts on unmount
  useEffect(() => {
    return () => {
      timeoutsRef.current.forEach(timeout => clearTimeout(timeout));
    };
  }, []);

  return {
    messages,
    addMessage,
    removeMessage,
    clearAll,
    showSuccess,
    showError,
    showWarning,
    showInfo,
    showOperationFeedback,
    showLoadingFeedback
  };
};

// Hook for form validation feedback
export const useFormFeedback = () => {
  const [fieldErrors, setFieldErrors] = useState<Record<string, string>>({});
  const [formMessage, setFormMessage] = useState<{
    type: FeedbackType;
    message: string;
  } | null>(null);

  const setFieldError = useCallback((field: string, error: string) => {
    setFieldErrors(prev => ({
      ...prev,
      [field]: error
    }));
  }, []);

  const clearFieldError = useCallback((field: string) => {
    setFieldErrors(prev => {
      const newErrors = { ...prev };
      delete newErrors[field];
      return newErrors;
    });
  }, []);

  const clearAllFieldErrors = useCallback(() => {
    setFieldErrors({});
  }, []);

  const setFormSuccess = useCallback((message: string) => {
    setFormMessage({ type: 'success', message });
    setFieldErrors({});
  }, []);

  const setFormError = useCallback((message: string) => {
    setFormMessage({ type: 'error', message });
  }, []);

  const clearFormMessage = useCallback(() => {
    setFormMessage(null);
  }, []);

  const hasErrors = Object.keys(fieldErrors).length > 0;

  return {
    fieldErrors,
    formMessage,
    hasErrors,
    setFieldError,
    clearFieldError,
    clearAllFieldErrors,
    setFormSuccess,
    setFormError,
    clearFormMessage
  };
};

// Hook for operation status tracking
export const useOperationStatus = () => {
  const [operations, setOperations] = useState<Record<string, {
    status: 'idle' | 'loading' | 'success' | 'error';
    message?: string;
    timestamp: number;
  }>>({});

  const setOperationStatus = useCallback((
    operationId: string,
    status: 'idle' | 'loading' | 'success' | 'error',
    message?: string
  ) => {
    setOperations(prev => ({
      ...prev,
      [operationId]: {
        status,
        message,
        timestamp: Date.now()
      }
    }));
  }, []);

  const startOperation = useCallback((operationId: string, message?: string) => {
    setOperationStatus(operationId, 'loading', message);
  }, [setOperationStatus]);

  const completeOperation = useCallback((operationId: string, message?: string) => {
    setOperationStatus(operationId, 'success', message);
  }, [setOperationStatus]);

  const failOperation = useCallback((operationId: string, message?: string) => {
    setOperationStatus(operationId, 'error', message);
  }, [setOperationStatus]);

  const resetOperation = useCallback((operationId: string) => {
    setOperationStatus(operationId, 'idle');
  }, [setOperationStatus]);

  const getOperationStatus = useCallback((operationId: string) => {
    return operations[operationId] || { status: 'idle' as const, timestamp: 0 };
  }, [operations]);

  const isOperationLoading = useCallback((operationId: string) => {
    return operations[operationId]?.status === 'loading';
  }, [operations]);

  return {
    operations,
    setOperationStatus,
    startOperation,
    completeOperation,
    failOperation,
    resetOperation,
    getOperationStatus,
    isOperationLoading
  };
};

import { rateLimitService, RateLimitConfig, rateLimitConfigs } from '../services/rateLimitService';
import sentryService from '../services/sentryService';

export interface RateLimitMiddlewareOptions {
  config: RateLimitConfig;
  getUserId?: () => string | undefined;
  getClientIP?: () => string | undefined;
  onLimitExceeded?: (result: any) => void;
  skipCondition?: () => boolean;
}

export interface RateLimitResponse {
  allowed: boolean;
  remaining: number;
  resetTime: Date;
  retryAfter?: number;
  message?: string;
}

/**
 * Rate limiting middleware for client-side API calls
 */
export class RateLimitMiddleware {
  /**
   * Check rate limit before making an API call
   */
  static async checkRateLimit(
    action: string,
    options: RateLimitMiddlewareOptions
  ): Promise<RateLimitResponse> {
    try {
      // Skip rate limiting if condition is met
      if (options.skipCondition && options.skipCondition()) {
        return {
          allowed: true,
          remaining: options.config.maxRequests,
          resetTime: new Date(Date.now() + options.config.windowMs)
        };
      }

      const userId = options.getUserId?.();
      const clientIP = options.getClientIP?.();

      const result = await rateLimitService.checkRateLimit(
        options.config,
        userId,
        clientIP
      );

      if (!result.allowed) {
        const message = `Rate limit exceeded for ${action}. Please try again in ${result.retryAfter} seconds.`;
        
        // Call custom handler if provided
        if (options.onLimitExceeded) {
          options.onLimitExceeded(result);
        }

        // Log rate limit violation
        sentryService.addBreadcrumb(
          `Rate limit exceeded for action: ${action}`,
          'security',
          'warning',
          { action, userId, clientIP, result }
        );

        return {
          ...result,
          message
        };
      }

      return result;
    } catch (error) {
      sentryService.captureError(
        error instanceof Error ? error : new Error('Rate limit check failed'),
        {
          action: 'rate_limit_middleware',
          additionalData: { action, options }
        }
      );

      // Allow request on error
      return {
        allowed: true,
        remaining: options.config.maxRequests,
        resetTime: new Date(Date.now() + options.config.windowMs)
      };
    }
  }

  /**
   * Record the result of an API call for rate limiting purposes
   */
  static async recordRequest(
    action: string,
    success: boolean,
    options: RateLimitMiddlewareOptions
  ): Promise<void> {
    try {
      const userId = options.getUserId?.();
      const clientIP = options.getClientIP?.();

      await rateLimitService.recordRequest(
        options.config,
        success,
        userId,
        clientIP
      );
    } catch (error) {
      // Don't throw errors for recording failures
      sentryService.captureError(
        error instanceof Error ? error : new Error('Failed to record rate limit request'),
        {
          action: 'record_rate_limit_request',
          additionalData: { action, success, options }
        }
      );
    }
  }

  /**
   * Create a rate-limited wrapper for API functions
   */
  static createRateLimitedWrapper<T extends (...args: any[]) => Promise<any>>(
    fn: T,
    action: string,
    options: RateLimitMiddlewareOptions
  ): T {
    return (async (...args: Parameters<T>) => {
      // Check rate limit before executing
      const rateLimitResult = await this.checkRateLimit(action, options);
      
      if (!rateLimitResult.allowed) {
        throw new Error(rateLimitResult.message || 'Rate limit exceeded');
      }

      let success = false;
      try {
        const result = await fn(...args);
        success = true;
        
        // Record successful request
        await this.recordRequest(action, success, options);
        
        return result;
      } catch (error) {
        // Record failed request
        await this.recordRequest(action, success, options);
        throw error;
      }
    }) as T;
  }

  /**
   * Get current rate limit status
   */
  static getRateLimitStatus(
    options: RateLimitMiddlewareOptions
  ): any {
    const userId = options.getUserId?.();
    const clientIP = options.getClientIP?.();

    return rateLimitService.getRateLimitStatus(
      options.config,
      userId,
      clientIP
    );
  }

  /**
   * Reset rate limit for current user/IP
   */
  static resetRateLimit(options: RateLimitMiddlewareOptions): void {
    const userId = options.getUserId?.();
    const clientIP = options.getClientIP?.();

    rateLimitService.resetRateLimit(userId, clientIP, options.config.keyGenerator);
  }
}

