import { apiErrorHandler, ApiError } from './apiErrorHandler';
import { ErrorContext } from './sentryService';
import { supabase } from '../supabase';
import { Database } from '../../types/supabase';
import { ApiResponseHandler, ApiResult } from './ApiResponseHandler';
import { securityMiddleware } from '../middleware/securityMiddleware';
import { csrfService } from './csrfService';
import { sessionService } from './sessionService';

export interface ApiClientConfig {
  baseURL?: string;
  timeout?: number;
  retries?: number;
  enableLogging?: boolean;
}

export interface ApiResponse<T = any> {
  data?: T;
  error?: ApiError;
  success: boolean;
  status?: number;
}

/**
 * Centralized API client with standardized error handling
 * Provides a unified interface for all API operations
 */
export class ApiClient {
  private config: ApiClientConfig;
  private fetchWrapper: ReturnType<typeof apiErrorHandler.createFetchWrapper>;

  constructor(config: ApiClientConfig = {}) {
    this.config = {
      timeout: 30000,
      retries: 3,
      enableLogging: true,
      ...config
    };

    this.fetchWrapper = apiErrorHandler.createFetchWrapper();
    
    // Initialize security middleware
    this.initializeSecurity();
  }

  /**
   * Initialize security middleware and interceptors
   */
  private initializeSecurity(): void {
    // Set up request interceptor for security
    const originalFetchWrapper = this.fetchWrapper;
    
    this.fetchWrapper = async <T>(
      url: string,
      options: RequestInit = {},
      context?: ErrorContext
    ): Promise<T> => {
      // Apply security middleware to request
      const securityResult = await securityMiddleware.validateRequest({
        headers: options.headers as Record<string, string>,
        method: options.method,
        url,
        body: options.body
      });

      if (!securityResult.isValid) {
        throw new Error(`Security validation failed: ${securityResult.errors.join(', ')}`);
      }

      // Add security headers
      const enhancedOptions: RequestInit = {
        ...options,
        headers: {
          ...options.headers,
          ...securityResult.headers
        }
      };

      // Add CSRF token for state-changing requests
      if (this.isStateChangingRequest(options.method)) {
        const csrfHeaders = csrfService.getTokenHeader();
        enhancedOptions.headers = {
          ...enhancedOptions.headers,
          ...csrfHeaders
        };
      }

      return originalFetchWrapper<T>(url, enhancedOptions, context);
    };
  }

  /**
   * Check if request method is state-changing
   */
  private isStateChangingRequest(method?: string): boolean {
    if (!method) return false;
    return ['POST', 'PUT', 'PATCH', 'DELETE'].includes(method.toUpperCase());
  }

  /**
   * Generic API call wrapper that standardizes all responses
   */
  async call<T>(
    operation: () => Promise<T>,
    endpoint: string,
    method: string = 'GET',
    context?: ErrorContext
  ): Promise<ApiResponse<T>> {
    try {
      const data = await apiErrorHandler.handleApiCall(
        operation,
        endpoint,
        method,
        context
      );

      return {
        data,
        success: true,
        status: 200
      };
    } catch (error) {
      const apiError = error as ApiError;
      
      return {
        error: apiError,
        success: false,
        status: apiError.status
      };
    }
  }

  /**
   * Supabase query wrapper with standardized error handling
   */
  async supabaseQuery<T>(
    queryFn: () => Promise<{ data: T | null; error: any }>,
    operation: string,
    context?: ErrorContext
  ): Promise<ApiResponse<T>> {
    return this.call(
      async () => {
        const result = await queryFn();
        const { data, error } = result;
        
        if (error) {
          apiErrorHandler.handleSupabaseError(error, operation, context);
        }
        
        return data;
      },
      `supabase/${operation}`,
      'POST',
      context
    );
  }

  /**
   * HTTP GET request
   */
  async get<T>(
    url: string,
    options: RequestInit = {},
    context?: ErrorContext
  ): Promise<ApiResponse<T>> {
    return this.call(
      () => this.fetchWrapper<T>(url, { ...options, method: 'GET' }, context),
      url,
      'GET',
      context
    );
  }

  /**
   * HTTP POST request
   */
  async post<T>(
    url: string,
    data?: any,
    options: RequestInit = {},
    context?: ErrorContext
  ): Promise<ApiResponse<T>> {
    const requestOptions: RequestInit = {
      ...options,
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        ...options.headers
      },
      body: data ? JSON.stringify(data) : undefined
    };

    return this.call(
      () => this.fetchWrapper<T>(url, requestOptions, context),
      url,
      'POST',
      context
    );
  }

  /**
   * HTTP PUT request
   */
  async put<T>(
    url: string,
    data?: any,
    options: RequestInit = {},
    context?: ErrorContext
  ): Promise<ApiResponse<T>> {
    const requestOptions: RequestInit = {
      ...options,
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
        ...options.headers
      },
      body: data ? JSON.stringify(data) : undefined
    };

    return this.call(
      () => this.fetchWrapper<T>(url, requestOptions, context),
      url,
      'PUT',
      context
    );
  }

  /**
   * HTTP DELETE request
   */
  async delete<T>(
    url: string,
    options: RequestInit = {},
    context?: ErrorContext
  ): Promise<ApiResponse<T>> {
    return this.call(
      () => this.fetchWrapper<T>(url, { ...options, method: 'DELETE' }, context),
      url,
      'DELETE',
      context
    );
  }

  /**
   * Products API methods
   */
  products = {
    getAll: async (context?: ErrorContext): Promise<ApiResult<Database['public']['Tables']['products']['Row'][]>> => {
      return ApiResponseHandler.handleArrayResponse(
        supabase.from('products').select('*').eq('status', 'published'),
        {
          component: 'ApiClient',
          action: 'products/getAll',
          ...context
        }
      );
    },

    getById: async (id: string, context?: ErrorContext): Promise<ApiResult<Database['public']['Tables']['products']['Row']>> => {
      // Validate required fields
      if (!id || id.trim() === '') {
        return {
          error: 'Product ID is required',
          success: false,
          details: { providedId: id }
        };
      }

      return ApiResponseHandler.handleSingleResponse(
        supabase.from('products').select('*').eq('id', id).single(),
        {
          component: 'ApiClient',
          action: 'products/getById',
          additionalData: { productId: id },
          ...context
        }
      );
    },

    create: async (product: Database['public']['Tables']['products']['Insert'], context?: ErrorContext): Promise<ApiResult<Database['public']['Tables']['products']['Row']>> => {
      // Validate required fields
      const validation = ApiResponseHandler.validateRequiredFields(
        product,
        ['title', 'price'],
        'products/create'
      );

      if (!validation.isValid) {
        return {
          error: `Missing required fields: ${validation.missingFields.join(', ')}`,
          success: false,
          details: { missingFields: validation.missingFields }
        };
      }

      return ApiResponseHandler.handleSingleResponse(
        supabase.from('products').insert(product).select().single(),
        {
          component: 'ApiClient',
          action: 'products/create',
          additionalData: { productData: product },
          ...context
        }
      );
    },

    update: async (id: string, updates: Database['public']['Tables']['products']['Update'], context?: ErrorContext): Promise<ApiResult<Database['public']['Tables']['products']['Row']>> => {
      // Validate required fields
      if (!id || id.trim() === '') {
        return {
          error: 'Product ID is required',
          success: false,
          details: { providedId: id }
        };
      }

      return ApiResponseHandler.handleSingleResponse(
        supabase.from('products').update(updates).eq('id', id).select().single(),
        {
          component: 'ApiClient',
          action: 'products/update',
          additionalData: { productId: id, updates },
          ...context
        }
      );
    },

    delete: async (id: string, context?: ErrorContext): Promise<ApiResult<void>> => {
      // Validate required fields
      if (!id || id.trim() === '') {
        return {
          error: 'Product ID is required',
          success: false,
          details: { providedId: id }
        };
      }

      const result = await ApiResponseHandler.handleSupabaseResponse(
        supabase.from('products').delete().eq('id', id),
        {
          component: 'ApiClient',
          action: 'products/delete',
          additionalData: { productId: id },
          ...context
        }
      );

      if (result.success) {
        return { data: undefined, success: true } as ApiResult<void>;
      }
      return result as ApiResult<void>;
    }
  };

  /**
   * Orders API methods
   */
  orders = {
    getAll: (context?: ErrorContext) =>
      this.supabaseQuery(
        async () => await supabase.from('orders').select('*, order_items(*, products(*))').order('created_at', { ascending: false }),
        'orders/getAll',
        context
      ),

    getById: (id: string, context?: ErrorContext) =>
      this.supabaseQuery(
        async () => await supabase.from('orders').select('*, order_items(*, products(*))').eq('id', id).single(),
        'orders/getById',
        context
      ),

    create: (order: Database['public']['Tables']['orders']['Insert'], context?: ErrorContext) =>
      this.supabaseQuery(
        async () => await supabase.from('orders').insert(order).select().single(),
        'orders/create',
        context
      ),

    updateStatus: (id: string, status: string, context?: ErrorContext) =>
      this.supabaseQuery(
        async () => await supabase.from('orders').update({ status }).eq('id', id).select().single(),
        'orders/updateStatus',
        context
      )
  };

  /**
   * Authentication methods
   */
  auth = {
    signUp: (email: string, password: string, fullName: string, context?: ErrorContext) =>
      this.call(
        async () => {
          const { data, error } = await supabase.auth.signUp({
            email,
            password,
            options: {
              data: { full_name: fullName }
            }
          });

          if (error) throw error;
          return data;
        },
        'auth/signup',
        'POST',
        context
      ),

    signIn: (email: string, password: string, context?: ErrorContext) =>
      this.call(
        async () => {
          const { data, error } = await supabase.auth.signInWithPassword({
            email,
            password
          });

          if (error) throw error;
          return data;
        },
        'auth/signin',
        'POST',
        context
      ),

    signOut: (context?: ErrorContext) =>
      this.call(
        async () => {
          const { error } = await supabase.auth.signOut();
          if (error) throw error;
        },
        'auth/signout',
        'POST',
        context
      ),

    getCurrentUser: (context?: ErrorContext) =>
      this.call(
        async () => {
          const { data: { session }, error } = await supabase.auth.getSession();
          if (error) throw error;
          return session?.user;
        },
        'auth/session',
        'GET',
        context
      )
  };
}

// Create singleton instance
export const apiClient = new ApiClient();

export default apiClient;

import { PostgrestError } from '@supabase/supabase-js';
import { apiErrorHandler } from './apiErrorHandler';
import { ErrorContext } from './sentryService';

export interface ApiResponse<T> {
  data: T | null;
  error: PostgrestError | null;
  success: boolean;
}

export interface StandardizedResponse<T> {
  data: T;
  success: true;
}

export interface StandardizedError {
  error: string;
  success: false;
  details?: any;
}

export type ApiResult<T> = StandardizedResponse<T> | StandardizedError;

/**
 * Standardized API response handler that ensures consistent response parsing
 * and proper null/undefined checks across all API calls
 */
export class ApiResponseHandler {
  /**
   * Handles Supabase query responses with standardized error handling and null checks
   */
  static async handleSupabaseResponse<T>(
    queryBuilder: any,
    context: ErrorContext
  ): Promise<ApiResult<T>> {
    try {
      const { data, error } = await queryBuilder;

      if (error) {
        return {
          error: error.message || 'Database error occurred',
          success: false,
          details: { error, context: context.action }
        };
      }

      if (data === null || data === undefined) {
        return {
          error: 'No data returned from query',
          success: false,
          details: { context: context.action }
        };
      }

      return {
        data,
        success: true
      };
    } catch (err) {
      return {
        error: err instanceof Error ? err.message : 'Unknown error occurred',
        success: false,
        details: { error: err, context: context.action }
      };
    }
  }

  /**
   * Handles array responses with proper validation
   */
  static async handleArrayResponse<T>(
    queryBuilder: any,
    context: ErrorContext
  ): Promise<ApiResult<T[]>> {
    try {
      const { data, error } = await queryBuilder;

      if (error) {
        return {
          error: error.message || 'Database error occurred',
          success: false,
          details: { error, context: context.action }
        };
      }

      // For arrays, null/undefined should return empty array
      const safeData = data ?? [];

      return {
        data: safeData,
        success: true
      };
    } catch (err) {
      return {
        error: err instanceof Error ? err.message : 'Unknown error occurred',
        success: false,
        details: { error: err, context: context.action }
      };
    }
  }

  /**
   * Handles single record responses with proper validation
   */
  static async handleSingleResponse<T>(
    queryBuilder: any,
    context: ErrorContext,
    allowNull: boolean = false
  ): Promise<ApiResult<T | null>> {
    try {
      const { data, error } = await queryBuilder;

      if (error) {
        return {
          error: error.message || 'Database error occurred',
          success: false,
          details: { error, context: context.action }
        };
      }

      if (!allowNull && (data === null || data === undefined)) {
        return {
          error: 'Record not found',
          success: false,
          details: { context: context.action }
        };
      }

      return {
        data,
        success: true
      };
    } catch (err) {
      return {
        error: err instanceof Error ? err.message : 'Unknown error occurred',
        success: false,
        details: { error: err, context: context.action }
      };
    }
  }

  /**
   * Safely accesses nested object properties with fallback values
   */
  static safeGet<T>(obj: any, path: string, defaultValue: T): T {
    try {
      const keys = path.split('.');
      let current = obj;

      for (const key of keys) {
        if (current === null || current === undefined || !(key in current)) {
          return defaultValue;
        }
        current = current[key];
      }

      return current !== null && current !== undefined ? current : defaultValue;
    } catch {
      return defaultValue;
    }
  }

  /**
   * Validates required fields in API response data
   */
  static validateRequiredFields<T extends Record<string, any>>(
    data: T,
    requiredFields: (keyof T)[],
    context: string
  ): { isValid: boolean; missingFields: string[] } {
    const missingFields: string[] = [];

    for (const field of requiredFields) {
      const value = data[field];
      if (value === null || value === undefined || value === '') {
        missingFields.push(String(field));
      }
    }

    if (missingFields.length > 0) {
      console.warn(`Missing required fields in ${context}:`, missingFields);
    }

    return {
      isValid: missingFields.length === 0,
      missingFields
    };
  }

  /**
   * Transforms API response data with type safety
   */
  static transformResponse<TInput, TOutput>(
    data: TInput,
    transformer: (input: TInput) => TOutput,
    context: string
  ): ApiResult<TOutput> {
    try {
      const transformed = transformer(data);
      return {
        data: transformed,
        success: true
      };
    } catch (err) {
      return {
        error: err instanceof Error ? err.message : 'Transformation error occurred',
        success: false,
        details: { error: err, context: `transform_${context}`, originalData: data }
      };
    }
  }

  /**
   * Handles paginated responses with proper validation
   */
  static async handlePaginatedResponse<T>(
    queryBuilder: any,
    context: ErrorContext
  ): Promise<ApiResult<{ items: T[]; total: number; hasMore: boolean }>> {
    try {
      const { data, error, count } = await queryBuilder;

      if (error) {
        return {
          error: error.message || 'Database error occurred',
          success: false,
          details: { error, context: context.action }
        };
      }

      const safeData = data ?? [];
      const safeCount = count ?? 0;

      return {
        data: {
          items: safeData,
          total: safeCount,
          hasMore: safeData.length < safeCount
        },
        success: true
      };
    } catch (err) {
      return {
        error: err instanceof Error ? err.message : 'Unknown error occurred',
        success: false,
        details: { error: err, context: context.action }
      };
    }
  }
}

/**
 * Utility function to check if an API result is successful
 */
export function isSuccessResult<T>(result: ApiResult<T>): result is StandardizedResponse<T> {
  return result.success === true;
}

/**
 * Utility function to check if an API result is an error
 */
export function isErrorResult<T>(result: ApiResult<T>): result is StandardizedError {
  return result.success === false;
}

/**
 * Utility function to extract data from successful API result or throw error
 */
export function extractData<T>(result: ApiResult<T>): T {
  if (isSuccessResult(result)) {
    return result.data;
  }
  throw new Error(result.error);
}

/**
 * Utility function to safely extract data with fallback
 */
export function extractDataSafe<T>(result: ApiResult<T>, fallback: T): T {
  if (isSuccessResult(result)) {
    return result.data;
  }
  return fallback;
}

/**
 * Critical Path Monitoring Service
 * Monitors performance and health of critical user journeys
 */

interface CriticalPathMetric {
  path: string;
  step: string;
  startTime: number;
  endTime?: number;
  duration?: number;
  success: boolean;
  error?: string;
  metadata?: Record<string, any>;
}

interface CriticalPathSummary {
  path: string;
  totalDuration: number;
  steps: number;
  successRate: number;
  averageDuration: number;
  errors: string[];
}

class CriticalPathMonitoringService {
  private activePaths: Map<string, CriticalPathMetric[]> = new Map();
  private completedPaths: CriticalPathMetric[][] = [];
  private pathSummaries: Map<string, CriticalPathSummary> = new Map();

  // Define critical paths for the e-commerce application
  private readonly CRITICAL_PATHS = {
    USER_REGISTRATION: 'user_registration',
    USER_LOGIN: 'user_login',
    PRODUCT_BROWSE: 'product_browse',
    ADD_TO_CART: 'add_to_cart',
    CHECKOUT_FLOW: 'checkout_flow',
    ORDER_COMPLETION: 'order_completion',
    ADMIN_LOGIN: 'admin_login',
    ADMIN_PRODUCT_MANAGEMENT: 'admin_product_management',
    ADMIN_ORDER_MANAGEMENT: 'admin_order_management'
  } as const;

  public startPath(pathName: string, metadata?: Record<string, any>): string {
    const pathId = `${pathName}_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
    
    const initialMetric: CriticalPathMetric = {
      path: pathName,
      step: 'start',
      startTime: performance.now(),
      success: true,
      metadata
    };

    this.activePaths.set(pathId, [initialMetric]);
    
    console.log(` Started critical path: ${pathName} (${pathId})`);
    return pathId;
  }

  public addStep(pathId: string, stepName: string, metadata?: Record<string, any>): void {
    const pathMetrics = this.activePaths.get(pathId);
    if (!pathMetrics) {
      console.warn(`Path ${pathId} not found`);
      return;
    }

    const stepMetric: CriticalPathMetric = {
      path: pathMetrics[0].path,
      step: stepName,
      startTime: performance.now(),
      success: true,
      metadata
    };

    pathMetrics.push(stepMetric);
    console.log(` Added step to ${pathMetrics[0].path}: ${stepName}`);
  }

  public completeStep(pathId: string, success: boolean = true, error?: string): void {
    const pathMetrics = this.activePaths.get(pathId);
    if (!pathMetrics || pathMetrics.length === 0) {
      console.warn(`Path ${pathId} not found or empty`);
      return;
    }

    const lastStep = pathMetrics[pathMetrics.length - 1];
    lastStep.endTime = performance.now();
    lastStep.duration = lastStep.endTime - lastStep.startTime;
    lastStep.success = success;
    
    if (error) {
      lastStep.error = error;
    }

    console.log(` Completed step ${lastStep.step} in ${lastStep.duration?.toFixed(2)}ms`);
  }

  public completePath(pathId: string, success: boolean = true, error?: string): CriticalPathSummary | null {
    const pathMetrics = this.activePaths.get(pathId);
    if (!pathMetrics) {
      console.warn(`Path ${pathId} not found`);
      return null;
    }

    // Complete the last step if not already completed
    const lastStep = pathMetrics[pathMetrics.length - 1];
    if (!lastStep.endTime) {
      this.completeStep(pathId, success, error);
    }

    // Calculate path summary
    const pathName = pathMetrics[0].path;
    const totalDuration = pathMetrics.reduce((total, metric) => total + (metric.duration || 0), 0);
    const successfulSteps = pathMetrics.filter(metric => metric.success).length;
    const successRate = (successfulSteps / pathMetrics.length) * 100;
    const errors = pathMetrics.filter(metric => metric.error).map(metric => metric.error!);

    const summary: CriticalPathSummary = {
      path: pathName,
      totalDuration,
      steps: pathMetrics.length,
      successRate,
      averageDuration: totalDuration / pathMetrics.length,
      errors
    };

    // Store completed path
    this.completedPaths.push([...pathMetrics]);
    this.activePaths.delete(pathId);

    // Update path summary statistics
    this.updatePathSummary(pathName, summary);

    console.log(` Completed critical path: ${pathName}`, summary);

    // Send to monitoring service in production
    if (import.meta.env.PROD) {
      this.sendPathMetrics(pathMetrics, summary);
    }

    return summary;
  }

  private updatePathSummary(pathName: string, newSummary: CriticalPathSummary): void {
    const existingSummary = this.pathSummaries.get(pathName);
    
    if (!existingSummary) {
      this.pathSummaries.set(pathName, newSummary);
      return;
    }

    // Calculate running averages
    const totalPaths = this.completedPaths.filter(path => path[0].path === pathName).length;
    
    const updatedSummary: CriticalPathSummary = {
      path: pathName,
      totalDuration: (existingSummary.totalDuration + newSummary.totalDuration) / 2,
      steps: newSummary.steps,
      successRate: (existingSummary.successRate + newSummary.successRate) / 2,
      averageDuration: (existingSummary.averageDuration + newSummary.averageDuration) / 2,
      errors: [...existingSummary.errors, ...newSummary.errors].slice(-10) // Keep last 10 errors
    };

    this.pathSummaries.set(pathName, updatedSummary);
  }

  private async sendPathMetrics(metrics: CriticalPathMetric[], summary: CriticalPathSummary): Promise<void> {
    try {
      const payload = {
        metrics,
        summary,
        timestamp: new Date().toISOString(),
        userAgent: navigator.userAgent,
        url: window.location.href
      };

      // In production, send to your monitoring service
      console.log('Sending critical path metrics:', payload);
      
      // Example implementation:
      // await fetch('/api/monitoring/critical-paths', {
      //   method: 'POST',
      //   headers: { 'Content-Type': 'application/json' },
      //   body: JSON.stringify(payload)
      // });
    } catch (error) {
      console.warn('Failed to send critical path metrics:', error);
    }
  }

  // Convenience methods for common critical paths
  public startUserRegistration(metadata?: Record<string, any>): string {
    return this.startPath(this.CRITICAL_PATHS.USER_REGISTRATION, metadata);
  }

  public startUserLogin(metadata?: Record<string, any>): string {
    return this.startPath(this.CRITICAL_PATHS.USER_LOGIN, metadata);
  }

  public startProductBrowse(metadata?: Record<string, any>): string {
    return this.startPath(this.CRITICAL_PATHS.PRODUCT_BROWSE, metadata);
  }

  public startAddToCart(metadata?: Record<string, any>): string {
    return this.startPath(this.CRITICAL_PATHS.ADD_TO_CART, metadata);
  }

  public startCheckoutFlow(metadata?: Record<string, any>): string {
    return this.startPath(this.CRITICAL_PATHS.CHECKOUT_FLOW, metadata);
  }

  public startOrderCompletion(metadata?: Record<string, any>): string {
    return this.startPath(this.CRITICAL_PATHS.ORDER_COMPLETION, metadata);
  }

  public startAdminLogin(metadata?: Record<string, any>): string {
    return this.startPath(this.CRITICAL_PATHS.ADMIN_LOGIN, metadata);
  }

  public startAdminProductManagement(metadata?: Record<string, any>): string {
    return this.startPath(this.CRITICAL_PATHS.ADMIN_PRODUCT_MANAGEMENT, metadata);
  }

  public startAdminOrderManagement(metadata?: Record<string, any>): string {
    return this.startPath(this.CRITICAL_PATHS.ADMIN_ORDER_MANAGEMENT, metadata);
  }

  // Analytics and reporting methods
  public getPathSummary(pathName: string): CriticalPathSummary | undefined {
    return this.pathSummaries.get(pathName);
  }

  public getAllPathSummaries(): Map<string, CriticalPathSummary> {
    return new Map(this.pathSummaries);
  }

  public getActivePaths(): string[] {
    return Array.from(this.activePaths.keys());
  }

  public getCompletedPathsCount(): number {
    return this.completedPaths.length;
  }

  public generateReport(): string {
    let report = '\n Critical Path Monitoring Report\n';
    report += '=====================================\n\n';

    this.pathSummaries.forEach((summary, pathName) => {
      report += `  ${pathName.toUpperCase()}\n`;
      report += `   Duration: ${summary.totalDuration.toFixed(2)}ms (avg: ${summary.averageDuration.toFixed(2)}ms)\n`;
      report += `   Success Rate: ${summary.successRate.toFixed(1)}%\n`;
      report += `   Steps: ${summary.steps}\n`;
      
      if (summary.errors.length > 0) {
        report += `   Recent Errors: ${summary.errors.slice(-3).join(', ')}\n`;
      }
      
      report += '\n';
    });

    report += ` Total Completed Paths: ${this.completedPaths.length}\n`;
    report += ` Active Paths: ${this.activePaths.size}\n`;

    return report;
  }
}

