import { toast } from 'sonner';
import sentryService, { ErrorContext } from './sentryService';
import { apiErrorHandler } from './apiErrorHandler';

export enum LogLevel {
  DEBUG = 'debug',
  INFO = 'info',
  WARN = 'warn',
  ERROR = 'error'
}

export interface LogContext extends ErrorContext {
  level?: LogLevel;
  timestamp?: string;
  userAgent?: string;
  url?: string;
}

export interface ErrorHandlerConfig {
  enableConsoleLogging: boolean;
  enableSentryLogging: boolean;
  enableUserNotifications: boolean;
  logLevel: LogLevel;
  isDevelopment: boolean;
  isProduction: boolean;
}

/**
 * Centralized error handling service to replace console.error statements
 * Provides structured logging for development vs production environments
 */
export class ErrorHandler {
  private config: ErrorHandlerConfig;

  constructor() {
    this.config = {
      enableConsoleLogging: import.meta.env.DEV,
      enableSentryLogging: import.meta.env.PROD,
      enableUserNotifications: true,
      logLevel: import.meta.env.DEV ? LogLevel.DEBUG : LogLevel.ERROR,
      isDevelopment: import.meta.env.DEV,
      isProduction: import.meta.env.PROD
    };
  }

  /**
   * Updates the error handler configuration
   */
  configure(config: Partial<ErrorHandlerConfig>) {
    this.config = { ...this.config, ...config };
  }

  /**
   * Creates a standardized log context
   */
  private createLogContext(context?: Partial<LogContext>): LogContext {
    return {
      timestamp: new Date().toISOString(),
      userAgent: navigator.userAgent,
      url: window.location.href,
      ...context
    };
  }

  /**
   * Determines if a log level should be processed
   */
  private shouldLog(level: LogLevel): boolean {
    const levels = [LogLevel.DEBUG, LogLevel.INFO, LogLevel.WARN, LogLevel.ERROR];
    const currentLevelIndex = levels.indexOf(this.config.logLevel);
    const messageLevelIndex = levels.indexOf(level);
    
    return messageLevelIndex >= currentLevelIndex;
  }

  /**
   * Formats console output for development
   */
  private formatConsoleOutput(level: LogLevel, message: string, data?: any): void {
    if (!this.config.enableConsoleLogging || !this.shouldLog(level)) return;

    const timestamp = new Date().toLocaleTimeString();
    const prefix = `[${timestamp}] ${level.toUpperCase()}:`;

    switch (level) {
      case LogLevel.DEBUG:
        console.debug(prefix, message, data);
        break;
      case LogLevel.INFO:
        console.info(prefix, message, data);
        break;
      case LogLevel.WARN:
        console.warn(prefix, message, data);
        break;
      case LogLevel.ERROR:
        console.error(prefix, message, data);
        break;
    }
  }

  /**
   * Logs debug messages (development only)
   */
  debug(message: string, context?: Partial<LogContext>): void {
    if (!this.config.isDevelopment) return;

    const logContext = this.createLogContext({ ...context, level: LogLevel.DEBUG });
    
    this.formatConsoleOutput(LogLevel.DEBUG, message, logContext);
    
    if (this.config.enableSentryLogging) {
      sentryService.addBreadcrumb(message, 'debug', 'debug', logContext.additionalData);
    }
  }

  /**
   * Logs informational messages
   */
  info(message: string, context?: Partial<LogContext>): void {
    const logContext = this.createLogContext({ ...context, level: LogLevel.INFO });
    
    this.formatConsoleOutput(LogLevel.INFO, message, logContext);
    
    if (this.config.enableSentryLogging) {
      sentryService.addBreadcrumb(message, logContext.component || 'app', 'info', logContext.additionalData);
    }
  }

  /**
   * Logs warning messages
   */
  warn(message: string, context?: Partial<LogContext>): void {
    const logContext = this.createLogContext({ ...context, level: LogLevel.WARN });
    
    this.formatConsoleOutput(LogLevel.WARN, message, logContext);
    
    if (this.config.enableSentryLogging) {
      sentryService.addBreadcrumb(message, logContext.component || 'app', 'warning', logContext.additionalData);
    }

    // Show user notification for warnings in production
    if (this.config.isProduction && this.config.enableUserNotifications) {
      toast.warning(message);
    }
  }

  /**
   * Logs error messages and handles error objects
   */
  error(error: Error | string, context?: Partial<LogContext>): void {
    const logContext = this.createLogContext({ ...context, level: LogLevel.ERROR });
    
    let errorMessage: string;
    let errorObject: Error;

    if (error instanceof Error) {
      errorMessage = error.message;
      errorObject = error;
    } else {
      errorMessage = error;
      errorObject = new Error(error);
    }

    this.formatConsoleOutput(LogLevel.ERROR, errorMessage, {
      ...logContext,
      stack: errorObject.stack,
      name: errorObject.name
    });

    // Log to Sentry
    if (this.config.enableSentryLogging) {
      sentryService.captureError(errorObject, logContext);
    }

    // Show user notification
    if (this.config.enableUserNotifications) {
      const userMessage = this.getUserFriendlyMessage(errorObject);
      toast.error(userMessage);
    }
  }

  /**
   * Handles API-specific errors
   */
  apiError(error: Error | string, operation: string, context?: Partial<LogContext>): void {
    const enhancedContext = {
      ...context,
      component: 'api',
      action: operation,
      additionalData: {
        ...context?.additionalData,
        operation,
        online: navigator.onLine
      }
    };

    this.error(error, enhancedContext);
  }

  /**
   * Handles authentication errors
   */
  authError(error: Error | string, context?: Partial<LogContext>): void {
    const enhancedContext = {
      ...context,
      component: 'auth',
      additionalData: {
        ...context?.additionalData,
        sessionExists: !!localStorage.getItem('supabase.auth.token')
      }
    };

    this.error(error, enhancedContext);
  }

  /**
   * Handles database/Supabase errors
   */
  databaseError(error: Error | string, operation: string, context?: Partial<LogContext>): void {
    const enhancedContext = {
      ...context,
      component: 'database',
      action: operation,
      additionalData: {
        ...context?.additionalData,
        operation
      }
    };

    this.error(error, enhancedContext);
  }

  /**
   * Handles network/fetch errors
   */
  networkError(error: Error | string, url?: string, context?: Partial<LogContext>): void {
    const enhancedContext = {
      ...context,
      component: 'network',
      additionalData: {
        ...context?.additionalData,
        url,
        online: navigator.onLine,
        connectionType: (navigator as any).connection?.effectiveType
      }
    };

    this.error(error, enhancedContext);
  }

  /**
   * Handles validation errors
   */
  validationError(errors: Record<string, string> | string, context?: Partial<LogContext>): void {
    const errorMessage = typeof errors === 'string' 
      ? errors 
      : Object.values(errors).join(', ');

    const enhancedContext = {
      ...context,
      component: 'validation',
      additionalData: {
        ...context?.additionalData,
        validationErrors: errors
      }
    };

    this.error(errorMessage, enhancedContext);
  }

  /**
   * Gets user-friendly error messages
   */
  private getUserFriendlyMessage(error: Error): string {
    const message = error.message.toLowerCase();

    if (message.includes('network') || message.includes('fetch')) {
      return 'Network connection failed. Please check your internet connection.';
    }

    if (message.includes('auth') || message.includes('jwt') || message.includes('token')) {
      return 'Your session has expired. Please sign in again.';
    }

    if (message.includes('permission') || message.includes('forbidden')) {
      return 'You do not have permission to perform this action.';
    }

    if (message.includes('not found') || message.includes('404')) {
      return 'The requested resource was not found.';
    }

    if (message.includes('validation') || message.includes('invalid')) {
      return 'Please check your input and try again.';
    }

    if (message.includes('server') || message.includes('500')) {
      return 'Server error. Our team has been notified.';
    }

    // Return original message if no pattern matches, but sanitize it
    return error.message || 'An unexpected error occurred. Please try again.';
  }

  /**
   * Creates a performance timer for measuring operation duration
   */
  createTimer(operation: string): () => void {
    const startTime = performance.now();
    
    return () => {
      const duration = performance.now() - startTime;
      this.info(`Operation completed: ${operation}`, {
        component: 'performance',
        additionalData: {
          operation,
          duration: `${duration.toFixed(2)}ms`
        }
      });
    };
  }

  /**
   * Logs performance metrics
   */
  performance(operation: string, duration: number, context?: Partial<LogContext>): void {
    this.info(`Performance: ${operation} took ${duration.toFixed(2)}ms`, {
      ...context,
      component: 'performance',
      additionalData: {
        ...context?.additionalData,
        operation,
        duration
      }
    });
  }

  /**
   * Replacement for console.error - use this instead of console.error
   */
  logError = this.error.bind(this);

  /**
   * Replacement for console.warn - use this instead of console.warn
   */
  logWarning = this.warn.bind(this);

  /**
   * Replacement for console.log - use this instead of console.log
   */
  logInfo = this.info.bind(this);

  /**
   * Replacement for console.debug - use this instead of console.debug
   */
  logDebug = this.debug.bind(this);
}

// Create singleton instance
export const errorHandler = new ErrorHandler();

// Export convenience functions for easy migration from console methods
export const logError = errorHandler.logError;
export const logWarning = errorHandler.logWarning;
export const logInfo = errorHandler.logInfo;
export const logDebug = errorHandler.logDebug;

export default errorHandler;

import sentryService, { ErrorContext } from './sentryService';
import { errorHandler } from './ErrorHandler';
import { logger } from './Logger';

export interface BreadcrumbData {
  message: string;
  category: string;
  level: 'debug' | 'info' | 'warning' | 'error';
  data?: Record<string, any>;
  timestamp?: string;
}

export interface UserContext {
  id: string;
  email?: string;
  username?: string;
  role?: string;
  subscription?: string;
}

export interface SessionContext {
  sessionId: string;
  startTime: string;
  duration?: number;
  pageViews: number;
  interactions: number;
  errors: number;
}

export interface PerformanceMetrics {
  pageLoadTime?: number;
  firstContentfulPaint?: number;
  largestContentfulPaint?: number;
  cumulativeLayoutShift?: number;
  firstInputDelay?: number;
  timeToInteractive?: number;
}

/**
 * Enhanced error monitoring service with comprehensive tracking
 * Integrates with Sentry and provides additional monitoring capabilities
 */
export class ErrorMonitoring {
  private breadcrumbs: BreadcrumbData[] = [];
  private maxBreadcrumbs = 100;
  private sessionContext: SessionContext;
  private userContext: UserContext | null = null;
  private performanceMetrics: PerformanceMetrics = {};
  private isInitialized = false;

  constructor() {
    this.sessionContext = {
      sessionId: this.generateSessionId(),
      startTime: new Date().toISOString(),
      pageViews: 0,
      interactions: 0,
      errors: 0
    };
  }

  /**
   * Initialize error monitoring
   */
  async initialize(): Promise<void> {
    if (this.isInitialized) return;

    try {
      // Initialize Sentry service
      await sentryService.initialize();

      // Set up performance monitoring
      this.setupPerformanceMonitoring();

      // Set up user interaction tracking
      this.setupInteractionTracking();

      // Set up navigation tracking
      this.setupNavigationTracking();

      // Set up error tracking
      this.setupErrorTracking();

      this.isInitialized = true;

      this.addBreadcrumb('Error monitoring initialized', 'system', 'info', {
        sessionId: this.sessionContext.sessionId
      });

      logger.info('Error monitoring service initialized', {
        component: 'ErrorMonitoring',
        additionalData: {
          sessionId: this.sessionContext.sessionId,
          sentryEnabled: true // Assume enabled if initialization succeeded
        }
      });
    } catch (error) {
      errorHandler.error(error instanceof Error ? error : new Error(String(error)), {
        component: 'ErrorMonitoring',
        action: 'initialize'
      });
    }
  }

  /**
   * Generate unique session ID
   */
  private generateSessionId(): string {
    return `session_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  /**
   * Set user context for error tracking
   */
  setUserContext(user: UserContext): void {
    this.userContext = user;
    
    // Set user context in Sentry
    sentryService.setUserContext(user.id, user.email, {
      username: user.username,
      role: user.role,
      subscription: user.subscription
    });

    this.addBreadcrumb('User context set', 'auth', 'info', {
      userId: user.id,
      role: user.role
    });

    logger.info('User context updated', {
      component: 'ErrorMonitoring',
      additionalData: {
        userId: user.id,
        role: user.role
      }
    });
  }

  /**
   * Clear user context
   */
  clearUserContext(): void {
    this.userContext = null;
    sentryService.clearUserContext();

    this.addBreadcrumb('User context cleared', 'auth', 'info');

    logger.info('User context cleared', {
      component: 'ErrorMonitoring'
    });
  }

  /**
   * Add breadcrumb for debugging
   */
  addBreadcrumb(
    message: string,
    category: string,
    level: BreadcrumbData['level'] = 'info',
    data?: Record<string, any>
  ): void {
    const breadcrumb: BreadcrumbData = {
      message,
      category,
      level,
      data,
      timestamp: new Date().toISOString()
    };

    // Add to local breadcrumbs
    this.breadcrumbs.push(breadcrumb);
    if (this.breadcrumbs.length > this.maxBreadcrumbs) {
      this.breadcrumbs.shift();
    }

    // Add to Sentry
    sentryService.addBreadcrumb(message, category, level, data);
  }

  /**
   * Capture error with enhanced context
   */
  captureError(error: Error, context?: Partial<ErrorContext>): void {
    this.sessionContext.errors++;

    const enhancedContext: ErrorContext = {
      ...context,
      additionalData: {
        ...context?.additionalData,
        sessionContext: this.sessionContext,
        userContext: this.userContext,
        performanceMetrics: this.performanceMetrics,
        breadcrumbs: this.breadcrumbs.slice(-10), // Last 10 breadcrumbs
        url: window.location.href,
        userAgent: navigator.userAgent,
        timestamp: new Date().toISOString(),
        viewport: {
          width: window.innerWidth,
          height: window.innerHeight
        },
        connection: this.getConnectionInfo(),
        memory: this.getMemoryInfo()
      }
    };

    // Log to our error handler
    errorHandler.error(error, enhancedContext);

    // Add error breadcrumb
    this.addBreadcrumb(`Error occurred: ${error.message}`, 'error', 'error', {
      errorName: error.name,
      errorStack: error.stack?.split('\n').slice(0, 3).join('\n') // First 3 lines of stack
    });
  }

  /**
   * Capture message with context
   */
  captureMessage(
    message: string,
    level: 'debug' | 'info' | 'warning' | 'error' = 'info',
    context?: Partial<ErrorContext>
  ): void {
    const enhancedContext: ErrorContext = {
      ...context,
      additionalData: {
        ...context?.additionalData,
        sessionContext: this.sessionContext,
        userContext: this.userContext,
        timestamp: new Date().toISOString()
      }
    };

    sentryService.captureMessage(message, level, enhancedContext);

    this.addBreadcrumb(message, 'message', level, context?.additionalData);
  }

  /**
   * Track user interaction
   */
  trackInteraction(
    action: string,
    element: string,
    details?: Record<string, any>
  ): void {
    this.sessionContext.interactions++;

    this.addBreadcrumb(`User interaction: ${action}`, 'user', 'info', {
      action,
      element,
      ...details
    });

    logger.userAction(action, element, details, {
      component: 'ErrorMonitoring'
    });
  }

  /**
   * Track page view
   */
  trackPageView(path: string, title?: string): void {
    this.sessionContext.pageViews++;

    this.addBreadcrumb(`Page view: ${path}`, 'navigation', 'info', {
      path,
      title,
      pageViews: this.sessionContext.pageViews
    });

    logger.navigation(document.referrer || 'direct', path, {
      component: 'ErrorMonitoring',
      additionalData: { title }
    });
  }

  /**
   * Track performance metrics
   */
  trackPerformance(metrics: Partial<PerformanceMetrics>): void {
    this.performanceMetrics = { ...this.performanceMetrics, ...metrics };

    this.addBreadcrumb('Performance metrics updated', 'performance', 'info', metrics);

    // Log slow performance
    if (metrics.pageLoadTime && metrics.pageLoadTime > 3000) {
      logger.warn('Slow page load detected', {
        component: 'ErrorMonitoring',
        additionalData: metrics
      });
    }
  }

  /**
   * Get connection information
   */
  private getConnectionInfo(): any {
    const connection = (navigator as any).connection;
    if (!connection) return null;

    return {
      effectiveType: connection.effectiveType,
      downlink: connection.downlink,
      rtt: connection.rtt,
      saveData: connection.saveData
    };
  }

  /**
   * Get memory information
   */
  private getMemoryInfo(): any {
    const memory = (performance as any).memory;
    if (!memory) return null;

    return {
      usedJSHeapSize: memory.usedJSHeapSize,
      totalJSHeapSize: memory.totalJSHeapSize,
      jsHeapSizeLimit: memory.jsHeapSizeLimit
    };
  }

  /**
   * Set up performance monitoring
   */
  private setupPerformanceMonitoring(): void {
    // Monitor page load performance
    window.addEventListener('load', () => {
      setTimeout(() => {
        const navigation = performance.getEntriesByType('navigation')[0] as PerformanceNavigationTiming;
        if (navigation) {
          this.trackPerformance({
            pageLoadTime: navigation.loadEventEnd - navigation.fetchStart,
            timeToInteractive: navigation.domInteractive - navigation.fetchStart
          });
        }

        // Monitor Web Vitals if available
        if ('web-vitals' in window) {
          this.setupWebVitalsMonitoring();
        }
      }, 0);
    });
  }

  /**
   * Set up Web Vitals monitoring
   */
  private setupWebVitalsMonitoring(): void {
    // This would integrate with web-vitals library if available
    // For now, we'll use basic performance API
    
    const observer = new PerformanceObserver((list) => {
      for (const entry of list.getEntries()) {
        if (entry.entryType === 'paint') {
          if (entry.name === 'first-contentful-paint') {
            this.trackPerformance({
              firstContentfulPaint: entry.startTime
            });
          }
        }
      }
    });

    try {
      observer.observe({ entryTypes: ['paint'] });
    } catch (error) {
      // Performance observer not supported
    }
  }

  /**
   * Set up user interaction tracking
   */
  private setupInteractionTracking(): void {
    // Track clicks
    document.addEventListener('click', (event) => {
      const target = event.target as HTMLElement;
      if (target) {
        const tagName = target.tagName.toLowerCase();
        const id = target.id;
        const className = target.className;
        
        this.trackInteraction('click', tagName, {
          id,
          className,
          text: target.textContent?.slice(0, 50)
        });
      }
    });

    // Track form submissions
    document.addEventListener('submit', (event) => {
      const target = event.target as HTMLFormElement;
      if (target) {
        this.trackInteraction('form_submit', 'form', {
          id: target.id,
          action: target.action,
          method: target.method
        });
      }
    });
  }

  /**
   * Set up navigation tracking
   */
  private setupNavigationTracking(): void {
    // Track initial page load
    this.trackPageView(window.location.pathname, document.title);

    // Track navigation changes (for SPAs)
    let currentPath = window.location.pathname;
    
    const checkForNavigation = () => {
      if (window.location.pathname !== currentPath) {
        currentPath = window.location.pathname;
        this.trackPageView(currentPath, document.title);
      }
    };

    // Check for navigation changes
    setInterval(checkForNavigation, 1000);

    // Listen for popstate events
    window.addEventListener('popstate', () => {
      setTimeout(checkForNavigation, 0);
    });
  }

  /**
   * Set up error tracking
   */
  private setupErrorTracking(): void {
    // Track unhandled promise rejections
    window.addEventListener('unhandledrejection', (event) => {
      const error = event.reason instanceof Error ? event.reason : new Error(String(event.reason));
      this.captureError(error, {
        component: 'window',
        action: 'unhandled_promise_rejection'
      });
    });

    // Track global errors
    window.addEventListener('error', (event) => {
      const error = event.error || new Error(event.message);
      this.captureError(error, {
        component: 'window',
        action: 'global_error',
        additionalData: {
          filename: event.filename,
          lineno: event.lineno,
          colno: event.colno
        }
      });
    });
  }

  /**
   * Get monitoring statistics
   */
  getStats(): {
    session: SessionContext;
    breadcrumbs: number;
    user: UserContext | null;
    performance: PerformanceMetrics;
  } {
    return {
      session: {
        ...this.sessionContext,
        duration: Date.now() - new Date(this.sessionContext.startTime).getTime()
      },
      breadcrumbs: this.breadcrumbs.length,
      user: this.userContext,
      performance: this.performanceMetrics
    };
  }

  /**
   * Export monitoring data for debugging
   */
  exportData(): string {
    return JSON.stringify({
      session: this.sessionContext,
      user: this.userContext,
      breadcrumbs: this.breadcrumbs,
      performance: this.performanceMetrics,
      stats: this.getStats()
    }, null, 2);
  }
}

