/**
 * Health Check Service
 * Provides health monitoring endpoints and system status checks
 */

interface HealthCheckResult {
  status: 'healthy' | 'degraded' | 'unhealthy';
  timestamp: string;
  version: string;
  uptime: number;
  checks: {
    database: HealthStatus;
    authentication: HealthStatus;
    externalServices: HealthStatus;
    performance: HealthStatus;
  };
  metadata: {
    buildVersion: string;
    deploymentTime: string;
    environment: string;
  };
}

interface HealthStatus {
  status: 'pass' | 'warn' | 'fail';
  responseTime?: number;
  message?: string;
  lastChecked: string;
}

class HealthCheckService {
  private startTime: number;
  private version: string;

  constructor() {
    this.startTime = Date.now();
    this.version = import.meta.env.VITE_APP_VERSION || '1.0.0';
  }

  async performHealthCheck(): Promise<HealthCheckResult> {
    const timestamp = new Date().toISOString();
    const uptime = Date.now() - this.startTime;

    const checks = await Promise.allSettled([
      this.checkDatabase(),
      this.checkAuthentication(),
      this.checkExternalServices(),
      this.checkPerformance()
    ]);

    const [database, authentication, externalServices, performance] = checks.map(
      result => result.status === 'fulfilled' ? result.value : this.createFailedCheck('Check failed')
    );

    const overallStatus = this.determineOverallStatus([database, authentication, externalServices, performance]);

    return {
      status: overallStatus,
      timestamp,
      version: this.version,
      uptime,
      checks: {
        database,
        authentication,
        externalServices,
        performance
      },
      metadata: {
        buildVersion: this.version,
        deploymentTime: import.meta.env.VITE_BUILD_TIME || timestamp,
        environment: import.meta.env.MODE
      }
    };
  }

  private async checkDatabase(): Promise<HealthStatus> {
    const startTime = performance.now();
    
    try {
      // Import Supabase client dynamically to avoid circular dependencies
      const { supabase } = await import('@/lib/supabase');
      
      // Simple query to test database connectivity
      const { data, error } = await supabase
        .from('products')
        .select('id')
        .limit(1);

      const responseTime = performance.now() - startTime;

      if (error) {
        return {
          status: 'fail',
          responseTime,
          message: `Database error: ${error.message}`,
          lastChecked: new Date().toISOString()
        };
      }

      return {
        status: responseTime > 1000 ? 'warn' : 'pass',
        responseTime,
        message: responseTime > 1000 ? 'Slow database response' : 'Database connection healthy',
        lastChecked: new Date().toISOString()
      };
    } catch (error) {
      return {
        status: 'fail',
        responseTime: performance.now() - startTime,
        message: `Database connection failed: ${error}`,
        lastChecked: new Date().toISOString()
      };
    }
  }

  private async checkAuthentication(): Promise<HealthStatus> {
    const startTime = performance.now();
    
    try {
      const { supabase } = await import('@/lib/supabase');
      
      // Check if auth service is responsive
      const { data: { session }, error } = await supabase.auth.getSession();
      
      const responseTime = performance.now() - startTime;

      if (error) {
        return {
          status: 'fail',
          responseTime,
          message: `Auth service error: ${error.message}`,
          lastChecked: new Date().toISOString()
        };
      }

      return {
        status: 'pass',
        responseTime,
        message: 'Authentication service healthy',
        lastChecked: new Date().toISOString()
      };
    } catch (error) {
      return {
        status: 'fail',
        responseTime: performance.now() - startTime,
        message: `Auth service failed: ${error}`,
        lastChecked: new Date().toISOString()
      };
    }
  }

  private async checkExternalServices(): Promise<HealthStatus> {
    const startTime = performance.now();
    
    try {
      // Check if we can reach external services (like CDN, payment providers, etc.)
      const supabaseUrl = import.meta.env.VITE_SUPABASE_URL;
      
      if (!supabaseUrl) {
        return {
          status: 'fail',
          responseTime: performance.now() - startTime,
          message: 'Supabase URL not configured',
          lastChecked: new Date().toISOString()
        };
      }

      // Simple connectivity check
      const response = await fetch(`${supabaseUrl}/rest/v1/`, {
        method: 'HEAD',
        headers: {
          'apikey': import.meta.env.VITE_SUPABASE_ANON_KEY || ''
        }
      });

      const responseTime = performance.now() - startTime;

      if (!response.ok) {
        return {
          status: 'fail',
          responseTime,
          message: `External service error: ${response.status}`,
          lastChecked: new Date().toISOString()
        };
      }

      return {
        status: responseTime > 2000 ? 'warn' : 'pass',
        responseTime,
        message: responseTime > 2000 ? 'Slow external service response' : 'External services healthy',
        lastChecked: new Date().toISOString()
      };
    } catch (error) {
      return {
        status: 'fail',
        responseTime: performance.now() - startTime,
        message: `External services failed: ${error}`,
        lastChecked: new Date().toISOString()
      };
    }
  }

  private async checkPerformance(): Promise<HealthStatus> {
    const startTime = performance.now();
    
    try {
      // Check memory usage if available
      let memoryStatus = 'pass';
      let memoryMessage = 'Performance metrics healthy';

      if ('memory' in performance) {
        const memory = (performance as any).memory;
        const usedMB = memory.usedJSHeapSize / 1024 / 1024;
        const limitMB = memory.jsHeapSizeLimit / 1024 / 1024;
        const usagePercent = (usedMB / limitMB) * 100;

        if (usagePercent > 80) {
          memoryStatus = 'warn';
          memoryMessage = `High memory usage: ${usagePercent.toFixed(1)}%`;
        } else if (usagePercent > 90) {
          memoryStatus = 'fail';
          memoryMessage = `Critical memory usage: ${usagePercent.toFixed(1)}%`;
        }
      }

      const responseTime = performance.now() - startTime;

      return {
        status: memoryStatus as 'pass' | 'warn' | 'fail',
        responseTime,
        message: memoryMessage,
        lastChecked: new Date().toISOString()
      };
    } catch (error) {
      return {
        status: 'warn',
        responseTime: performance.now() - startTime,
        message: `Performance check failed: ${error}`,
        lastChecked: new Date().toISOString()
      };
    }
  }

  private createFailedCheck(message: string): HealthStatus {
    return {
      status: 'fail',
      message,
      lastChecked: new Date().toISOString()
    };
  }

  private determineOverallStatus(checks: HealthStatus[]): 'healthy' | 'degraded' | 'unhealthy' {
    const failedChecks = checks.filter(check => check.status === 'fail').length;
    const warnChecks = checks.filter(check => check.status === 'warn').length;

    if (failedChecks > 0) {
      return failedChecks >= 2 ? 'unhealthy' : 'degraded';
    }

    if (warnChecks > 1) {
      return 'degraded';
    }

    return 'healthy';
  }

  // Expose health check endpoint
  async getHealthStatus(): Promise<HealthCheckResult> {
    return this.performHealthCheck();
  }

  // Simple liveness check
  isAlive(): boolean {
    return true;
  }

  // Readiness check
  async isReady(): Promise<boolean> {
    try {
      const health = await this.performHealthCheck();
      return health.status !== 'unhealthy';
    } catch {
      return false;
    }
  }
}

// Create singleton instance
export const healthCheckService = new HealthCheckService();

// Export health check endpoints for use in routing
export const healthCheckEndpoints = {
  // Full health check
  '/health': () => healthCheckService.getHealthStatus(),
  
  // Simple liveness probe
  '/health/live': () => ({ status: 'ok', timestamp: new Date().toISOString() }),
  
  // Readiness probe
  '/health/ready': async () => {
    const ready = await healthCheckService.isReady();
    return { 
      status: ready ? 'ready' : 'not ready', 
      timestamp: new Date().toISOString() 
    };
  }
};

export default healthCheckService;

import { ErrorHandler, LogLevel, LogContext } from './ErrorHandler';
import sentryService from './sentryService';

export interface LogEntry {
  level: LogLevel;
  message: string;
  timestamp: string;
  context?: LogContext;
  component?: string;
  action?: string;
}

export interface LoggerConfig {
  enableConsoleOutput: boolean;
  enableRemoteLogging: boolean;
  enableLocalStorage: boolean;
  maxLocalStorageEntries: number;
  logLevel: LogLevel;
}

/**
 * Structured logging system for development vs production environments
 * Provides centralized logging with different outputs based on environment
 */
export class Logger {
  private errorHandler: ErrorHandler;
  private config: LoggerConfig;
  private localStorageKey = 'app_logs';

  constructor(errorHandler: ErrorHandler) {
    this.errorHandler = errorHandler;
    this.config = {
      enableConsoleOutput: import.meta.env.DEV,
      enableRemoteLogging: import.meta.env.PROD,
      enableLocalStorage: import.meta.env.DEV,
      maxLocalStorageEntries: 1000,
      logLevel: import.meta.env.DEV ? LogLevel.DEBUG : LogLevel.WARN
    };
  }

  /**
   * Updates logger configuration
   */
  configure(config: Partial<LoggerConfig>) {
    this.config = { ...this.config, ...config };
  }

  /**
   * Creates a log entry
   */
  private createLogEntry(
    level: LogLevel,
    message: string,
    context?: Partial<LogContext>
  ): LogEntry {
    return {
      level,
      message,
      timestamp: new Date().toISOString(),
      context: {
        timestamp: new Date().toISOString(),
        userAgent: navigator.userAgent,
        url: window.location.href,
        ...context
      },
      component: context?.component,
      action: context?.action
    };
  }

  /**
   * Stores log entry in localStorage (development only)
   */
  private storeLogEntry(entry: LogEntry): void {
    if (!this.config.enableLocalStorage) return;

    try {
      const existingLogs = this.getStoredLogs();
      const updatedLogs = [entry, ...existingLogs].slice(0, this.config.maxLocalStorageEntries);
      
      localStorage.setItem(this.localStorageKey, JSON.stringify(updatedLogs));
    } catch (error) {
      // Silently fail if localStorage is not available
    }
  }

  /**
   * Gets stored logs from localStorage
   */
  getStoredLogs(): LogEntry[] {
    if (!this.config.enableLocalStorage) return [];

    try {
      const logs = localStorage.getItem(this.localStorageKey);
      return logs ? JSON.parse(logs) : [];
    } catch (error) {
      return [];
    }
  }

  /**
   * Clears stored logs
   */
  clearStoredLogs(): void {
    if (!this.config.enableLocalStorage) return;

    try {
      localStorage.removeItem(this.localStorageKey);
    } catch (error) {
      // Silently fail
    }
  }

  /**
   * Exports logs as JSON for debugging
   */
  exportLogs(): string {
    const logs = this.getStoredLogs();
    return JSON.stringify(logs, null, 2);
  }

  /**
   * Logs debug messages (development only)
   */
  debug(message: string, context?: Partial<LogContext>): void {
    const entry = this.createLogEntry(LogLevel.DEBUG, message, context);
    
    this.storeLogEntry(entry);
    this.errorHandler.debug(message, context);
  }

  /**
   * Logs informational messages
   */
  info(message: string, context?: Partial<LogContext>): void {
    const entry = this.createLogEntry(LogLevel.INFO, message, context);
    
    this.storeLogEntry(entry);
    this.errorHandler.info(message, context);
  }

  /**
   * Logs warning messages
   */
  warn(message: string, context?: Partial<LogContext>): void {
    const entry = this.createLogEntry(LogLevel.WARN, message, context);
    
    this.storeLogEntry(entry);
    this.errorHandler.warn(message, context);
  }

  /**
   * Logs error messages
   */
  error(error: Error | string, context?: Partial<LogContext>): void {
    const message = error instanceof Error ? error.message : error;
    const entry = this.createLogEntry(LogLevel.ERROR, message, context);
    
    this.storeLogEntry(entry);
    this.errorHandler.error(error, context);
  }

  /**
   * Logs API operations with timing
   */
  apiCall(
    operation: string,
    method: string,
    endpoint: string,
    duration?: number,
    success: boolean = true,
    context?: Partial<LogContext>
  ): void {
    const message = `API ${method} ${endpoint} ${success ? 'succeeded' : 'failed'}${
      duration ? ` in ${duration.toFixed(2)}ms` : ''
    }`;

    const logContext = {
      ...context,
      component: 'api',
      action: operation,
      additionalData: {
        ...context?.additionalData,
        method,
        endpoint,
        duration,
        success
      }
    };

    if (success) {
      this.info(message, logContext);
    } else {
      this.error(message, logContext);
    }
  }

  /**
   * Logs user actions for analytics
   */
  userAction(
    action: string,
    component: string,
    details?: Record<string, any>,
    context?: Partial<LogContext>
  ): void {
    const message = `User action: ${action} in ${component}`;
    
    const logContext = {
      ...context,
      component,
      action,
      additionalData: {
        ...context?.additionalData,
        userAction: action,
        details
      }
    };

    this.info(message, logContext);

    // Send to analytics in production
    if (this.config.enableRemoteLogging) {
      sentryService.addBreadcrumb(message, component, 'info', details);
    }
  }

  /**
   * Logs performance metrics
   */
  performance(
    operation: string,
    duration: number,
    component?: string,
    context?: Partial<LogContext>
  ): void {
    const message = `Performance: ${operation} took ${duration.toFixed(2)}ms`;
    
    const logContext = {
      ...context,
      component: component || 'performance',
      action: operation,
      additionalData: {
        ...context?.additionalData,
        operation,
        duration
      }
    };

    // Only log slow operations in production
    if (import.meta.env.PROD && duration < 1000) {
      return;
    }

    this.info(message, logContext);
  }

  /**
   * Creates a performance timer
   */
  startTimer(operation: string, component?: string): () => void {
    const startTime = performance.now();
    
    return () => {
      const duration = performance.now() - startTime;
      this.performance(operation, duration, component);
    };
  }

  /**
   * Logs component lifecycle events (development only)
   */
  componentLifecycle(
    component: string,
    event: 'mount' | 'unmount' | 'update' | 'error',
    details?: Record<string, any>
  ): void {
    if (!import.meta.env.DEV) return;

    const message = `Component ${component} ${event}`;
    
    this.debug(message, {
      component,
      action: event,
      additionalData: details
    });
  }

  /**
   * Logs navigation events
   */
  navigation(from: string, to: string, context?: Partial<LogContext>): void {
    const message = `Navigation: ${from}  ${to}`;
    
    this.info(message, {
      ...context,
      component: 'router',
      action: 'navigate',
      additionalData: {
        ...context?.additionalData,
        from,
        to
      }
    });
  }

  /**
   * Gets log statistics for debugging
   */
  getLogStats(): {
    total: number;
    byLevel: Record<LogLevel, number>;
    byComponent: Record<string, number>;
    recent: LogEntry[];
  } {
    const logs = this.getStoredLogs();
    
    const byLevel = logs.reduce((acc, log) => {
      acc[log.level] = (acc[log.level] || 0) + 1;
      return acc;
    }, {} as Record<LogLevel, number>);

    const byComponent = logs.reduce((acc, log) => {
      const component = log.component || 'unknown';
      acc[component] = (acc[component] || 0) + 1;
      return acc;
    }, {} as Record<string, number>);

    return {
      total: logs.length,
      byLevel,
      byComponent,
      recent: logs.slice(0, 10)
    };
  }
}

// Create singleton instance
export const logger = new Logger(new ErrorHandler());

// Export convenience functions
export const logDebug = logger.debug.bind(logger);
export const logInfo = logger.info.bind(logger);
export const logWarn = logger.warn.bind(logger);
export const logError = logger.error.bind(logger);
export const logApiCall = logger.apiCall.bind(logger);
export const logUserAction = logger.userAction.bind(logger);
export const logPerformance = logger.performance.bind(logger);
export const startTimer = logger.startTimer.bind(logger);

export default logger;

/**
 * Production Monitoring Service
 * Provides comprehensive monitoring for production environments
 */

interface PerformanceMetric {
  name: string;
  value: number;
  timestamp: string;
  metadata?: Record<string, any>;
}

interface ErrorEvent {
  message: string;
  stack?: string;
  url: string;
  line?: number;
  column?: number;
  timestamp: string;
  userAgent: string;
  userId?: string;
  sessionId: string;
}

interface UserInteraction {
  type: 'click' | 'navigation' | 'form_submit' | 'error';
  target: string;
  timestamp: string;
  duration?: number;
  metadata?: Record<string, any>;
}

class ProductionMonitoringService {
  private sessionId: string;
  private userId?: string;
  private metrics: PerformanceMetric[] = [];
  private errors: ErrorEvent[] = [];
  private interactions: UserInteraction[] = [];
  private isProduction: boolean;

  constructor() {
    this.sessionId = this.generateSessionId();
    this.isProduction = import.meta.env.PROD;
    
    if (this.isProduction) {
      this.initializeMonitoring();
    }
  }

  private generateSessionId(): string {
    return `session_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  private initializeMonitoring(): void {
    // Global error handler
    window.addEventListener('error', (event) => {
      this.captureError({
        message: event.message,
        stack: event.error?.stack,
        url: event.filename,
        line: event.lineno,
        column: event.colno,
        timestamp: new Date().toISOString(),
        userAgent: navigator.userAgent,
        userId: this.userId,
        sessionId: this.sessionId
      });
    });

    // Unhandled promise rejection handler
    window.addEventListener('unhandledrejection', (event) => {
      this.captureError({
        message: `Unhandled Promise Rejection: ${event.reason}`,
        stack: event.reason?.stack,
        url: window.location.href,
        timestamp: new Date().toISOString(),
        userAgent: navigator.userAgent,
        userId: this.userId,
        sessionId: this.sessionId
      });
    });

    // Performance observer for Core Web Vitals
    this.initializePerformanceObserver();

    // User interaction tracking
    this.initializeInteractionTracking();

    // Periodic metrics collection
    this.startPeriodicCollection();
  }

  private initializePerformanceObserver(): void {
    if ('PerformanceObserver' in window) {
      try {
        // Largest Contentful Paint (LCP)
        const lcpObserver = new PerformanceObserver((list) => {
          const entries = list.getEntries();
          const lastEntry = entries[entries.length - 1];
          
          this.captureMetric({
            name: 'largest_contentful_paint',
            value: lastEntry.startTime,
            timestamp: new Date().toISOString(),
            metadata: {
              element: (lastEntry as any).element?.tagName,
              url: (lastEntry as any).url
            }
          });
        });
        lcpObserver.observe({ entryTypes: ['largest-contentful-paint'] });

        // First Input Delay (FID)
        const fidObserver = new PerformanceObserver((list) => {
          const entries = list.getEntries();
          entries.forEach((entry) => {
            this.captureMetric({
              name: 'first_input_delay',
              value: (entry as any).processingStart - entry.startTime,
              timestamp: new Date().toISOString(),
              metadata: {
                eventType: (entry as any).name
              }
            });
          });
        });
        fidObserver.observe({ entryTypes: ['first-input'] });

        // Cumulative Layout Shift (CLS)
        const clsObserver = new PerformanceObserver((list) => {
          let clsValue = 0;
          const entries = list.getEntries();
          
          entries.forEach((entry) => {
            if (!(entry as any).hadRecentInput) {
              clsValue += (entry as any).value;
            }
          });

          this.captureMetric({
            name: 'cumulative_layout_shift',
            value: clsValue,
            timestamp: new Date().toISOString()
          });
        });
        clsObserver.observe({ entryTypes: ['layout-shift'] });

        // Navigation timing
        const navigationObserver = new PerformanceObserver((list) => {
          const entries = list.getEntries();
          entries.forEach((entry) => {
            const navEntry = entry as PerformanceNavigationTiming;
            
            this.captureMetric({
              name: 'page_load_time',
              value: navEntry.loadEventEnd - navEntry.fetchStart,
              timestamp: new Date().toISOString(),
              metadata: {
                domContentLoaded: navEntry.domContentLoadedEventEnd - navEntry.fetchStart,
                firstByte: navEntry.responseStart - navEntry.fetchStart,
                domComplete: navEntry.domComplete - navEntry.fetchStart
              }
            });
          });
        });
        navigationObserver.observe({ entryTypes: ['navigation'] });

      } catch (error) {
        console.warn('Performance observer initialization failed:', error);
      }
    }
  }

  private initializeInteractionTracking(): void {
    // Track critical user interactions
    document.addEventListener('click', (event) => {
      const target = event.target as HTMLElement;
      const targetInfo = this.getElementInfo(target);

      this.captureInteraction({
        type: 'click',
        target: targetInfo,
        timestamp: new Date().toISOString()
      });
    });

    // Track form submissions
    document.addEventListener('submit', (event) => {
      const form = event.target as HTMLFormElement;
      const formInfo = this.getElementInfo(form);

      this.captureInteraction({
        type: 'form_submit',
        target: formInfo,
        timestamp: new Date().toISOString()
      });
    });

    // Track navigation
    let navigationStartTime = performance.now();
    
    const trackNavigation = () => {
      const duration = performance.now() - navigationStartTime;
      
      this.captureInteraction({
        type: 'navigation',
        target: window.location.pathname,
        timestamp: new Date().toISOString(),
        duration
      });
      
      navigationStartTime = performance.now();
    };

    // Listen for route changes (for SPA)
    window.addEventListener('popstate', trackNavigation);
    
    // Override pushState and replaceState to track programmatic navigation
    const originalPushState = history.pushState;
    const originalReplaceState = history.replaceState;
    
    history.pushState = function(...args) {
      originalPushState.apply(history, args);
      trackNavigation();
    };
    
    history.replaceState = function(...args) {
      originalReplaceState.apply(history, args);
      trackNavigation();
    };
  }

  private getElementInfo(element: HTMLElement): string {
    const tag = element.tagName.toLowerCase();
    const id = element.id ? `#${element.id}` : '';
    const className = element.className ? `.${element.className.split(' ').join('.')}` : '';
    const text = element.textContent?.slice(0, 50) || '';
    
    return `${tag}${id}${className} "${text}"`.trim();
  }

  private startPeriodicCollection(): void {
    // Collect metrics every 30 seconds
    setInterval(() => {
      this.collectSystemMetrics();
    }, 30000);

    // Send batched data every 5 minutes
    setInterval(() => {
      this.sendBatchedData();
    }, 300000);
  }

  private collectSystemMetrics(): void {
    // Memory usage
    if ('memory' in performance) {
      const memory = (performance as any).memory;
      
      this.captureMetric({
        name: 'memory_usage',
        value: memory.usedJSHeapSize,
        timestamp: new Date().toISOString(),
        metadata: {
          total: memory.totalJSHeapSize,
          limit: memory.jsHeapSizeLimit
        }
      });
    }

    // Connection information
    if ('connection' in navigator) {
      const connection = (navigator as any).connection;
      
      this.captureMetric({
        name: 'network_info',
        value: connection.downlink || 0,
        timestamp: new Date().toISOString(),
        metadata: {
          effectiveType: connection.effectiveType,
          rtt: connection.rtt,
          saveData: connection.saveData
        }
      });
    }
  }

  public captureMetric(metric: PerformanceMetric): void {
    this.metrics.push(metric);
    
    // Keep only last 100 metrics to prevent memory issues
    if (this.metrics.length > 100) {
      this.metrics = this.metrics.slice(-100);
    }

    // Send critical metrics immediately
    if (this.isCriticalMetric(metric)) {
      this.sendMetric(metric);
    }
  }

  public captureError(error: ErrorEvent): void {
    this.errors.push(error);
    
    // Keep only last 50 errors
    if (this.errors.length > 50) {
      this.errors = this.errors.slice(-50);
    }

    // Send errors immediately in production
    if (this.isProduction) {
      this.sendError(error);
    }
  }

  public captureInteraction(interaction: UserInteraction): void {
    this.interactions.push(interaction);
    
    // Keep only last 200 interactions
    if (this.interactions.length > 200) {
      this.interactions = this.interactions.slice(-200);
    }
  }

  private isCriticalMetric(metric: PerformanceMetric): boolean {
    const criticalMetrics = [
      'largest_contentful_paint',
      'first_input_delay',
      'cumulative_layout_shift'
    ];
    
    return criticalMetrics.includes(metric.name);
  }

  private async sendMetric(metric: PerformanceMetric): Promise<void> {
    if (!this.isProduction) return;

    try {
      // In a real implementation, send to your analytics service
      // Example: Sentry, DataDog, Google Analytics, etc.
      console.log('Sending metric:', metric);
      
      // Example implementation:
      // await fetch('/api/metrics', {
      //   method: 'POST',
      //   headers: { 'Content-Type': 'application/json' },
      //   body: JSON.stringify(metric)
      // });
    } catch (error) {
      console.warn('Failed to send metric:', error);
    }
  }

  private async sendError(error: ErrorEvent): Promise<void> {
    if (!this.isProduction) return;

    try {
      // In a real implementation, send to your error tracking service
      console.error('Sending error:', error);
      
      // Example implementation:
      // await fetch('/api/errors', {
      //   method: 'POST',
      //   headers: { 'Content-Type': 'application/json' },
      //   body: JSON.stringify(error)
      // });
    } catch (sendError) {
      console.warn('Failed to send error:', sendError);
    }
  }

  private async sendBatchedData(): Promise<void> {
    if (!this.isProduction || this.metrics.length === 0) return;

    try {
      const batchData = {
        sessionId: this.sessionId,
        userId: this.userId,
        metrics: this.metrics.splice(0),
        interactions: this.interactions.splice(0),
        timestamp: new Date().toISOString()
      };

      console.log('Sending batched data:', batchData);
      
      // Example implementation:
      // await fetch('/api/analytics/batch', {
      //   method: 'POST',
      //   headers: { 'Content-Type': 'application/json' },
      //   body: JSON.stringify(batchData)
      // });
    } catch (error) {
      console.warn('Failed to send batched data:', error);
    }
  }

  public setUserId(userId: string): void {
    this.userId = userId;
  }

  public getSessionId(): string {
    return this.sessionId;
  }

  public getMetrics(): PerformanceMetric[] {
    return [...this.metrics];
  }

  public getErrors(): ErrorEvent[] {
    return [...this.errors];
  }

  public getInteractions(): UserInteraction[] {
    return [...this.interactions];
  }
}

