      expect(result.length).toBe(100);
    });
  });

  describe('sanitizeFormData', () => {
    it('should sanitize string fields', () => {
      const input = {
        name: 'John<script>alert("xss")</script>',
        email: 'john@example.com',
        age: 25
      };
      
      const result = sanitizationService.sanitizeFormData(input);
      expect(result.name).toBe('Johnscriptalert("xss")/script');
      expect(result.email).toBe('john@example.com');
      expect(result.age).toBe(25);
    });

    it('should handle array fields', () => {
      const input = {
        tags: ['tag1<script>', 'tag2', 'tag3>alert']
      };
      
      const result = sanitizationService.sanitizeFormData(input);
      expect(result.tags).toEqual(['tag1script', 'tag2', 'tag3alert']);
    });

    it('should apply field-specific configuration', () => {
      const input = {
        content: 'This is a very long content that should be truncated'
      };
      
      const config = {
        content: { maxLength: 10 }
      };
      
      const result = sanitizationService.sanitizeFormData(input, config);
      expect(result.content).toBe('This is a ');
    });
  });

  describe('sanitizeRichText', () => {
    it('should preserve allowed HTML tags', () => {
      const input = '<p>Hello <strong>world</strong></p>';
      const result = sanitizationService.sanitizeRichText(input);
      expect(result).toBe(input); // Mocked to return as-is
    });

    it('should enforce length limit', () => {
      const input = 'a'.repeat(15000);
      const result = sanitizationService.sanitizeRichText(input, 1000);
      expect(result).toBe('a'.repeat(1000) + '...');
    });
  });

  describe('error handling', () => {
    it('should handle errors gracefully and return safe defaults', () => {
      // Mock DOMPurify to throw an error
      const DOMPurify = require('dompurify').default;
      DOMPurify.sanitize.mockImplementationOnce(() => {
        throw new Error('DOMPurify error');
      });

      const result = sanitizationService.sanitizeHtml('<p>test</p>');
      expect(result).toBe('');
    });
  });
});

import sentryService, { ErrorContext } from './sentryService';

export interface ApiError extends Error {
  status?: number;
  statusText?: string;
  code?: string;
  details?: any;
  endpoint?: string;
  method?: string;
}

export interface RetryConfig {
  maxRetries: number;
  baseDelay: number;
  maxDelay: number;
  backoffFactor: number;
  retryCondition?: (error: ApiError) => boolean;
}

export interface ApiErrorHandlerConfig {
  enableRetry: boolean;
  retryConfig: RetryConfig;
  enableLogging: boolean;
  enableUserNotification: boolean;
}

class ApiErrorHandlerService {
  private defaultConfig: ApiErrorHandlerConfig = {
    enableRetry: true,
    retryConfig: {
      maxRetries: 3,
      baseDelay: 1000,
      maxDelay: 10000,
      backoffFactor: 2,
      retryCondition: (error: ApiError) => {
        // Retry on network errors and 5xx server errors
        return !error.status || error.status >= 500 || error.status === 0;
      }
    },
    enableLogging: true,
    enableUserNotification: true
  };

  /**
   * Creates a standardized API error from various error types
   */
  createApiError(
    error: any,
    endpoint?: string,
    method?: string,
    context?: ErrorContext
  ): ApiError {
    let apiError: ApiError;

    if (error instanceof Error) {
      apiError = error as ApiError;
    } else if (typeof error === 'string') {
      apiError = new Error(error) as ApiError;
    } else {
      apiError = new Error('Unknown API error') as ApiError;
    }

    // Enhance error with API-specific information
    if (error?.response) {
      apiError.status = error.response.status;
      apiError.statusText = error.response.statusText;
      apiError.details = error.response.data;
    } else if (error?.status) {
      apiError.status = error.status;
      apiError.statusText = error.statusText;
    }

    apiError.endpoint = endpoint;
    apiError.method = method;

    // Determine error code based on status
    if (!apiError.code) {
      apiError.code = this.getErrorCode(apiError.status);
    }

    return apiError;
  }

  /**
   * Gets a standardized error code based on HTTP status
   */
  private getErrorCode(status?: number): string {
    if (!status) return 'NETWORK_ERROR';
    
    if (status >= 500) return 'SERVER_ERROR';
    if (status === 404) return 'NOT_FOUND';
    if (status === 403) return 'FORBIDDEN';
    if (status === 401) return 'UNAUTHORIZED';
    if (status === 400) return 'BAD_REQUEST';
    if (status === 429) return 'RATE_LIMITED';
    if (status >= 400) return 'CLIENT_ERROR';
    
    return 'UNKNOWN_ERROR';
  }

  /**
   * Gets a user-friendly error message
   */
  getUserFriendlyMessage(error: ApiError): string {
    const status = error.status;
    
    if (!navigator.onLine) {
      return 'You appear to be offline. Please check your internet connection.';
    }

    switch (status) {
      case 0:
        return 'Unable to connect to the server. Please check your internet connection.';
      case 400:
        return error.details?.message || 'Invalid request. Please check your input and try again.';
      case 401:
        return 'Your session has expired. Please log in again.';
      case 403:
        return 'You don\'t have permission to perform this action.';
      case 404:
        return 'The requested resource was not found.';
      case 408:
        return 'The request timed out. Please try again.';
      case 429:
        return 'Too many requests. Please wait a moment before trying again.';
      case 500:
        return 'Server error. Our team has been notified and is working on a fix.';
      case 502:
      case 503:
      case 504:
        return 'Service temporarily unavailable. Please try again in a few minutes.';
      default:
        if (status && status >= 500) {
          return 'Server error. Please try again later.';
        }
        return error.message || 'An unexpected error occurred. Please try again.';
    }
  }

  /**
   * Logs error to monitoring service
   */
  private logErrorInternal(error: ApiError, context?: ErrorContext) {
    if (!this.defaultConfig.enableLogging) return;

    const errorContext: ErrorContext = {
      ...context,
      action: 'api_call',
      additionalData: {
        endpoint: error.endpoint,
        method: error.method,
        status: error.status,
        statusText: error.statusText,
        code: error.code,
        details: error.details,
        online: navigator.onLine,
        timestamp: new Date().toISOString()
      }
    };

    sentryService.captureError(error, errorContext);
  }

  /**
   * Public method to log errors
   */
  logError(error: Error, context?: ErrorContext) {
    if (!this.defaultConfig.enableLogging) return;

    const errorContext: ErrorContext = {
      ...context,
      additionalData: {
        ...context?.additionalData,
        timestamp: new Date().toISOString()
      }
    };

    sentryService.captureError(error, errorContext);
  }

  /**
   * Public method to log warnings
   */
  logWarning(message: string, context?: ErrorContext) {
    if (!this.defaultConfig.enableLogging) return;

    sentryService.addBreadcrumb(
      message,
      context?.component || 'app',
      'warning',
      context?.additionalData
    );
  }

  /**
   * Public method to log info messages
   */
  logInfo(message: string, context?: ErrorContext) {
    if (!this.defaultConfig.enableLogging) return;

    sentryService.addBreadcrumb(
      message,
      context?.component || 'app',
      'info',
      context?.additionalData
    );
  }

  /**
   * Determines if an error should be retried
   */
  private shouldRetry(error: ApiError, retryCount: number, config: RetryConfig): boolean {
    if (retryCount >= config.maxRetries) return false;
    if (config.retryCondition) return config.retryCondition(error);
    
    // Default retry logic
    const status = error.status;
    
    // Don't retry client errors (4xx) except for specific cases
    if (status && status >= 400 && status < 500) {
      return status === 408 || status === 429; // Timeout or rate limit
    }
    
    // Retry network errors and server errors
    return !status || status >= 500;
  }

  /**
   * Calculates retry delay with exponential backoff
   */
  private calculateRetryDelay(retryCount: number, config: RetryConfig): number {
    const delay = config.baseDelay * Math.pow(config.backoffFactor, retryCount);
    return Math.min(delay, config.maxDelay);
  }

  /**
   * Wraps an API call with error handling and retry logic
   */
  async handleApiCall<T>(
    apiCall: () => Promise<T>,
    endpoint?: string,
    method?: string,
    context?: ErrorContext,
    config?: Partial<ApiErrorHandlerConfig>
  ): Promise<T> {
    const finalConfig = { ...this.defaultConfig, ...config };
    let lastError: ApiError;
    let retryCount = 0;

    while (retryCount <= finalConfig.retryConfig.maxRetries) {
      try {
        const result = await apiCall();
        
        // Log successful retry if this wasn't the first attempt
        if (retryCount > 0) {
          sentryService.addBreadcrumb(
            `API call succeeded after ${retryCount} retries`,
            'api',
            'info',
            {
              endpoint,
              method,
              retryCount
            }
          );
        }
        
        return result;
      } catch (error) {
        lastError = this.createApiError(error, endpoint, method, context);
        
        // Log the error
        this.logErrorInternal(lastError, context);
        
        // Check if we should retry
        if (!finalConfig.enableRetry || !this.shouldRetry(lastError, retryCount, finalConfig.retryConfig)) {
          break;
        }
        
        retryCount++;
        
        // Add breadcrumb for retry attempt
        sentryService.addBreadcrumb(
          `API call failed, retrying (${retryCount}/${finalConfig.retryConfig.maxRetries})`,
          'api',
          'warning',
          {
            endpoint,
            method,
            error: lastError.message,
            status: lastError.status,
            retryCount
          }
        );
        
        // Wait before retrying
        const delay = this.calculateRetryDelay(retryCount - 1, finalConfig.retryConfig);
        await new Promise(resolve => setTimeout(resolve, delay));
      }
    }

    // All retries failed, throw the last error
    throw lastError;
  }

  /**
   * Handles Supabase-specific errors
   */
  handleSupabaseError(error: any, operation: string, context?: ErrorContext): never {
    const apiError = this.createApiError(error, `supabase/${operation}`, 'POST', context);
    
    // Enhance with Supabase-specific error information
    if (error?.code) {
      apiError.code = error.code;
    }
    
    if (error?.details) {
      apiError.details = error.details;
    }

    // Log Supabase error
    this.logErrorInternal(apiError, {
      ...context,
      action: 'supabase_operation',
      additionalData: {
        operation,
        supabaseCode: error?.code,
        supabaseDetails: error?.details,
        supabaseHint: error?.hint
      }
    });

    throw apiError;
  }

  /**
   * Creates a wrapper for fetch calls with error handling
   */
  createFetchWrapper() {
    return async <T>(
      url: string,
      options: RequestInit = {},
      context?: ErrorContext
    ): Promise<T> => {
      return this.handleApiCall(
        async () => {
          const response = await fetch(url, options);
          
          if (!response.ok) {
            const error = new Error(`HTTP ${response.status}: ${response.statusText}`) as ApiError;
            error.status = response.status;
            error.statusText = response.statusText;
            error.endpoint = url;
            error.method = options.method || 'GET';
            
            try {
              error.details = await response.json();
            } catch {
              // Response body is not JSON
            }
            
            throw error;
          }
          
          return response.json();
        },
        url,
        options.method || 'GET',
        context
      );
    };
  }
}

// Create singleton instance
export const apiErrorHandler = new ApiErrorHandlerService();

// Export convenience functions
export const handleApiCall = apiErrorHandler.handleApiCall.bind(apiErrorHandler);
export const handleSupabaseError = apiErrorHandler.handleSupabaseError.bind(apiErrorHandler);
export const createFetchWrapper = apiErrorHandler.createFetchWrapper.bind(apiErrorHandler);

export default apiErrorHandler;

/**
 * Advanced Caching Service
 * Implements multiple caching strategies for optimal performance
 */

import { memoryCache, CACHE_TIMES } from '@/lib/config/cacheConfig';

interface CacheOptions {
  ttl?: number;
  storage?: 'memory' | 'localStorage' | 'sessionStorage' | 'indexedDB';
  compress?: boolean;
  encrypt?: boolean;
}

interface CacheEntry {
  data: any;
  timestamp: number;
  ttl: number;
  version: string;
  compressed?: boolean;
  encrypted?: boolean;
}

class CacheService {
  private version = import.meta.env.VITE_APP_VERSION || '1.0.0';
  private compressionThreshold = 1024; // Compress data larger than 1KB

  /**
   * Set data in cache with specified options
   */
  async set(key: string, data: any, options: CacheOptions = {}): Promise<void> {
    const {
      ttl = CACHE_TIMES.SHORT,
      storage = 'memory',
      compress = false,
      encrypt = false
    } = options;

    let processedData = data;

    // Compress data if requested and above threshold
    if (compress && this.getDataSize(data) > this.compressionThreshold) {
      processedData = await this.compressData(data);
    }

    // Encrypt data if requested
    if (encrypt) {
      processedData = await this.encryptData(processedData);
    }

    const entry: CacheEntry = {
      data: processedData,
      timestamp: Date.now(),
      ttl,
      version: this.version,
      compressed: compress,
      encrypted: encrypt
    };

    switch (storage) {
      case 'memory':
        memoryCache.set(key, entry, ttl);
        break;
      case 'localStorage':
        this.setLocalStorage(key, entry);
        break;
      case 'sessionStorage':
        this.setSessionStorage(key, entry);
        break;
      case 'indexedDB':
        await this.setIndexedDB(key, entry);
        break;
    }
  }

  /**
   * Get data from cache
   */
  async get(key: string, storage: 'memory' | 'localStorage' | 'sessionStorage' | 'indexedDB' = 'memory'): Promise<any | null> {
    let entry: CacheEntry | null = null;

    switch (storage) {
      case 'memory':
        entry = memoryCache.get(key);
        break;
      case 'localStorage':
        entry = this.getLocalStorage(key);
        break;
      case 'sessionStorage':
        entry = this.getSessionStorage(key);
        break;
      case 'indexedDB':
        entry = await this.getIndexedDB(key);
        break;
    }

    if (!entry) return null;

    // Check version compatibility
    if (entry.version !== this.version) {
      await this.delete(key, storage);
      return null;
    }

    // Check expiration
    if (Date.now() - entry.timestamp > entry.ttl) {
      await this.delete(key, storage);
      return null;
    }

    let data = entry.data;

    // Decrypt data if encrypted
    if (entry.encrypted) {
      data = await this.decryptData(data);
    }

    // Decompress data if compressed
    if (entry.compressed) {
      data = await this.decompressData(data);
    }

    return data;
  }

  /**
   * Delete data from cache
   */
  async delete(key: string, storage: 'memory' | 'localStorage' | 'sessionStorage' | 'indexedDB' = 'memory'): Promise<void> {
    switch (storage) {
      case 'memory':
        memoryCache.delete(key);
        break;
      case 'localStorage':
        localStorage.removeItem(this.getStorageKey(key));
        break;
      case 'sessionStorage':
        sessionStorage.removeItem(this.getStorageKey(key));
        break;
      case 'indexedDB':
        await this.deleteIndexedDB(key);
        break;
    }
  }

  /**
   * Clear all cache data
   */
  async clear(storage?: 'memory' | 'localStorage' | 'sessionStorage' | 'indexedDB'): Promise<void> {
    if (!storage) {
      // Clear all storages
      memoryCache.clear();
      this.clearLocalStorage();
      this.clearSessionStorage();
      await this.clearIndexedDB();
      return;
    }

    switch (storage) {
      case 'memory':
        memoryCache.clear();
        break;
      case 'localStorage':
        this.clearLocalStorage();
        break;
      case 'sessionStorage':
        this.clearSessionStorage();
        break;
      case 'indexedDB':
        await this.clearIndexedDB();
        break;
    }
  }

  /**
   * Get cache statistics
   */
  getStats(): {
    memory: { size: number; entries: number };
    localStorage: { size: number; entries: number };
    sessionStorage: { size: number; entries: number };
  } {
    return {
      memory: {
        size: memoryCache.size(),
        entries: memoryCache.size()
      },
      localStorage: {
        size: this.getStorageSize(localStorage),
        entries: this.getStorageEntries(localStorage)
      },
      sessionStorage: {
        size: this.getStorageSize(sessionStorage),
        entries: this.getStorageEntries(sessionStorage)
      }
    };
  }

  /**
   * Cleanup expired entries
   */
  async cleanup(): Promise<void> {
    // Cleanup localStorage
    this.cleanupStorage(localStorage);
    
    // Cleanup sessionStorage
    this.cleanupStorage(sessionStorage);
    
    // Cleanup IndexedDB
    await this.cleanupIndexedDB();
  }

  // Private helper methods

  private getStorageKey(key: string): string {
    return `naaz-cache-${key}`;
  }

  private setLocalStorage(key: string, entry: CacheEntry): void {
    try {
      localStorage.setItem(this.getStorageKey(key), JSON.stringify(entry));
    } catch (error) {
      // Handle quota exceeded error
      this.handleStorageQuotaExceeded('localStorage');
    }
  }

  private getLocalStorage(key: string): CacheEntry | null {
    try {
      const item = localStorage.getItem(this.getStorageKey(key));
      return item ? JSON.parse(item) : null;
    } catch (error) {
      return null;
    }
  }

  private setSessionStorage(key: string, entry: CacheEntry): void {
    try {
      sessionStorage.setItem(this.getStorageKey(key), JSON.stringify(entry));
    } catch (error) {
      this.handleStorageQuotaExceeded('sessionStorage');
    }
  }

  private getSessionStorage(key: string): CacheEntry | null {
    try {
      const item = sessionStorage.getItem(this.getStorageKey(key));
      return item ? JSON.parse(item) : null;
    } catch (error) {
      return null;
    }
  }

  private async setIndexedDB(key: string, entry: CacheEntry): Promise<void> {
    return new Promise((resolve, reject) => {
      const request = indexedDB.open('naaz-cache', 1);
      
      request.onerror = () => reject(request.error);
      
      request.onsuccess = () => {
        const db = request.result;
        const transaction = db.transaction(['cache'], 'readwrite');
        const store = transaction.objectStore('cache');
        
        store.put({ key, ...entry });
        transaction.oncomplete = () => resolve();
        transaction.onerror = () => reject(transaction.error);
      };
      
      request.onupgradeneeded = () => {
        const db = request.result;
        if (!db.objectStoreNames.contains('cache')) {
          db.createObjectStore('cache', { keyPath: 'key' });
        }
      };
    });
  }

  private async getIndexedDB(key: string): Promise<CacheEntry | null> {
    return new Promise((resolve, reject) => {
      const request = indexedDB.open('naaz-cache', 1);
      
      request.onerror = () => reject(request.error);
      
      request.onsuccess = () => {
        const db = request.result;
        const transaction = db.transaction(['cache'], 'readonly');
        const store = transaction.objectStore('cache');
        const getRequest = store.get(key);
        
        getRequest.onsuccess = () => {
          const result = getRequest.result;
          resolve(result ? { ...result, key: undefined } : null);
        };
        
        getRequest.onerror = () => resolve(null);
      };
    });
  }

  private async deleteIndexedDB(key: string): Promise<void> {
    return new Promise((resolve, reject) => {
      const request = indexedDB.open('naaz-cache', 1);
      
      request.onsuccess = () => {
        const db = request.result;
        const transaction = db.transaction(['cache'], 'readwrite');
        const store = transaction.objectStore('cache');
        
        store.delete(key);
        transaction.oncomplete = () => resolve();
        transaction.onerror = () => reject(transaction.error);
      };
    });
  }

  private async clearIndexedDB(): Promise<void> {
    return new Promise((resolve, reject) => {
      const request = indexedDB.open('naaz-cache', 1);
      
      request.onsuccess = () => {
        const db = request.result;
        const transaction = db.transaction(['cache'], 'readwrite');
        const store = transaction.objectStore('cache');
        
        store.clear();
        transaction.oncomplete = () => resolve();
        transaction.onerror = () => reject(transaction.error);
      };
    });
  }

  private async cleanupIndexedDB(): Promise<void> {
    return new Promise((resolve, reject) => {
      const request = indexedDB.open('naaz-cache', 1);
      
      request.onsuccess = () => {
        const db = request.result;
        const transaction = db.transaction(['cache'], 'readwrite');
        const store = transaction.objectStore('cache');
        const getAllRequest = store.getAll();
        
        getAllRequest.onsuccess = () => {
          const entries = getAllRequest.result;
          const now = Date.now();
          
          entries.forEach(entry => {
            if (now - entry.timestamp > entry.ttl) {
              store.delete(entry.key);
            }
          });
          
          transaction.oncomplete = () => resolve();
        };
      };
    });
  }

  private clearLocalStorage(): void {
    const keys = Object.keys(localStorage);
    keys.forEach(key => {
      if (key.startsWith('naaz-cache-')) {
        localStorage.removeItem(key);
      }
    });
  }

  private clearSessionStorage(): void {
    const keys = Object.keys(sessionStorage);
    keys.forEach(key => {
      if (key.startsWith('naaz-cache-')) {
        sessionStorage.removeItem(key);
      }
    });
  }

  private cleanupStorage(storage: Storage): void {
    const keys = Object.keys(storage);
    const now = Date.now();
    
    keys.forEach(key => {
      if (key.startsWith('naaz-cache-')) {
        try {
          const entry: CacheEntry = JSON.parse(storage.getItem(key) || '');
          if (now - entry.timestamp > entry.ttl) {
            storage.removeItem(key);
          }
        } catch (error) {
          // Remove invalid entries
          storage.removeItem(key);
        }
      }
    });
  }

  private getStorageSize(storage: Storage): number {
    let size = 0;
    const keys = Object.keys(storage);
    
    keys.forEach(key => {
      if (key.startsWith('naaz-cache-')) {
        size += (storage.getItem(key) || '').length;
      }
    });
    
    return size;
  }

  private getStorageEntries(storage: Storage): number {
    return Object.keys(storage).filter(key => key.startsWith('naaz-cache-')).length;
  }

  private handleStorageQuotaExceeded(storageType: string): void {
    import('../utils/consoleMigration').then(({ logWarning }) => {
      logWarning(`${storageType} quota exceeded, clearing old cache entries`);
    });
    
    // Clear old entries to make space
    if (storageType === 'localStorage') {
      this.cleanupStorage(localStorage);
    } else if (storageType === 'sessionStorage') {
      this.cleanupStorage(sessionStorage);
    }
  }

  private getDataSize(data: any): number {
    return JSON.stringify(data).length;
  }

  private async compressData(data: any): Promise<string> {
    // Simple compression using built-in compression
    const jsonString = JSON.stringify(data);
    
    if ('CompressionStream' in window) {
      const stream = new CompressionStream('gzip');
      const writer = stream.writable.getWriter();
      const reader = stream.readable.getReader();
      
      writer.write(new TextEncoder().encode(jsonString));
      writer.close();
      
      const chunks: Uint8Array[] = [];
      let done = false;
      
      while (!done) {
        const { value, done: readerDone } = await reader.read();
        done = readerDone;
        if (value) chunks.push(value);
      }
      
      const compressed = new Uint8Array(chunks.reduce((acc, chunk) => acc + chunk.length, 0));
      let offset = 0;
      for (const chunk of chunks) {
        compressed.set(chunk, offset);
        offset += chunk.length;
      }
      
      return btoa(String.fromCharCode(...compressed));
    }
    
    // Fallback: return original data
    return jsonString;
  }

  private async decompressData(compressedData: string): Promise<any> {
    if ('DecompressionStream' in window) {
      try {
        const compressed = Uint8Array.from(atob(compressedData), c => c.charCodeAt(0));
        const stream = new DecompressionStream('gzip');
        const writer = stream.writable.getWriter();
        const reader = stream.readable.getReader();
        
        writer.write(compressed);
        writer.close();
        
        const chunks: Uint8Array[] = [];
        let done = false;
        
        while (!done) {
          const { value, done: readerDone } = await reader.read();
          done = readerDone;
          if (value) chunks.push(value);
        }
        
        const decompressed = new Uint8Array(chunks.reduce((acc, chunk) => acc + chunk.length, 0));
        let offset = 0;
        for (const chunk of chunks) {
          decompressed.set(chunk, offset);
          offset += chunk.length;
        }
        
        const jsonString = new TextDecoder().decode(decompressed);
        return JSON.parse(jsonString);
      } catch (error) {
        // Fallback: try to parse as regular JSON
        return JSON.parse(compressedData);
      }
    }
    
    // Fallback: parse as regular JSON
    return JSON.parse(compressedData);
  }

  private async encryptData(data: any): Promise<string> {
    // Simple encryption for demo purposes
    // In production, use proper encryption libraries
    const jsonString = JSON.stringify(data);
    return btoa(jsonString);
  }

  private async decryptData(encryptedData: string): Promise<any> {
    // Simple decryption for demo purposes
    try {
      const jsonString = atob(encryptedData);
      return JSON.parse(jsonString);
    } catch (error) {
      throw new Error('Failed to decrypt data');
    }
  }
}

