import { supabase } from '@/integrations/supabase/client';
import type { Tables } from '@/integrations/supabase/types';

export interface CartItem {
  id?: string;
  productId: string;
  name: string;
  price: string;
  image: string;
  quantity: number;
  lastModified: number;
  isLocal?: boolean;
}

export interface CartState {
  items: CartItem[];
  totalItems: number;
  subtotal: number;
  lastSynced: number;
  syncInProgress: boolean;
}

export interface OfflineOperation {
  id: string;
  type: 'add' | 'update' | 'remove' | 'clear';
  productId?: string;
  quantity?: number;
  cartItemId?: string;
  timestamp: number;
  retryCount: number;
}

class CartPersistenceService {
  private readonly CART_STORAGE_KEY = 'naaz-cart-v2';
  private readonly OFFLINE_QUEUE_KEY = 'naaz-cart-offline-queue';
  private readonly SYNC_METADATA_KEY = 'naaz-cart-sync-metadata';
  private readonly MAX_RETRY_ATTEMPTS = 3;
  private readonly SYNC_INTERVAL = 30000; // 30 seconds
  
  private syncTimer: NodeJS.Timeout | null = null;
  private isOnline = navigator.onLine;

  constructor() {
    this.setupNetworkListeners();
    this.startPeriodicSync();
  }

  /**
   * Save cart state to localStorage with metadata
   */
  saveCartToLocal(cart: CartState): void {
    try {
      const cartData = {
        ...cart,
        lastSaved: Date.now()
      };
      localStorage.setItem(this.CART_STORAGE_KEY, JSON.stringify(cartData));
    } catch (error) {
      console.error('Failed to save cart to localStorage:', error);
    }
  }

  /**
   * Load cart state from localStorage
   */
  loadCartFromLocal(): CartState | null {
    try {
      const savedCart = localStorage.getItem(this.CART_STORAGE_KEY);
      if (!savedCart) return null;
      
      const cartData = JSON.parse(savedCart);
      return {
        items: cartData.items || [],
        totalItems: cartData.totalItems || 0,
        subtotal: cartData.subtotal || 0,
        lastSynced: cartData.lastSynced || 0,
        syncInProgress: false
      };
    } catch (error) {
      console.error('Failed to load cart from localStorage:', error);
      localStorage.removeItem(this.CART_STORAGE_KEY);
      return null;
    }
  }

  /**
   * Add operation to offline queue
   */
  private addToOfflineQueue(operation: Omit<OfflineOperation, 'id' | 'timestamp' | 'retryCount'>): void {
    try {
      const queue = this.getOfflineQueue();
      const newOperation: OfflineOperation = {
        ...operation,
        id: `${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
        timestamp: Date.now(),
        retryCount: 0
      };
      
      queue.push(newOperation);
      localStorage.setItem(this.OFFLINE_QUEUE_KEY, JSON.stringify(queue));
    } catch (error) {
      console.error('Failed to add operation to offline queue:', error);
    }
  }

  /**
   * Get offline operations queue
   */
  private getOfflineQueue(): OfflineOperation[] {
    try {
      const queue = localStorage.getItem(this.OFFLINE_QUEUE_KEY);
      return queue ? JSON.parse(queue) : [];
    } catch (error) {
      console.error('Failed to get offline queue:', error);
      return [];
    }
  }

  /**
   * Clear offline operations queue
   */
  private clearOfflineQueue(): void {
    localStorage.removeItem(this.OFFLINE_QUEUE_KEY);
  }

  /**
   * Sync cart with server for authenticated users
   */
  async syncWithServer(userId: string): Promise<CartState> {
    if (!this.isOnline) {
      throw new Error('Cannot sync while offline');
    }

    try {
      // First, process offline queue
      await this.processOfflineQueue(userId);

      // Fetch current server state
      const { data: serverCartItems, error } = await supabase
        .from('cart_items')
        .select(`
          *,
          products(
            id,
            name,
            price,
            images
          )
        `)
        .eq('user_id', userId);

      if (error) throw error;

      // Get local cart state
      const localCart = this.loadCartFromLocal();
      
      // Resolve conflicts and merge
      const mergedCart = await this.resolveConflicts(
        localCart,
        serverCartItems || [],
        userId
      );

      // Save merged state locally
      this.saveCartToLocal(mergedCart);

      return mergedCart;
    } catch (error) {
      console.error('Failed to sync with server:', error);
      throw error;
    }
  }

  /**
   * Resolve conflicts between local and server cart states
   */
  private async resolveConflicts(
    localCart: CartState | null,
    serverItems: any[],
    userId: string
  ): Promise<CartState> {
    const mergedItems: CartItem[] = [];
    const serverItemsMap = new Map(
      serverItems.map(item => [item.product_id, item])
    );

    // Process local items
    if (localCart?.items) {
      for (const localItem of localCart.items) {
        const serverItem = serverItemsMap.get(localItem.productId);
        
        if (serverItem) {
          // Item exists on both sides - use most recent
          const serverModified = new Date(serverItem.updated_at).getTime();
          const localModified = localItem.lastModified;
          
          if (localModified > serverModified) {
            // Local is newer - update server
            await this.updateServerItem(userId, serverItem.id, localItem.quantity);
            mergedItems.push(localItem);
          } else {
            // Server is newer - use server data
            mergedItems.push(this.convertServerItemToLocal(serverItem));
          }
          
          serverItemsMap.delete(localItem.productId);
        } else {
          // Item only exists locally - add to server
          await this.addServerItem(userId, localItem);
          mergedItems.push(localItem);
        }
      }
    }

    // Add remaining server items that don't exist locally
    for (const serverItem of serverItemsMap.values()) {
      mergedItems.push(this.convertServerItemToLocal(serverItem));
    }

    return this.calculateCartTotals(mergedItems);
  }

  /**
   * Convert server cart item to local format
   */
  private convertServerItemToLocal(serverItem: any): CartItem {
    return {
      id: serverItem.id,
      productId: serverItem.product_id,
      name: serverItem.products.name,
      price: serverItem.products.price.toString(),
      image: serverItem.products.images?.[0] || '/placeholder.svg',
      quantity: serverItem.quantity,
      lastModified: new Date(serverItem.updated_at).getTime(),
      isLocal: false
    };
  }

  /**
   * Update server cart item
   */
  private async updateServerItem(userId: string, cartItemId: string, quantity: number): Promise<void> {
    const { error } = await supabase
      .from('cart_items')
      .update({ 
        quantity,
        updated_at: new Date().toISOString()
      })
      .eq('id', cartItemId)
      .eq('user_id', userId);

    if (error) throw error;
  }

  /**
   * Add new item to server cart
   */
  private async addServerItem(userId: string, item: CartItem): Promise<void> {
    const { error } = await supabase
      .from('cart_items')
      .insert({
        user_id: userId,
        product_id: item.productId,
        quantity: item.quantity
      });

    if (error) throw error;
  }

  /**
   * Calculate cart totals
   */
  private calculateCartTotals(items: CartItem[]): CartState {
    const totalItems = items.reduce((sum, item) => sum + item.quantity, 0);
    const subtotal = items.reduce((sum, item) => sum + (parseFloat(item.price) * item.quantity), 0);
    
    return {
      items,
      totalItems,
      subtotal,
      lastSynced: Date.now(),
      syncInProgress: false
    };
  }

  /**
   * Process offline operations queue
   */
  private async processOfflineQueue(userId: string): Promise<void> {
    const queue = this.getOfflineQueue();
    const failedOperations: OfflineOperation[] = [];

    for (const operation of queue) {
      try {
        await this.executeOperation(userId, operation);
      } catch (error) {
        console.error('Failed to execute offline operation:', error);
        
        if (operation.retryCount < this.MAX_RETRY_ATTEMPTS) {
          failedOperations.push({
            ...operation,
            retryCount: operation.retryCount + 1
          });
        }
      }
    }

    // Update queue with failed operations
    if (failedOperations.length > 0) {
      localStorage.setItem(this.OFFLINE_QUEUE_KEY, JSON.stringify(failedOperations));
    } else {
      this.clearOfflineQueue();
    }
  }

  /**
   * Execute a single offline operation
   */
  private async executeOperation(userId: string, operation: OfflineOperation): Promise<void> {
    switch (operation.type) {
      case 'add':
        if (operation.productId && operation.quantity) {
          await this.addServerItem(userId, {
            productId: operation.productId,
            quantity: operation.quantity,
            name: '', // Will be populated by server
            price: '0',
            image: '',
            lastModified: operation.timestamp
          });
        }
        break;
        
      case 'update':
        if (operation.cartItemId && operation.quantity) {
          await this.updateServerItem(userId, operation.cartItemId, operation.quantity);
        }
        break;
        
      case 'remove':
        if (operation.cartItemId) {
          const { error } = await supabase
            .from('cart_items')
            .delete()
            .eq('id', operation.cartItemId)
            .eq('user_id', userId);
          
          if (error) throw error;
        }
        break;
        
      case 'clear':
        const { error } = await supabase
          .from('cart_items')
          .delete()
          .eq('user_id', userId);
        
        if (error) throw error;
        break;
    }
  }

  /**
   * Add cart operation (with offline support)
   */
  async addCartOperation(
    userId: string | null,
    type: OfflineOperation['type'],
    data: Partial<OfflineOperation>
  ): Promise<void> {
    if (!userId || !this.isOnline) {
      // Add to offline queue
      this.addToOfflineQueue({
        type,
        productId: data.productId,
        quantity: data.quantity,
        cartItemId: data.cartItemId
      });
      return;
    }

    try {
      await this.executeOperation(userId, {
        id: '',
        type,
        productId: data.productId,
        quantity: data.quantity,
        cartItemId: data.cartItemId,
        timestamp: Date.now(),
        retryCount: 0
      });
    } catch (error) {
      // Add to offline queue on failure
      this.addToOfflineQueue({
        type,
        productId: data.productId,
        quantity: data.quantity,
        cartItemId: data.cartItemId
      });
      throw error;
    }
  }

  /**
   * Setup network status listeners
   */
  private setupNetworkListeners(): void {
    window.addEventListener('online', () => {
      this.isOnline = true;
      this.handleReconnection();
    });

    window.addEventListener('offline', () => {
      this.isOnline = false;
    });
  }

  /**
   * Handle reconnection - sync pending operations
   */
  private async handleReconnection(): Promise<void> {
    const queue = this.getOfflineQueue();
    if (queue.length > 0) {
      // Trigger sync for authenticated users
      const event = new CustomEvent('cart-reconnection', {
        detail: { hasOfflineOperations: true }
      });
      window.dispatchEvent(event);
    }
  }

  /**
   * Start periodic sync for authenticated users
   */
  private startPeriodicSync(): void {
    if (this.syncTimer) {
      clearInterval(this.syncTimer);
    }

    this.syncTimer = setInterval(() => {
      const event = new CustomEvent('cart-periodic-sync');
      window.dispatchEvent(event);
    }, this.SYNC_INTERVAL);
  }

  /**
   * Cleanup resources
   */
  destroy(): void {
    if (this.syncTimer) {
      clearInterval(this.syncTimer);
      this.syncTimer = null;
    }
    
    window.removeEventListener('online', this.handleReconnection);
    window.removeEventListener('offline', () => {});
  }

  /**
   * Get sync status information
   */
  getSyncStatus(): {
    isOnline: boolean;
    hasOfflineOperations: boolean;
    lastSynced: number;
  } {
    const cart = this.loadCartFromLocal();
    const queue = this.getOfflineQueue();
    
    return {
      isOnline: this.isOnline,
      hasOfflineOperations: queue.length > 0,
      lastSynced: cart?.lastSynced || 0
    };
  }
}

export const cartPersistenceService = new CartPersistenceService();

import { supabase } from '../supabase';
import sentryService from './sentryService';

export interface CSRFToken {
  token: string;
  expiresAt: Date;
  sessionId?: string;
}

export interface CSRFValidationResult {
  isValid: boolean;
  error?: string;
  newToken?: string;
}

class CSRFService {
  private readonly TOKEN_HEADER = 'X-CSRF-Token';
  private readonly TOKEN_STORAGE_KEY = 'csrf_token';
  private readonly TOKEN_EXPIRY_MS = 60 * 60 * 1000; // 1 hour
  private readonly SESSION_STORAGE_KEY = 'csrf_session';

  /**
   * Generate a new CSRF token
   */
  generateToken(): CSRFToken {
    try {
      // Generate a cryptographically secure random token
      const array = new Uint8Array(32);
      crypto.getRandomValues(array);
      const token = Array.from(array, byte => byte.toString(16).padStart(2, '0')).join('');
      
      const expiresAt = new Date(Date.now() + this.TOKEN_EXPIRY_MS);
      const sessionId = this.getOrCreateSessionId();

      const csrfToken: CSRFToken = {
        token,
        expiresAt,
        sessionId
      };

      // Store token in localStorage for client-side access
      this.storeToken(csrfToken);

      sentryService.addBreadcrumb(
        'CSRF token generated',
        'security',
        'info',
        { tokenLength: token.length, expiresAt }
      );

      return csrfToken;
    } catch (error) {
      sentryService.captureError(
        error instanceof Error ? error : new Error('Failed to generate CSRF token'),
        {
          action: 'generate_csrf_token'
        }
      );

      // Fallback to timestamp-based token if crypto is not available
      const fallbackToken = `fallback_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
      return {
        token: fallbackToken,
        expiresAt: new Date(Date.now() + this.TOKEN_EXPIRY_MS)
      };
    }
  }

  /**
   * Get the current CSRF token, generating a new one if needed
   */
  getToken(): CSRFToken {
    try {
      const stored = this.getStoredToken();
      
      if (stored && this.isTokenValid(stored)) {
        return stored;
      }

      // Generate new token if none exists or expired
      return this.generateToken();
    } catch (error) {
      sentryService.captureError(
        error instanceof Error ? error : new Error('Failed to get CSRF token'),
        {
          action: 'get_csrf_token'
        }
      );

      return this.generateToken();
    }
  }

  /**
   * Validate a CSRF token
   */
  async validateToken(
    providedToken: string,
    sessionId?: string
  ): Promise<CSRFValidationResult> {
    try {
      if (!providedToken || typeof providedToken !== 'string') {
        return {
          isValid: false,
          error: 'CSRF token is required'
        };
      }

      const stored = this.getStoredToken();
      
      if (!stored) {
        return {
          isValid: false,
          error: 'No CSRF token found in session'
        };
      }

      // Check if token matches
      if (stored.token !== providedToken) {
        sentryService.addBreadcrumb(
          'CSRF token mismatch detected',
          'security',
          'warning',
          { providedToken: providedToken.substring(0, 8) + '...', sessionId }
        );

        return {
          isValid: false,
          error: 'Invalid CSRF token'
        };
      }

      // Check if token is expired
      if (!this.isTokenValid(stored)) {
        const newToken = this.generateToken();
        return {
          isValid: false,
          error: 'CSRF token has expired',
          newToken: newToken.token
        };
      }

      // Check session ID if provided
      if (sessionId && stored.sessionId && stored.sessionId !== sessionId) {
        sentryService.addBreadcrumb(
          'CSRF session ID mismatch detected',
          'security',
          'warning',
          { providedSessionId: sessionId, storedSessionId: stored.sessionId }
        );

        return {
          isValid: false,
          error: 'Session mismatch'
        };
      }

      // Log successful validation for monitoring
      await this.logCSRFValidation(providedToken, true, sessionId);

      return {
        isValid: true
      };
    } catch (error) {
      sentryService.captureError(
        error instanceof Error ? error : new Error('CSRF token validation failed'),
        {
          action: 'validate_csrf_token',
          additionalData: { providedToken: providedToken?.substring(0, 8) + '...', sessionId }
        }
      );

      return {
        isValid: false,
        error: 'Token validation failed'
      };
    }
  }

  /**
   * Refresh the CSRF token
   */
  refreshToken(): CSRFToken {
    this.clearStoredToken();
    return this.generateToken();
  }

  /**
   * Clear the CSRF token
   */
  clearToken(): void {
    this.clearStoredToken();
    this.clearSessionId();
  }

  /**
   * Get CSRF token for HTTP headers
   */
  getTokenHeader(): Record<string, string> {
    const token = this.getToken();
    return {
      [this.TOKEN_HEADER]: token.token
    };
  }

  /**
   * Middleware function to add CSRF token to requests
   */
  addCSRFToRequest = (config: any) => {
    const token = this.getToken();
    
    if (!config.headers) {
      config.headers = {};
    }
    
    config.headers[this.TOKEN_HEADER] = token.token;
    
    return config;
  };

  /**
   * Check if a token is valid (not expired)
   */
  private isTokenValid(token: CSRFToken): boolean {
    return new Date() < token.expiresAt;
  }

  /**
   * Store token in localStorage
   */
  private storeToken(token: CSRFToken): void {
    try {
      localStorage.setItem(this.TOKEN_STORAGE_KEY, JSON.stringify({
        token: token.token,
        expiresAt: token.expiresAt.toISOString(),
        sessionId: token.sessionId
      }));
    } catch (error) {
      // Handle localStorage not available (e.g., private browsing)
      sentryService.addBreadcrumb(
        'Failed to store CSRF token in localStorage',
        'system',
        'warning'
      );
    }
  }

  /**
   * Get stored token from localStorage
   */
  private getStoredToken(): CSRFToken | null {
    try {
      const stored = localStorage.getItem(this.TOKEN_STORAGE_KEY);
      if (!stored) return null;

      const parsed = JSON.parse(stored);
      return {
        token: parsed.token,
        expiresAt: new Date(parsed.expiresAt),
        sessionId: parsed.sessionId
      };
    } catch (error) {
      // Clear invalid stored token
      this.clearStoredToken();
      return null;
    }
  }

  /**
   * Clear stored token
   */
  private clearStoredToken(): void {
    try {
      localStorage.removeItem(this.TOKEN_STORAGE_KEY);
    } catch (error) {
      // Ignore errors
    }
  }

  /**
   * Get or create session ID
   */
  private getOrCreateSessionId(): string {
    try {
      let sessionId = sessionStorage.getItem(this.SESSION_STORAGE_KEY);
      
      if (!sessionId) {
        sessionId = `session_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
        sessionStorage.setItem(this.SESSION_STORAGE_KEY, sessionId);
      }
      
      return sessionId;
    } catch (error) {
      // Fallback if sessionStorage is not available
      return `fallback_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
    }
  }

  /**
   * Clear session ID
   */
  private clearSessionId(): void {
    try {
      sessionStorage.removeItem(this.SESSION_STORAGE_KEY);
    } catch (error) {
      // Ignore errors
    }
  }

  /**
   * Log CSRF validation attempts for monitoring
   */
  private async logCSRFValidation(
    token: string,
    success: boolean,
    sessionId?: string
  ): Promise<void> {
    try {
      await supabase.from('csrf_validation_logs').insert({
        token_hash: this.hashToken(token),
        success,
        session_id: sessionId,
        timestamp: new Date().toISOString(),
        user_agent: navigator.userAgent
      });
    } catch (error) {
      // Don't throw errors for logging failures
      sentryService.addBreadcrumb(
        'Failed to log CSRF validation',
        'system',
        'warning'
      );
    }
  }

  /**
   * Create a hash of the token for logging (security)
   */
  private hashToken(token: string): string {
    // Simple hash for logging purposes (not cryptographic)
    let hash = 0;
    for (let i = 0; i < token.length; i++) {
      const char = token.charCodeAt(i);
      hash = ((hash << 5) - hash) + char;
      hash = hash & hash; // Convert to 32-bit integer
    }
    return hash.toString(16);
  }
}

// Create singleton instance
export const csrfService = new CSRFService();

/**
 * React hook for CSRF protection
 */
export const useCSRF = () => {
  const getToken = () => csrfService.getToken();
  const refreshToken = () => csrfService.refreshToken();
  const clearToken = () => csrfService.clearToken();
  const getHeaders = () => csrfService.getTokenHeader();

  return {
    getToken,
    refreshToken,
    clearToken,
    getHeaders,
    validateToken: csrfService.validateToken.bind(csrfService)
  };
};

export default csrfService;

