import { payuService } from './payuService';
import { paypalService } from './paypalService';
import { supabase } from '../supabase';

export interface PaymentMethod {
  id: string;
  name: string;
  type: 'domestic' | 'international';
  provider: 'payu' | 'paypal' | 'cod';
  supportedCurrencies: string[];
  fees: {
    percentage: number;
    fixed: number;
  };
  processingTime: string;
  description: string;
  icon: string;
  enabled: boolean;
}

export interface PaymentRequest {
  orderId: string;
  amount: number;
  currency: string;
  customerInfo: {
    firstName: string;
    lastName: string;
    email: string;
    phone: string;
    country: string;
  };
  shippingAddress?: {
    addressLine1: string;
    addressLine2?: string;
    city: string;
    state: string;
    postalCode: string;
    countryCode: string;
  };
  productInfo: string;
  metadata?: Record<string, any>;
}

export interface PaymentResult {
  success: boolean;
  transactionId?: string;
  paymentMethod: string;
  amount: number;
  currency: string;
  status: 'pending' | 'completed' | 'failed' | 'cancelled';
  redirectUrl?: string;
  error?: string;
  metadata?: Record<string, any>;
}

export interface PaymentAnalytics {
  totalTransactions: number;
  successfulTransactions: number;
  failedTransactions: number;
  totalAmount: number;
  averageAmount: number;
  topPaymentMethods: Array<{
    method: string;
    count: number;
    percentage: number;
  }>;
  conversionRate: number;
  fraudDetectionAlerts: number;
}

class PaymentOrchestrator {
  private paymentMethods: PaymentMethod[] = [
    {
      id: 'cod',
      name: 'Cash on Delivery',
      type: 'domestic',
      provider: 'cod',
      supportedCurrencies: ['INR'],
      fees: { percentage: 0, fixed: 0 },
      processingTime: 'On delivery',
      description: 'Pay when you receive your order',
      icon: '',
      enabled: true
    },
    {
      id: 'payu_upi',
      name: 'UPI Payment',
      type: 'domestic',
      provider: 'payu',
      supportedCurrencies: ['INR'],
      fees: { percentage: 1.5, fixed: 0 },
      processingTime: 'Instant',
      description: 'Pay instantly using UPI',
      icon: '',
      enabled: true
    },
    {
      id: 'payu_card',
      name: 'Credit/Debit Card',
      type: 'domestic',
      provider: 'payu',
      supportedCurrencies: ['INR'],
      fees: { percentage: 2.5, fixed: 0 },
      processingTime: 'Instant',
      description: 'Pay with your card via PayU',
      icon: '',
      enabled: true
    },
    {
      id: 'paypal',
      name: 'PayPal',
      type: 'international',
      provider: 'paypal',
      supportedCurrencies: ['USD', 'EUR', 'GBP', 'CAD', 'AUD', 'JPY'],
      fees: { percentage: 3.5, fixed: 0.30 },
      processingTime: 'Instant',
      description: 'Secure international payments',
      icon: '',
      enabled: true
    }
  ];

  /**
   * Get available payment methods based on user location and preferences
   */
  getAvailablePaymentMethods(userLocation: string, currency: string = 'INR'): PaymentMethod[] {
    const isDomestic = userLocation === 'IN' || currency === 'INR';
    
    return this.paymentMethods.filter(method => {
      if (!method.enabled) return false;
      
      if (isDomestic) {
        return method.type === 'domestic' && method.supportedCurrencies.includes(currency);
      } else {
        return method.type === 'international' && method.supportedCurrencies.includes(currency);
      }
    });
  }

  /**
   * Detect user location for payment method selection
   */
  async detectUserLocation(): Promise<string> {
    try {
      const response = await fetch('https://ipapi.co/json/');
      const data = await response.json();
      return data.country_code || 'IN';
    } catch (error) {
      console.error('Location detection error:', error);
      return 'IN'; // Default to India
    }
  }

  /**
   * Calculate payment fees
   */
  calculateFees(amount: number, paymentMethodId: string): { fees: number; total: number } {
    const method = this.paymentMethods.find(m => m.id === paymentMethodId);
    if (!method) {
      return { fees: 0, total: amount };
    }

    const percentageFee = (amount * method.fees.percentage) / 100;
    const totalFees = percentageFee + method.fees.fixed;
    
    return {
      fees: Math.round(totalFees * 100) / 100,
      total: Math.round((amount + totalFees) * 100) / 100
    };
  }

  /**
   * Process payment with retry logic and fallback options
   */
  async processPayment(
    paymentRequest: PaymentRequest,
    paymentMethodId: string,
    retryCount: number = 0
  ): Promise<PaymentResult> {
    const maxRetries = 3;
    
    try {
      // Log payment attempt
      await this.logPaymentAttempt(paymentRequest, paymentMethodId);

      // Fraud detection
      const fraudCheck = await this.performFraudDetection(paymentRequest);
      if (fraudCheck.isHighRisk) {
        return {
          success: false,
          paymentMethod: paymentMethodId,
          amount: paymentRequest.amount,
          currency: paymentRequest.currency,
          status: 'failed',
          error: 'Payment blocked due to security concerns'
        };
      }

      // Route to appropriate payment provider
      const result = await this.routePayment(paymentRequest, paymentMethodId);
      
      // Log payment result
      await this.logPaymentResult(paymentRequest.orderId, result);
      
      return result;
    } catch (error) {
      console.error('Payment processing error:', error);
      
      // Retry logic
      if (retryCount < maxRetries) {
        console.log(`Retrying payment (attempt ${retryCount + 1}/${maxRetries})`);
        await this.delay(1000 * Math.pow(2, retryCount)); // Exponential backoff
        return this.processPayment(paymentRequest, paymentMethodId, retryCount + 1);
      }

      // Try fallback payment method
      const fallbackMethod = await this.getFallbackPaymentMethod(paymentMethodId, paymentRequest.customerInfo.country);
      if (fallbackMethod && fallbackMethod.id !== paymentMethodId) {
        console.log(`Trying fallback payment method: ${fallbackMethod.id}`);
        return this.processPayment(paymentRequest, fallbackMethod.id);
      }

      return {
        success: false,
        paymentMethod: paymentMethodId,
        amount: paymentRequest.amount,
        currency: paymentRequest.currency,
        status: 'failed',
        error: error instanceof Error ? error.message : 'Payment processing failed'
      };
    }
  }

  /**
   * Route payment to appropriate provider
   */
  private async routePayment(paymentRequest: PaymentRequest, paymentMethodId: string): Promise<PaymentResult> {
    const method = this.paymentMethods.find(m => m.id === paymentMethodId);
    if (!method) {
      throw new Error('Invalid payment method');
    }

    switch (method.provider) {
      case 'cod':
        return this.processCODPayment(paymentRequest);
      
      case 'payu':
        return this.processPayUPayment(paymentRequest, paymentMethodId);
      
      case 'paypal':
        return this.processPayPalPayment(paymentRequest);
      
      default:
        throw new Error('Unsupported payment provider');
    }
  }

  /**
   * Process Cash on Delivery payment
   */
  private async processCODPayment(paymentRequest: PaymentRequest): Promise<PaymentResult> {
    // COD is always successful at this stage
    return {
      success: true,
      transactionId: `COD_${paymentRequest.orderId}_${Date.now()}`,
      paymentMethod: 'cod',
      amount: paymentRequest.amount,
      currency: paymentRequest.currency,
      status: 'pending'
    };
  }

  /**
   * Process PayU payment
   */
  private async processPayUPayment(paymentRequest: PaymentRequest, paymentMethodId: string): Promise<PaymentResult> {
    if (paymentMethodId === 'payu_upi') {
      const result = await payuService.processUPIPayment({
        orderId: paymentRequest.orderId,
        amount: paymentRequest.amount,
        upiId: paymentRequest.metadata?.upiId || ''
      });

      return {
        success: result.success,
        paymentMethod: 'payu_upi',
        amount: paymentRequest.amount,
        currency: paymentRequest.currency,
        status: result.success ? 'pending' : 'failed',
        error: result.error,
        metadata: {
          qrCode: result.qrCode,
          deepLink: result.deepLink
        }
      };
    } else {
      // Card payment - redirect to PayU
      const { formData } = await payuService.createPayment({
        orderId: paymentRequest.orderId,
        amount: paymentRequest.amount,
        productInfo: paymentRequest.productInfo,
        firstName: paymentRequest.customerInfo.firstName,
        email: paymentRequest.customerInfo.email,
        phone: paymentRequest.customerInfo.phone
      });

      return {
        success: true,
        paymentMethod: 'payu_card',
        amount: paymentRequest.amount,
        currency: paymentRequest.currency,
        status: 'pending',
        redirectUrl: `${(import.meta.env as any).VITE_PAYU_BASE_URL || 'https://test.payu.in'}/_payment`,
        metadata: { formData }
      };
    }
  }

  /**
   * Process PayPal payment
   */
  private async processPayPalPayment(paymentRequest: PaymentRequest): Promise<PaymentResult> {
    const paypalOrder = await paypalService.createOrder({
      orderId: paymentRequest.orderId,
      amount: paymentRequest.amount,
      currency: paymentRequest.currency,
      description: paymentRequest.productInfo,
      customerInfo: {
        firstName: paymentRequest.customerInfo.firstName,
        lastName: paymentRequest.customerInfo.lastName,
        email: paymentRequest.customerInfo.email,
        phone: paymentRequest.customerInfo.phone
      },
      shippingAddress: paymentRequest.shippingAddress
    });

    const approvalUrl = paypalOrder.links.find(link => link.rel === 'approve')?.href;

    return {
      success: true,
      transactionId: paypalOrder.id,
      paymentMethod: 'paypal',
      amount: paymentRequest.amount,
      currency: paymentRequest.currency,
      status: 'pending',
      redirectUrl: approvalUrl
    };
  }

  /**
   * Perform fraud detection
   */
  private async performFraudDetection(paymentRequest: PaymentRequest): Promise<{ isHighRisk: boolean; riskScore: number; reasons: string[] }> {
    const reasons: string[] = [];
    let riskScore = 0;

    // Check for suspicious patterns
    if (paymentRequest.amount > 50000) {
      riskScore += 20;
      reasons.push('High transaction amount');
    }

    // Check email domain
    const emailDomain = paymentRequest.customerInfo.email.split('@')[1];
    const suspiciousDomains = ['tempmail.com', '10minutemail.com', 'guerrillamail.com'];
    if (suspiciousDomains.includes(emailDomain)) {
      riskScore += 30;
      reasons.push('Suspicious email domain');
    }

    // Check for rapid successive transactions
    const recentTransactions = await this.getRecentTransactions(paymentRequest.customerInfo.email, 10);
    if (recentTransactions.length > 5) {
      riskScore += 25;
      reasons.push('Multiple recent transactions');
    }

    return {
      isHighRisk: riskScore > 50,
      riskScore,
      reasons
    };
  }

  /**
   * Get fallback payment method
   */
  private async getFallbackPaymentMethod(failedMethodId: string, userCountry: string): Promise<PaymentMethod | null> {
    const availableMethods = this.getAvailablePaymentMethods(userCountry);
    
    // Remove the failed method and return the next best option
    const fallbackMethods = availableMethods.filter(m => m.id !== failedMethodId);
    
    // Prioritize COD for domestic users as ultimate fallback
    if (userCountry === 'IN') {
      return fallbackMethods.find(m => m.id === 'cod') || fallbackMethods[0] || null;
    }
    
    return fallbackMethods[0] || null;
  }

  /**
   * Get recent transactions for fraud detection
   */
  private async getRecentTransactions(email: string, minutes: number): Promise<any[]> {
    try {
      const cutoffTime = new Date(Date.now() - minutes * 60 * 1000).toISOString();
      
      const { data, error } = await supabase
        .from('payment_logs')
        .select('*')
        .eq('customer_email', email)
        .gte('created_at', cutoffTime);

      if (error) throw error;
      return data || [];
    } catch (error) {
      console.error('Error fetching recent transactions:', error);
      return [];
    }
  }

  /**
   * Log payment attempt
   */
  private async logPaymentAttempt(paymentRequest: PaymentRequest, paymentMethodId: string): Promise<void> {
    try {
      await supabase
        .from('payment_logs')
        .insert([{
          order_id: paymentRequest.orderId,
          payment_method: paymentMethodId,
          amount: paymentRequest.amount,
          currency: paymentRequest.currency,
          customer_email: paymentRequest.customerInfo.email,
          customer_country: paymentRequest.customerInfo.country,
          status: 'initiated',
          created_at: new Date().toISOString()
        }]);
    } catch (error) {
      console.error('Failed to log payment attempt:', error);
    }
  }

  /**
   * Log payment result
   */
  private async logPaymentResult(orderId: string, result: PaymentResult): Promise<void> {
    try {
      await supabase
        .from('payment_logs')
        .update({
          status: result.status,
          transaction_id: result.transactionId,
          error_message: result.error,
          updated_at: new Date().toISOString()
        })
        .eq('order_id', orderId);
    } catch (error) {
      console.error('Failed to log payment result:', error);
    }
  }

  /**
   * Get payment analytics
   */
  async getPaymentAnalytics(startDate: Date, endDate: Date): Promise<PaymentAnalytics> {
    try {
      const { data, error } = await supabase
        .from('payment_logs')
        .select('*')
        .gte('created_at', startDate.toISOString())
        .lte('created_at', endDate.toISOString());

      if (error) throw error;

      const transactions = data || [];
      const successful = transactions.filter(t => t.status === 'completed');
      const failed = transactions.filter(t => t.status === 'failed');

      const totalAmount = successful.reduce((sum, t) => sum + t.amount, 0);
      const averageAmount = successful.length > 0 ? totalAmount / successful.length : 0;

      // Calculate top payment methods
      const methodCounts: Record<string, number> = {};
      transactions.forEach(t => {
        methodCounts[t.payment_method] = (methodCounts[t.payment_method] || 0) + 1;
      });

      const topPaymentMethods = Object.entries(methodCounts)
        .map(([method, count]) => ({
          method,
          count,
          percentage: (count / transactions.length) * 100
        }))
        .sort((a, b) => b.count - a.count);

      return {
        totalTransactions: transactions.length,
        successfulTransactions: successful.length,
        failedTransactions: failed.length,
        totalAmount,
        averageAmount,
        topPaymentMethods,
        conversionRate: transactions.length > 0 ? (successful.length / transactions.length) * 100 : 0,
        fraudDetectionAlerts: transactions.filter(t => t.fraud_score > 50).length
      };
    } catch (error) {
      console.error('Error fetching payment analytics:', error);
      throw error;
    }
  }

  /**
   * Utility function for delays
   */
  private delay(ms: number): Promise<void> {
    return new Promise(resolve => setTimeout(resolve, ms));
  }

  /**
   * Verify payment status across all providers
   */
  async verifyPaymentStatus(orderId: string, paymentMethodId: string): Promise<PaymentResult> {
    const method = this.paymentMethods.find(m => m.id === paymentMethodId);
    if (!method) {
      throw new Error('Invalid payment method');
    }

    switch (method.provider) {
      case 'payu':
        const payuResult = await payuService.verifyPayment('', orderId);
        return {
          success: payuResult.status === 'success',
          transactionId: payuResult.transactionId,
          paymentMethod: paymentMethodId,
          amount: payuResult.amount,
          currency: 'INR',
          status: payuResult.status === 'success' ? 'completed' : 'failed',
          error: payuResult.error
        };

      case 'paypal':
        // PayPal verification would be handled through webhooks
        // This is a placeholder for manual verification
        return {
          success: false,
          paymentMethod: paymentMethodId,
          amount: 0,
          currency: 'USD',
          status: 'pending',
          error: 'PayPal verification requires webhook handling'
        };

      default:
        return {
          success: true,
          paymentMethod: paymentMethodId,
          amount: 0,
          currency: 'INR',
          status: 'completed'
        };
    }
  }
}

export const paymentOrchestrator = new PaymentOrchestrator();

import { supabase } from '../supabase';

export interface PayPalConfig {
  clientId: string;
  clientSecret: string;
  baseUrl: string;
  currency: string;
}

export interface PayPalOrderRequest {
  orderId: string;
  amount: number;
  currency: string;
  description: string;
  customerInfo: {
    firstName: string;
    lastName: string;
    email: string;
    phone?: string;
  };
  shippingAddress?: {
    addressLine1: string;
    addressLine2?: string;
    city: string;
    state: string;
    postalCode: string;
    countryCode: string;
  };
}

export interface PayPalOrder {
  id: string;
  status: string;
  links: Array<{
    href: string;
    rel: string;
    method: string;
  }>;
}

export interface PayPalCapture {
  id: string;
  status: string;
  amount: {
    currency_code: string;
    value: string;
  };
  final_capture: boolean;
}

export interface PayPalWebhook {
  id: string;
  event_type: string;
  resource: any;
  summary: string;
  resource_type: string;
  event_version: string;
  create_time: string;
}

class PayPalService {
  private config: PayPalConfig;
  private accessToken: string | null = null;
  private tokenExpiry: number = 0;

  constructor() {
    this.config = {
      clientId: import.meta.env.VITE_PAYPAL_CLIENT_ID || '',
      clientSecret: import.meta.env.VITE_PAYPAL_CLIENT_SECRET || '',
      baseUrl: import.meta.env.VITE_PAYPAL_BASE_URL || 'https://api-m.sandbox.paypal.com',
      currency: 'USD'
    };
  }

  /**
   * Get PayPal access token
   */
  private async getAccessToken(): Promise<string> {
    if (this.accessToken && Date.now() < this.tokenExpiry) {
      return this.accessToken;
    }

    try {
      const response = await fetch(`${this.config.baseUrl}/v1/oauth2/token`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
          'Authorization': `Basic ${btoa(`${this.config.clientId}:${this.config.clientSecret}`)}`
        },
        body: 'grant_type=client_credentials'
      });

      if (!response.ok) {
        throw new Error(`Failed to get access token: ${response.statusText}`);
      }

      const data = await response.json();
      this.accessToken = data.access_token;
      this.tokenExpiry = Date.now() + (data.expires_in * 1000) - 60000; // Refresh 1 minute early

      return this.accessToken;
    } catch (error) {
      console.error('PayPal access token error:', error);
      throw new Error('Failed to authenticate with PayPal');
    }
  }

  /**
   * Convert amount to PayPal currency format
   */
  private convertCurrency(amountINR: number, targetCurrency: string = 'USD'): number {
    // Simple conversion rate - in production, use real-time rates
    const conversionRates: Record<string, number> = {
      'USD': 0.012, // 1 INR = 0.012 USD (approximate)
      'EUR': 0.011, // 1 INR = 0.011 EUR (approximate)
      'GBP': 0.0095, // 1 INR = 0.0095 GBP (approximate)
      'INR': 1
    };

    const rate = conversionRates[targetCurrency] || conversionRates['USD'];
    return Math.round(amountINR * rate * 100) / 100; // Round to 2 decimal places
  }

  /**
   * Create PayPal order
   */
  async createOrder(orderRequest: PayPalOrderRequest): Promise<PayPalOrder> {
    try {
      const accessToken = await this.getAccessToken();
      const convertedAmount = this.convertCurrency(orderRequest.amount, orderRequest.currency);

      const orderData = {
        intent: 'CAPTURE',
        purchase_units: [{
          reference_id: orderRequest.orderId,
          amount: {
            currency_code: orderRequest.currency,
            value: convertedAmount.toFixed(2)
          },
          description: orderRequest.description,
          shipping: orderRequest.shippingAddress ? {
            address: {
              address_line_1: orderRequest.shippingAddress.addressLine1,
              address_line_2: orderRequest.shippingAddress.addressLine2 || '',
              admin_area_2: orderRequest.shippingAddress.city,
              admin_area_1: orderRequest.shippingAddress.state,
              postal_code: orderRequest.shippingAddress.postalCode,
              country_code: orderRequest.shippingAddress.countryCode
            }
          } : undefined
        }],
        payer: {
          name: {
            given_name: orderRequest.customerInfo.firstName,
            surname: orderRequest.customerInfo.lastName
          },
          email_address: orderRequest.customerInfo.email,
          phone: orderRequest.customerInfo.phone ? {
            phone_number: {
              national_number: orderRequest.customerInfo.phone
            }
          } : undefined
        },
        application_context: {
          return_url: `${window.location.origin}/payment/paypal/success`,
          cancel_url: `${window.location.origin}/payment/paypal/cancel`,
          brand_name: 'Naaz Books',
          locale: 'en-US',
          landing_page: 'BILLING',
          shipping_preference: 'SET_PROVIDED_ADDRESS',
          user_action: 'PAY_NOW'
        }
      };

      const response = await fetch(`${this.config.baseUrl}/v2/checkout/orders`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${accessToken}`,
          'PayPal-Request-Id': `${orderRequest.orderId}-${Date.now()}`
        },
        body: JSON.stringify(orderData)
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(`PayPal order creation failed: ${errorData.message || response.statusText}`);
      }

      const order = await response.json();
      
      // Log the order creation
      await this.logPayPalTransaction(orderRequest.orderId, 'order_created', order.id, convertedAmount, orderRequest.currency);

      return order;
    } catch (error) {
      console.error('PayPal order creation error:', error);
      throw error;
    }
  }

  /**
   * Capture PayPal payment
   */
  async capturePayment(paypalOrderId: string): Promise<PayPalCapture> {
    try {
      const accessToken = await this.getAccessToken();

      const response = await fetch(`${this.config.baseUrl}/v2/checkout/orders/${paypalOrderId}/capture`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${accessToken}`
        }
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(`PayPal capture failed: ${errorData.message || response.statusText}`);
      }

      const captureData = await response.json();
      const capture = captureData.purchase_units[0].payments.captures[0];

      // Log the capture
      await this.logPayPalTransaction(
        captureData.purchase_units[0].reference_id,
        'payment_captured',
        paypalOrderId,
        parseFloat(capture.amount.value),
        capture.amount.currency_code,
        capture.id
      );

      return capture;
    } catch (error) {
      console.error('PayPal capture error:', error);
      throw error;
    }
  }

  /**
   * Get order details
   */
  async getOrderDetails(paypalOrderId: string): Promise<any> {
    try {
      const accessToken = await this.getAccessToken();

      const response = await fetch(`${this.config.baseUrl}/v2/checkout/orders/${paypalOrderId}`, {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${accessToken}`
        }
      });

      if (!response.ok) {
        throw new Error(`Failed to get order details: ${response.statusText}`);
      }

      return await response.json();
    } catch (error) {
      console.error('PayPal get order details error:', error);
      throw error;
    }
  }

  /**
   * Handle PayPal webhook
   */
  async handleWebhook(webhookData: PayPalWebhook): Promise<{ success: boolean; message: string }> {
    try {
      const { event_type, resource } = webhookData;

      switch (event_type) {
        case 'CHECKOUT.ORDER.APPROVED':
          await this.handleOrderApproved(resource);
          break;
        case 'PAYMENT.CAPTURE.COMPLETED':
          await this.handlePaymentCaptured(resource);
          break;
        case 'PAYMENT.CAPTURE.DENIED':
          await this.handlePaymentDenied(resource);
          break;
        case 'CHECKOUT.ORDER.VOIDED':
          await this.handleOrderVoided(resource);
          break;
        default:
          console.log(`Unhandled PayPal webhook event: ${event_type}`);
      }

      return { success: true, message: 'Webhook processed successfully' };
    } catch (error) {
      console.error('PayPal webhook handling error:', error);
      return { success: false, message: 'Failed to process webhook' };
    }
  }

  /**
   * Handle order approved webhook
   */
  private async handleOrderApproved(resource: any): Promise<void> {
    const orderId = resource.purchase_units[0].reference_id;
    await this.updateOrderStatus(orderId, 'approved', resource.id);
  }

  /**
   * Handle payment captured webhook
   */
  private async handlePaymentCaptured(resource: any): Promise<void> {
    const orderId = resource.supplementary_data?.related_ids?.order_id;
    if (orderId) {
      await this.updateOrderStatus(orderId, 'completed', resource.id);
    }
  }

  /**
   * Handle payment denied webhook
   */
  private async handlePaymentDenied(resource: any): Promise<void> {
    const orderId = resource.supplementary_data?.related_ids?.order_id;
    if (orderId) {
      await this.updateOrderStatus(orderId, 'denied', resource.id);
    }
  }

  /**
   * Handle order voided webhook
   */
  private async handleOrderVoided(resource: any): Promise<void> {
    const orderId = resource.purchase_units[0].reference_id;
    await this.updateOrderStatus(orderId, 'voided', resource.id);
  }

  /**
   * Update order status in database
   */
  private async updateOrderStatus(orderId: string, status: string, paypalTransactionId: string): Promise<void> {
    try {
      await supabase
        .from('orders')
        .update({
          payment_status: status,
          paypal_transaction_id: paypalTransactionId,
          updated_at: new Date().toISOString()
        })
        .eq('id', orderId);
    } catch (error) {
      console.error('Failed to update order status:', error);
    }
  }

  /**
   * Log PayPal transaction
   */
  private async logPayPalTransaction(
    orderId: string,
    action: string,
    paypalOrderId: string,
    amount: number,
    currency: string,
    captureId?: string
  ): Promise<void> {
    try {
      await supabase
        .from('paypal_transactions')
        .insert([{
          order_id: orderId,
          paypal_order_id: paypalOrderId,
          action,
          amount,
          currency,
          capture_id: captureId,
          created_at: new Date().toISOString()
        }]);
    } catch (error) {
      console.error('Failed to log PayPal transaction:', error);
    }
  }

  /**
   * Refund payment
   */
  async refundPayment(captureId: string, amount?: number, currency?: string): Promise<any> {
    try {
      const accessToken = await this.getAccessToken();

      const refundData: any = {};
      if (amount && currency) {
        refundData.amount = {
          value: amount.toFixed(2),
          currency_code: currency
        };
      }

      const response = await fetch(`${this.config.baseUrl}/v2/payments/captures/${captureId}/refund`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${accessToken}`
        },
        body: JSON.stringify(refundData)
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(`PayPal refund failed: ${errorData.message || response.statusText}`);
      }

      return await response.json();
    } catch (error) {
      console.error('PayPal refund error:', error);
      throw error;
    }
  }

  /**
   * Get supported currencies
   */
  getSupportedCurrencies(): string[] {
    return ['USD', 'EUR', 'GBP', 'CAD', 'AUD', 'JPY'];
  }

  /**
   * Detect user's currency based on location
   */
  async detectUserCurrency(): Promise<string> {
    try {
      // Simple IP-based currency detection
      const response = await fetch('https://ipapi.co/json/');
      const data = await response.json();
      
      const currencyMap: Record<string, string> = {
        'US': 'USD',
        'GB': 'GBP',
        'CA': 'CAD',
        'AU': 'AUD',
        'JP': 'JPY',
        'IN': 'USD' // For Indian customers, use USD for international payments
      };

      return currencyMap[data.country_code] || 'USD';
    } catch (error) {
      console.error('Currency detection error:', error);
      return 'USD'; // Default to USD
    }
  }
}

export const paypalService = new PayPalService();

import { supabase } from '@/integrations/supabase/client';
import type { 
  EnhancedReview, 
  ReviewStats, 
  ReviewModerationAction, 
  ReviewReportData, 
  OrderFeedbackData,
  SentimentAnalysisResult 
} from '@/types/review';

export class ReviewService {
  // Get reviews for a product with enhanced data
  static async getProductReviews(
    productId: string, 
    options: {
      status?: 'pending' | 'approved' | 'rejected';
      limit?: number;
      offset?: number;
      userId?: string;
    } = {}
  ): Promise<EnhancedReview[]> {
    const { status = 'approved', limit = 50, offset = 0, userId } = options;
    
    let query = supabase
      .from('reviews')
      .select(`
        *,
        profiles!inner(name, avatar_url),
        review_analytics(sentiment_score, sentiment_label, keywords),
        ${userId ? `review_helpful_votes!left(is_helpful)` : ''}
      `)
      .eq('product_id', productId)
      .eq('status', status)
      .order('created_at', { ascending: false })
      .range(offset, offset + limit - 1);

    if (userId) {
      query = query.eq('review_helpful_votes.user_id', userId);
    }

    const { data, error } = await query;
    
    if (error) throw error;
    
    return (data as any[]).map(review => ({
      ...review,
      user_helpful_vote: userId ? review.review_helpful_votes?.[0]?.is_helpful : undefined,
      is_verified_purchase: review.verified_purchase
    }));
  }

  // Get review statistics for a product
  static async getProductReviewStats(productId: string): Promise<ReviewStats> {
    const { data, error } = await supabase
      .rpc('get_product_review_stats', { product_uuid: parseInt(productId) });
    
    if (error) throw error;
    
    return data[0] || {
      total_reviews: 0,
      average_rating: 0,
      rating_distribution: { '5': 0, '4': 0, '3': 0, '2': 0, '1': 0 },
      sentiment_distribution: { positive: 0, neutral: 0, negative: 0 }
    };
  }

  // Create a new review
  static async createReview(reviewData: {
    productId: string;
    userId: string;
    rating: number;
    title?: string;
    comment?: string;
    wouldRecommend?: boolean;
  }) {
    // Check if user has purchased the product
    const { data: orderItems } = await supabase
      .from('order_items')
      .select('orders!inner(user_id, status)')
      .eq('product_id', reviewData.productId)
      .eq('orders.user_id', reviewData.userId)
      .in('orders.status', ['delivered', 'completed']);

    const verifiedPurchase = orderItems && orderItems.length > 0;

    const { data, error } = await supabase
      .from('reviews')
      .insert({
        product_id: parseInt(reviewData.productId),
        user_id: reviewData.userId,
        rating: reviewData.rating,
        title: reviewData.title,
        comment: reviewData.comment,
        would_recommend: reviewData.wouldRecommend || false,
        verified_purchase: verifiedPurchase,
        status: 'pending' // All reviews start as pending for moderation
      })
      .select()
      .single();

    if (error) throw error;

    // Analyze sentiment if comment exists
    if (reviewData.comment) {
      await this.analyzeSentiment(data.id, reviewData.comment);
    }

    return data;
  }

  // Update review helpful votes
  static async voteHelpful(reviewId: number, userId: string, isHelpful: boolean) {
    const { error } = await supabase
      .from('review_helpful_votes')
      .upsert({
        review_id: reviewId,
        user_id: userId,
        is_helpful: isHelpful
      });

    if (error) throw error;
  }

  // Report a review
  static async reportReview(reportData: ReviewReportData & { reporterId: string }) {
    const { error } = await supabase
      .from('review_reports')
      .insert({
        review_id: reportData.reviewId,
        reporter_id: reportData.reporterId,
        reason: reportData.reason,
        description: reportData.description
      });

    if (error) throw error;

    // Increment reported count
    const { data: currentReview } = await supabase
      .from('reviews')
      .select('reported_count')
      .eq('id', reportData.reviewId)
      .single();
    
    if (currentReview) {
      await supabase
        .from('reviews')
        .update({ 
          reported_count: (currentReview.reported_count || 0) + 1
        })
        .eq('id', reportData.reviewId);
    }
  }

  // Moderate review (admin only)
  static async moderateReview(moderationData: ReviewModerationAction & { moderatorId: string }) {
    const { error } = await supabase
      .from('reviews')
      .update({
        status: moderationData.action === 'approve' ? 'approved' : 'rejected',
        moderated_by: moderationData.moderatorId,
        moderated_at: new Date().toISOString(),
        moderation_notes: moderationData.notes
      })
      .eq('id', moderationData.reviewId);

    if (error) throw error;
  }

  // Get pending reviews for moderation
  static async getPendingReviews(limit = 50, offset = 0) {
    const { data, error } = await supabase
      .from('reviews')
      .select(`
        *,
        profiles!inner(name, avatar_url),
        products!inner(title),
        review_reports(reason, description, created_at)
      `)
      .eq('status', 'pending')
      .order('created_at', { ascending: true })
      .range(offset, offset + limit - 1);

    if (error) throw error;
    return data;
  }

  // Create order feedback
  static async createOrderFeedback(feedbackData: OrderFeedbackData & { userId: string }) {
    const { error } = await supabase
      .from('order_feedback')
      .insert({
        order_id: feedbackData.orderId,
        user_id: feedbackData.userId,
        overall_rating: feedbackData.overallRating,
        delivery_rating: feedbackData.deliveryRating,
        packaging_rating: feedbackData.packagingRating,
        would_recommend: feedbackData.wouldRecommend,
        feedback_text: feedbackData.feedbackText,
        improvement_suggestions: feedbackData.improvementSuggestions
      });

    if (error) throw error;
  }

  // Get order feedback
  static async getOrderFeedback(orderId: number) {
    const { data, error } = await supabase
      .from('order_feedback')
      .select('*')
      .eq('order_id', orderId)
      .single();

    if (error && error.code !== 'PGRST116') throw error;
    return data;
  }

  // Simple sentiment analysis (can be enhanced with external APIs)
  private static async analyzeSentiment(reviewId: number, comment: string): Promise<SentimentAnalysisResult> {
    // Basic sentiment analysis using keyword matching
    const positiveWords = ['good', 'great', 'excellent', 'amazing', 'love', 'perfect', 'wonderful', 'fantastic', 'awesome', 'best'];
    const negativeWords = ['bad', 'terrible', 'awful', 'hate', 'worst', 'horrible', 'disappointing', 'poor', 'useless'];
    
    const words = comment.toLowerCase().split(/\s+/);
    const wordCount = words.length;
    
    let positiveCount = 0;
    let negativeCount = 0;
    
    words.forEach(word => {
      if (positiveWords.includes(word)) positiveCount++;
      if (negativeWords.includes(word)) negativeCount++;
    });
    
    const score = positiveCount > negativeCount ? 
      Math.min(0.8, positiveCount / wordCount * 5) : 
      Math.max(-0.8, -(negativeCount / wordCount * 5));
    
    const label = score > 0.1 ? 'positive' : score < -0.1 ? 'negative' : 'neutral';
    
    // Extract keywords (simple approach - can be enhanced)
    const keywords = words
      .filter(word => word.length > 3)
      .filter(word => !['this', 'that', 'with', 'from', 'they', 'have', 'been'].includes(word))
      .slice(0, 5);
    
    const readabilityScore = Math.min(1, wordCount / 50); // Simple readability metric
    
    const result: SentimentAnalysisResult = {
      score,
      label,
      keywords,
      readabilityScore,
      wordCount
    };
    
    // Store analytics
    await supabase
      .from('review_analytics')
      .insert({
        review_id: reviewId,
        sentiment_score: score,
        sentiment_label: label,
        keywords,
        readability_score: readabilityScore,
        word_count: wordCount
      });
    
    return result;
  }

  // Get review analytics dashboard data
  static async getReviewAnalytics(dateRange?: { from: string; to: string }) {
    let query = supabase
      .from('reviews')
      .select(`
        *,
        review_analytics(sentiment_label, sentiment_score),
        products(title, category)
      `);

    if (dateRange) {
      query = query
        .gte('created_at', dateRange.from)
        .lte('created_at', dateRange.to);
    }

    const { data, error } = await query;
    if (error) throw error;

    // Process analytics data
    const totalReviews = data.length;
    const averageRating = data.reduce((sum, review) => sum + review.rating, 0) / totalReviews;
    
    const sentimentCounts = data.reduce((acc, review) => {
      const sentiment = review.review_analytics?.[0]?.sentiment_label || 'neutral';
      acc[sentiment] = (acc[sentiment] || 0) + 1;
      return acc;
    }, {} as Record<string, number>);

    const categoryBreakdown = data.reduce((acc, review) => {
      const category = review.products?.category || 'Unknown';
      if (!acc[category]) {
        acc[category] = { count: 0, averageRating: 0, totalRating: 0 };
      }
      acc[category].count++;
      acc[category].totalRating += review.rating;
      acc[category].averageRating = acc[category].totalRating / acc[category].count;
      return acc;
    }, {} as Record<string, any>);

    return {
      totalReviews,
      averageRating,
      sentimentCounts,
      categoryBreakdown,
      reviewsOverTime: data.map(review => ({
        date: review.created_at,
        rating: review.rating,
        sentiment: review.review_analytics?.[0]?.sentiment_label
      }))
    };
  }
}

import DOMPurify from 'dompurify';
import sentryService from './sentryService';

export interface SanitizationOptions {
  allowedTags?: string[];
  allowedAttributes?: Record<string, string[]>;
  stripTags?: boolean;
  maxLength?: number;
  preserveWhitespace?: boolean;
}

class SanitizationService {
  private defaultHtmlConfig = {
    ALLOWED_TAGS: ['p', 'br', 'strong', 'em', 'u', 'ol', 'ul', 'li', 'h1', 'h2', 'h3', 'h4', 'h5', 'h6'],
    ALLOWED_ATTR: ['class'],
    KEEP_CONTENT: true,
    RETURN_DOM: false,
    RETURN_DOM_FRAGMENT: false,
    RETURN_DOM_IMPORT: false
  };

  private strictConfig = {
    ALLOWED_TAGS: [],
    ALLOWED_ATTR: [],
    KEEP_CONTENT: true,
    RETURN_DOM: false,
    RETURN_DOM_FRAGMENT: false,
    RETURN_DOM_IMPORT: false
  };

  /**
   * Sanitizes HTML content for safe display
   */
  sanitizeHtml(
    input: string,
    options: SanitizationOptions = {}
  ): string {
    if (!input || typeof input !== 'string') {
      return '';
    }

    try {
      const config: any = {
        ...this.defaultHtmlConfig,
        ...(options.allowedTags && { ALLOWED_TAGS: options.allowedTags }),
        ...(options.allowedAttributes && { ALLOWED_ATTR: options.allowedAttributes })
      };

      let sanitized = DOMPurify.sanitize(input, config) as unknown as string;

      // Apply additional processing
      if (options.maxLength && sanitized.length > options.maxLength) {
        sanitized = sanitized.substring(0, options.maxLength) + '...';
      }

      if (!options.preserveWhitespace) {
        sanitized = sanitized.replace(/\s+/g, ' ').trim();
      }

      return sanitized;
    } catch (error) {
      sentryService.captureError(
        error instanceof Error ? error : new Error('HTML sanitization failed'),
        {
          action: 'sanitize_html',
          additionalData: {
            inputLength: input.length,
            options
          }
        }
      );
      
      // Return empty string on error for security
      return '';
    }
  }

  /**
   * Strips all HTML tags and returns plain text
   */
  stripHtml(input: string, maxLength?: number): string {
    if (!input || typeof input !== 'string') {
      return '';
    }

    try {
      let sanitized = DOMPurify.sanitize(input, this.strictConfig) as string;
      
      // Remove any remaining HTML entities
      sanitized = sanitized
        .replace(/&nbsp;/g, ' ')
        .replace(/&amp;/g, '&')
        .replace(/&lt;/g, '<')
        .replace(/&gt;/g, '>')
        .replace(/&quot;/g, '"')
        .replace(/&#x27;/g, "'")
        .replace(/&#x2F;/g, '/')
        .replace(/\s+/g, ' ')
        .trim();

      if (maxLength && sanitized.length > maxLength) {
        sanitized = sanitized.substring(0, maxLength) + '...';
      }

      return sanitized;
    } catch (error) {
      sentryService.captureError(
        error instanceof Error ? error : new Error('HTML stripping failed'),
        {
          action: 'strip_html',
          additionalData: {
            inputLength: input.length,
            maxLength
          }
        }
      );
      
      return '';
    }
  }

  /**
   * Sanitizes user input for database storage
   */
  sanitizeInput(input: string, maxLength?: number): string {
    if (!input || typeof input !== 'string') {
      return '';
    }

    try {
      // Remove HTML tags
      let sanitized = this.stripHtml(input);

      // Remove potentially dangerous characters
      sanitized = sanitized
        .replace(/[<>]/g, '') // Remove angle brackets
        .replace(/javascript:/gi, '') // Remove javascript: protocol
        .replace(/on\w+\s*=/gi, '') // Remove event handlers
        .replace(/data:/gi, '') // Remove data: protocol
        .trim();

      if (maxLength && sanitized.length > maxLength) {
        sanitized = sanitized.substring(0, maxLength);
      }

      return sanitized;
    } catch (error) {
      sentryService.captureError(
        error instanceof Error ? error : new Error('Input sanitization failed'),
        {
          action: 'sanitize_input',
          additionalData: {
            inputLength: input.length,
            maxLength
          }
        }
      );
      
      return '';
    }
  }

  /**
   * Sanitizes email addresses
   */
  sanitizeEmail(email: string): string {
    if (!email || typeof email !== 'string') {
      return '';
    }

    return email
      .toLowerCase()
      .trim()
      .replace(/[^\w@.-]/g, '') // Only allow word characters, @, ., and -
      .substring(0, 254); // RFC 5321 limit
  }

  /**
   * Sanitizes phone numbers
   */
  sanitizePhone(phone: string): string {
    if (!phone || typeof phone !== 'string') {
      return '';
    }

    return phone
      .replace(/[^\d+()-\s]/g, '') // Only allow digits, +, (), -, and spaces
      .trim()
      .substring(0, 20);
  }

  /**
   * Sanitizes URLs
   */
  sanitizeUrl(url: string): string {
    if (!url || typeof url !== 'string') {
      return '';
    }

    try {
      // Remove dangerous protocols
      const sanitized = url
        .trim()
        .replace(/^javascript:/gi, '')
        .replace(/^data:/gi, '')
        .replace(/^vbscript:/gi, '');

      // Validate URL format
      if (sanitized && !sanitized.match(/^https?:\/\//i)) {
        return `https://${sanitized}`;
      }

      return sanitized.substring(0, 2048); // Reasonable URL length limit
    } catch (error) {
      sentryService.captureError(
        error instanceof Error ? error : new Error('URL sanitization failed'),
        {
          action: 'sanitize_url',
          additionalData: { url }
        }
      );
      
      return '';
    }
  }

  /**
   * Sanitizes file names
   */
  sanitizeFileName(fileName: string): string {
    if (!fileName || typeof fileName !== 'string') {
      return '';
    }

    return fileName
      .replace(/[^a-zA-Z0-9.-]/g, '_') // Replace special chars with underscore
      .replace(/_{2,}/g, '_') // Replace multiple underscores with single
      .replace(/^_+|_+$/g, '') // Remove leading/trailing underscores
      .substring(0, 255); // File system limit
  }

  /**
   * Sanitizes search queries
   */
  sanitizeSearchQuery(query: string): string {
    if (!query || typeof query !== 'string') {
      return '';
    }

    return query
      .replace(/[<>]/g, '') // Remove angle brackets
      .replace(/['"]/g, '') // Remove quotes to prevent injection
      .replace(/\s+/g, ' ') // Normalize whitespace
      .trim()
      .substring(0, 100); // Reasonable search query length
  }

  /**
   * Sanitizes form data object
   */
  sanitizeFormData<T extends Record<string, unknown>>(
    data: T,
    fieldConfig: Partial<Record<keyof T, SanitizationOptions>> = {}
  ): T {
    const sanitized = { ...data };

    try {
      Object.keys(sanitized).forEach(key => {
        const value = sanitized[key];
        const config = fieldConfig[key as keyof T] || {};

        if (typeof value === 'string') {
          if (config.stripTags) {
            (sanitized as any)[key] = this.stripHtml(value, config.maxLength);
          } else {
            (sanitized as any)[key] = this.sanitizeInput(value, config.maxLength);
          }
        } else if (Array.isArray(value)) {
          (sanitized as any)[key] = value.map(item => 
            typeof item === 'string' 
              ? this.sanitizeInput(item, config.maxLength)
              : item
          );
        }
      });

      return sanitized;
    } catch (error) {
      sentryService.captureError(
        error instanceof Error ? error : new Error('Form data sanitization failed'),
        {
          action: 'sanitize_form_data',
          additionalData: {
            fieldCount: Object.keys(data).length,
            fieldConfig
          }
        }
      );
      
      return data; // Return original data if sanitization fails
    }
  }

  /**
   * Validates and sanitizes rich text content (for blog posts, descriptions, etc.)
   */
  sanitizeRichText(content: string, maxLength: number = 10000): string {
    if (!content || typeof content !== 'string') {
      return '';
    }

    const allowedTags = [
      'p', 'br', 'strong', 'em', 'u', 'ol', 'ul', 'li', 
      'h1', 'h2', 'h3', 'h4', 'h5', 'h6', 'blockquote',
      'a', 'img'
    ];

    const allowedAttributes = {
      'a': ['href', 'title'],
      'img': ['src', 'alt', 'title', 'width', 'height'],
      '*': ['class']
    };

    return this.sanitizeHtml(content, {
      allowedTags,
      allowedAttributes,
      maxLength,
      preserveWhitespace: true
    });
  }

  /**
   * Advanced input validation with multiple security checks
   */
  validateAndSanitizeInput(
    input: string,
    options: {
      maxLength?: number;
      minLength?: number;
      allowedPatterns?: RegExp[];
      blockedPatterns?: RegExp[];
      requireAlphanumeric?: boolean;
      allowSpecialChars?: string[];
      customValidator?: (input: string) => boolean | { isValid: boolean; error?: string };
    } = {}
  ): { isValid: boolean; sanitized: string; errors: string[] } {
    const errors: string[] = [];
    let sanitized = input;

    try {
      // Basic sanitization
      sanitized = this.sanitizeInput(input);

      // Length validation
      if (options.minLength && sanitized.length < options.minLength) {
        errors.push(`Input must be at least ${options.minLength} characters long`);
      }

      if (options.maxLength && sanitized.length > options.maxLength) {
        errors.push(`Input must be no more than ${options.maxLength} characters long`);
        sanitized = sanitized.substring(0, options.maxLength);
      }

      // Pattern validation
      if (options.allowedPatterns) {
        const matchesAllowed = options.allowedPatterns.some(pattern => pattern.test(sanitized));
        if (!matchesAllowed) {
          errors.push('Input contains invalid characters');
        }
      }

      if (options.blockedPatterns) {
        const matchesBlocked = options.blockedPatterns.some(pattern => pattern.test(sanitized));
        if (matchesBlocked) {
          errors.push('Input contains prohibited content');
        }
      }

      // Alphanumeric validation
      if (options.requireAlphanumeric) {
        const allowedChars = options.allowSpecialChars?.join('') || '';
        const alphanumericPattern = new RegExp(`^[a-zA-Z0-9${allowedChars.replace(/[.*+?^${}()|[\]\\]/g, '\\$&')}]+$`);
        if (!alphanumericPattern.test(sanitized)) {
          errors.push('Input must contain only alphanumeric characters and allowed special characters');
        }
      }

      // Custom validation
      if (options.customValidator) {
        const customResult = options.customValidator(sanitized);
        if (typeof customResult === 'object') {
          if (!customResult.isValid) {
            errors.push(customResult.error || 'Input failed custom validation');
          }
        } else if (!customResult) {
          errors.push('Input failed custom validation');
        }
      }

      return {
        isValid: errors.length === 0,
        sanitized,
        errors
      };
    } catch (error) {
      sentryService.captureError(
        error instanceof Error ? error : new Error('Input validation failed'),
        {
          action: 'validate_and_sanitize_input',
          additionalData: { input: input?.substring(0, 100), options }
        }
      );

      return {
        isValid: false,
        sanitized: '',
        errors: ['Input validation failed']
      };
    }
  }

  /**
   * Sanitize and validate SQL-like inputs to prevent injection
   */
  sanitizeSQLInput(input: string): string {
    if (!input || typeof input !== 'string') {
      return '';
    }

    return input
      .replace(/['"`;\\]/g, '') // Remove dangerous SQL characters
      .replace(/\b(SELECT|INSERT|UPDATE|DELETE|DROP|CREATE|ALTER|EXEC|UNION|SCRIPT)\b/gi, '') // Remove SQL keywords
      .replace(/--/g, '') // Remove SQL comments
      .replace(/\/\*/g, '') // Remove SQL block comments start
      .replace(/\*\//g, '') // Remove SQL block comments end
      .trim()
      .substring(0, 1000); // Reasonable length limit
  }

  /**
   * Sanitize NoSQL injection attempts
   */
  sanitizeNoSQLInput(input: any): any {
    if (typeof input === 'string') {
      return this.sanitizeInput(input);
    }

    if (Array.isArray(input)) {
      return input.map(item => this.sanitizeNoSQLInput(item));
    }

    if (typeof input === 'object' && input !== null) {
      // Remove dangerous MongoDB operators
      const dangerousKeys = ['$where', '$regex', '$ne', '$gt', '$lt', '$gte', '$lte', '$in', '$nin', '$exists'];
      const sanitized: any = {};

      for (const [key, value] of Object.entries(input)) {
        if (!dangerousKeys.includes(key)) {
          sanitized[this.sanitizeInput(key)] = this.sanitizeNoSQLInput(value);
        }
      }

      return sanitized;
    }

    return input;
  }

  /**
   * Validate and sanitize JSON input
   */
  sanitizeJSONInput(input: string, maxDepth: number = 5): { isValid: boolean; sanitized: any; error?: string } {
    try {
      if (!input || typeof input !== 'string') {
        return { isValid: false, sanitized: null, error: 'Invalid JSON input' };
      }

      // Basic sanitization
      const sanitizedInput = this.sanitizeInput(input);

      // Parse JSON
      const parsed = JSON.parse(sanitizedInput);

      // Check depth to prevent deeply nested objects
      const checkDepth = (obj: any, depth: number = 0): boolean => {
        if (depth > maxDepth) return false;
        
        if (typeof obj === 'object' && obj !== null) {
          if (Array.isArray(obj)) {
            return obj.every(item => checkDepth(item, depth + 1));
          } else {
            return Object.values(obj).every(value => checkDepth(value, depth + 1));
          }
        }
        
        return true;
      };

      if (!checkDepth(parsed)) {
        return { isValid: false, sanitized: null, error: 'JSON structure too deeply nested' };
      }

      // Recursively sanitize the parsed object
      const sanitizeObject = (obj: any): any => {
        if (typeof obj === 'string') {
          return this.sanitizeInput(obj);
        }
        
        if (Array.isArray(obj)) {
          return obj.map(sanitizeObject);
        }
        
        if (typeof obj === 'object' && obj !== null) {
          const sanitized: any = {};
          for (const [key, value] of Object.entries(obj)) {
            sanitized[this.sanitizeInput(key)] = sanitizeObject(value);
          }
          return sanitized;
        }
        
        return obj;
      };

      return {
        isValid: true,
        sanitized: sanitizeObject(parsed)
      };
    } catch (error) {
      return {
        isValid: false,
        sanitized: null,
        error: 'Invalid JSON format'
      };
    }
  }

  /**
   * Comprehensive XSS protection
   */
  preventXSS(input: string, context: 'html' | 'attribute' | 'javascript' | 'css' = 'html'): string {
    if (!input || typeof input !== 'string') {
      return '';
    }

    let sanitized = input;

    switch (context) {
      case 'html':
        sanitized = this.sanitizeHtml(input, { stripTags: true });
        break;
      
      case 'attribute':
        sanitized = input
          .replace(/[<>"'&]/g, (match) => {
            const entities: Record<string, string> = {
              '<': '&lt;',
              '>': '&gt;',
              '"': '&quot;',
              "'": '&#x27;',
              '&': '&amp;'
            };
            return entities[match] || match;
          });
        break;
      
      case 'javascript':
        sanitized = input
          .replace(/[<>"'&\\]/g, (match) => {
            const escapes: Record<string, string> = {
              '<': '\\u003c',
              '>': '\\u003e',
              '"': '\\u0022',
              "'": '\\u0027',
              '&': '\\u0026',
              '\\': '\\\\'
            };
            return escapes[match] || match;
          });
        break;
      
      case 'css':
        sanitized = input.replace(/[<>"'&\\]/g, '');
        break;
    }

    return sanitized;
  }
}

// Create singleton instance
export const sanitizationService = new SanitizationService();

export default sanitizationService;

import { supabase } from '../supabase';
import sentryService from './sentryService';

export interface SessionConfig {
  maxAge: number; // Session timeout in milliseconds
  renewalThreshold: number; // Renew session when this much time is left (milliseconds)
  maxSessions: number; // Maximum concurrent sessions per user
  requireReauth: boolean; // Require re-authentication for sensitive operations
}

export interface SessionInfo {
  sessionId: string;
  userId: string;
  createdAt: Date;
  lastActivity: Date;
  expiresAt: Date;
  isActive: boolean;
  deviceInfo?: string;
  ipAddress?: string;
}

export interface SessionValidationResult {
  isValid: boolean;
  session?: SessionInfo;
  requiresRenewal?: boolean;
  error?: string;
}

class SessionService {
  private readonly SESSION_STORAGE_KEY = 'app_session';
  private readonly ACTIVITY_STORAGE_KEY = 'last_activity';
  private readonly DEFAULT_CONFIG: SessionConfig = {
    maxAge: 24 * 60 * 60 * 1000, // 24 hours
    renewalThreshold: 60 * 60 * 1000, // 1 hour
    maxSessions: 5,
    requireReauth: true
  };

  private config: SessionConfig;
  private activityTimer: NodeJS.Timeout | null = null;

  constructor(config: Partial<SessionConfig> = {}) {
    this.config = { ...this.DEFAULT_CONFIG, ...config };
    this.startActivityTracking();
  }

  /**
   * Create a new session
   */
  async createSession(userId: string, deviceInfo?: string): Promise<SessionInfo> {
    try {
      const sessionId = this.generateSessionId();
      const now = new Date();
      const expiresAt = new Date(now.getTime() + this.config.maxAge);

      const sessionInfo: SessionInfo = {
        sessionId,
        userId,
        createdAt: now,
        lastActivity: now,
        expiresAt,
        isActive: true,
        deviceInfo: deviceInfo || this.getDeviceInfo(),
        ipAddress: await this.getClientIP()
      };

      // Store session in Supabase
      await supabase.from('user_sessions').insert({
        session_id: sessionId,
        user_id: userId,
        created_at: now.toISOString(),
        last_activity: now.toISOString(),
        expires_at: expiresAt.toISOString(),
        is_active: true,
        device_info: sessionInfo.deviceInfo,
        ip_address: sessionInfo.ipAddress
      });

      // Store session locally
      this.storeSession(sessionInfo);

      // Clean up old sessions
      await this.cleanupOldSessions(userId);

      sentryService.addBreadcrumb(
        'New session created',
        'auth',
        'info',
        { sessionId, userId, expiresAt }
      );

      return sessionInfo;
    } catch (error) {
      sentryService.captureError(
        error instanceof Error ? error : new Error('Failed to create session'),
        {
          action: 'create_session',
          additionalData: { userId, deviceInfo }
        }
      );
      throw error;
    }
  }

  /**
   * Validate current session
   */
  async validateSession(): Promise<SessionValidationResult> {
    try {
      const storedSession = this.getStoredSession();
      
      if (!storedSession) {
        return {
          isValid: false,
          error: 'No session found'
        };
      }

      // Check if session is expired
      if (new Date() > storedSession.expiresAt) {
        await this.destroySession(storedSession.sessionId);
        return {
          isValid: false,
          error: 'Session expired'
        };
      }

      // Verify session in database
      const { data: dbSession, error } = await supabase
        .from('user_sessions')
        .select('*')
        .eq('session_id', storedSession.sessionId)
        .eq('is_active', true)
        .single();

      if (error || !dbSession) {
        await this.destroyLocalSession();
        return {
          isValid: false,
          error: 'Session not found in database'
        };
      }

      // Check if session needs renewal
      const timeUntilExpiry = storedSession.expiresAt.getTime() - Date.now();
      const requiresRenewal = timeUntilExpiry < this.config.renewalThreshold;

      // Update last activity
      await this.updateActivity(storedSession.sessionId);

      return {
        isValid: true,
        session: storedSession,
        requiresRenewal
      };
    } catch (error) {
      sentryService.captureError(
        error instanceof Error ? error : new Error('Session validation failed'),
        {
          action: 'validate_session'
        }
      );

      return {
        isValid: false,
        error: 'Session validation failed'
      };
    }
  }

  /**
   * Renew current session
   */
  async renewSession(): Promise<SessionInfo> {
    try {
      const validation = await this.validateSession();
      
      if (!validation.isValid || !validation.session) {
        throw new Error('Cannot renew invalid session');
      }

      const newExpiresAt = new Date(Date.now() + this.config.maxAge);
      const updatedSession: SessionInfo = {
        ...validation.session,
        expiresAt: newExpiresAt,
        lastActivity: new Date()
      };

      // Update session in database
      await supabase
        .from('user_sessions')
        .update({
          expires_at: newExpiresAt.toISOString(),
          last_activity: new Date().toISOString()
        })
        .eq('session_id', validation.session.sessionId);

      // Update local storage
      this.storeSession(updatedSession);

      sentryService.addBreadcrumb(
        'Session renewed',
        'auth',
        'info',
        { sessionId: validation.session.sessionId, newExpiresAt }
      );

      return updatedSession;
    } catch (error) {
      sentryService.captureError(
        error instanceof Error ? error : new Error('Failed to renew session'),
        {
          action: 'renew_session'
        }
      );
      throw error;
    }
  }

  /**
   * Destroy a specific session
   */
  async destroySession(sessionId: string): Promise<void> {
    try {
      // Mark session as inactive in database
      await supabase
        .from('user_sessions')
        .update({ is_active: false })
        .eq('session_id', sessionId);

      // Clear local storage if it's the current session
      const storedSession = this.getStoredSession();
      if (storedSession && storedSession.sessionId === sessionId) {
        this.destroyLocalSession();
      }

      sentryService.addBreadcrumb(
        'Session destroyed',
        'auth',
        'info',
        { sessionId }
      );
    } catch (error) {
      sentryService.captureError(
        error instanceof Error ? error : new Error('Failed to destroy session'),
        {
          action: 'destroy_session',
          additionalData: { sessionId }
        }
      );
    }
  }

  /**
   * Destroy all sessions for a user
   */
  async destroyAllSessions(userId: string, exceptSessionId?: string): Promise<void> {
    try {
      let query = supabase
        .from('user_sessions')
        .update({ is_active: false })
        .eq('user_id', userId);

      if (exceptSessionId) {
        query = query.neq('session_id', exceptSessionId);
      }

      await query;

      // Clear local storage if current session is being destroyed
      const storedSession = this.getStoredSession();
      if (storedSession && storedSession.userId === userId && 
          (!exceptSessionId || storedSession.sessionId !== exceptSessionId)) {
        this.destroyLocalSession();
      }

      sentryService.addBreadcrumb(
        'All user sessions destroyed',
        'auth',
        'info',
        { userId, exceptSessionId }
      );
    } catch (error) {
      sentryService.captureError(
        error instanceof Error ? error : new Error('Failed to destroy all sessions'),
        {
          action: 'destroy_all_sessions',
          additionalData: { userId, exceptSessionId }
        }
      );
    }
  }

  /**
   * Get all active sessions for a user
   */
  async getUserSessions(userId: string): Promise<SessionInfo[]> {
    try {
      const { data: sessions, error } = await supabase
        .from('user_sessions')
        .select('*')
        .eq('user_id', userId)
        .eq('is_active', true)
        .order('last_activity', { ascending: false });

      if (error) throw error;

      return sessions.map(session => ({
        sessionId: session.session_id,
        userId: session.user_id,
        createdAt: new Date(session.created_at),
        lastActivity: new Date(session.last_activity),
        expiresAt: new Date(session.expires_at),
        isActive: session.is_active,
        deviceInfo: session.device_info,
        ipAddress: session.ip_address
      }));
    } catch (error) {
      sentryService.captureError(
        error instanceof Error ? error : new Error('Failed to get user sessions'),
        {
          action: 'get_user_sessions',
          additionalData: { userId }
        }
      );
      return [];
    }
  }

  /**
   * Update session activity
   */
  private async updateActivity(sessionId: string): Promise<void> {
    try {
      const now = new Date();
      
      // Update database
      await supabase
        .from('user_sessions')
        .update({ last_activity: now.toISOString() })
        .eq('session_id', sessionId);

      // Update local storage
      localStorage.setItem(this.ACTIVITY_STORAGE_KEY, now.toISOString());
    } catch (error) {
      // Don't throw errors for activity updates
      sentryService.addBreadcrumb(
        'Failed to update session activity',
        'system',
        'warning'
      );
    }
  }

  /**
   * Start tracking user activity
   */
  private startActivityTracking(): void {
    // Update activity every 5 minutes
    this.activityTimer = setInterval(async () => {
      const session = this.getStoredSession();
      if (session) {
        await this.updateActivity(session.sessionId);
      }
    }, 5 * 60 * 1000);

    // Track user interactions
    const events = ['click', 'keypress', 'scroll', 'mousemove'];
    const throttledUpdate = this.throttle(async () => {
      const session = this.getStoredSession();
      if (session) {
        await this.updateActivity(session.sessionId);
      }
    }, 60000); // Throttle to once per minute

    events.forEach(event => {
      document.addEventListener(event, throttledUpdate, { passive: true });
    });
  }

  /**
   * Clean up old/expired sessions
   */
  private async cleanupOldSessions(userId: string): Promise<void> {
    try {
      // Get all active sessions for user
      const sessions = await this.getUserSessions(userId);
      
      // If user has too many sessions, deactivate oldest ones
      if (sessions.length > this.config.maxSessions) {
        const sessionsToDeactivate = sessions
          .sort((a, b) => a.lastActivity.getTime() - b.lastActivity.getTime())
          .slice(0, sessions.length - this.config.maxSessions);

        for (const session of sessionsToDeactivate) {
          await this.destroySession(session.sessionId);
        }
      }

      // Clean up expired sessions
      await supabase
        .from('user_sessions')
        .update({ is_active: false })
        .lt('expires_at', new Date().toISOString());
    } catch (error) {
      sentryService.addBreadcrumb(
        'Failed to cleanup old sessions',
        'system',
        'warning'
      );
    }
  }

  /**
   * Generate a unique session ID
   */
  private generateSessionId(): string {
    const array = new Uint8Array(32);
    crypto.getRandomValues(array);
    return Array.from(array, byte => byte.toString(16).padStart(2, '0')).join('');
  }

  /**
   * Get device information
   */
  private getDeviceInfo(): string {
    try {
      return `${navigator.userAgent} | ${window.screen.width}x${window.screen.height}`;
    } catch (error) {
      return 'Unknown Device';
    }
  }

  /**
   * Get client IP (placeholder - would be handled server-side)
   */
  private async getClientIP(): Promise<string> {
    try {
      // In a real implementation, this would be handled server-side
      // For client-side, we'll use a placeholder
      return 'client-side';
    } catch (error) {
      return 'unknown';
    }
  }

  /**
   * Store session in localStorage
   */
  private storeSession(session: SessionInfo): void {
    try {
      localStorage.setItem(this.SESSION_STORAGE_KEY, JSON.stringify({
        sessionId: session.sessionId,
        userId: session.userId,
        createdAt: session.createdAt.toISOString(),
        lastActivity: session.lastActivity.toISOString(),
        expiresAt: session.expiresAt.toISOString(),
        isActive: session.isActive,
        deviceInfo: session.deviceInfo,
        ipAddress: session.ipAddress
      }));
    } catch (error) {
      sentryService.addBreadcrumb(
        'Failed to store session in localStorage',
        'system',
        'warning'
      );
    }
  }

  /**
   * Get stored session from localStorage
   */
  private getStoredSession(): SessionInfo | null {
    try {
      const stored = localStorage.getItem(this.SESSION_STORAGE_KEY);
      if (!stored) return null;

      const parsed = JSON.parse(stored);
      return {
        sessionId: parsed.sessionId,
        userId: parsed.userId,
        createdAt: new Date(parsed.createdAt),
        lastActivity: new Date(parsed.lastActivity),
        expiresAt: new Date(parsed.expiresAt),
        isActive: parsed.isActive,
        deviceInfo: parsed.deviceInfo,
        ipAddress: parsed.ipAddress
      };
    } catch (error) {
      this.destroyLocalSession();
      return null;
    }
  }

  /**
   * Destroy local session data
   */
  private destroyLocalSession(): void {
    try {
      localStorage.removeItem(this.SESSION_STORAGE_KEY);
      localStorage.removeItem(this.ACTIVITY_STORAGE_KEY);
    } catch (error) {
      // Ignore errors
    }
  }

  /**
   * Throttle function calls
   */
  private throttle<T extends (...args: any[]) => any>(
    func: T,
    limit: number
  ): (...args: Parameters<T>) => void {
    let inThrottle: boolean;
    return function(this: any, ...args: Parameters<T>) {
      if (!inThrottle) {
        func.apply(this, args);
        inThrottle = true;
        setTimeout(() => inThrottle = false, limit);
      }
    };
  }

  /**
   * Cleanup resources
   */
  destroy(): void {
    if (this.activityTimer) {
      clearInterval(this.activityTimer);
    }
  }
}

// Create singleton instance
export const sessionService = new SessionService();

/**
 * React hook for session management
 */
export const useSession = () => {
  const validateSession = () => sessionService.validateSession();
  const renewSession = () => sessionService.renewSession();
  const destroySession = (sessionId: string) => sessionService.destroySession(sessionId);
  const destroyAllSessions = (userId: string, except?: string) => 
    sessionService.destroyAllSessions(userId, except);
  const getUserSessions = (userId: string) => sessionService.getUserSessions(userId);

  return {
    validateSession,
    renewSession,
    destroySession,
    destroyAllSessions,
    getUserSessions
  };
};

export default sessionService;

import { createClient } from '@supabase/supabase-js';
import { Database } from '../types/supabase';
import { apiErrorHandler } from './services/apiErrorHandler';
import sentryService from './services/sentryService';
import { getSupabaseConfig, validateRequiredEnvVars, env } from './config/env';
import { preventMultipleSupabaseClients } from './utils/consoleErrorFixes';

// Prevent multiple client initialization
if (!preventMultipleSupabaseClients()) {
  throw new Error('Supabase client already initialized');
}

// Validate required environment variables
validateRequiredEnvVars(['VITE_SUPABASE_URL', 'VITE_SUPABASE_ANON_KEY']);

const config = getSupabaseConfig();

export const supabase = createClient<Database>(
  config.url,
  config.anonKey,
  {
    auth: {
      autoRefreshToken: true,
      persistSession: true,
      detectSessionInUrl: true
    },
    global: {
      headers: {
        'x-application-name': 'naazbookdepot',
        'x-app-version': env.VITE_APP_VERSION
      }
    },
    realtime: {
      params: {
        eventsPerSecond: 10
      }
    }
  }
);

// Enhanced Supabase error handler
export const handleSupabaseError = (error: any, operation: string, context?: any) => {
  return apiErrorHandler.handleSupabaseError(error, operation, {
    component: 'supabase',
    additionalData: context
  });
};

// Enhanced authentication helpers with error handling
export const signUp = async (email: string, password: string, fullName: string) => {
  return apiErrorHandler.handleApiCall(
    async () => {
      const { data, error } = await supabase.auth.signUp({
        email,
        password,
        options: {
          data: {
            full_name: fullName
          }
        }
      });

      if (error) {
        throw error;
      }

      sentryService.addBreadcrumb(
        'User signed up successfully',
        'auth',
        'info',
        { email }
      );

      return data;
    },
    'auth/signup',
    'POST',
    {
      action: 'user_signup',
      additionalData: { email }
    }
  );
};

export const signIn = async (email: string, password: string) => {
  return apiErrorHandler.handleApiCall(
    async () => {
      const { data, error } = await supabase.auth.signInWithPassword({
        email,
        password
      });

      if (error) {
        throw error;
      }

      sentryService.addBreadcrumb(
        'User signed in successfully',
        'auth',
