        { email }
      );

      // Set user context in Sentry
      if (data.user) {
        sentryService.setUserContext(data.user.id, data.user.email);
      }

      return data;
    },
    'auth/signin',
    'POST',
    {
      action: 'user_signin',
      additionalData: { email }
    }
  );
};

