      console.log(bundleAnalyzer.generateReport());
      
      const warnings = bundleAnalyzer.checkBundleWarnings();
      if (warnings.length > 0) {
        console.warn(' Bundle Warnings:');
        warnings.forEach(warning => console.warn(`  - ${warning}`));
      }
    }, 2000);
  });
}

