import React, { useState, useEffect } from 'react';
import { useQuery } from '@tanstack/react-query';
import { supabase } from '../integrations/supabase/client';
import { toast } from 'sonner';
import { handleApiError as handleApiErrorNew } from './consoleMigration';

// Global error handler for API calls
export const handleApiError = (error: unknown, customMessage?: string) => {
  handleApiErrorNew(error, 'api_call', { customMessage });
  
  let message = customMessage || 'Something went wrong';
  
  if (error?.message) {
    message = error.message;
  } else if (error?.error_description) {
    message = error.error_description;
  } else if (typeof error === 'string') {
    message = error;
  }
  
  // Don't show error toast for authentication errors in development
  if (process.env.NODE_ENV === 'development' && message.includes('JWT')) {
    return;
  }
  
  toast.error(message);
};

// Enhanced useQuery wrapper with error handling
export const useApiQuery = <T>(key: unknown[], queryFn: () => Promise<T>, options?: Record<string, unknown>) => {
  return useQuery({
    queryKey: key,
    queryFn: async () => {
      try {
        return await queryFn();
      } catch (error) {
        handleApiError(error);
        throw error;
      }
    },
    retry: (failureCount: number, error: { status?: number }) => {
      // Don't retry on authentication errors
      if (error?.status === 401 || error?.status === 403) {
        return false;
      }
      return failureCount < 2;
    },
    staleTime: 5 * 60 * 1000, // 5 minutes
    ...options
  });
};

// Loading component with skeleton
export const LoadingSpinner = ({ size = 'md', text }: { size?: 'sm' | 'md' | 'lg'; text?: string }) => {
  const sizeClasses = {
    sm: 'w-4 h-4',
    md: 'w-8 h-8',
    lg: 'w-12 h-12'
  };

  return (
    <div className="flex flex-col items-center justify-center p-8">
      <div className={`animate-spin rounded-full border-b-2 border-naaz-green ${sizeClasses[size]}`}></div>
      {text && <p className="mt-4 text-gray-600">{text}</p>}
    </div>
  );
};

// Error boundary hook
export const useErrorBoundary = () => {
  const [error, setError] = useState<Error | null>(null);

  const resetError = () => setError(null);

  const captureError = (error: Error) => {
    setError(error);
    handleApiError(error);
  };

  useEffect(() => {
    if (error) {
      throw error;
    }
  }, [error]);

  return { captureError, resetError };
};

// Form validation utilities
export const validateEmail = (email: string): boolean => {
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  return emailRegex.test(email);
};

export const validatePhone = (phone: string): boolean => {
  const phoneRegex = /^[6-9]\d{9}$/;
  return phoneRegex.test(phone);
};

export const validateRequired = (value: string): boolean => {
  return value.trim().length > 0;
};

// Debounce hook for search inputs
export const useDebounce = (value: string, delay: number) => {
  const [debouncedValue, setDebouncedValue] = useState(value);

  useEffect(() => {
    const handler = setTimeout(() => {
      setDebouncedValue(value);
    }, delay);

    return () => {
      clearTimeout(handler);
    };
  }, [value, delay]);

  return debouncedValue;
};

// Local storage hook with error handling
export const useLocalStorage = <T>(key: string, initialValue: T) => {
  const [storedValue, setStoredValue] = useState<T>(() => {
    try {
      const item = window.localStorage.getItem(key);
      return item ? JSON.parse(item) : initialValue;
    } catch (error) {
      import('./consoleMigration').then(({ handleApiError }) => {
        handleApiError(error, 'localStorage_read', { key });
      });
      return initialValue;
    }
  });

  const setValue = (value: T | ((val: T) => T)) => {
    try {
      const valueToStore = value instanceof Function ? value(storedValue) : value;
      setStoredValue(valueToStore);
      window.localStorage.setItem(key, JSON.stringify(valueToStore));
    } catch (error) {
      import('./consoleMigration').then(({ handleApiError: handleApiErrorNew }) => {
        handleApiErrorNew(error, 'localStorage_write', { key });
      });
      handleApiError(error, 'Failed to save data locally');
    }
  };

  return [storedValue, setValue] as const;
};

// Image loading hook with fallback
export const useImageLoader = (src: string, fallback: string) => {
  const [imageSrc, setImageSrc] = useState<string>(src);
  const [isLoading, setIsLoading] = useState<boolean>(true);
  const [hasError, setHasError] = useState<boolean>(false);

  useEffect(() => {
    setIsLoading(true);
    setHasError(false);
    setImageSrc(src);

    const img = new Image();
    img.onload = () => {
      setIsLoading(false);
    };
    img.onerror = () => {
      setIsLoading(false);
      setHasError(true);
      setImageSrc(fallback);
    };
    img.src = src;
  }, [src, fallback]);

  return { imageSrc, isLoading, hasError };
};

// Intersection Observer hook for lazy loading
export const useIntersectionObserver = (
  elementRef: React.RefObject<Element>,
  options?: IntersectionObserverInit
) => {
  const [isIntersecting, setIsIntersecting] = useState(false);

  useEffect(() => {
    const element = elementRef.current;
    if (!element) return;

    const observer = new IntersectionObserver(
      ([entry]) => {
        setIsIntersecting(entry.isIntersecting);
      },
      options
    );

    observer.observe(element);

    return () => {
      observer.unobserve(element);
    };
  }, [elementRef, options]);

  return isIntersecting;
};

// Supabase error handler
export const handleSupabaseError = (error: unknown, context?: string) => {
  if (!error) return;

  let message = 'An unexpected error occurred';
  
  if (error.message) {
    message = error.message;
  } else if (error.error_description) {
    message = error.error_description;
  }

  if (context) {
    message = `${context}: ${message}`;
  }

  import('./consoleMigration').then(({ handleDatabaseError }) => {
    handleDatabaseError(error, 'supabase_operation', { context });
  });
  toast.error(message);
};

// Safe async function wrapper
export const safeAsync = async <T>(
  asyncFn: () => Promise<T>,
  errorMessage?: string
): Promise<T | null> => {
  try {
    return await asyncFn();
  } catch (error) {
    handleApiError(error, errorMessage);
    return null;
  }
};

export default {
  handleApiError,
  useApiQuery,
  LoadingSpinner,
  useErrorBoundary,
  validateEmail,
  validatePhone,
  validateRequired,
  useDebounce,
  useLocalStorage,
  useImageLoader,
  useIntersectionObserver,
  handleSupabaseError,
  safeAsync
};

/**
 * Performance Optimization Utilities
 * Provides utilities for bundle optimization and performance monitoring
 */

// Preload critical routes for better performance
export const preloadCriticalRoutes = () => {
  if (typeof window !== 'undefined') {
    // Preload critical routes that users are likely to visit
    const criticalRoutes = [
      () => import('@/pages/Products'),
      () => import('@/pages/Cart'),
      () => import('@/pages/ProductPage'),
    ];

    // Use requestIdleCallback to preload during idle time
    if ('requestIdleCallback' in window) {
      window.requestIdleCallback(() => {
        criticalRoutes.forEach(route => {
          route().catch(() => {
            // Silently fail if preloading fails
          });
        });
      });
    } else {
      // Fallback for browsers without requestIdleCallback
      setTimeout(() => {
        criticalRoutes.forEach(route => {
          route().catch(() => {
            // Silently fail if preloading fails
          });
        });
      }, 2000);
    }
  }
};

// Optimize images with lazy loading and proper sizing
export const optimizeImageLoading = () => {
  if (typeof window !== 'undefined' && 'IntersectionObserver' in window) {
    const imageObserver = new IntersectionObserver((entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          const img = entry.target as HTMLImageElement;
          if (img.dataset.src) {
            img.src = img.dataset.src;
            img.removeAttribute('data-src');
            imageObserver.unobserve(img);
          }
        }
      });
    });

    // Observe all images with data-src attribute
    document.querySelectorAll('img[data-src]').forEach(img => {
      imageObserver.observe(img);
    });
  }
};

// Bundle size analyzer for development
export const analyzeBundleSize = () => {
  if (import.meta.env.DEV) {
    console.group(' Bundle Analysis');
    
    // Estimate chunk sizes based on loaded modules
    const loadedModules = performance.getEntriesByType('navigation');
    console.log('Navigation timing:', loadedModules);
    
    // Monitor resource loading
    const resources = performance.getEntriesByType('resource');
    const jsResources = resources.filter(r => r.name.includes('.js'));
    const cssResources = resources.filter(r => r.name.includes('.css'));
    
    console.log('JavaScript resources:', jsResources.length);
    console.log('CSS resources:', cssResources.length);
    
    // Calculate total transfer size
    const totalTransferSize = resources.reduce((total, resource) => {
      return total + (resource.transferSize || 0);
    }, 0);
    
    console.log(`Total transfer size: ${(totalTransferSize / 1024).toFixed(2)} KB`);
    console.groupEnd();
  }
};

// Cache optimization strategies
export const optimizeCaching = () => {
  if ('serviceWorker' in navigator && import.meta.env.PROD) {
    // Register service worker for caching in production
    navigator.serviceWorker.register('/sw.js').catch(() => {
      // Silently fail if service worker registration fails
    });
  }
};

// Memory usage monitoring
export const monitorMemoryUsage = () => {
  if (import.meta.env.DEV && 'memory' in performance) {
    const memory = (performance as any).memory;
    console.log('Memory usage:', {
      used: `${(memory.usedJSHeapSize / 1024 / 1024).toFixed(2)} MB`,
      total: `${(memory.totalJSHeapSize / 1024 / 1024).toFixed(2)} MB`,
      limit: `${(memory.jsHeapSizeLimit / 1024 / 1024).toFixed(2)} MB`
    });
  }
};

// Performance metrics collection
export const collectPerformanceMetrics = () => {
  if (typeof window !== 'undefined') {
    // Collect Core Web Vitals
    const observer = new PerformanceObserver((list) => {
      list.getEntries().forEach((entry) => {
        if (import.meta.env.DEV && entry.name.includes('LCP') || entry.name.includes('FID') || entry.name.includes('CLS')) {
          console.log(`${entry.name}: ${entry.value}`);
        }
        
        // In production, send to analytics service
        if (import.meta.env.PROD) {
          // Send to analytics service (implement based on your analytics provider)
          // analytics.track('performance_metric', {
          //   name: entry.name,
          //   value: entry.value,
          //   rating: entry.rating
          // });
        }
      });
    });

    // Observe different performance metrics
    try {
      observer.observe({ entryTypes: ['largest-contentful-paint', 'first-input', 'layout-shift'] });
    } catch (error) {
      // Some browsers might not support all entry types
      console.warn('Performance observer not fully supported:', error);
    }
  }
};

// Initialize all performance optimizations
export const initializePerformanceOptimizations = () => {
  preloadCriticalRoutes();
  optimizeImageLoading();
  optimizeCaching();
  
  if (import.meta.env.DEV) {
    analyzeBundleSize();
    monitorMemoryUsage();
  }
  
  collectPerformanceMetrics();
};

// Export individual functions for selective use
export default {
  preloadCriticalRoutes,
  optimizeImageLoading,
  analyzeBundleSize,
  optimizeCaching,
  monitorMemoryUsage,
  collectPerformanceMetrics,
  initializePerformanceOptimizations
};

/**
 * Tree shaking utilities and dead code elimination helpers
 */

interface ModuleUsage {
  module: string;
  exports: string[];
  usedExports: Set<string>;
  unusedExports: string[];
}

class TreeShakingAnalyzer {
  private moduleUsage: Map<string, ModuleUsage> = new Map();
  private isEnabled: boolean;

  constructor() {
    this.isEnabled = import.meta.env.DEV;
  }

  /**
   * Track module export usage
   */
  trackExportUsage(moduleName: string, exportName: string): void {
    if (!this.isEnabled) return;

    const usage = this.moduleUsage.get(moduleName) || {
      module: moduleName,
      exports: [],
      usedExports: new Set(),
      unusedExports: []
    };

    usage.usedExports.add(exportName);
    this.moduleUsage.set(moduleName, usage);
  }

  /**
   * Register all exports for a module
   */
  registerModuleExports(moduleName: string, exports: string[]): void {
    if (!this.isEnabled) return;

    const usage = this.moduleUsage.get(moduleName) || {
      module: moduleName,
      exports: [],
      usedExports: new Set(),
      unusedExports: []
    };

    usage.exports = exports;
    usage.unusedExports = exports.filter(exp => !usage.usedExports.has(exp));
    this.moduleUsage.set(moduleName, usage);
  }

  /**
   * Get unused exports report
   */
  getUnusedExports(): ModuleUsage[] {
    return Array.from(this.moduleUsage.values())
      .filter(usage => usage.unusedExports.length > 0);
  }

  /**
   * Generate tree shaking report
   */
  generateTreeShakingReport(): string {
    const unusedExports = this.getUnusedExports();
    
    let report = ' Tree Shaking Analysis\n';
    report += '========================\n\n';

    if (unusedExports.length === 0) {
      report += ' No unused exports detected!\n';
      return report;
    }

    report += `Found ${unusedExports.length} modules with unused exports:\n\n`;

    unusedExports.forEach(usage => {
      report += ` ${usage.module}\n`;
      report += `   Used: ${Array.from(usage.usedExports).join(', ') || 'none'}\n`;
      report += `   Unused: ${usage.unusedExports.join(', ')}\n\n`;
    });

    return report;
  }
}

/**
 * Utility functions for better tree shaking
 */
export const treeShakingUtils = {
  /**
   * Create a tree-shakable export wrapper
   */
  createTreeShakableExport: <T>(
    factory: () => T,
    condition: boolean | (() => boolean) = true
  ): T | null => {
    const shouldInclude = typeof condition === 'function' ? condition() : condition;
    return shouldInclude ? factory() : null;
  },

  /**
   * Conditional feature loading
   */
  loadFeature: async <T>(
    featureName: string,
    loader: () => Promise<T>,
    condition: boolean | (() => boolean) = true
  ): Promise<T | null> => {
    const shouldLoad = typeof condition === 'function' ? condition() : condition;
    
    if (!shouldLoad) {
      return null;
    }

    try {
      return await loader();
    } catch (error) {
      import('./consoleMigration').then(({ logError }) => {
        logError(`Failed to load feature: ${featureName}`, { error });
      });
      return null;
    }
  },

  /**
   * Environment-specific imports
   */
  loadForEnvironment: async <T>(
    environment: 'development' | 'production' | 'test',
    loader: () => Promise<T>
  ): Promise<T | null> => {
    const currentEnv = import.meta.env.MODE;
    
    if (currentEnv !== environment) {
      return null;
    }

    return await loader();
  },

  /**
   * Feature flag based imports
   */
  loadWithFeatureFlag: async <T>(
    flagName: string,
    loader: () => Promise<T>,
    defaultValue: boolean = false
  ): Promise<T | null> => {
    // Check environment variable
    const envFlag = import.meta.env[`VITE_FEATURE_${flagName.toUpperCase()}`];
    const isEnabled = envFlag ? envFlag === 'true' : defaultValue;

    if (!isEnabled) {
      return null;
    }

    return await loader();
  }
};

/**
 * Dead code elimination helpers
 */
export const deadCodeElimination = {
  /**
   * Mark code as development-only (will be removed in production)
   */
  devOnly: <T>(code: () => T): T | undefined => {
    if (import.meta.env.DEV) {
      return code();
    }
    return undefined;
  },

  /**
   * Mark code as production-only
   */
  prodOnly: <T>(code: () => T): T | undefined => {
    if (import.meta.env.PROD) {
      return code();
    }
    return undefined;
  },

  /**
   * Conditional code execution
   */
  when: <T>(condition: boolean, code: () => T): T | undefined => {
    if (condition) {
      return code();
    }
    return undefined;
  },

  /**
   * Debug-only code (removed in production builds)
   */
  debug: (code: () => void): void => {
    if (import.meta.env.DEV && import.meta.env.VITE_DEBUG === 'true') {
      code();
    }
  }
};

/**
 * Bundle size optimization helpers
 */
export const bundleOptimization = {
  /**
   * Lazy load heavy dependencies
   */
  lazyLoadDependency: async <T>(
    dependencyName: string,
    loader: () => Promise<T>
  ): Promise<T> => {
    try {
      return await loader();
    } catch (error) {
      throw new Error(`Failed to load dependency: ${dependencyName}`);
    }
  },

  /**
   * Split vendor chunks intelligently
   */
  getVendorChunkName: (id: string): string | undefined => {
    if (!id.includes('node_modules')) return undefined;

    // Core React ecosystem
    if (id.includes('react') || id.includes('react-dom')) {
      return 'vendor-react';
    }

    // UI libraries
    if (id.includes('@radix-ui') || id.includes('lucide-react')) {
      return 'vendor-ui';
    }

    // Data libraries
    if (id.includes('@supabase') || id.includes('@tanstack')) {
      return 'vendor-data';
    }

    // Utility libraries
    if (id.includes('date-fns') || id.includes('clsx') || id.includes('zod')) {
      return 'vendor-utils';
    }

    return 'vendor-misc';
  },

  /**
   * Check if module should be externalized
   */
  shouldExternalize: (id: string): boolean => {
    // Externalize large libraries that are commonly available via CDN
    const externalizable = [
      'react',
      'react-dom',
      'lodash',
      'moment'
    ];

    return externalizable.some(lib => id.includes(lib));
  }
};

// Create analyzer instance
export const treeShakingAnalyzer = new TreeShakingAnalyzer();

// Development utilities
if (import.meta.env.DEV) {
  (window as any).treeShakingAnalyzer = treeShakingAnalyzer;
  
  // Generate report after page load
  window.addEventListener('load', () => {
    setTimeout(() => {
      console.log(treeShakingAnalyzer.generateTreeShakingReport());
    }, 3000);
  });
}

export default treeShakingAnalyzer;

import React, { useState, useEffect } from 'react';
